-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1111','Przedstawiciele władz publicznych','Przedstawiciele władz publicznych określają, formułują i wpływają na politykę państwa oraz kierują jej realizacją na szczeblu krajowym, samorządowym lub międzynarodowym; kontrolują prace rządu i jego agend oceniając wykonanie budżetu państwa i realizację zadań przedstawionych parlamentowi do akceptacji; oceniają funkcjonowanie administracji państwowej; reprezentują interesy wyborców i działają w ich imieniu, dysponując środkami materialnymi na realizację zleconych przez państwo zadań. Do grupy tej należą parlamentarzyści (poseł, senator), politycy (premier, minister, prezydent) oraz przedstawiciele władzy samorządowej (marszałek, prezydent miasta, burmistrz, starosta, radny).','Zadania wykonywane przez przedstawicieli władz publicznych zazwyczaj obejmują: przewodniczenie lub uczestnictwo w działaniach organów ustawodawczych i administracji publicznej oraz określanie, formułowanie polityki władz na szczeblu krajowym, samorządowym i międzynarodowym; opiniowanie projektów aktów prawnych przedstawionych przez rząd, ugrupowania poselskie lub związkowe, występowanie z własnymi inicjatywami ustawodawczymi, uchwalanie ustaw i wydawanie aktów wykonawczych do ustaw; analizowanie sytuacji politycznej, społecznej i gospodarczej w państwie i w poszczególnych dziedzinach jego funkcjonowania; formułowanie programów rozwoju państwa; udział w pracach komisji powoływanych przez władze administracyjne; przygotowywanie projektów budżetów państwa i jednostek samorządowych, ich uchwalanie i kontrolowanie ich wykonania; kierowanie, nadzorowanie i kontrolowanie działalności organów, urzędów i jednostek administracji rządowej i samorządowej oraz sprawowanie nadzoru pod względem zgodności ich działania z przepisami prawa; badanie kwestii istotnych dla społeczeństwa i propagowane interesów wyborców, których reprezentują; uczestniczenie w uroczystościach i spotkaniach z różnymi grupami społecznymi, poznawania opinii publicznej i udzielania informacji na temat planów rządowych; prowadzenie negocjacji z innymi parlamentarzystami w celu pogodzenia różniących się interesów oraz tworzenia polityki i porozumień; kierowanie, nadzorowanie i kontrolowanie działalności organów, urzędów i jednostek administracji rządowej i samorządowej oraz sprawowanie kontroli pod względem zgodności ich działania z przepisami prawa. Do ich zadań może także należeć kierowanie działaniami urzędników organów administracji publicznej i samorządowej, agencji rządowych w zakresie interpretowania i wdrażania polityki państwa.',1);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1112','Wyżsi urzędnicy administracji publicznej','Wyżsi urzędnicy administracji publicznej kierują jednostkami organizacyjnymi struktury administracyjnej na szczeblu centralnym, lokalnym i samorządowym; nadzorują interpretowanie i wdrażanie polityki rządowej i prawodawstwa przez organy administracji publicznej; reprezentują kraj poza jego granicami i działają w jego imieniu; planują, organizują, nadzorują, kontrolują i oceniają działalność organów administracji publicznej na szczeblu lokalnym, wojewódzkim, regionalnym i krajowym, zgodnie z prawodawstwem i polityką ustanowionymi przez organy administracji publicznej i organy ustawodawcze; doradzają organom administracji publicznej w kwestiach politycznych. Grupa ta obejmuje na przykład sekretarzy generalnych w kancelariach: sejmu, senatu, rządu, prezydenta, podsekretarzy stanu, dyrektorów generalnych w ministerstwach oraz wyższych urzędników placówek dyplomatycznych.','Zadania wykonywane przez wyższych urzędników administracji publicznej zazwyczaj obejmują: kierowanie określoną jednostką struktury organizacyjnej, przygotowywanie projektów ustaw lub projektów decyzji oraz zmian do nich; ustanawianie celów organów administracji publicznej lub agencji rządowych zgodnie z prawodawstwem i polityką państwa; formułowanie lub zatwierdzanie i ocenianie programów oraz procedur dotyczących wdrażania polityki państwa w oparciu o wspólne działania i konsultacje z rządem; dokonywanie przeglądu, ocenianie i zatwierdzanie dokumentów, akt i sprawozdań prezentowanych przez przedstawicieli władz samorządowych oraz instytucji centralnych; opracowywanie odpowiednich procedur i wytycznych do przeprowadzania kontroli budżetu; koordynowanie działań z innymi urzędnikami wyższego szczebla w administracji publicznej; przedstawianie do oceny programów politycznych i budżetu komisjom legislacyjnym i komitetom rządowym; pomoc w interpretowaniu i wdrażaniu polityki i prawodawstwa państwa przez organy administracji publicznej i agencje rządowe. Do ich zadań może także należeć kontrolowanie i ocenianie pracy podległych pracowników.',2);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1113','Wyżsi urzędnicy władz samorządowych','Wyżsi urzędnicy władz samorządowych kierują pracą urzędów oraz jednostek organizacyjnych samorządu terytorialnego na szczeblu województwa, powiatu, miasta, gminy; podejmują decyzje wynikające z upoważnień ustawowych oraz poleceń otrzymywanych od zwierzchniej władzy samorządowej; wykonują zadania i obowiązki o charakterze ustawodawczym, administracyjnym i przedstawicielskim, w oparciu o tradycje oraz podział praw i obowiązków między władzami na szczeblu lokalnym (powiat, miasto, gmina), wojewódzkim, regionalnym oraz krajowym; reprezentują interesy obywateli, społeczności lokalnych wobec organów administracji państwowej, jednostek gospodarczych i innych organizacji; działają na rzecz mieszkańców (gminy, miasta, powiatu) ponosząc bezpośrednią odpowiedzialność za swoje działania w środowisku lokalnym. Grupa ta obejmuje na przykład sołtysów, wójtów, kierowników wydziałów urzędu miasta, powiatu, gminy.','Zadania wykonywane przez wyższych urzędników władz samorządowych zazwyczaj obejmują: podejmowanie decyzji w zakresie działania urzędu lub jednostki, którymi kierują; gospodarowanie jej mieniem (przydzielanie prawa do użytkowania wspólnych terenów i innych zasobów, rozstrzyganie problemów i konfliktów między członkami społeczności lokalnych); pełnienie obowiązków przedstawicielskich w ważnych wydarzeniach; reprezentowanie społeczności podczas posiedzeń rad na szczeblu lokalnym (gminy, miasta, powiatu), wojewódzkim i regionalnym; informowanie społeczności o zasadach i przepisach ustanowionych przez sejm i rząd. Do ich zadań może także należeć kontrolowanie i ocenianie pracy podległych pracowników.',3);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1114','Zawodowi działacze organizacji członkowskich','Zawodowi działacze organizacji członkowskich określają, formułują i wdrażają politykę, program, zasady i przepisy organizacji członkowskich takich jak: partie polityczne, związki zawodowe, stowarzyszenia i organizacje społeczne i zawodowe, organizacje pracodawców, zrzeszenia handlu i przemysłu, organizacje pozarządowe (humanitarne lub charytatywne), związki sportowe; kierują wdrażaniem tej polityki oraz reprezentują ich organizacje i działają w ich imieniu. Grupa ta obejmuje na przykład zawodowych działaczy organizacji pracodawców, organizacji politycznej, organizacji pozarządowej, organizacji związkowej.','Zadania wykonywane przez zawodowych działaczy organizacji członkowskich zazwyczaj obejmują: określanie i formułowanie polityki, zasad i przepisów organizacji; kierowanie jednostkami organizacyjnymi; planowanie, koordynowanie i dokonywanie oceny działalności oraz wyników organizacji; prowadzenie negocjacji w imieniu organizacji, jej członków i odpowiednich grup społecznych; wspieranie interesów organizacji oraz jej członków wobec władzy ustawodawczej, wykonawczej i społeczeństwa; przedstawianie sprawozdań radom i zarządom organów zarządczych, członkom organizacji i agencjom finansującym; kierowanie wydziałami i sekcjami organizacji; wdrażanie polityki, programów, zasad i przepisów organizacji; przygotowywanie procedur dotyczących przeprowadzania kontroli budżetowej; monitorowanie i ocenianie wyników organizacji lub przedsiębiorstwa; reprezentowanie organizacji podczas oficjalnych wydarzeń, posiedzeń zarządów, negocjacji, kongresów, otwartych spotkań i forów. Do ich zadań może także należeć kontrolowanie i ocenianie pracy podległych pracowników.',4);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1120','Dyrektorzy generalni i zarządzający','Dyrektorzy generalni i zarządzający kierują przedsiębiorstwami i organizacjami; formułują politykę, planują, nadzorują, koordynują i oceniają główne funkcje przedsiębiorstwa lub organizacji przy wsparciu kadry kierowniczej, mając na uwadze cele i zadania określone przez organ założycielski, radę nadzorczą, przed którymi odpowiadają za podejmowane działania i ich wyniki. Grupa ta obejmuje na przykład dyrektorów do spraw administracyjnych, do spraw badawczo rozwojowych, personalnych, finansowych, handlowych, marketingu, rozwoju biznesu, sprzedaży, technicznych, wykonawczych, prezesów, rektorów, syndyków.','Zadania wykonywane przez dyrektorów generalnych i zarządzających zazwyczaj obejmują: działania dotyczące funkcjonowania przedsiębiorstwa lub organizacji w zakresie planowania, kierowania i koordynowania; dokonywanie przeglądu działań i wyników przedsiębiorstwa lub organizacji oraz przedstawianie sprawozdań radom dyrektorów i organom zarządzającym; określanie celów, strategii, polityki i programów przedsiębiorstw lub organizacji; zapewnianie ogólnego przywództwa i zarządzania przedsiębiorstwu lub organizacji; ustanawianie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; przydzielanie zasobów materialnych, ludzkich i finansowych do realizacji polityki i programów organizacyjnych; monitorowanie i ocenianie wyników organizacji lub przedsiębiorstwa pod kątem ustanowionych celów i polityki; przeprowadzanie konsultacji z podległymi pracownikami wyższego szczebla oraz dokonywanie przeglądu zaleceń i sprawozdań; reprezentowanie organizacji podczas oficjalnych wydarzeń, posiedzeń zarządów, negocjacji, kongresów, seminariów, otwartych spotkań wyjaśniających i forów; rekrutowanie lub akceptowanie rekrutacji pracowników wyższego szczebla; zapewnianie zgodności działalności organizacji z odpowiednim prawodawstwem i uregulowaniami. Do ich zadań może także należeć nadzorowanie innych pracowników.',5);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1211','Kierownicy do spraw finansowych','Kierownicy do spraw finansowych planują, nadzorują i koordynują działania finansowe przedsiębiorstwa lub organizacji w porozumieniu z kierownikami wyższego szczebla i kierownikami innych działów lub sekcji. Grupa ta obejmuje na przykład głównych księgowych, kierowników biur rachunkowych, działów finansowych, itp.','Zadania wykonywane przez kierowników do spraw finansowych zazwyczaj obejmują: planowanie, koordynowanie i nadzorowanie działań związanych z obsługą finansowo-księgową przedsiębiorstwa lub organizacji oraz kierowanie nimi; dokonywanie oceny sytuacji finansowej przedsiębiorstwa lub organizacji; planowanie wydatków i przeprowadzanie konsultacji dotyczących ich celowości z dyrektorem generalnym i kierownikami innych działów lub sekcji; ustalanie budżetów i zarządzanie nimi; kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów finansowych; określanie strategii i procedur pozyskiwania środków finansowych koniecznych do prowadzenia działalności gospodarczej i ich inwestowania; planowanie codziennych działań i kierowanie nimi; nadzorowanie rekrutacji, szkoleń i osiągnięć pracowników; reprezentowanie przedsiębiorstwa lub organizacji w kontaktach z organami zewnętrznymi. Do ich zadań może także należeć nadzorowanie innych pracowników.',6);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1212','Kierownicy do spraw zarządzania zasobami ludzkimi','Kierownicy do spraw zarządzania zasobami ludzkimi planują, nadzorują i koordynują politykę kadrową instytucji, przedsiębiorstwa, organizacji dotyczącą: rekrutacji pracowników, ich rozwoju zawodowego, szkoleń, awansów i wynagrodzeń oraz stosunków pracy, a także działań w zakresie bezpieczeństwa i higieny pracy. Grupa ta obejmuje na przykład kierowników działów kadrowo-płacowych, szkoleń, zarządzania zasobami ludzkimi, a także kierowników przedsiębiorstw świadczących usługi w tym zakresie.','Zadania wykonywane przez kierowników do spraw zarządzania zasobami ludzkimi zazwyczaj obejmują: planowanie i koordynowanie działań dotyczących polityki kadrowej przedsiębiorstwa lub organizacji; staży i praktyk zawodowych pracowników, stosunków pracy między pracodawcą a pracownikiem; planowanie i organizowanie procedur dotyczących rekrutacji i ścieżek rozwoju zawodowego pracowników: szkoleń, awansów, ocen okresowych, zwalniania pracowników: panowanie i organizowanie negocjacji oraz procedur dotyczących określania struktury i poziomu płac; nadzorowanie stanu bezpieczeństwa i higieny pracy oraz programów i działań w tym zakresie; planowanie i opracowywanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; nadzorowanie opracowania i wdrażania systemów informacji zarządczej; zapewnianie zgodności ze standardami i przepisami prawnymi dotyczącymi praw pracowniczych, bezpieczeństwa i higieny pracy oraz równości szans pracowników; nadzorowanie rekrutacji, szkoleń i osiągnięć pracowników; przeprowadzanie konsultacji z kierownictwem wyższego szczebla i kierownikami innych działów w zakresie swoich kompetencji; reprezentowanie przedsiębiorstwa lub organizacji w kontaktach z organami zewnętrznymi; wykonywanie prac pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników oraz świadczenie usług w zakresie zarządzania zasobami ludzkimi na rzecz innych przedsiębiorstw i organizacji.',7);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1213','Kierownicy do spraw strategicznych i planowania','Kierownicy do spraw strategicznych i zarządzania planują, organizują, nadzorują i koordynują działania dotyczące doradztwa i planowania strategicznego w ministerstwach i agencjach rządowych lub w pozarządowych organizacjach i agencjach sektora prywatnego; zarządzają działaniami przedsiębiorstw, które świadczą usługi w zakresie spraw strategicznych i planowania. Grupa ta obejmuje na przykład dyrektorów departamentów, kierowników do spraw strategicznych i planowania, naczelników i kierowników wydziałów.','Zadania wykonywane przez kierowników do spraw strategicznych i planowania zazwyczaj obejmują: opracowywanie, wdrażanie i monitorowanie strategicznych planów, programów, strategii, procesów, systemów i procedur w celu osiągnięcia celów i wymaganych standardów pracy; opracowywanie badań i analiz strategicznych, kierowanie i zarządzanie tego rodzaju badaniami i analizami oraz uczestniczenie w ich realizacji; koordynowanie i wdrażanie zaplanowanych strategii i praktyk; kierowanie i zarządzanie działaniami pracowników zajmującymi się opracowywaniem strategii i planowaniem strategicznym; nadzorowanie rekrutacji, szkoleń i osiągnięć pracowników; reprezentowanie przedsiębiorstwa lub organizacji podczas negocjacji, kongresów, seminariów, otwartych spotkań wyjaśniających i forów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',8);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1219','Kierownicy do spraw obsługi biznesu i zarządzania gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą kierownicy do spraw obsługi biznesu i zarządzania gdzie indziej nie zaklasyfikowani w ramach grupy średniej 121 – Kierownicy do spraw obsługi biznesu i zarządzania. Grupa ta obejmuje na przykład osoby zajmujące się kierowaniem działów administracyjno-gospodarczych, małego przedsiębiorstwa obsługi biznesu, małego przedsiębiorstwa usług osobistych, porządkowych i pokrewnych.','Zadania wykonywane przez kierowników do spraw obsługi biznesu i zarządzania gdzie indziej niesklasyfikowanych zazwyczaj obejmują: zapewnianie kierownictwu wyższego szczebla wsparcia, badań i doradztwa administracyjnego, operacyjnego oraz w zakresie strategicznego planowania, w odniesieniu do kwestii takich, jak: zarządzanie obiektami i usługami administracyjnymi; tworzenie zasobów administracyjnych i materialnych organizacji oraz zarządzanie nimi; opracowywanie i wdrażanie wytycznych dotyczących spraw administracyjnych i proceduralnych do stosowania przez pracowników; analizowanie złożonych kwestii dotyczących zarządzania zasobami i inicjatyw mających wpływ na działalność organizacji oraz przygotowywanie sprawozdań, korespondencji i opinii na ten temat; udzielanie informacji i wsparcia w przygotowaniu sprawozdań finansowych i budżetów; kierowanie i zarządzanie kadrą administracyjną oraz dbanie o jej rozwój zawodowy w celu zapewniania sprawności działań biznesowych; ustalanie budżetów i zarządzanie nimi; kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; planowanie codziennych działań i kierowanie nimi; nadzorowanie rekrutacji, szkolenia i osiągnięć pracowników; reprezentowanie przedsiębiorstwa lub organizacji podczas negocjacji, kongresów, seminariów, otwartych spotkań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',9);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1221','Kierownicy do spraw marketingu i sprzedaży','Kierownicy do spraw sprzedaży i marketingu planują, nadzorują i koordynują działania dotyczące sprzedaży i marketingu prowadzone przez przedsiębiorstwo lub organizację świadczące usługi w tym zakresie na rzecz innych przedsiębiorstw i organizacji.','Zadania wykonywane w grupie kierowników do spraw marketingu i sprzedaży zazwyczaj obejmują: planowanie i przeprowadzanie badań marketingowych; prowadzenie prac związanych z rozwojem, testowaniem oraz wprowadzaniem nowych produktów i usług na rynek; określanie cen, metod sprzedaży i dystrybucji; planowanie, koordynowanie i organizowanie specjalnych programów dotyczących sprzedaży oraz programów marketingowych w oparciu o rejestry sprzedaży i oceny rynku; ustalanie cenników, warunków rabatów i dostaw oraz określanie budżetów przeznaczonych na promocje sprzedaży, metod sprzedaży, specjalnych zachęt i kampanii; ustanawianie procedur operacyjnych i administracyjnych związanych z działaniami w zakresie sprzedaży i marketingu oraz kierowanie tymi procedurami; kierowanie i zarządzanie działaniami pracowników w zakresie sprzedaży i marketingu; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; przygotowywanie raportów z zakresu prowadzonych działań dla potrzeb dyrekcji przedsiębiorstwa lub organizacji; reprezentowanie przedsiębiorstwa lub organizacji podczas kongresów dotyczących sprzedaży i marketingu, targów i wystaw handlowych i innych forów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',10);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1222','Kierownicy do spraw reklamy i public relations','Kierownicy do spraw reklamy i public relations planują, nadzorują i koordynują działania dotyczące reklamy, public relations oraz informacji publicznej prowadzonej przez przedsiębiorstwa lub organizacje świadczące usługi w tym zakresie na rzecz innych przedsiębiorstw i organizacji.','Zadania wykonywane w grupie kierowników do spraw reklamy i public relations zazwyczaj obejmują: planowanie i koordynowanie działalności przedsiębiorstwa lub organizacji w zakresie reklamy i public relations oraz kierowanie tą działalnością; negocjowanie umów o świadczenie usług reklamowych z klientami lub gazetami, stacjami radiowymi i telewizyjnymi, organizacjami sportowy i kulturalnymi oraz agencjami reklamowymi; planowanie programów informacyjnych i zarządzanie nimi w celu informowania parlamentarzystów, środków masowego przekazu i społeczeństwa o planach, osiągnięciach i opiniach przedsiębiorstwa lub organizacji; kierowanie i zarządzanie działaniami pracowników w zakresie reklamy i public relations; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; ustanawianie procedur administracyjnych i kierowanie nimi; planowanie codziennych działań i kierowanie nimi; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',11);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1223','Kierownicy do spraw badań i rozwoju','Kierownicy do spraw badań i rozwoju planują, nadzorują i koordynują działania dotyczące rozwoju oraz badań prowadzonych przez przedsiębiorstwo lub organizację świadczącą usługi w tym zakresie na rzecz innych przedsiębiorstw i organizacji.','Zadania wykonywane w grupie kierowników do spraw badań i rozwoju zazwyczaj obejmują: planowanie i koordynowanie działań w zakresie badań i rozwoju, wykonywanych wewnętrznie lub zleconych zewnętrznym organizacjom badawczym, a także kierowanie takimi działaniami w celu opracowania nowych lub poprawy istniejących procesów technicznych, produktów, wiedzy lub sposobów użytkowania materiałów; planowanie programu działań przedsiębiorstwa lub organizacji dotyczącego badań i rozwoju, określanie celów i wymagań budżetowych; kierowanie i zarządzanie działaniami pracowników w zakresie badań i rozwoju; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; ustanawianie procedur operacyjnych i administracyjnych i kierowanie nimi; planowanie codziennych działań i kierowanie nimi; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; reprezentowanie przedsiębiorstwa lub organizacji podczas kongresów, seminariów i konferencji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',12);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1311','Kierownicy produkcji w rolnictwie i leśnictwie','Kierownicy produkcji w rolnictwie i leśnictwie planują, nadzorują i koordynują działalność produkcyjną w ramach działań na szeroką skalę w zakresie rolnictwa, ogrodnictwa, leśnictwa takich jak: prowadzenie plantacji, dużych gospodarstw rolnych i spółdzielni rolniczych, prowadzenie właściwej gospodarki leśnej do celów uprawy i zbioru roślin uprawnych oraz terenów leśnych.','Zadania wykonywane w grupie kierowników produkcji w rolnictwie i leśnictwie zazwyczaj obejmują: monitorowanie działań podejmowanych na rynku rolnym i leśnym oraz planowanie produkcji w celu wywiązania się z zobowiązań umownych i zareagowania oraz odpowiedzenia na popyt na rynku; ustalanie budżetów i zarządzanie nimi, monitorowanie wielkości oraz kosztów produkcji, gromadzenie informacji dotyczących praktyk stosowanych w zarządzaniu gospodarstwem oraz przygotowywanie sprawozdań finansowych i sprawozdań z działalności gospodarstw rolnych i leśnych; prowadzenie rozmów z nabywcami w celu ustalenia warunków sprzedaży plonów; zawieranie umów z rolnikami lub niezależnymi właścicielami w sprawie produkcji upraw rolnych i leśnych oraz zarządzania ich produkcją; planowanie rodzaju, intensywności i kolejności prac w gospodarstwie rolnym lub leśnym (np. określanie najlepszego terminu sadzenia i opryskiwania zbiorów); analizowanie gleby w celu określenia rodzajów i ilości nawozów wymaganych do maksymalnej produkcji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',13);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1312','Kierownicy produkcji w zakładach akwakultury i rybołówstwie','Kierownicy produkcji w zakładach akwakultury i rybołówstwie planują, nadzorują i koordynują działalność na szeroką skalę w zakresie akwakultury i rybołówstwa w celu łowienia oraz dokonywania głównego odłowu sezonowego i hodowli ryb, skorupiaków i mięczaków i innych organizmów wodnych, jako produkt dochodowy lub w celu ich uwolnienia do wód słodkich lub słonych.','Zadania wykonywane w grupie kierowników produkcji w zakładach akwakultury i rybołówstwie zazwyczaj obejmują: monitorowanie działań podejmowanych na rynku upraw wodnych i rybołówstwa oraz planowanie produkcji i działalności w zakresie rybołówstwa w celu wywiązania się z zobowiązań umownych i zareagowania na popyt na rynku; ustalanie budżetów i zarządzanie nimi, monitorowanie wielkości i kosztów produkcji, gromadzenie informacji o praktykach stosowanych w zarządzaniu rybołówstwem oraz przygotowywanie sprawozdań finansowych z działalności gospodarstwa; prowadzenie rozmów z nabywcą w celu ustalenia warunków sprzedaży plonów i połowów; zwieranie umów z kapitanami statków rybackich lub właścicielami statków i gospodarstw akwakultury dotyczących połowów lub zarządzania produkcją; przeprowadzanie i organizowanie badań zasobów akwakultury i stad rybnych w celu zidentyfikowania chorób lub pasożytów; opracowywanie i koordynowanie działań w celu zwiększenia wylęgu i wskaźników wzrostu hodowli ryb oraz zapobiegania rozwojowi chorób w stacjach wylęgowych; monitorowanie środowiska hodowli ryb w celu utrzymania i poprawy warunków życia wodnego; nadzorowanie i monitorowanie przetrzymywania ryb i miotania ikry, inkubacji jaj i hodowli narybku oraz stosowanie wiedzy dotyczącej zarządzania i technik hodowli ryb; koordynowanie wyboru i utrzymywania lęgów ryb; nadzorowanie i monitorowanie przemieszczania dojrzałych ryb do jezior, stawów, potoków lub basenów do celów handlowych; kupowanie maszyn, urządzeń i zasobów, takich jak: statki i sieci; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników akwakultury i rybołówstwa oraz wykonawców; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',14);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1321','Kierownicy do spraw produkcji przemysłowej','Kierownicy do spraw produkcji przemysłowej planują, nadzorują i koordynują działalność w produkcji przetwórczej dużych przedsiębiorstw lub małych firm przetwórczych.','Zadania wykonywane w grupie kierowników do spraw produkcji przemysłowej zazwyczaj obejmują: określanie, realizowanie i monitorowanie strategii, polityki i planów dotyczących produkcji; szczegółowe planowanie działalności produkcyjnej pod kątem jakości i wielkości, kosztu i dostępnego czasu produkcji oraz wymogów dotyczących siły roboczej; kontrolowanie eksploatacji zakładu produkcyjnego i procedur jakości przez planowanie konserwacji maszyn i urządzeń, wyznaczanie czasu pracy oraz dostarczanie części zamiennych i narzędzi; ustalanie budżetów i zarządzanie nimi, monitorowanie wielkości i kosztów produkcji oraz dostosowywanie procedur i zasobów w celu ograniczenia kosztów do minimum; nadzorowanie zakupu i instalowania nowych maszyn i urządzeń; kontrolowanie przygotowywania ewidencji produkcji i sprawozdań z produkcji; koordynowanie wdrażania wymogów dotyczących bezpieczeństwa i higieny pracy; określanie możliwości biznesowych i ustalanie produktów, które mają być produkowane; badanie i wdrażanie wymogów regulacyjnych i statutowych mających wpływ na procesy wytwarzania i środowisko naturalne; nadzorowanie sporządzania wyceny za produkcję towarów specjalistycznych oraz zawieranie umów z klientami i dostawcami; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',15);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1322','Kierownicy w górnictwie','Kierownicy w górnictwie planują, nadzorują i koordynują działalność produkcyjną w zakresie głębinowej i odkrywkowej eksploatacji złóż oraz wydobycia ropy i gazu.','Zadania wykonywane w grupie kierowników w górnictwie zazwyczaj obejmują: określenia miejsc wydobycia złóż oraz wyceny ich wydobycia w konsultacji z innymi z przełożonymi; opracowywanie strategii usuwania wydobytych surowców; ocenianie wydajności kopalni w celu określenia przydatności pracowników, stosowanych urządzeń i technologii oraz wprowadzenia zmian w programie prac wydobywczych lub zmian dotyczących instalowanych urządzeń; planowanie działalności produkcyjnej pod kątem jakości i wielkości, kosztów i dostępnego czasu produkcji oraz wymogów dotyczących siły roboczej; kontrolowanie eksploatacji zakładu produkcyjnego i procedur jakości przez planowanie konserwacji, wyznaczanie czasu wykonywanych zadań oraz dostarczanie urządzeń; ustalanie budżetów i zarządzanie nimi, monitorowanie wielkości i kosztów produkcji oraz dostosowywanie procedur i zasobów w celu ograniczenia kosztów; nadzorowanie zakupu i instalowania nowych maszyn i urządzeń; kontrolowanie przygotowywania ewidencji produkcji i sprawozdań z produkcji; koordynowanie wdrażania wymogów dotyczących bezpieczeństwa i higieny pracy; badanie i wdrażanie wymogów regulacyjnych i statutowych mających wpływ na procesy wytwarzania i środowisko naturalne; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',16);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1323','Kierownicy do spraw budownictwa','Kierownicy do spraw budownictwa planują, nadzorują i koordynują realizację projektów i budowę budynków mieszkalnych i innych obiektów w zakresie inżynierii lądowej.','Zadania wykonywane w grupie kierowników do spraw budownictwa zazwyczaj obejmują: interpretowanie rysunków i specyfikacji architektonicznych; kierowanie zespołem podległych pracowników; kierowanie budową zgodnie z projektem, pozwoleniem na budowę oraz obowiązującymi przepisami prawa; prowadzenie dokumentacji budowy; nabywanie i dostarczanie materiałów, instalacji i urządzeń; prowadzenie negocjacji z właścicielami budynków, deweloperami i podwykonawcami zaangażowanymi w proces budowy w celu zapewnienia zakończenia realizacji projektu w terminie oraz w ramach zaplanowanego budżetu; przygotowywanie ofert przetargowych oraz ofert na zamówienia publiczne; uruchamianie i realizowanie skoordynowanych programów pracy na terenach budowy; zapewnianie zgodności z przepisami prawa budowlanego oraz standardami wykonania, jakości, kosztów i bezpieczeństwa; przedstawianie planów władzom lokalnym; realizowanie budowy w ramach umowy lub zawieranie umów o podwykonawstwo wyspecjalizowanych usług budowlanych; organizowanie inspekcji budowlanych przeprowadzanych przez właściwe organy; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; uczestniczenie w czynnościach odbioru obiektu; nadzorowanie przebiegu rekrutacji, szkoleń oraz osiągnięć pracowników i podwykonawców; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',17);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1324','Kierownicy do spraw logistyki i dziedzin pokrewnych','Kierownicy do spraw logistyki i dziedzin pokrewnych planują, nadzorują i koordynują dostawy, transport, przechowywanie i dystrybucję towarów.','Zadania wykonywane w grupie kierowników do spraw logistyki i dziedzin pokrewnych zazwyczaj obejmują: określanie, wdrażanie i monitorowanie strategii, polityki i planów dotyczących zakupu, przechowywania i dystrybucji towarów; planowanie, koordynowanie i nadzorowanie działań przedsiębiorstwa lub organizacji dotyczących zaopatrzenia, gospodarki magazynowej i dystrybucji; przygotowywanie i wdrażanie planów w celu utrzymania wymaganego poziomu zapasów; negocjowanie umów z dostawcami w celu spełnienia wymogów jakości, kosztów i dostawy; monitorowanie i dokonywanie przeglądu systemów przechowywania i inwentaryzacji towarów; kontrolowania poziomu zapasów; planowanie i wdrażanie systemów kontroli magazynowej; kontrolowanie wydatków i zapewnianie efektywnego wykorzystania zasobów; określanie i nadzorowanie stosowania procedur operacyjnych i administracyjnych; organizowanie prac związanych z przyjmowaniem i dystrybucją towarów, ich przemieszczaniem i magazynowaniem; analizowanie bieżących stanów zapasów magazynowych, harmonizowanie terminów dostaw i dystrybucji towarów; planowanie i wdrażanie systemów kontroli magazynowej; kontrolowanie wydatków i zapewnianie efektywnego wykorzystania zasobów; określanie i nadzorowanie stosowania procedur operacyjnych i administracyjnych; planowanie codziennych działań i kierowanie nimi; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; przygotowywanie raportów z zakresu prowadzonych działań dla potrzeb przedsiębiorstwa lub organizacji; reprezentowanie swojej jednostki organizacyjnej wobec innych jednostek w przedsiębiorstwie lub wobec instytucji zewnętrznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',18);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1330','Kierownicy do spraw technologii informatycznych i telekomunikacyjnych','Kierownicy do spraw technologii informatycznych i telekomunikacyjnych planują, nadzorują i koordynują zakup, rozwój, utrzymanie i użytkowanie systemów komputerowych i telekomunikacyjnych.','Zadania wykonywane w grupie kierowników do spraw technologii informatycznych i telekomunikacyjnych zazwyczaj obejmują: planowanie, koordynowanie i nadzorowanie działań związanych z tworzeniem, rozwojem i utrzymaniem systemu informatycznego i telekomunikacyjnego w przedsiębiorstwie lub organizacji; przeprowadzanie konsultacji z użytkownikami, kierownictwem, sprzedawcami i technikami w celu oceny potrzeb w zakresie dokonywania obliczeń i wymogów dotyczących systemu oraz określanie technologii w celu spełnienia tych potrzeb; formułowane i nadzorowanie strategii, polityki i planów dotyczących technologii informacyjno-komunikacyjnych (TIC); nadzorowanie wyboru i instalacji zasobów TIC oraz organizowanie szkoleń dla użytkowników; kierowanie operacjami TIC, analizowanie przepływu pracy, ustalanie priorytetów, opracowywanie standardów oraz wyznaczanie terminów; nadzorowanie bezpieczeństwa systemów TIC; przydzielanie i dokonywanie przeglądu prac analityków systemów, programistów i innych pracowników do spraw urządzeń komputerowych oraz zarządzanie i kierowanie tymi pracami; ocenianie wykorzystywania technologii przez organizację i jej potrzeb w tym zakresie oraz wydawanie zaleceń dotyczących usprawnień, takich jak: wprowadzanie nowych wersji sprzętu komputerowego i oprogramowania; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; ustanawianie procedur operacyjnych i administracyjnych oraz kierowanie nimi; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; reprezentowanie przedsiębiorstwa lub organizacji podczas kongresów, seminariów i konferencji dotyczących TIC; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',19);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1341','Kierownicy w instytucjach opieki nad dziećmi','Kierownicy w instytucjach opieki nad dziećmi planują, nadzorują, koordynują i oceniają świadczenie opieki nad dziećmi w czasie przed i po zajęciach programowych, podczas wakacji i w ośrodkach opieki dziennej w przedszkolach, żłobkach, wioskach dziecięcych, w centrach rozrywki.','Zadania wykonywane w grupie kierownikach w instytucjach opieki nad dziećmi zazwyczaj obejmują: opracowywanie i realizowanie programów zapewniających wzrost rozwoju fizycznego, społecznego, emocjonalnego i intelektualnego dzieci; ustalanie i monitorowanie budżetów oraz planowanie przydziału środków przeznaczonych na płace pracowników, wyżywienie dzieci, zaopatrzenie w pomoce dydaktyczne i materiały, wyposażenie i konserwację urządzeń do zabawy oraz nauki dzieci: nadzorowanie i koordynowanie świadczenia opieki nad dziećmi w czasie, przed i po zajęciach programowych oraz w ośrodkach i centrach opieki dziennej i wakacyjnej; kierowanie działaniami opiekunów dzieci w zakresie zapewniania opieki i nadzoru nad małymi dziećmi oraz nadzorowanie tych działań; zarządzanie infrastrukturą fizyczną i zapewnianie konserwacji wszystkich budynków i wyposażenia w taki sposób, aby ośrodki były bezpiecznym obszarem dla dzieci, pracowników i odwiedzających; dokonywanie przeglądu i interpretacji przepisów państwowych oraz opracowywanie procedur w celu spełnienia tych przepisów (np. dotyczących bezpieczeństwa i ochrony); monitorowanie postępów dzieci oraz przeprowadzanie konsultacji z rodzicami i opiekunami; przygotowywanie i prowadzenie rejestrów i księgowości dla ośrodków opieki nad dziećmi; prowadzenie przebiegu rekrutacji i oceny pracowników oraz koordynowanie ich rozwoju zawodowego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',20);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1342','Kierownicy w instytucjach opieki zdrowotnej','Kierownicy w instytucjach opieki zdrowotnej planują, nadzorują, koordynują i oceniają świadczenie klinicznych i środowiskowych usług opieki zdrowotnej w szpitalach, klinikach, zakładach opieki zdrowotnej, hospicjach i innych placówkach opieki zdrowotnej.','Zadania wykonywane w grupie kierowników w instytucjach opieki zdrowotnej zazwyczaj obejmują: kierowanie i zarządzanie usługami, infrastrukturą, organizacją lub ośrodkiem opieki zdrowotnej; koordynowanie działalności personelu medycznego i pielęgniarskiego, pracowników technicznych, biurowych, usługowych i innych pracowników oraz nadzorowanie i ocenianie tych działań; ustanawianie celów i kryteriów ewaluacyjnych lub operacyjnych dla zarządzanych jednostek; nadzorowanie i przeprowadzanie rekrutacji, zatrudnianie i szkolenie pracowników; opracowywanie, wdrażanie i monitorowanie procedur, strategii i standardów dotyczących wyników pracy dla personelu medycznego, pielęgniarskiego, technicznego i administracyjnego; monitorowanie stopnia wykorzystywania urządzeń diagnostycznych miejsc w szpitalach, infrastruktury oraz kadry w celu zapewnienia efektywnego wykorzystywania zasobów i dokonania oceny zapotrzebowania na dodatkowych pracowników, wyposażenia placówek i świadczenia usług; kontrolowanie działań administracyjnych, takich jak: planowanie budżetu, przygotowywanie sprawozdań i dokonywanie wydatków na zaopatrzenie, wyposażenie i usługi; współpraca z innymi dostawcami usług zdrowotnych i socjalnych, zarządami i organami finansującymi w celu koordynowania świadczenia usług; doradzanie organom państwowym w zakresie środków służących polepszeniu usług i infrastruktury w obszarze opieki zdrowotnej i socjalnej; reprezentowanie organizacji podczas negocjacji, kongresów, seminariów, otwartych spotkań wyjaśniających i forów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',21);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1343','Kierownicy w instytucjach opieki nad osobami starszymi','Kierownicy w instytucjach opieki nad osobami starszymi planują, nadzorują i oceniają świadczenie usług w domach opieki spokojnej starości usług w zakresie opieki indywidualnej na rzecz osób indywidualnych oraz rodzin, potrzebujących tego rodzaju usług w związku ze skutkami starzenia.','Zadania wykonywane w grupie kierowników w instytucjach opieki nad osobami starszymi zazwyczaj obejmują: kierowanie i zarządzanie usługami, infrastrukturą, organizacją lub ośrodkiem; kierowanie działaniami personelu medycznego i pielęgniarskiego, pracowników technicznych, biurowych, usługowych i innych pracowników oraz nadzorowanie i ocenianie tych działań; ustanawianie celów i kryteriów ewaluacyjnych lub operacyjnych dla zarządzanych jednostek; nadzorowanie i przeprowadzanie rekrutacji, zatrudnianie oraz szkolenie pracowników; opracowywanie, wdrażanie i monitorowanie procedur, strategii oraz standardów dotyczących wyników pracy dla personelu pielęgniarskiego, technicznego i administracyjnego; koordynowanie programów socjalnych i usług w zakresie opieki dla osób starszych, a także zarządzanie nimi; kontrolowanie działań administracyjnych, takich jak: planowanie budżetu, przygotowywanie sprawozdań i dokonywanie wydatków; współpraca z innymi dostawcami usług zdrowotnych i socjalnych, zarządami i organami finansującymi w celu koordynowania świadczenia usług; doradzanie organom państwowym i samorządowym w zakresie środków służących polepszeniu usług i infrastruktury w obszarze opieki zdrowotnej i socjalnej; reprezentowanie organizacji podczas negocjacji, kongresów, seminariów, otwartych spotkań wyjaśniających i forów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',22);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1344','Kierownicy w instytucjach pomocy społecznej','Kierownicy w instytucjach pomocy społecznej planują, nadzorują i koordynują realizację programów dotyczących usług socjalnych i środowiskowych w domach dziecka, domach opieki społecznej, w ośrodkach pomocy rodzinie, takich jak: zasiłki dla osób o niskich dochodach, pomoc rodzinna, usługi w zakresie opieki nad dziećmi, osobami chorymi i starszymi i innego rodzaju usług.','Zadania wykonywane w grupie kierowników w instytucjach pomocy społecznej zazwyczaj obejmują: kierowanie i zarządzania usługami, infrastrukturą, organizacją lub ośrodkiem; opracowywanie, wdrażanie i monitorowanie procedur, strategii i standardów pracy pracowników; monitorowanie i ocenianie zasobów przeznaczonych na świadczenie usług w zakresie: opieki socjalnej, mieszkalnictwa, innych usług socjalnych; kontrolowanie działań administracyjnych, takich jak: planowanie budżetu, przygotowywanie sprawozdań, dokonywanie wydatków na zaopatrzenie, wyposażenie i usługi; współpraca z innymi dostawcami usług zdrowotnych i opieki socjalnej, zarządami, organami finansującymi w celu omówienia obszarów współpracy w zakresie świadczonych usług; doradzanie organom państwowym w obszarze opieki socjalnej; kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; ustanawianie procedur operacyjnych i administracyjnych oraz kierowanie nimi; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; reprezentowanie organizacji podczas kongresów, seminariów i konferencji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',23);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1345','Kierownicy w instytucjach edukacyjnych','Kierownicy w instytucjach edukacyjnych planują, nadzorują, koordynują i oceniają aspekty edukacyjne i administracyjne usług edukacyjnych w szkołach podstawowych, gimnazjach, szkołach ponadgimnazjalnych oraz wyższych.','Zadania wykonywane w grupie kierowników w instytucjach edukacyjnych zazwyczaj obejmują: określanie programów edukacyjnych w oparciu o ramy ustanowione przez władze oświatowe i organy państwowe; wdrażanie systemów i procedur w celu monitorowania wyników osiąganych w szkołach oraz naboru uczniów i studentów; nadzorowanie działań administracyjnych dotyczących rekrutacji i naboru uczniów i studentów oraz realizowania usług edukacyjnych; kontrolowanie działań administracyjnych, takich jak: planowanie budżetu, przygotowywanie sprawozdań i dokonywanie wydatków na zaopatrzenie, wyposażenie i usługi; zapewnianie wytycznych i wskazówek nauczycielom, wykładowcom, pracownikom administracyjnym oraz uczniom i studentom; ocenianie pracy nauczycieli i wykładowców poprzez hospitacje podczas lekcji, obserwację metod nauczania oraz badanie materiałów dydaktycznych; promowanie programu edukacyjnego, usługi edukacyjnej lub instytucji edukacyjnej większej społeczności; nadzorowanie konserwacji infrastruktury edukacyjnej; opracowywanie i egzekwowane kodeksu postępowania w celu stworzenia bezpiecznego i sprzyjającego nauce środowiska dla uczniów i nauczycieli; planowanie i realizowanie metod gromadzenia dodatkowych funduszy wspólnie z rodzicami, społecznością lokalną i sponsorami; kontrolowanie przebiegu rekrutacji i szkoleń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',24);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1346','Kierownicy w instytucjach finansowych i ubezpieczeniowych','Kierownicy w instytucjach finansowych i ubezpieczeniowych planują, nadzorują i koordynują funkcjonowanie oddziałów instytucji świadczących usługi finansowe i ubezpieczeniowe, takich jak: banki, agencje doradztwa finansowego, towarzystwa i agencje ubezpieczeniowe; zapewniają doradztwo i pomoc klientom w sprawach finansowych i ubezpieczeniowych.','Zadania wykonywane w grupie kierowników w instytucjach finansowych i ubezpieczeniowych zazwyczaj obejmują: planowanie, nadzorowanie i koordynowanie działań pracowników instytucji; nawiązywanie i utrzymywanie stosunków z klientami indywidualnymi i biznesowymi; zapewnianie klientom doradztwa i pomocy w zakresie ich potrzeb finansowych i ubezpieczeniowych; badanie, ocenianie i rozpatrywanie wniosków o pożyczkę i ubezpieczenie; monitorowanie decyzji w sprawie przedłużenia kredytu; przeprowadzanie dochodzeń finansowych; nadzorowanie przepływu środków pieniężnych i instrumentów finansowych oraz przygotowywanie sprawozdań finansowych i regulacyjnych; zatwierdzania lub odrzucania linii pożyczkowych w odniesieniu do kredytów komercyjnych, kredytów hipotecznych oraz pożyczek indywidualnych; koordynowanie współpracy z innymi oddziałami przedsiębiorstwa; zarządzanie budżetami, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; nadzorowanie przebiegu rekrutacji, szkoleń i osiągnięć pracowników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',25);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1349','Kierownicy w instytucjach usług wyspecjalizowanych gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą kierownicy w instytucjach usług wyspecjalizowanych nie zaklasyfikowani do grupy średniej 134 Kierownicy w instytucjach usług wyspecjalizowanych. Grupa ta obejmuje na przykład osoby, które planują, nadzorują i koordynują działaniami realizowanymi przez instytucje zajmujące się: wychowaniem, edukacją i opieką penitencjarną, ochroną osób i mienia, obsługą prawną, zarządzaniem jakości w przedsiębiorstwach.','Zadania wykonywane w grupie kierowników w instytucjach usług wyspecjalizowanych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: planowanie, nadzorowanie i koordynowanie działalności prowadzonej przez organy administracji rządowej i samorządowej w sferze: finansowej, podatkowej, gospodarczej, kulturalnej, edukacji, wychowania, opieki penitencjarnej, opieki prawnej, ochrony osób i mienia. Do zadań wykonywanych w tej grupie można także zaliczyć: koordynowanie działalności związanej z wymiarem sprawiedliwości oraz dotyczącej porządku i bezpieczeństwa publicznego; kierowanie i koordynowanie działalności zakładów karnych oraz zakładów wychowawczych dla nieletnich; kierowanie i koordynowanie pracy agencji ochrony mienia i osób; świadczenie usług audytorskich w zakresie zarządzania jakością w przedsiębiorstwie; prowadzenie kancelarii prawnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',26);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1411','Kierownicy w hotelarstwie','Kierownicy w hotelarstwie planują, organizują i nadzorują działalność hoteli, moteli i pensjonatów, które świadczą usługi noclegowe i inne usługi towarzyszące.','Zadania wykonywane w grupie kierowników w hotelarstwie zazwyczaj obejmują: kierowanie działaniami w zakresie rezerwacji, przyjmowania gości, obsługi pokojowej i utrzymywania porządku oraz nadzorowanie tych działań; nadzorowanie uzgodnień w zakresie bezpieczeństwa oraz utrzymywania ogrodu i konserwacji budynku; planowanie działań związanych z prowadzeniem baru, restauracji, organizowaniem uroczystości i konferencji oraz nadzorowanie tych działań; przestrzeganie przepisów dotyczących alkoholu, gier hazardowych i ochrony zdrowia oraz innych przepisów i uregulowań; ocenianie i dokonywanie przeglądu poziomu zadowolenia klientów; nadzorowanie działań w zakresie księgowości i kupna; planowanie budżetu przedsiębiorstwa; kontrolowanie rekrutacji, szkolenia i nadzoru pracowników; zapewnianie zgodności działalności lokalu z obowiązującymi przepisami bhp i ppoż.; udzielanie gościom informacji na temat turystyki lokalnej oraz organizowanie wycieczek i przewozów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',27);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1412','Kierownicy w gastronomii','Kierownicy w gastronomii planują, organizują i nadzorują działalność restauracji, kawiarni, barów, pubów i innych lokali gastronomicznych, które świadczą usługi restauracyjne i cateringowe.','Zadania wykonywane w grupie kierowników w gastronomii zazwyczaj obejmują: planowanie karty dań wspólnie z szefami kuchni i kucharzami; planowanie i organizowane specjalnych uroczystości; planowanie zakupu i ustalanie cen towarów zgodnie z budżetem; prowadzenie rejestru poziomu zapasów i transakcji finansowych; zapewnienie zgodności pomieszczeń restauracyjnych, kuchni i pomieszczeń do przechowywania żywności z obowiązującymi przepisami bezpieczeństwa i higieny pracy, funkcjonalności oraz odpowiedniego wyglądu; przeprowadzanie rozmów z klientami w celu oceny ich zadowolenia z posiłku i obsługi; rekrutowanie, szkolenie i nadzorowanie personelu kelnerskiego i kuchennego oraz ustalanie harmonogramu ich pracy; przyjmowanie rezerwacji, witanie gości i pomaganie w przyjmowaniu zamówień; negocjowanie warunków z klientami i dostawcami; zapewnianie zgodności działalności lokalu z obowiązującymi przepisami bhp i ppoż.; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',28);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1420','Kierownicy w handlu detalicznym i hurtowym','Kierownicy w handlu detalicznym i hurtowym planują, organizują, koordynują i kontrolują działalność przedsiębiorstw, które prowadzą detaliczną lub hurtową sprzedaż towarów. Są odpowiedzialni za budżet, personel oraz strategiczny i operacyjny nadzór hurtowni, sklepów lub działów organizacyjnych w ramach sklepów i supermarketów, które prowadzą sprzedaż określonych rodzajów produktów.','Zadania wykonywane w grupie kierowników w handlu detalicznym i hurtowym zazwyczaj obejmują: określanie produktów przeznaczonych do sprzedaży, poziomu zapasów towaru i standardów usług; formułowanie i wdrażanie strategii zakupów i strategii marketingowej oraz ustalanie cen; promowanie i reklamowanie towarów i usług; prowadzenie rejestru poziomu zapasów i transakcji finansowych; planowanie budżetu przedsiębiorstwa; kontrolowanie przebiegu rekrutacji, szkoleń i nadzoru pracowników; zapewnianie zgodności z przepisami bhp i ppoż. Do tej grupy zaklasyfikowani zostali również kierowników dużych przedsiębiorstw detalicznych takich jak supermarkety i domy i galerie handlowe. Kierownicy do spraw handlu detalicznego i hurtowego są często zatrudniani w stosunkowo małych przedsiębiorstwach, które nie posiadają hierarchii kierownictwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',29);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1431','Kierownicy do spraw sportu, rekreacji i rozrywki','Kierownicy do spraw sportu, rekreacji i rozrywki, organizują i kontrolują działalność przedsiębiorstw, które świadczą usługi sportowe, artystyczne, teatralne oraz inne usługi kulturalne, rekreacyjne, rozrywkowe i sportowe na rzecz środowiska w bibliotekach, teatrach, kinach, klubach fitness, muzeach, klubach sportowych itp.','Zadania wykonywane w grupie kierowników do spraw sportu, rekreacji i rozrywki zazwyczaj obejmują: planowanie i sporządzanie oferty działalności dotyczącej organizowania rozrywek, atrakcji i wydarzeń kulturalnych, programów sportowych i fitness, które mają być proponowane przez ośrodek; zapewnianie utrzymania infrastruktury w czystości i dobrym stanie technicznym; zapoznawanie się z aktualnymi informacjami na temat nowych tendencji i kierunków rozwoju w zakresie sztuki kreatywnej; organizowanie przedstawień teatralnych, koncertów zespołów muzycznych i orkiestr, wystaw; doradzanie w zakresie dostępnej infrastruktury i propagowanie kampanii reklamowych w związku z wydarzeniami, pokazami i działaniami; sprawdzanie i nadzorowanie wszystkich wpływów pieniężnych oraz przeprowadzanie regularnych kontroli zapasów; ustalanie budżetów i zarządzanie nimi, kontrolowanie wydatków i zapewnianie efektywnego wykorzystywania zasobów; planowanie codziennych działań i kierowanie nimi; kontrolowanie rekrutacji, nadzoru i osiągnięć pracowników; zapewnianie zgodności z przepisami bezpieczeństwa i higieny; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',30);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('1439','Kierownicy do spraw innych typów usług gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą kierownicy do spraw innych typów usług nie zaklasyfikowani do grupy średnie 143 Kierownicy do spraw innych typów usług. Grupa ta obejmuje na przykład osoby, które planują, nadzorują i koordynują świadczenie w zakresie usług: biur podróży, centrów konferencyjnych, handlowych, obsługi telefonicznej, biur tłumaczeń, firm windykacyjnych, firm porządkowych, firm zajmujących się obsługą kempingów.','Zadania wykonywane w grupie kierowników do spraw innych typów usług gdzie indziej niesklasyfikowanych zazwyczaj obejmują: planowanie, nadzorowanie i koordynowanie działalności: biur podróży; biur tłumaczeń; centrów handlowych, centrów konferencyjnych, firm windykacyjnych, sprzątających oraz zajmujących się obsługą kempingów, parkingów, itp. Kierownicy z tej grupy odpowiadają za wdrażanie polityki firmy; wykonywanie szacunków finansowych planowanej działalności; dokonywanie rekrutacji pracowników i ich szkolenie; prowadzenie negocjacji z kontrahentami i klientami; planowanie i kontrolę wykorzystania środków i zasobów firmy; kierowanie bieżącą działalnością firmy; organizowanie i nadzorowanie pracy podległych pracowników; nadzorowanie stosowania przepisów bhp, ppoż. oraz ochrony środowiska; reprezentowanie firmy wobec instytucji zewnętrznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',31);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2111','Fizycy i astronomowie','Fizycy i astronomowie prowadzą badania oraz ulepszają lub opracowują koncepcje, teorie i metody operacyjne dotyczące materii, przestrzeni kosmicznej, czasu, energii, sił i pól fizycznych, a także wzajemnych powiązań między zjawiskami fizycznymi. Stosują oni wiedzę naukową z dziedziny fizyki i astronomii w obszarze przemysłowym, medycznym, wojskowym i innych obszarach.','Zadania wykonywane w grupie fizyków i astronomów zazwyczaj obejmują: prowadzenie badań i ulepszanie lub opracowywanie koncepcji, teorii, instrumentarium, oprogramowania i metod operacyjnych w obszarze fizyki i astronomii; przeprowadzanie doświadczeń, testów i analiz w zakresie struktury i cech materii w obszarach takich, jak: mechanika, termodynamika, elektronika, komunikacja, produkcja i dystrybucja energii, aerodynamika, optyka i lasery, teledetekcja, medycyna, akustyka techniczna, magnetyzm i fizyka jądrowa; ocenianie wyników badań i doświadczeń oraz formułowanie wniosków, głównie za pomocą technik i modeli matematycznych; stosowanie zasad, technik i procesów w celu opracowania lub ulepszenia przemysłowych, medycznych, wojskowych i innych praktycznych zastosowań zasad i technik fizycznych oraz astronomicznych; zapewnianie bezpiecznego i skutecznego stosowania promieniowania u pacjentów do celów diagnostycznych lub terapeutycznych, zgodnie z zaleceniem lekarza; testowanie, oddawanie do użytku i ocenianie urządzeń wykorzystywanych w zastosowaniach takich jak: obrazowanie, opieka medyczna i dozymetria; doradzanie lekarzom i innym pracownikom w zakresie optymalizowania równowagi między korzystnymi i szkodliwymi skutkami promieniowania; obserwowanie, analizowanie i interpretowanie zjawisk niebieskich oraz opracowywanie metod, modelów i technik numerycznych w celu zwiększenia wiedzy w dziedzinach takich, jak: nawigacja, komunikacja satelitarna, badanie przestrzeni kosmicznej, ciała niebieskie i promieniowanie kosmiczne; opracowywanie, wdrażanie i stosowanie standardów i protokołów dotyczących pomiaru zjawisk fizycznych oraz wykorzystywania technologii jądrowej w zastosowaniach przemysłowych i medycznych; przygotowywanie dokumentacji naukowej i sprawozdań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',32);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2112','Meteorolodzy','Meteorolodzy przygotowują krótko lub długoterminowe prognozy pogody wykorzystywane w lotnictwie, żegludze, rolnictwie i innych obszarach oraz do celów informowania szerokiej grupy odbiorców. Prowadzą badania dotyczące składu, struktury i dynamiki atmosfery.','Zadania wykonywane w grupie meteorologów zazwyczaj obejmują: badanie kierunku i prędkości ruchów powietrza, ciśnienia, temperatur, wilgotności, fizycznych i chemicznych oraz innych zjawisk takich, jak: formacje chmur i opady, anomalie elektryczne lub promieniowanie słoneczne; analizowanie danych gromadzonych przez stacje meteorologiczne, za pomocą radarów, obrazów satelitarnych i komputerowych modeli prognostycznych do celów przedstawiania i prognozowania warunków pogodowych; przygotowywanie i przedstawianie krótko i długoterminowych map pogodowych, prognoz i ostrzeżeń w związku ze zjawiskami atmosferycznymi takimi, jak; wichury, sztormy, powodzie i inne zjawiska stanowiące zagrożenia dla życia i mienia; przeprowadzanie doświadczeń w zakresie rozpraszania mgły, „siania chmur”, zwiększania opadów deszczu oraz innych rodzajów programów dotyczących modyfikacji pogody; opracowywanie i testowanie matematycznych oraz komputerowych modeli pogody i klimatu do celów doświadczalnych lub operacyjnych; uczestniczenie w badaniach dotyczących wpływu pogody na środowisko naturalne; analizowanie wpływu projektów przemysłowych i działalności człowieka na klimat i jakość powietrza oraz współpraca ze środowiskami specjalistów nauk społecznych, inżynierów i ekonomistów w celu opracowania odpowiednich strategii łagodzących; udział w projektowaniu i wprowadzaniu nowych urządzeń i procedur do celów gromadzenia danych meteorologicznych; ulepszanie lub opracowywanie koncepcji, teorii i metod operacyjnych dotyczących składu, struktury i dynamiki atmosfery oraz prowadzenie badań w tym zakresie; przygotowywanie dokumentacji naukowej i sprawozdań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',33);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2113','Chemicy','Chemicy prowadzą badania, ulepszają i opracowują koncepcje, teorie i metody działania oraz stosują wiedzę naukową z dziedziny chemii do celów uzyskania nowej wiedzy lub produktów i procesów przemysłowych oraz kontroli jakości i procesów.','Zadania wykonywane w grupie chemików zazwyczaj obejmują: prowadzenie badań oraz ulepszanie lub opracowywanie koncepcji, instrumentów, teorii i metod działania w dziedzinach związanych z chemią; przeprowadzanie doświadczeń, testów i analiz w celu zbadania składu chemicznego, energii chemicznej oraz zmian chemicznych zachodzących w różnych, naturalnych lub syntetycznych, substancjach, materiałach i produktach; opracowywanie procedur służących do kontroli środowiska, kontroli jakości i innych procedur dla producentów i użytkowników; realizowanie programów dotyczących gromadzenia i analizowania prób i danych w celu zidentyfikowania i ilościowego określenia środowiskowych substancji trujących; uczestniczenie w interdyscyplinarnych badaniach naukowych i projektach rozwojowych we współpracy z inżynierami chemii, biologami, mikrobiologami, agronomami, geologami lub innymi specjalistami; wykorzystywanie mikroorganizmów w celu przekształcania substancji w nowe związki chemiczne; określanie sposobów wzmacniania lub łączenia materiałów oraz opracowywanie nowych materiałów; odtwarzanie i syntetyzowanie naturalnie występujących substancji oraz tworzenie nowych sztucznych substancji; przygotowywanie dokumentacji naukowej i sprawozdań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',34);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2114','Specjaliści nauk o Ziemi','Specjaliści nauk o Ziemi prowadzą badania, ulepszają lub opracowują koncepcje, teorie i metody działania oraz stosują wiedzę naukową z zakresu: geografii, geologii i geofizyki, hydrologii, oceanologii w obszarach takich, jak: poszukiwanie i wydobycie ropy, gazu i kopalin, ochrona wody; inżynieria lądowa; telekomunikacja i nawigacja oraz dokonywanie oceny i łagodzenie wpływu projektów dotyczących rozwoju i utylizacji odpadów na środowisko naturalne.','Zadania wykonywane w grupie specjalistów nauk o Ziemi zazwyczaj obejmują: prowadzenie badań oraz ulepszanie lub opracowywanie koncepcji, teorii i metod operacyjnych w obszarze geologii, geofizyki, hydrologii, oceanologii; analizowanie składu i budowy skorupy ziemskiej, badanie skał, minerałów, skamielin i innych materiałów w celu określenia procesów wpływających na rozwój Ziemi, określania przebiegu ewolucji organizmów żywych w przeszłości, ustalania charakteru i chronologii formacji geologicznych oraz dokonywanie oceny ich zastosowań w praktyce; interpretowanie danych z badań i przygotowywanie geologicznych sprawozdań, map, tabel, a także wykresów, raportów i innych dokumentów; stosowanie wiedzy geologicznej do celów rozwiązywania problemów napotykanych podczas realizacji projektów inżynierii lądowej takich, jak: budowa tam, mostów, tuneli, dużych budynków oraz projektów melioracyjnych; stosowanie różnych programów w zakresie teledetekcji do celów badania i dokonywania pomiarów sił sejsmicznych, grawitacyjnych, elektrycznych, termicznych i magnetycznych oddziaływujących na Ziemię; szacowanie wagi, wielkości i masy Ziemi oraz składu i struktury jej wnętrza, badanie cech, aktywności i przewidywalności wulkanów, lodowców i trzęsień ziemi; wytyczanie rozkładu ziemskiego pola magnetycznego oraz stosowanie tych informacji i innych zebranych danych do celów nadawczych, nawigacyjnych i innych celów; badanie i dokonywanie pomiarów cech fizycznych mórz i atmosfery oraz występujących pomiędzy nimi powiązań takich, jak: wymiana energii cieplnej; lokalizowanie i określanie cech i wielkości pokładów ropy, gazu i kopalin za pomocą metod sejsmologicznych, grawimetrycznych, magnetycznych, elektrycznych lub radiometrycznych; identyfikowanie pokładów materiałów budowlanych oraz określanie ich cech i adekwatności do wykorzystania jako kruszywo betonowe, nawierzchni drogowej lub do innych zastosowań; badanie ruchów, występowania i cech fizycznych wód gruntowych i powierzchniowych; doradzanie w obszarach takich, jak: zarządzanie odpadami, wybieranie tras i lokalizacji oraz przywracanie skażonych terenów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',35);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2120','Matematycy, aktuariusze i pokrewni','Matematycy, aktuariusze i pokrewni prowadzą badania oraz ulepszają lub opracowują matematyczne i statystyczne koncepcje, teorie oraz modele i techniki operacyjne, a także doradzają, jak wykorzystywać w praktyce dorobek matematyki i statystyki w obszarach takich, jak: nauki techniczne, ekonomiczne, społeczne, medyczne i inne.','Zadania wykonywane w grupie matematyków, aktuariuszy i pokrewnych zazwyczaj obejmują: analizowanie, ulepszanie i opracowywanie matematycznych i statystycznych teorii i technik; doradzanie lub stosowanie zasad modeli i technik matematycznych do szerokiego wachlarza zadań w dziedzinie inżynierii, rolnictwa i medycyny; przeprowadzanie analiz logicznych problemów w zakresie zarządzania, pod kątem efektywności nakładów oraz formułowanie modeli matematycznych dla rozwiązywanego problemu; opracowywanie i wprowadzanie do praktyki programów emerytalnych, systemów ubezpieczeń życiowych, zdrowotnych, socjalnych i innych rodzajów ubezpieczeń; stosowanie matematyki, statystyki, rachunku prawdopodobieństwa i teorii ryzyka do oceny potencjalnych skutków finansowych przyszłych wydarzeń; opracowywanie kwestionariuszy, planowanie i organizowanie sondaży i innych metod gromadzenia danych statystycznych do przewidywania zdarzeń; ocenianie, przetwarzanie, analizowanie i interpretowanie danych statystycznych oraz ich publikowanie; doradzanie lub stosowanie różnych metod w zakresie: gromadzenia danych, metod i technik statystycznych, określania wiarygodności wyników, w szczególności, w takich obszarach jak: ekonomia, medycyna, nauk społecznych i przyrodniczych; przygotowywanie dokumentacji naukowej i sprawozdań z badań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',36);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2131','Biolodzy i pokrewni','Biolodzy i specjaliści nauk pokrewnych badają żywe organizmy oraz ich wzajemne interakcje i interakcje ze środowiskiem naturalnym, a także stosują tę wiedzę w celu rozwiązania problemów człowieka związanych ze zdrowiem i środowiskiem. Prowadzą badania w różnych dziedzinach takich, jak m.in.: antropologia, biochemia, biofizyka, biologia, biotechnologia, genetyka, mikrobiologia.','Zadania wykonywane w grupie biologów i pokrewnych zazwyczaj obejmują: prowadzenie badań w laboratoriach oraz w terenie w celu zwiększenia wiedzy naukowej na temat organizmów żywych, uzyskania nowych informacji, testowania hipotez, rozwiązywania problemów w obszarach takich, jak: środowisko naturalne, rolnictwo i zdrowie, a także w celu opracowania nowych produktów, procesów i technik do zastosowań farmaceutycznych, rolnych i środowiskowych; opracowywanie i przeprowadzanie doświadczeń i testów; gromadzenie próbek z organizmów ludzkich, z okazów zwierząt, owadów i roślin, badanie ich pochodzenia, rozwoju, formy chemicznej i fizycznej, struktury, składu oraz procesów życiowych i reprodukcyjnych; badanie organizmów żywych za pomocą różnorodnych specjalistycznych urządzeń, instrumentów, technologii i technik takich, jak: mikroskopy elektronowe, telemetria, system nawigacji satelitarnej, biotechnologia, inżynieria genetyczna, analiza obrazów cyfrowych, modelowanie komputerowe; identyfikowanie, klasyfikowanie, rejestrowanie i monitorowanie organizmów żywych oraz prowadzenie baz danych; sporządzanie dokumentów naukowych i sprawozdań, zawierających opisy wyników badań i nowych wniosków oraz ich udostępniane w środowiskom naukowym; planowanie i przeprowadzanie oceny skutków dla środowiska naturalnego w celu określenia zmian spowodowanych przez czynniki naturalne i ludzkie; zapewnianie doradztwa władzom państwowym, organizacjom i przedsiębiorstwom w obszarach takich, jak: ochrona zasobów naturalnych i zarządzanie nimi, skutki zmian klimatu i zanieczyszczeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',37);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2132','Specjaliści w zakresie rolnictwa, leśnictwa i pokrewni','Specjaliści w zakresie rolnictwa, leśnictwa i pokrewni prowadzą badania oraz zapewniają pomoc i doradztwo w zakresie zarządzania gospodarstwem rolnym, lasem i rybołówstwem, w zakresie upraw, nawożenia, zbioru plonów, erozji i składu gleby, zapobiegania chorobom, odżywiania, rotacji upraw, zootechniki, klasyfikacji gruntów i rzeczoznawstwa rolniczego. Opracowują techniki służące do zwiększania wydajności oraz badają i opracowują plany i strategie w zakresie zarządzania ziemią rybołówstwem.','Zadania wykonywane w grupie specjalistów w zakresie rolnictwa, leśnictwa i pokrewnych zazwyczaj obejmują: gromadzenie i analizowanie danych i prób związanych z plonami, paszą, glebą, jakością wody i innymi czynnikami wpływającymi na produkcję rolną, leśną i w zakresie rybołówstwa; doradzanie w zakresie technik służących poprawie produkcji roślin uprawnych, zwierząt hodowlanych i ryb oraz alternatywnych możliwości produkcji; doradzanie w zakresie programów dotyczących chorób zwierząt hodowlanych i roślin uprawnych, kontroli szkodników i chwastów, hodowli i żywienia zwierząt; badanie czynników środowiskowych wpływających na produkcję plonów o przeznaczeniu handlowym, uprawę pasz, hodowlę zwierząt, stada ryb oraz na wzrost i zdrowie drzew leśnych; badanie skutków technik uprawy, gleb, owadów, chorób i praktyk w zakresie rybołówstwa na produkcję zwierząt, roślin uprawnych, lasów i ryb; badanie migracji, wzrostu, żywienia i miotania ikry ryb oraz opracowywanie metod zbierania, zapładniania, inkubowania i wylęgania ikry; badanie cech gleby i jej wartości użytkowej oraz wydajności oraz stosowanie uzyskanych wyników do celów opracowywania ulepszonych praktyk w zakresie rolnictwa, ogrodnictwa i leśnictwa; opracowywanie procedur i technik do celów rozwiązywania problemów w zakresie rolnictwa i zwiększania wydajności produkcji; zarządzanie zasobami leśnymi i zasobami rybołówstwa w celu wykorzystania w sposób maksymalny ich długoterminowych korzyści handlowych, rekreacyjnych i środowiskowych; badanie rozmnażanie i uprawy drzew leśnych, metod wpływających na szybszy wzrost drzew oraz skutków przerzedzania lasów; badanie, planowanie i wdrażanie procedur zarządzania w celu zwalczania skutków wywoływanych przez pożary, powodzie, susze, erozję gleby, szkodniki i choroby; przygotowywanie sprawozdań naukowych oraz przeprowadzanie sesji doradczo-informacyjnych i wykładów dla środowisk związanych z rolnictwem, leśnictwem i rybołówstwem oraz dla innych zainteresowanych środowisk; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',38);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2133','Specjaliści do spraw ochrony środowiska','Specjaliści do spraw ochrony środowiska badają i oceniają wpływ skutków działalności człowieka na środowisko naturalne, takich jak: zanieczyszczenie powietrza i wody, hałas, skażenie gleb, zmiana klimatu, odpady toksyczne oraz wyczerpanie i degradacja zasobów naturalnych. Opracowują plany i rozwiązania w celu ochrony i przywracania środowiska naturalnego oraz ograniczania do minimum i uniknięcia dalszych szkód.','Zadania wykonywane w grupie specjalistów do spraw ochrony środowiska zazwyczaj obejmują: prowadzenie badań, wykonywanie testów, gromadzenie próbek, przeprowadzanie analiz w terenie oraz w laboratorium w celu określenia źródeł problemów związanych z ochroną środowiska; zalecanie sposobów zapobiegania negatywnym skutkom degradacji środowiska, ich kontroli i naprawy; wydawanie zaleceń w sprawie możliwości kontynuowania działań zapobiegającym degradacji środowiska; planowanie i koordynowanie wdrażania systemów zarządzania środowiskiem w celu umożliwienia organizacjom określania, monitorowania i kontrolowania wpływu ich działalności, produktów i usług na stan środowiska; przeprowadzanie audytów w celu oceny oddziaływania prowadzonej działalności na środowisko, procesów, wytwarzanych odpadów, hałasu i substancji toksycznych; ocenianie zgodności działań organizacji z państwowymi i wewnętrznymi przepisami i wytycznymi dotyczącymi ochrony środowiska, identyfikowanie naruszenia przepisów dotyczących ochrony środowiska oraz ustalanie odpowiednich działań naprawczych; zapewnianie przedsiębiorstwo i organizacjom usług dotyczących doradztwa i wsparcia technicznego w zakresie najlepszych sposobów rozwiązywania problemów związanych z ochroną środowiska w celu zmniejszenia szkód środowiskowych i ograniczenia do minimum strat finansowych; opracowywanie planów ochrony środowiska; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',39);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2141','Inżynierowie do spraw przemysłu i produkcji','Inżynierowie do spraw przemysłu i produkcji prowadzą badania oraz projektują, organizują i nadzorują budowę, funkcjonowanie i konserwację zakładów przetwórczych i instalacji. Opracowują programy koordynacji produkcji i oceniają ich opłacalność oraz bezpieczeństwo.','Zadania wykonywane w inżynierów do spraw przemysłu i produkcji zazwyczaj obejmują: analizę struktur organizacyjnych zakładów przemysłowych i informacji o projektowanych zmianach w celu określenia funkcji i zadań pracowników i zespołów jednostek roboczych; opracowywanie programów pomiarów pracy i analizowanie próbek pracy w celu określenia standardów wykorzystania potencjału siły roboczej; analizę wykorzystania siły roboczej, rozplanowania obiektu, danych operacyjnych i planów produkcji oraz kosztów w celu określenia optymalnej wydajności pracowników i sprzętu; sporządzanie specyfikacji produkcji i określanie materiałów, sprzętu, instalacji rurowych, zdolności produkcyjnych zakładu; organizowanie i nadzorowanie prac projektowych i dostawy materiałów, instalacji i sprzętu; określenie standardów i zasad dotyczących instalacji, zmiany, kontroli jakości, testowania, inspekcji i konserwacji budowy zgodnie z zasadami inżynieryjnymi i przepisami dotyczącymi bezpieczeństwa; inspekcję zakładu w celu zwiększenia i utrzymania wydajności; nadzorowanie konserwacji budynków zakładu i sprzętu oraz koordynację wymogów dotyczących nowych projektów, badań i planów konserwacji; doradztwo dla zarządu w zakresie nowych metod i technik produkcji oraz sprzętu; współpracę z działami zajmującymi się zakupem, przechowywaniem i kontrolą materiałów w celu zapewnienia stałego strumienia dostaw; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',40);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2142','Inżynierowie budownictwa','Inżynierowie budownictwa lądowego i wodnego zajmują się projektowaniem i nadzorowaniem budowy oraz prowadzeniem badań i doradztwem w tym zakresie; nadzorowaniem funkcjonowania i konserwacji obiektów inżynierii lądowej i wodnej. Prowadzą badania i doradztwo w zakresie aspektów technologicznych poszczególnych materiałów używanych do budowy obiektów.','Zadania wykonywane w grupie inżynierów budownictwa lądowego i wodnego zazwyczaj obejmują: prowadzenie badań i opracowywanie nowych lub ulepszanie istniejących teorii i metod związanych z inżynierią lądową i wodną; projektowanie oraz doradztwo dotyczące takich obiektów jak: mosty, zapory, doki, drogi, lotniska, drogi kolejowe, kanały, rurociągi, systemy usuwania odpadów i ochrony przeciwpowodziowej, budynki przemysłowe i inne duże budowle; określanie metod budowy, materiałów i norm jakości oraz nadzorowanie prac budowlanych; tworzenie systemów kontroli w celu zapewnienia skutecznego funkcjonowania obiektów, jak również bezpieczeństwa i ochrony środowiska; organizowanie i nadzorowanie konserwacji i napraw istniejących obiektów inżynierii lądowej i wodnej; analizę zachowania gleby i skał pod naciskiem proponowanych do budowy obiektów i projektowanie fundamentów pod obiekty; badanie stabilności obiektów oraz zachowania i trwałości materiałów stosowanych do ich budowy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',41);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2143','Inżynierowie inżynierii środowiska','Inżynierowie inżynierii środowiska zajmują się projektowaniem, nadzorowaniem, prowadzeniem badań i doradztwem w zakresie projektowania i eksploatacji urządzeń dostarczających zaopatrzenie w wodę, ciepło, gaz, odprowadzanie i oczyszczanie ścieków, usuwanie odpadów itp. Zapobiegają, kontrolują lub naprawiają negatywne skutki działalności człowieka przy wykorzystaniu różnych dziedzin inżynierii środowiska; dokonują oceny możliwości rozpoczynania budowy urządzeń gazowych, instalacji sieci energetycznej, instalacji sanitarnych, systemów wodociągowych i kanalizacyjnych; stosują zasady inżynieryjne w odniesieniu do kontroli bezpieczeństwa jądrowego i ochrony radiologicznej oraz zanieczyszczeń, recyklingu i usuwania odpadów.','Zadania wykonywane w inżynierów inżynierii środowiska zazwyczaj obejmują: prowadzenie badań, ocenianie i sporządzanie sprawozdawczości dotyczącej wpływu trwającej i proponowanej budowy obiektów inżynierii lądowej i wodnej oraz innych działań na środowisko; kontrolę obiektów przemysłowych i komunalnych oraz programów w celu dokonania oceny skuteczności i zapewnienia zgodności z przepisami dotyczącymi ochrony środowiska; projektowanie i nadzorowanie procesów i sprzętu służących do kontroli jakości wody, powietrza lub gleby, zarządzania nią i jej rekultywacją; zapewnianie wsparcia inżynieryjnego i technicznego przy projektach rekultywacji środowiska i projektach, które są zaskarżane do sądów przez wskazywanie możliwości zastosowania regulacji prawnych w tych projektach; monitorowanie postępów programów poprawy stanu środowiska; świadczenie usług doradczych dla przedsiębiorstw i agencji rządowych dotyczących procedur mających na celu oczyszczanie zdegradowanych terenów, aby chronić ludzi i środowisko; współpraca z sozologami, planistami, technikami do spraw odpadów niebezpiecznych, inżynierami m.in. z takich dziedzin jak: inżynieria urządzeń gazowych, instalacji sieci energetycznej, instalacji sanitarnych, systemów wodociągowych i kanalizacyjnych, inżynierii kontroli bezpieczeństwa jądrowego i ochrony radiologicznej oraz specjalistami w dziedzinie prawa i biznesu w celu rozwiązywania problemów związanych ze środowiskiem; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',42);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2144','Inżynierowie mechanicy','Inżynierowie mechanicy zajmują się projektowaniem i nadzorowaniem produkcji samolotów, statków, maszyn i urządzeń energetycznych oraz instalacji przemysłowych i sprzętu; nadzorowaniem funkcjonowania urządzeń oraz ich konserwacją i naprawami. Prowadzą badania i doradztwo w zakresie aspektów mechanicznych poszczególnych materiałów, produktów lub procesów.','Zadania wykonywane w grupie inżynierów mechaników zazwyczaj obejmują: projektowanie maszyn i narzędzi do celów produkcji, górnictwa, budownictwa, rolnictwa i innych gałęzi gospodarki; projektowanie silników parowych, spalinowych i innych silników nieelektrycznych stosowanych do napędu lokomotyw, pojazdów drogowych, samolotów, maszyn przemysłowych i innych maszyn; projektowanie kadłubów, nadbudów i systemów napędu statków, instalacji przemysłowych i sprzętu do wyzwalania, kontroli i wykorzystania energii; systemów ogrzewania, wentylacji, urządzeń sterowych, pomp i innego sprzętu mechanicznego; projektowanie kadłubów samolotowych, podwozi i innego sprzętu samolotowego; projektowanie systemów zawieszenia, hamulców, wnętrz nadwozia i części pojazdów drogowych; projektowanie nieelektrycznych części urządzeń lub produktów, takich jak: komputery, przyrządy precyzyjne, aparaty fotograficzne i projektory; określanie standardów i procedur kontroli w celu zapewnienia wydajnego funkcjonowania i bezpieczeństwa urządzeń, maszyn, narzędzi, silników elektrycznych i spalinowych, instalacji przemysłowych, sprzętu lub systemów; zapewnienie zgodności sprzętu, działań i konserwacji ze specyfikacjami projektowymi i normami bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',43);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2145','Inżynierowie chemicy i pokrewni','Inżynierowie chemicy zajmują się opracowywaniem, nadzorowaniem, prowadzeniem badań i doradztwem w zakresie procesów chemicznych na skalę komercyjną oraz produkcją różnych substancji i produktów, takich jak: ropa naftowa, leki lub materiały syntetyczne. Opracowują technologie przetwarzana i utrwalania surowców roślinnych (zboża, surowce oleiste, owoce, warzywa, ziemniaki, buraki cukrowe, tytoń) i surowców pochodzenia zwierzęcego (mięso, ryby, mleko, jaja). Nadzorują konserwację i naprawę instalacji chemicznych i sprzętu oraz zajmują się badaniami i doradztwem w zakresie chemicznych aspektów poszczególnych materiałów, produktów lub procesów.','Zadania wykonywane w grupie inżynierów chemików zazwyczaj obejmują: prowadzenie badań i opracowywanie procesów chemicznych związanych z rafinacją ropy naftowej oraz innych cieczy i gazów na skalę komercyjną; produkcję różnych substancji i produktów, takich jak: ropa naftowa, leki lub materiały syntetyczne; określanie metod, materiałów i norm jakości produkcji chemicznej i zapewnianie ich zgodności ze specyfikacjami; określanie standardów i procedur kontroli w celu zapewnienia bezpieczeństwa i wydajności produkcji chemicznej oraz bezpieczeństwa pracowników obsługujących urządzenia lub pracujących w pobliżu miejsca, w którym przeprowadzane są reakcje chemiczne; projektowanie sprzętu do zakładów chemicznych i opracowywanie procesów produkcji chemikaliów i produktów; przeprowadzanie testów na różnych etapach produkcji w celu określenia stopnia kontroli zmiennych, w tym temperatury, gęstości, ciężaru właściwego i ciśnienia; opracowywanie procedur bezpieczeństwa; oszacowywanie kosztów i postępów produkcji oraz przygotowywanie sprawozdań dotyczących postępu produkcji do celów zarządzania; prowadzenie badań i doradztwo w zakresie nowoczesnych technologii podnoszenia jakości produktów żywnościowych, opracowywanie nowych asortymentów produktów, instrukcji technologicznych, normatywów zużycia surowców i materiałów oraz instrukcji bezpiecznej obsługi maszyn i urządzeń; receptur; przeprowadzanie badań laboratoryjnych dotyczących etapów produkcji nowych produktów i ich testowanie w ramach działań na niewielką skalę, np. w zakładach pilotażowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',44);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2146','Inżynierowie górnictwa, metalurgii i pokrewni','Inżynierowie górnicy, metalurdzy i pokrewni zajmują się projektowaniem, opracowywaniem i prowadzeniem badań w zakresie metod wydobywania z ziemi minerałów, węgla, rud metali, wody, ropy naftowej, gazu; opracowywaniem nowych stopów metali i innych materiałów; prowadzeniem nadzoru nad technikami wytwarzania materiałów oraz przeróbką plastyczną metali, stopów i materiałów; prowadzeniem badań i doradztwem w zakresie aspektów górniczych i metalurgicznych poszczególnych materiałów, produktów lub procesów.','Zadania wykonywane w grupie inżynierów górników, metalurgów i pokrewnych zazwyczaj obejmują: określanie miejsca i planowanie wydobywania węgla, rud metali, minerałów niemetalicznych i materiałów budowlanych takich jak: kamień i żwir; określanie najodpowiedniejszych metod wydajnego ich wydobycia i pozyskiwania, rodzajów maszyn, które będą stosowane, planowanie i nadzorowanie budowy szybów i tuneli; określanie obszaru wierceń i opracowywanie metod kontroli przepływu wody, ropy naftowej lub gazu z odwiertów; planowanie i nadzorowanie przechowywania, wstępnego oczyszczania i transportu wody, ropy naftowej lub gazu; określanie norm i procedur bezpieczeństwa oraz sprzętu do udzielania pierwszej pomocy, w szczególności pod ziemią; prowadzenie badań, opracowywanie metod pozyskiwania metali z ich rud oraz doradztwo w zakresie ich stosowania; badanie właściwości metali i stopów, opracowywanie nowych stopów oraz nadzorowanie i doradztwo w zakresie aspektów technicznych produkcji i przetwarzania metali i stopów; kontynuowanie współpracy technicznej i konsultacji z innymi odpowiednimi specjalistami takimi jak: geolodzy i geofizycy; badanie złóż lub kopalni w celu dokonania oceny rentowności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',45);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2149','Inżynierowie gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą inżynierowie nie zaklasyfikowani do grupy średniej 214 Inżynierowie (z wyłączeniem elektrotechnologii) lub grupy średniej 215 Inżynierowie elektrotechnologii. Grupa ta obejmuje na przykład osoby zajmujące się opracowywaniem oraz prowadzeniem badań i doradztwem w zakresie: procedur i rozwiązań inżynieryjnych dotyczących audytu energetycznego, dozoru technicznego, automatyki i robotyki, awioniki, biocybernetyki i inżynierii biomedycznej, energetyki, gospodarki przestrzennej, inżynierii klinicznej, poligrafii, pożarnictwa, systemów zabezpieczeń technicznych osób i mienia, technologii betonów, ceramiki, drewna, przetwórstwa skóry, szkła, inżynierii transportu drogowego, kolejowego, itp.','Zadania wykonywane przez inżynierów gdzie indziej niesklasyfikowani zazwyczaj obejmują: stosowanie wiedzy inżynieryjnej do projektowania, opracowywania i oceny systemów i produktów biologiczno-medycznych, takich jak: sztuczne organy, protezy i oprzyrządowanie; projektowanie urządzeń stosowanych do różnych zabiegów medycznych, systemów obrazowania, takich jak: rezonans magnetyczny i urządzenia do automatycznego wstrzykiwania insuliny lub kontrolowania funkcji organizmu; projektowanie elementów przyrządów optycznych, takich jak: soczewki, mikroskopy, teleskopy, lasery, systemy dysków optycznych i inny sprzęt wykorzystujący właściwości światła; projektowanie i nadzorowanie budowy i funkcjonowania elektrowni oraz systemów przetwarzania i odzyskiwania paliw; badanie i doradztwo w zakresie procesów produkcyjnych związanych z nanotechnologią, technologią szkła, ceramiki, drewna, przetwórstwa skóry, włókiennictwa, poligrafii, opakowań i pakowania; ergonomią, monitoringiem systemów zabezpieczeń technicznych osób i mienia; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',46);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2151','Inżynierowie elektrycy','Inżynierowie elektrycy zajmują się projektowaniem, nadzorowaniem oraz prowadzeniem badań i doradztwem w zakresie budowy i funkcjonowania systemów, elementów, silników i urządzeń elektrycznych; nadzorowaniem i doradztwem w zakresie ich funkcjonowania, konserwacją i naprawami; badaniem i doradztwem w zakresie technologicznych aspektów materiałów, produktów i procesów inżynierii elektrycznej.','Zadania wykonywane w grupie inżynierów elektryków zazwyczaj obejmują: projektowanie elektrowni i systemów, które wytwarzają, przesyłają i dystrybuują energię elektryczną i doradztwo w tym zakresie; nadzorowanie, kontrolowanie imonitorowanie funkcjonowania elektrycznych systemów wytwarzania, przesyłania i dystrybucji energii; projektowanie systemów silników elektrycznych, trakcji elektrycznej i innych urządzeń lub elektrycznego sprzętu gospodarstwa domowego oraz doradztwo w tym zakresie; projektowanie instalacji elektrycznej i jej zastosowania w budynkach przemysłowych oraz w innych budynkach i obiektach; określanie standardów i procedur kontroli dotyczących monitorowania działania i bezpieczeństwa elektrycznych systemów wytwarzania i dystrybucji, silników i urządzeń; określanie metod produkcji systemów elektrycznych oraz konserwację i naprawę elektrycznych systemów, silników i urządzeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',47);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2152','Inżynierowie elektronicy','Inżynierowie elektronicy zajmują się projektowaniem, nadzorowaniem oraz prowadzeniem badań i doradztwem w zakresie budowy, funkcjonowania, konserwacji i naprawy elektronicznych systemów, prowadzeniem badań i doradztwem w zakresie technologicznych aspektów materiałów, produktów i procesów inżynierii elektronicznej, mechaniki precyzyjnej, projektowania produktów i procesów produkcyjnych.','Zadania wykonywane w grupie inżynierów elektroników zazwyczaj obejmują: projektowanie elektronicznych urządzeń i elementów, obwodów, półprzewodników i systemów; określanie metod produkcji i instalacji, materiałów i norm jakości oraz nadzorowanie produkcji lub prac instalacyjnych związanych z produktami i systemami elektronicznymi; określanie standardów i procedur kontroli w celu zapewnienia sprawnego funkcjonowania i bezpieczeństwa elektronicznych systemów, silników i urządzeń; organizowanie i nadzorowanie konserwacji i naprawy istniejących systemów i urządzeń elektronicznych; projektowanie obwodów i elementów elektronicznych do zastosowania w takich obszarach jak: sterowanie w lotnictwie i kosmonautyce, kontrola napędu i akustyka lub przyrządów i urządzeń sterujących; prowadzenie badań i doradztwo w zakresie systemów radarowych, telemetrycznych i zdalnego sterowania, mikrofalówek i innych urządzeń elektronicznych; opracowywanie algorytmów przetwarzania sygnałów i ich wprowadzanie poprzez odpowiedni wybór sprzętu komputerowego i oprogramowania; opracowywanie urządzeń i procedur testujących elementy, obwody i systemy elektroniczne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',48);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2153','Inżynierowie telekomunikacji','Inżynierowie telekomunikacji zajmują się projektowaniem, nadzorowaniem oraz prowadzeniem badań i doradztwem w zakresie budowy, funkcjonowania, konserwacji i naprawy systemów i urządzeń telekomunikacyjnych. Prowadzą badania i doradztwo w zakresie technologicznych aspektów materiałów, produktów lub procesów inżynierii telekomunikacyjnej.','Zadania wykonywane w grupie inżynierów telekomunikacji zazwyczaj obejmują: projektowanie telekomunikacyjnych urządzeń lub elementów, systemów, sprzętu i ośrodków dystrybucji oraz doradztwo w tym zakresie; określanie metod produkcji i instalacji, materiałów, norm jakości i standardów bezpieczeństwa oraz nadzorowanie produkcji lub prac instalacyjnych związanych z produktami i systemami telekomunikacyjnymi; organizowanie i nadzorowanie konserwacji i naprawy istniejących systemów, silników i urządzeń telekomunikacyjnych; prowadzenie badań i doradztwo w zakresie urządzeń telekomunikacyjnych; planowanie i projektowanie sieci komunikacyjnych w oparciu o przewodowe, światłowodowe i bezprzewodowe środki przekazu; opracowywanie algorytmów przetwarzania sygnałów i ich wprowadzanie poprzez odpowiedni wybór sprzętu komputerowego i oprogramowania; projektowanie sieci telekomunikacyjnych oraz systemów transmisji telewizyjnej i radiowej, w tym sieci kablowych i bezprzewodowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',49);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2161','Architekci','Architekci projektują budynki handlowe, przemysłowe, instytucjonalne, mieszkalne i rekreacyjne, planują i monitorują ich budowę, konserwację i remont; zajmują się aranżacją, modernizacją i projektowanie wnętrz.','Zadania wykonywane w grupie architektów zazwyczaj obejmują: opracowywanie nowych lub udoskonalanie istniejących teorii i metod projektowania budynków; przeprowadzanie inspekcji i konsultacje z klientami, kierownictwem i innymi zainteresowanymi stronami w celu określenia rodzaju, stylu i wielkości proponowanych budynków bądź zmian istniejących budynków; dostarczanie informacji dotyczących projektów, materiałów i przewidywanych terminów budowy; przygotowywanie dokumentacji projektów, w tym szkiców i rysunków w skali oraz włączanie elementów strukturalnych, mechanicznych i estetycznych do ostatecznych projektów; sporządzanie specyfikacji i dokumentacji dotyczącej zamówienia dla wykonawców projektu; zapraszanie do składania ofert w imieniu klientów; utrzymywanie niezbędnych kontaktów w celu zapewnienia wykonalności projektów w odniesieniu do stylu, kosztu, terminów i zgodności z przepisami; określanie i znajdowanie najlepszych rozwiązań problemów związanych z pełnioną funkcją i jakością wnętrz budynków oraz sporządzanie niezbędnych projektów, rysunków i planów; monitorowanie prac budowlanych lub remontowych w celu zapewnienia zgodności ze specyfikacjami i normami jakości; projektowanie i aranżacja wnętrz; wykańczanie i remontowanie wnętrz; utrzymywanie współpracy technicznej i konsultacji z innymi odpowiednimi specjalistami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',50);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2162','Architekcie krajobrazu','Architekci krajobrazu zajmują się szeroko pojętą opieką nad terenami zielonymi. Planują i projektują krajobrazy i otwarte przestrzenie, takie jak: parki, szkoły, instytucje, drogi, tereny zewnętrzne z myślą o obiektach o charakterze handlowym, przemysłowym i mieszkaniowym, planują i monitorują ich budowę oraz konserwację. Oprócz opieki nad zielenią, architekci krajobrazu planują również obiekty tzw. małej architektury: pomniki, murki, ławki, kosze na śmieci, itp.; zajmują się również projektowaniem zieleni wewnątrz budynków.','Zadania wykonywane w grupie architektów krajobrazu zazwyczaj obejmują: opracowywanie nowych lub udoskonalanie istniejących teorii i metod stosowanych w architekturze krajobrazu; przeprowadzanie inspekcji i konsultacje z klientami, kierownictwem i innymi zainteresowanymi stronami w celu określenia rodzaju, stylu i wielkości parków, dróg i innych otwartych przestrzeni; gromadzenie i analizowanie danych dotyczących cech krajobrazu i właściwości ekologicznych, ukształtowania terenu, gleb, roślinności, hydrologii terenu, cech wizualnych i obiektów stworzonych przez człowieka w celu sformułowania zaleceń dotyczących zagospodarowania terenu i przygotowania oświadczeń o oddziaływaniu na środowisko; przygotowywanie sprawozdań, planów terenu, rysunków roboczych, specyfikacji i kosztorysów zagospodarowania terenu, uwzględniających lokalizację i szczegóły projektów, w tym modelowanie terenu, struktury, roślinności i dostępu do nich; sporządzanie specyfikacji i dokumentacji dotyczącej zamówienia dla robotników budowlanych i wykonawców w zakresie inżynierii lądowej i wodnej oraz zapraszanie do składania ofert w imieniu klientów; utrzymywanie niezbędnych kontaktów w celu zapewnienia wykonalności projektów w odniesieniu do stylu, kosztu, terminów i zgodności z obowiązującymi przepisami; określanie i znajdowanie najlepszych rozwiązań problemów dotyczących ochrony zasobów naturalnych oraz przywracania zdegradowanych terenów do użyteczności; opracowywania planów ochrony parków krajobrazowych i narodowych oraz pomników przyrody; rewaloryzację obszarów miejskich oraz opracowywanie projektów zagospodarowania obszarów poprzemysłowych i porolniczych; kierowanie budową obszarów zielonych; opracowywanie zasad pielęgnacji i eksploatacji nowopowstających i istniejących obszarów zieleni; projektowanie zieleni wewnątrz budynków przy pomocy sztuki florystycznej; utrzymywanie współpracy technicznej i konsultacji z innymi odpowiednimi specjalistami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',51);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2163','Projektanci wzornictwa przemysłowego i odzieży','Projektanci wzornictwa przemysłowego i odzieży projektują i wdrażają do produkcji masowej i seryjnej i jednostkowej przedmioty codziennego użytku, przedmiotów, maszyn i urządzeń przemysłowych, odzież, kostiumy teatralne i biżuterię. opracowują produkty do wytwarzania i przygotowują projekty i specyfikacje odzieży do produkcji masowej, seryjnej i jednostkowej.','Zadania wykonywane w grupie projektantów wzornictwa przemysłowego i odzieży zazwyczaj obejmują: tworzenie koncepcji projektów odzieży, wyrobów włókienniczych, produktów przemysłowych, handlowych i konsumenckich oraz biżuterii; dostosowywanie względów estetycznych do wymogów technicznych, funkcjonalnych, ekologicznych i produkcyjnych; przygotowywanie szkiców, schematów, ilustracji, planów, próbek i modeli do prezentowanych koncepcji różnych projektów; negocjowanie rozwiązań projektowych z klientami, kierownictwem i pracownikami do spraw sprzedaży i produkcji; wybieranie, określanie i zalecanie materiałów funkcjonalnych i estetycznych metod produkcji i apretur do produkcji; szczegółowe opisywanie i dokumentowanie wybranego projektu do produkcji; przygotowywanie i zamawianie prototypów i próbek; nadzorowanie przygotowywania wzorów, programów i narzędzi oraz procesu wytwarzania; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',52);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2164','Urbaniści i inżynierowie ruchu drogowego','Urbaniści i inżynierowie ruchu drogowego opracowują i wdrażają plany i zamierzenia dotyczące kontroli zagospodarowania przestrzennego terenów miejskich i wiejskich oraz systemów ruchu drogowego. Prowadzą badania i udzielają porad dotyczących czynników gospodarczych, środowiskowych i społecznych mających wpływ na zagospodarowanie terenów i przepływu ruchu.','Zadania wykonywane w grupie urbanistów i inżynierów ruchu drogowego zazwyczaj obejmują: planowanie przestrzeni i koordynowanie rozwoju terenów miejskich i wiejskich; gromadzenie i analizowanie danych dotyczących czynników gospodarczych, prawnych, politycznych, kulturowych, demograficznych, socjologicznych, fizycznych i środowiskowych mających wpływ na zagospodarowanie terenów; konsultowanie się z organami państwa i władzami samorządowymi, społeczeństwem oraz specjalistami z takich dziedzin, jak: architektura, nauki społeczne, prawo; planowanie i doradztwo w zakresie zagospodarowania i rozwoju terenów przedstawianie planów, programów i projektów dla klientów grupowych i indywidualnych; doradztwo dla rządów, przedsiębiorstw i społeczności w zakresie kwestii i propozycji związanych z planowaniem przestrzeni miejskiej i regionalnej; weryfikowanie i ocenianie sprawozdań dotyczących wpływu projektowanych rozwiązań na środowisko; planowanie układu i koordynowanie rozwoju obszarów miejskich; planowanie i przygotowanie terenów pod parki, szkoły, instytucje, lotniska, drogi, obszary handlowe, przemysłowe i mieszkaniowe; planowanie i doradztwo w zakresie określania trasy i kontroli ruchu drogowego oraz systemów transportu publicznego pod kątem sprawności i bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',53);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2165','Kartografowie i geodeci','Kartografowie i geodeci określają dokładne położenie obiektów naturalnych i zbudowanych oraz granic gruntów, mórz, obszarów podziemnych i ciał niebieskich lub przygotowywaniem i uaktualnianiem ich map cyfrowych, graficznych i obrazkowych, wykresów lub innych rodzajów reprezentacji graficznej przy pomocy zasad matematycznych i naukowych.','Zadania wykonywane w grupie kartografów i geodetów zazwyczaj obejmują: badanie, mierzenie i opisywanie powierzchni ziemi, kopalni, powierzchni podziemnych, dna morza, rzeki i jeziora; określanie dokładnego położenia różnych obiektów i zapisywanie wyników badań w formie elektronicznej; sporządzanie wykresów i map do zastosowania w określaniu wód otwartych dla żeglugi i kanałów oraz w planowaniu budowy obiektów morskich; planowanie i prowadzenie badań wykonywanych za pomocą zdjęć lotniczych; projektowanie, gromadzenie i uaktualnianie map i wykresów przy pomocy zdjęć lotniczych i innych, zdjęć satelitarnych, dokumentacji i danych dotyczących badań, istniejących map i rejestrów, sprawozdań i statystyk; prowadzenie badań i opracowywanie systemów pomiaru geodezyjnego i fotogrametrycznego, systemów katastralnych i systemów informacji o gruntach; badania i doradztwo w zakresie technicznych, estetycznych i gospodarczych aspektów produkcji map; utrzymywanie współpracy technicznej i prowadzenie konsultacji z innymi odpowiednimi specjalistami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',54);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2166','Projektanci grafiki i multimediów','Projektanci grafiki i multimediów projektują treść i formę informacji do przekazu audiowizualnego w formie publikacji i przedstawienia ich przy pomocy mediów drukowanych, filmowych, elektronicznych, cyfrowych i innych multimediów. Tworzą efekty specjalne, animacje lub inne obrazy wizualne do zastosowania w grach komputerowych, filmach, nagraniach muzycznych, mediach drukowanych i reklamach. Projektują grafikę stron internetowych.','Zadania wykonywane w grupie projektantów grafiki i multimediów zazwyczaj obejmują: określanie celów i ograniczeń projektowych zamierzeń graficznych poprzez konsultacje z klientami i zainteresowanymi stronami; prowadzenie badań i analizowanie wymogów funkcjonalnych dotyczących projektowanych multimediów; tworzenie koncepcji projektowych dla przedmiotu przekazu multimedialnego; przygotowywanie szkiców, schematów, ilustracji i układów przedstawiających koncepcje projektowe; projektowanie złożonej grafiki i animacji zgodnych z wymogami dotyczącymi funkcjonalności, estetyki i kreatywności projektów graficznych; tworzenie dwu- i trójwymiarowych obrazów przedstawiających obiekty w ruchu lub ilustrujące proces przy zastosowaniu animacji komputerowej lub programów modelujących; negocjowanie rozwiązań projektowych z klientami, kierownictwem, pracownikami do spraw sprzedaży i produkcji; wybieranie, określanie lub zalecanie materiałów funkcjonalnych i estetycznych oraz mediów do publikacji; szczegółowe opisywanie i dokumentowanie wybranego projektu do produkcji; nadzorowanie publikacji w wybranych mediach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',55);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2211','Lekarze bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia','Lekarze bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia diagnozują, leczą choroby, schorzenia, obrażenia i inne rodzaje upośledzenia fizycznego i umysłowego u ludzi, zapobiegają im oraz utrzymują ogólne zdrowie poprzez stosowanie zasad i procedur współczesnej medycyny. Nie ograniczają swojej praktyki do określonych rodzajów chorób ani metod leczenia i mogą przyjmować odpowiedzialność za ciągłą i kompleksową opiekę medyczną nad osobami indywidualnymi, rodzinami i społecznościami.','Zadania wykonywane w grupie lekarze bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia zazwyczaj obejmują: badania fizykalne pacjentów i rozmowy z pacjentami i ich rodzinami mające na celu określenie ich stanu zdrowia; zlecanie badań laboratoryjnych, zdjęć rentgenowskich i innych procedur diagnostycznych oraz analizowanie wyników w celu określenia rodzaju zaburzeń lub chorób; opiekę medyczną nad pacjentami, w tym przepisywanie, monitorowanie leczenia i środków profilaktycznych oraz kierowanie nimi i doradztwo w tym zakresie; doradzanie osobom indywidualnym, rodzinom i społecznościom w zakresie zdrowia, żywienia i stylu życia w celu zapobiegania chorobom i zaburzeniom lub ich leczenia; wydawanie pacjentom i rodzinom skierowań na leczenie specjalistyczne w szpitalach, centrach rehabilitacji lub innych ośrodkach opieki zdrowotnej; określanie, rozpatrywanie i wydawanie skierowań w przypadku komplikacji przed porodem, w jego trakcie i po nim; rejestrowanie informacji medycznych o pacjentach i historii choroby oraz wymianę tych informacji z lekarzami specjalistami i innymi pracownikami służby zdrowia, jeżeli zachodzi konieczność dalszej opieki medycznej; zgłaszanie organom rządowym urodzeń, zgonów i chorób podlegających obowiązkowi zgłoszenia w celu spełnienia wymogów prawnych i wymogów związanych z wykonywanym zawodem; prowadzenie badań w zakresie zdrowia ludzi i usług medycznych oraz udostępnianie wyników badań w sprawozdaniach naukowych i innych publikacjach; planowanie programów mających na celu zapobieganie występowaniu i rozprzestrzenianiu się często występujących schorzeń oraz uczestnictwo w tych programach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',56);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2212','Lekarze specjaliści (ze specjalizacją II stopnia lub tytułem specjalisty)','Lekarze specjaliści (ze specjalizacją II stopnia lub tytułem specjalisty) diagnozują i leczą choroby, schorzenia, obrażenia i inne rodzaje upośledzenia fizycznego i umysłowego u ludzi oraz im zapobiegają poprzez stosowanie zasad i procedur współczesnej medycyny. Specjalizują się w określonych kategoriach chorób i typach pacjentów lub metodach leczenia i mogą prowadzić kształcenie medyczne oraz badania w wybranych obszarach specjalizacji. Zawody zaklasyfikowane do tej grupy wymagają dyplomu ukończenia studiów w zakresie medycyny ogólnej oraz podyplomowego klinicznego szkolenia specjalizacyjnego (z wyjątkiem specjalizacji w medycynie ogólnej) lub szkolenia równoważnego.','Zadania wykonywane w grupie lekarzy specjaliści (ze specjalizacją II stopnia lub tytułem specjalisty) zazwyczaj obejmują: badania fizykalne pacjentów i rozmowy z pacjentami oraz ich rodzinami w tym zakresie; analizowanie informacji medycznych dostarczonych przez lekarza lub innego pracownika służby zdrowia, który wydał skierowanie; zlecanie specjalistycznych badań diagnostycznych w celu określenia rodzaju zaburzeń lub chorób; przepisywanie leków, zlecanie leczenia, opieki anestezjologicznej, psychoterapii, programów rehabilitacji fizycznej i innych środków profilaktycznych i leczniczych oraz kierowanie nimi i monitorowanie reakcji pacjentów na leczenie; przeprowadzanie operacji ogólnych lub specjalistycznych; leczenie komplikacji występujących przed porodem, w jego trakcie i po nim; rejestrowanie informacji medycznych o pacjentach i wymianę tych informacji z innymi specjalistami ochrony zdrowia w celu zapewnienia kompleksowej opieki; zgłaszanie organom rządowym urodzeń, zgonów i chorób podlegających obowiązkowi zgłoszenia w celu spełnienia wymogów prawnych i wymogów związanych z wykonywanym zawodem; dostarczanie pacjentom, rodzinom i społecznościom informacji dotyczących środków profilaktycznych, leczenia i opieki w przypadku określonych dolegliwości; przeprowadzanie sekcji zwłok w celu określenia przyczyny zgonu; prowadzenie badań w zakresie określonych schorzeń i chorób oraz metod profilaktyki i leczenia, a także udostępnianie wyników tych badań w sprawozdaniach naukowych i innych publikacjach; planowanie programów mających na celu zapobieganie występowaniu i rozprzestrzenianiu się określonych chorób oraz uczestnictwo w tych programach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',57);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2221','Pielęgniarki bez specjalizacji lub w trakcie specjalizacji','Pielęgniarki bez specjalizacji lub w trakcie specjalizacji udzielają świadczeń zdrowotnych, a w szczególności świadczeń pielęgnacyjnych, zapobiegawczych, diagnostycznych, leczniczych, rehabilitacyjnych oraz z zakresu promocji zdrowia w stosunku do osób zdrowych, chorych, niepełnosprawnych, dzieci i młodzieży oraz ich rodzin, w warunkach stacjonarnej, ambulatoryjnej i domowej opieki zdrowotnej. Pracę wykonują pod kierunkiem pielęgniarek specjalistek i pod nadzorem pielęgniarek oddziałowych.','Zadania wykonywane w grupie pielęgniarek bez specjalizacji lub w trakcie specjalizacji zazwyczaj obejmują: rozpoznawanie warunków i potrzeb zdrowotnych; rozpoznawanie problemów pielęgnacyjnych; sprawowanie opieki pielęgnacyjnej; realizowanie zleceń lekarskich w procesie diagnostyki, leczenia i rehabilitacji; udzielanie w określonym zakresie świadczeń zapobiegawczych, diagnostycznych, leczniczych i rehabilitacyjnych; wykonywanie innych zadań, pod kierunkiem pielęgniarek specjalistek i nadzorem pielęgniarek oddziałowych.',58);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2222','Pielęgniarki z tytułem specjalisty','Pielęgniarki z tytułem specjalisty opiekują się osobami wymagającymi opieki pielęgniarskiej ze względu na wiek, obrażenia, chorobę lub innego rodzaju upośledzenia fizyczne, umysłowe lub ze względu na potencjalne zagrożenia dla zdrowia, a także leczą i wspierają takie osoby. Do ich obowiązków należy planowanie i sprawowanie opieki nad pacjentami, pracują indywidualna lub w zespołach z lekarzami lub innymi pracownikami służby zdrowia w zakresie stosowania w praktyce środków profilaktycznych i leczniczych.','Zadania wykonywane w grupie pielęgniarek z tytułem specjalisty zazwyczaj obejmują: planowanie, sprawowanie i ocenianie opieki pielęgniarskiej nad pacjentami zgodnie z praktyką i standardami współczesnego pielęgniarstwa; koordynowanie opieki nad pacjentami w konsultacji z innymi specjalistami ochrony zdrowia i członkami zespołów medycznych; opracowywanie i wdrażanie planów opieki obejmującej leczenie fizyczne, społeczne i psychologiczne pacjentów we współpracy z innymi specjalistami ochrony zdrowia; planowanie i zapewnianie opieki osobistej, leczenia i terapii, w tym podawanie leków i monitorowanie reakcji pacjentów na leczenie lub opiekę; przemywanie ran i zakładanie opatrunków i bandaży; kontrolowanie bólu i dolegliwości u pacjentów, łagodzenie bólu z zastosowaniem różnych terapii, w tym z zastosowaniem leków przeciwbólowych; udzielanie odpowiedzi na pytania pacjentów i rodzin oraz informowanie ich o zakresie profilaktyki, leczenia i opieki; prowadzenie badań w zakresie praktyk i procedur pielęgniarskich oraz udostępnianie wyników tych badań na przykład w publikacjach i sprawozdaniach naukowych. Do tej grupy zaklasyfikowane zostały również pielęgniarki zajmujące się jednocześnie kształceniem lub prowadzeniem badań i kliniczną praktyką pielęgniarską. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników służby zdrowia i opieki osobistej.',59);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2231','Położne bez specjalizacji lub w trakcie specjalizacji','Położne bez specjalizacji lub w trakcie specjalizacji zajmują się sprawowaniem opieki położniczej oraz kierowanie nią przed ciążą i porodem, w trakcie ciąży i porodu oraz po ciąży i porodzie. Świadczą usługi z zakresu opieki porodowej, aby zmniejszyć zagrożenie dla zdrowia kobiet i noworodków, pracując pod kierunkiem położnych specjalistek.','zazwyczaj obejmują: świadczenie usług z zakresu opieki i wsparcia dla kobiet przed ciążą i porodem, w trakcie ciąży i porodu oraz po ciąży i porodzie zgodnie z praktyką i standardami współczesnej opieki położniczej; udzielanie pomocy położniczej w nagłych przypadkach do czasu przybycia lekarza; prowadzenie działalności edukacyjno-zdrowotnej w zakresie przygotowania do życia w rodzinie, metod planowania rodziny oraz ochrony macierzyństwa i ojcostwa; realizowanie zleceń lekarskich w procesie diagnostyki, leczenia i rehabilitacji; wykonywanie innych zadań pokrewnych.',60);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2232','Położne z tytułem specjalisty','Położne z tytułem specjalisty zajmują się planowaniem i sprawowaniem opieki położniczej oraz kierowanie nią przed ciążą i porodem, w trakcie ciąży i porodu oraz po ciąży i porodzie. Świadczą usługi z zakresu opieki porodowej, aby zmniejszyć zagrożenie dla zdrowia kobiet i noworodków, pracując indywidualnie lub w zespołach z innymi pracownikami służby zdrowia.','Zadania wykonywane w grupie położnych z tytułem specjalisty zazwyczaj obejmują: planowanie, świadczenie i ocenianie usług z zakresu opieki i wsparcia dla kobiet i dzieci przed ciążą i porodem, w trakcie ciąży i porodu oraz po ciąży i porodzie zgodnie z praktyką i standardami współczesnej opieki położniczej; udzielanie porad kobietom i ich rodzinom oraz prowadzenie edukacji w zakresie zdrowia, żywienia, higieny, planów porodu i postępowania w nagłych wypadkach, karmienia piersią, opieki nad noworodkiem, planowania rodziny i antykoncepcji, stylu życia i innych kwestii związanych z ciążą i porodem; dokonywanie oceny przebiegu ciąży i porodu, leczenie komplikacji i rozpoznawanie sygnałów ostrzegawczych wymagających skierowania pacjentki do lekarza specjalizującego się w położnictwie; monitorowanie stanu zdrowia noworodków, leczenie komplikacji i rozpoznawanie sygnałów ostrzegawczych wymagających skierowania do lekarza specjalizującego się w neonatologii; kontrolowanie bólu i dolegliwości u kobiet podczas porodu i łagodzenie bólu przy zastosowaniu różnych terapii, w tym leków przeciwbólowych; zgłaszanie organom rządowym urodzeń w celu spełnienia wymogów prawnych i wymogów związanych z wykonywanym zawodem; prowadzenie badań w zakresie praktyk i procedur położniczych oraz udostępnianie wyników tych badań na przykład w publikacjach i sprawozdaniach naukowych; planowanie i prowadzenie edukacji w zakresie położnictwa w warunkach klinicznych i poza klinicznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników.',61);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2240','Specjaliści do spraw ratownictwa medycznego','Specjaliści do spraw ratownictwa medycznego udzielają szybkiej i sprawnej pomocy o charakterze ratowniczym w nagłych stanach zagrożenia życia, wypadkach i katastrofach. Pracują indywidualnie lub pod nadzorem lekarzy i stosują zaawansowane procedury kliniczne w leczeniu i profilaktyce chorób, obrażeń i innych rodzajów upośledzenia fizycznego lub umysłowego występujących w danych społecznościach.','Zadania wykonywane w grupie specjalistów do spraw ratownictwa medycznego zazwyczaj obejmują: wykonywanie zabiegów i podawanie leków lub stosowanie innych metod leczenia zgodnie z obowiązującymi przepisami w zakresie udzielania pomocy medycznej w nagłych wypadkach; monitorowanie zmian w stanie zdrowia pacjentów podczas transportu z i do szpitali lub innych ośrodków medycznych i rehabilitacyjnych związanych z ochroną zdrowia; badania fizykalne pacjentów i rozmowy z pacjentami i ich rodzinami mające na celu określenie ich stanu zdrowia oraz rejestrowanie informacji medycznych o pacjentach; przeprowadzanie podstawowych lub bardziej rutynowych zabiegów medycznych i chirurgicznych, wykonywanie lub zlecanie badań diagnostycznych, takich jak: zdjęcia rentgenowskie, elektrokardiogram i badania laboratoryjne; wykonywanie zabiegów, takich jak: zastrzyki, szczepienia, zakładanie szwów i opatrywanie ran oraz leczenie infekcji; pomaganie lekarzom podczas skomplikowanych operacji chirurgicznych; monitorowanie stanu pacjentów i ich reakcji na leczenie oraz rozpoznawanie sygnałów i objawów wymagających skierowania do lekarza; udzielanie pacjentom i rodzinom porad dotyczących diety, ćwiczeń i innych nawyków pomagających w profilaktyce oraz leczeniu chorób i zaburzeń; rozpoznawanie skomplikowanych lub nietypowych przypadków i kierowanie ich do lekarzy, szpitali lub innych miejsc specjalistycznej opieki; zgłaszanie organom rządowym urodzeń, zgonów i chorób podlegających obowiązkowi zgłoszenia w celu spełnienia wymogów prawnych i wymogów zgłaszania związanych z wykonywanym zawodem; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników.',62);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2251','Lekarze weterynarii bez specjalizacji lub w trakcie specjalizacji','Lekarze weterynarii bez specjalizacji lub w trakcie specjalizacji zajmują się diagnozowaniem, profilaktyką i leczeniem chorób, obrażeń i dysfunkcji u zwierząt. Mogą leczyć różne gatunki zwierząt lub specjalizować się w leczeniu jednej określonej grupy zwierząt lub w jednym obszarze specjalizacji, pod kierunkiem lekarzy weterynarii specjalistów lub świadczyć usługi dla przedsiębiorstw handlowych produkujących produkty biologiczne i farmaceutyczne.','Zadania wykonywane w grupie lekarzy weterynarii bez specjalizacji lub w trakcie specjalizacji zazwyczaj obejmują: rozpoznawanie i określanie rodzaju chorób poprzez badania fizykalne, badania laboratoryjne i z wykorzystaniem technik diagnozowania za pomocą obrazu, w tym radiografii i badań ultradźwiękowych; farmakologiczne leczenie zwierząt oraz podawanie i przepisywanie leków, środków przeciwbólowych oraz znieczulanie ogólne i miejscowe; opatrywanie ran; odbieranie porodów zwierząt; uczestnictwo w programach mających na celu zapobieganie występowaniu i rozprzestrzenianiu się chorób u zwierząt; szczepienie zwierząt przeciwko chorobom zakaźnym i badania pod kątem takich chorób oraz powiadamianie władz o występowaniu chorób zakaźnych u zwierząt; udzielanie porad w kwestii zdrowia, żywienia i karmienia, higieny, rozmnażania zwierząt i opieki nad nimi; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników.',63);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2252','Lekarze weterynarii specjaliści','Lekarze weterynarii specjaliści zajmują się diagnozowaniem, profilaktyką i leczeniem chorób, obrażeń i dysfunkcji u zwierząt. Mogą leczyć różne gatunki zwierząt lub specjalizować się w leczeniu jednej określonej grupy zwierząt lub w jednym obszarze specjalizacji, lub świadczyć usługi dla przedsiębiorstw handlowych produkujących produkty biologiczne i farmaceutyczne.','Zadania wykonywane w grupie lekarzy weterynarii specjalistów zazwyczaj obejmują: rozpoznawanie i określanie rodzaju chorób poprzez badania fizykalne, badania laboratoryjne i z wykorzystaniem technik diagnozowania za pomocą obrazu, w tym radiografii i badań ultradźwiękowych; farmakologiczne i chirurgiczne leczenie zwierząt oraz podawanie i przepisywanie leków, środków przeciwbólowych oraz znieczulanie ogólne i miejscowe; przeprowadzanie operacji, opatrywanie ran i nastawianie złamanych kości; odbieranie porodów zwierząt; uczestnictwo w programach mających na celu zapobieganie występowaniu i rozprzestrzenianiu się chorób u zwierząt; szczepienie zwierząt przeciwko chorobom zakaźnym i badania pod kątem takich chorób oraz powiadamianie władz o występowaniu chorób zakaźnych u zwierząt; przeprowadzanie sekcji zwłok w celu określenia przyczyny zgonu; udzielanie porad w kwestii zdrowia, żywienia i karmienia, higieny, rozmnażania zwierząt i opieki nad nimi; dokonywanie eutanazji zwierząt; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników.',64);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2261','Lekarze dentyści bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia','Lekarze dentyści bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia zajmują się diagnozowaniem, leczeniem i profilaktyką chorób, obrażeń i nieprawidłowości uzębienia, jamy ustnej, szczęki i okolic przyległych z zastosowaniem zasad i procedur współczesnej stomatologii, pracując pod kierunkiem lekarzy dentystów specjalistów.','Zadania wykonywane w grupie lekarzy dentystów bez specjalizacji, w trakcie specjalizacji lub ze specjalizacją I stopnia zazwyczaj obejmują: świadczenie usług z zakresu profilaktycznej opieki stomatologicznej; świadczenie usług z zakresu leczenia protetycznego oraz naprawy uszkodzonych zębów i leczenia próchnicy; przeprowadzanie zabiegów chirurgicznych takich jak: ekstrakcja zębów i biopsja oraz leczenie ortodontyczne; edukację pacjentów i ich rodzin w zakresie higieny dentystycznej, żywienia i innych środków mających na celu utrzymanie zdrowia jamy ustnej; organizowanie działań medycznych w skali lokalnej lub regionalnej w celu zwalczenia epidemii chorób jamy ustnej; przygotowywanie diagnoz i prognoz o stanie zagrożenia epidemiologicznego w zakresie układu stomatologicznego na danym terenie; pobieranie materiału biologicznego do badań histologicznych i bakteriologicznych; współpraca z technikami dentystycznymi w zakresie przygotowywania aparatów ortodontycznych; rozpoznawanie i różnicowanie chorób jamy ustnej z chorobami nowotworowymi, kierowanie pacjentów do poradni onkologicznych w celu postawienia diagnozy w sytuacjach podejrzenia o nowotwory jamy ustnej; pomoc w diagnozowaniu chorób ogólnych mających wpływ na stan jamy ustnej takich jak cukrzyca; zwracanie uwagi na działanie czynników szkodliwych na jamę ustną (badania przesiewowe próchnicy zębów, wad zgryzu, chorób przyzębia); wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie higienistów, asystentów stomatologicznych i innych pracowników.',65);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2262','Lekarze dentyści specjaliści (ze specjalizacją II stopnia lub tytułem specjalisty)','Lekarze dentyści specjaliści (ze specjalizacją II stopnia lub tytułem specjalisty) zajmują się diagnozowaniem, leczeniem i profilaktyką chorób, obrażeń i nieprawidłowości uzębienia, jamy ustnej, szczęki i okolic przyległych z zastosowaniem zasad i procedur współczesnej stomatologii. Stosują szeroki zakres specjalistycznych technik diagnostycznych, chirurgicznych i innych w celu promowania i przywracania zdrowia jamy ustnej. Lekarze dentyści prowadzą badania, doskonalą lub opracowują koncepcje i metody działania oraz stosują w praktyce wiedzę medyczną z zakresu stomatologii.','Zadania wykonywane przez lekarzy dentystów specjalistów (ze specjalizacją II stopnia lub tytułem specjalisty) zazwyczaj obejmują: prowadzenie badań układu stomatologicznego, w skład którego wchodzą: zęby i przyzębie, kości szczęki i żuchwy, mięśnie, rozpoznawanie i leczenie chorób jamy ustnej; leczenie zachowawcze, chirurgiczne, protetyczne i inne formy leczenia, w zależności od rodzaju zaburzeń i chorób zębów i jamy ustnej; pobieranie materiału biologicznego do badań histologicznych, bakteriologicznych i innych; kierowanie wczesną rehabilitacją chorych po zabiegach chirurgicznych w obrębie twarzoczaszki; współpraca z technikami dentystycznymi w zakresie przygotowywania aparatów ortodontycznych; rozpoznawanie i różnicowanie chorób jamy ustnej z chorobami nowotworowymi, kierowanie pacjentów do poradni onkologicznych w celu postawienia diagnozy w sytuacjach podejrzenia o nowotwory jamy ustnej; uczestniczenie w działaniach publicznych, mających na celu utrzymanie lub poprawę stanu zdrowia jamy ustnej i opieki stomatologicznej w społeczeństwie, zapobiegania chorobom zębów i wadom zgryzu; określanie przyczyn zmian błony śluzowej jamy ustnej w powiązaniu z substancjami chemicznymi działającymi w różnych gałęziach przemysłu oraz patologicznym działaniem niektórych leków; leczenie protetyczne ubytków w uzębieniu: poekstrakcyjnych, pourazowych, powstałych w następstwie paradontopatii i innych; organizowanie działań medycznych w skali lokalnej, regionalnej lub krajowej w celu zwalczenia epidemii chorób jamy ustnej; przygotowywanie diagnoz i prognoz o stanie zagrożenia epidemiologicznego w zakresie układu stomatologicznego na danym terenie; inicjowanie i organizowanie badań profilaktycznych osób narażonych na działanie czynników szkodliwych na jamę ustną (badania przesiewowe próchnicy zębów, wad zgryzu, chorób przyzębia), przygotowywanie i publikowanie opracowań naukowych i fachowych z zakresu swej specjalności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie higienistek, asystentek stomatologicznych i innych pracowników.',66);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2271','Diagności laboratoryjni bez specjalizacji lub w trakcie specjalizacji','Diagności laboratoryjni bez specjalizacji lub w trakcie specjalizacji wykonują (we współpracy z technikami), za pomocą specjalistycznych metod i aparatury laboratoryjnej badania diagnostyczne z zakresu: chemii klinicznej oraz analityki ogólnej, interpretują i oceniają wyniki badań.','Zadania wykonywane w grupie diagnostów laboratoryjnych bez specjalizacji lub w trakcie specjalizacji zazwyczaj obejmują: pobieranie i przechowywanie materiałów biologicznych do badań laboratoryjnych; przygotowywanie preparatów hematologicznych, bakteriologicznych i innych; wykonywanie badań laboratoryjnych z zakresu chemii klinicznej i analityki ogólnej; przeprowadzanie badań hematologicznych i serologicznych, w tym badań układu krzepnięcia i wykrywania konfliktu serologicznego; prowadzenie dokumentacji wykonanych badań z zastosowaniem metod komputerowych; doskonalenie swojej wiedzy i umiejętności, między innymi przez uczestnictwo w podyplomowym kształceniu ustawicznym; obsługiwanie komputerowych urządzeń do wieloparametrowej analizy płynów ustrojowych i aparatury pokrewnej; sprawowanie nadzoru nad aparaturą i odczynnikami chemicznymi i innymi materiałami oraz kontrolowanie ich przydatności; pełnienie dyżurów specjalistycznych i udzielanie konsultacji za pomocą środków łączności telefonicznej i wideofonicznej; wykonywanie innych zadań pokrewnych.',67);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2272','Diagności laboratoryjni specjaliści','Diagności laboratoryjni specjaliści wykonują (we współpracy z technikiem), za pomocą specjalistycznych metod i aparatury laboratoryjnej, bądź nadzorują wykonywanie badań diagnostycznych z zakresu chemii klinicznej, analityki ogólnej, hematologii, serologii, immunologii i bakteriologii oraz interpretują i oceniają wyniki badań.','Zadania wykonywane w grupie diagnostów laboratoryjnych specjalistów zazwyczaj obejmują: wykonywanie lub nadzorowanie czynności związanych z pobieraniem i przechowywaniem materiału biologicznego do badań laboratoryjnych; przygotowywanie lub nadzorowanie przygotowania preparatów hematologicznych, bakteriologicznych i innych; wykonywanie lub nadzorowanie wykonania badań laboratoryjnych z zakresu chemii klinicznej i analityki ogólne; przeprowadzanie badań hematologicznych i serologicznych, w tym badań układu krzepnięcia i wykrywania konfliktu serologicznego; ocenianie i interpretowanie wyników badań i (w porozumieniu z klinicystą) dobieranie odpowiednich badań na podstawie określania ich skuteczności diagnostycznej; prowadzenie kontroli jakości wyników badań laboratoryjnych oraz określanie zakresów wartości referencyjnych; nadzorowanie unieszkodliwiania użytego do badań materiału biologicznego; kontrolowanie przestrzegania przepisów bhp w laboratorium; prowadzenie dokumentacji wykonanych badań z zastosowaniem metod informatyki; konsultowanie zleceń klinicystów, prowadzenie doradztwa diagnostycznego i interpretacji wyników badań laboratoryjnych; nadzorowanie wydawania wyników badań, sprawdzanie ich i uwierzytelnianie (podpisywanie); doskonalenie swojej wiedzy i umiejętności, między innymi przez uczestnictwo w kształceniu ustawicznym; obsługiwanie komputerowych urządzeń do wieloparametrowej analizy płynów ustrojowych i aparatury pokrewnej; sprawowanie nadzoru nad aparaturą i odczynnikami chemicznymi i innymi materiałami oraz kontrolowanie ich przydatności; kierowanie zakładem lub pracownią diagnostyki laboratoryjnej; udział w doskonaleniu zawodowym (szkoleniu) innych pracowników medycznych oraz prowadzenie badań naukowych i publikowanie ich wyników. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy diagnostów laboratoryjnych bez specjalizacji lub w trakcie specjalizacji i innych pracowników',68);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2281','Farmaceuci bez specjalizacji lub w takcie specjalizacji','Farmaceuci bez specjalizacji lub w takcie specjalizacji przechowują, zabezpieczają, przygotowują i wydają produkty lecznicze oraz udzielają porad dotyczących ich odpowiedniego przyjmowania i niepożądanych działań leków, realizują recepty wydane przez lekarzy; prowadzą badania i doskonalą koncepcje i metody przygotowywania i dystrybucji leków, środków opatrunkowych, materiałów higienicznych i podobnych.','Zadania wykonywane w grupie farmaceutów bez specjalizacji lub w takcie specjalizacji zazwyczaj obejmują: przygotowywanie i nadzór nad przygotowywaniem produktów leczniczych – zgodnie z ordynacją wydaną na recepcie przez lekarzy, stomatologów i lekarzy weterynarii lub zgodnie z obowiązującymi wymogami Farmakopei Polskiej; kontrolowanie recept pod względem zalecanego dawkowania i zgodności terapeutycznej różnych leków oraz bezpieczeństwa farmakoterapii; wydawanie produktów leczniczych i materiałów medycznych w aptekach szpitalnych lub ekspediowanie ich w aptekach ogólnodostępnych; doradzanie klientom w zakresie leków wydawanych bez recepty i ich wydawanie oraz pomoc diagnostyczno-terapeutyczna w przypadku powszechnych dolegliwości; prowadzenie ewidencji wydawania niektórych leków, objętych szczególnymi przepisami, jak: narkotyków, produktów leczniczych bardzo silnie działających i środków odurzających powodujących uzależnienie; prowadzenie ilościowych i jakościowych badań leków, mających na celu określenie ich identyczności, czystości i mocy terapeutycznej; prowadzenie badań w celu opracowania i udoskonalenia środków farmakologicznych, kosmetyków i powiązanych produktów chemicznych; konsultacje z chemikami, inżynierami i innymi specjalistami w zakresie technik produkcji i składników uczestniczenie w działaniach mających na celu usprawnienie zaopatrzenia w leki; przygotowywanie opracowań naukowych z zakresu farmacji oraz wspomaganie lekarza w optymalizacji leczenia i informowanie go o wszelkich zmianach i nowościach na rynku farmaceutycznym. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy techników farmaceutycznych, praktykantów i sprzedawców farmaceutycznych i innych pracowników.',69);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2282','Farmaceuci specjaliści','Farmaceuci specjaliści zajmują się świadczeniem usług farmaceutycznych polegającym na sporządzaniu i wytwarzaniu produktów leczniczych, wydawaniu produktów leczniczych i wyrobów medycznych oraz udzielają rzetelnej i obiektywnej informacji dotyczącej działania produktów leczniczych i stosowania wyrobów medycznych w warunkach racjonalizacji farmakoterapii oraz sprawują opiekę nad przebiegiem staży zawodowych.','Zadania zawodowe w grupie farmaceutów specjalistów obejmują: sporządzanie i wytwarzanie produktów leczniczych; ocenię jakości leków recepturowych, leków aptecznych i leków gotowych; wydawanie produktów leczniczych i wyrobów medycznych będących przedmiotem obrotu w aptekach, działach farmacji szpitalnej i hurtowniach farmaceutycznych; sporządzanie leków recepturowych i leków aptecznych w aptekach; sprawowanie nadzoru nad wytwarzaniem, obrotem, przechowywaniem, wykorzystaniem i utylizacją produktów leczniczych i wyrobów medycznych, w tym rezerwami państwowymi; udzielanie informacji i porad dotyczących działania i stosowania produktów leczniczych i wyrobów medycznych będących przedmiotem obrotu w aptekach i hurtowniach farmaceutycznych; sprawowanie opieki farmaceutycznej polegającej na dokumentowanym procesie, w którym farmaceuta, współpracując z pacjentem i lekarzem, a w razie potrzeby z przedstawicielami innych zawodów medycznych, czuwa nad prawidłowym przebiegiem farmakoterapii w celu uzyskania określonych jej efektów poprawiających jakość życia pacjenta, prowadzenie działalności dydaktycznej w uczelniach medycznych, badań naukowych i prac rozwojowych w dziedzinie farmacji. Farmaceuci specjaliści świadczą także usługi farmaceutyczne z wykorzystaniem wiedzy i umiejętności zgodnych z programem szkolenia specjalizacyjnego w danej dziedzinie; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',70);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2291','Specjaliści do spraw higieny, bezpieczeństwa pracy i ochrony środowiska','Specjaliści do spraw higieny, bezpieczeństwa pracy i ochrony środowiska oceniają, planują i wdrażają programy, mające na celu rozpoznawanie, monitorowanie i kontrolowanie czynników środowiskowych, które mogą mieć potencjalny wpływ na zdrowie ludzi, zapewnianie bezpieczeństwa i zdrowia w miejscu pracy oraz zapobieganie chorobom lub obrażeniom spowodowanym czynnikami chemicznymi, fizycznymi, radiologicznymi i biologicznymi lub czynnikami ergonomicznymi.','Zadania wykonywane w grupie specjalistów do spraw higieny, bezpieczeństwa pracy i ochrony środowiska zazwyczaj obejmują: opracowywanie, wdrażanie i dokonywanie przeglądu programów i polityk mających na celu minimalizowanie potencjalnych zagrożeń środowiskowych i zawodowych dla zdrowia i bezpieczeństwa; przygotowywanie i wdrażanie planów oraz strategii dotyczących bezpiecznego, ekonomicznego i odpowiedniego usuwania odpadów gospodarczych, przemysłowych, medycznych i komunalnych; wdrażanie programów i strategii zapobiegawczych dotyczących chorób zaraźliwych, bezpieczeństwa żywności, systemów oczyszczania ścieków i usuwania odpadów, jakości wody wykorzystywanej do uprawiania sportów wodnych i w gospodarstwach domowych, substancji zanieczyszczonych i niebezpiecznych; określanie, zgłaszanie i dokumentowanie niebezpieczeństw, ocenianie i kontrolowanie zagrożeń w środowisku i miejscu pracy oraz doradztwo w zakresie przestrzegania odpowiednich przepisów; opracowywanie, wdrażanie i monitorowanie programów mających na celu zminimalizowanie zanieczyszczeń w miejscu pracy i w środowisku stanowiących zagrożenie chemiczne, fizyczne i biologiczne; doradztwo w zakresie metod zapobiegania, eliminowania, kontrolowania lub redukowania narażenia pracowników, uczniów, społeczeństwa i środowiska na zagrożenia radiologiczne i inne; promowanie zasad ergonomii w miejscu pracy dotyczące m.in.: obierania mebli, sprzętu i pracy do potrzeb pracowników; zapewnianie kształcenia, informacji, szkolenia i porad we wszystkich aspektach higieny pracy i zdrowia środowiskowego; rejestrowanie obrażeń i uszkodzenia sprzętu i zgłaszanie stanu bezpieczeństwa; koordynację ustaleń w zakresie odszkodowań, rehabilitacji i powrotu do pracy pracowników, którzy odnieśli obrażenia; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',71);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2292','Fizjoterapeuci','Fizjoterapeuci oceniają, planują i wdrażają programy rehabilitacji poprawiające lub przywracające funkcje motoryczne u ludzi, zwiększające możliwości ruchowe, leczące zespoły bólowe i pomagające w przypadku problemów fizycznych związanych z obrażeniami, chorobami i innymi rodzajami upośledzenia oraz zapobiegające takim problemom. Stosują szeroki zakres terapii fizycznych i technik, takich jak: ruch, ultradźwięki, rozgrzewanie, laser i inne techniki.','Zadania wykonywane w grupie fizjoterapeutów zazwyczaj obejmują: zlecanie badań mięśni, układu nerwowego, stawów i badań funkcji ruchowych oraz badanie problemów fizycznych u pacjentów; ustalanie z pacjentami celów leczenia i opracowywanie programów leczenia mających na celu ograniczenie bólu fizycznego, wzmocnienie mięśni, poprawienie funkcji sercowo-płucnych, sercowo-naczyniowych i oddechowych, przywracanie funkcji stawów i poprawianie równowagi i koordynacji; opracowywanie, wdrażanie i monitorowanie programów i leczenia z wykorzystaniem terapeutycznych właściwości ćwiczeń, ciepła, zimna, masażu, zabiegów, hydroterapii, elektroterapii, ultrafioletu i podczerwieni oraz ultradźwięków w leczeniu pacjentów; powiadamianie pacjentów i ich rodzin o zabiegach, które należy kontynuować poza kliniką; rejestrowanie informacji o stanie zdrowia pacjentów i reakcjach na leczenie w systemach archiwizacji danych i przekazywanie tych informacji innym specjalistom ochrony zdrowia niezbędnych do zapewnienia ciągłej i kompleksowej opieki; opracowywanie i wdrażanie programów monitorowania dolegliwości i zaburzeń fizycznych oraz zapobieganie im; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie pracy asystentów fizjoterapii i innych osób.',72);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2293','Dietetycy i specjaliści do spraw żywienia','Dietetycy i specjaliści do spraw żywienia oceniaj, planują i wdrażają programy mające na celu poprawę skutków wpływu żywności i żywienia na zdrowie ludzi. Opracowują oraz przygotowują różnego typu diety dla ludzi zdrowych i chorych i prowadzą działalność oświatową w zakresie prawidłowego żywienia.','Zadania wykonywane w grupie dietetyków i specjalistów do spraw żywienia zazwyczaj obejmują: udzielanie osobom indywidualnym, rodzinom i społecznościom porad dotyczących żywienia, planowanie diet i przygotowywanie żywności w celu zwiększenia korzyści zdrowotnych i ograniczenie potencjalnego zagrożenia dla zdrowia; planowanie diet i menu, nadzorowanie przygotowania i podawania posiłków oraz monitorowanie spożycia oraz jakości pożywienia w celu zapewnienia opieki w zakresie żywienia w miejscach, w których oferowane są usługi gastronomiczne; gromadzenie i dokonywanie oceny danych związanych ze stanem zdrowia i wyżywienia osób indywidualnych, grup i społeczności w oparciu o wartości odżywcze żywności; planowanie i dokonywanie oceny żywienia, programów interwencyjnych oraz kształcenia i szkolenia w celu podniesienia poziomów wyżywienia osób indywidualnych i społeczności; konsultacje z innymi specjalistami ochrony zdrowia i specjalistami w zakresie opieki w celu zaspokajania potrzeb związanych z dietą oraz żywieniem pacjentów; opracowywanie i ocenianie produktów spożywczych w celu spełnienia wymogów dotyczących żywienia; prowadzenie badań w zakresie żywienia i udostępnianie ich wyników między innymi na konferencjach naukowych; prowadzenie dokumentacji żywieniowej w placówkach służby zdrowia (kalkulacja kosztów, jadłospisy, plany żywieniowe, rozliczenia materiałowe); doradzanie w indywidualnych problemach doboru diety specjalnej (np. po określonym typie operacji, dla ludzi nie przyswajających pewnych składników pokarmowych); wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',73);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2294','Audiofonolodzy i logopedzi','Audiofonolodzy i logopedzi zajmują się oceną i leczeniem występujących wśród ludzi fizycznych zaburzeń słuchu, mowy, komunikacji i połykania. Zalecają stosowanie aparatów korekcyjnych lub terapii rehabilitacyjnych dla osób dotkniętych utratą słuchu, zaburzeniami mowy oraz powiązanymi problemami sensorycznymi i problemami układu nerwowego, udzielają porad w zakresie bezpieczeństwa słuchu i skutecznej komunikacji.','Zadania wykonywane w grupie audiofonologów i logopedów zazwyczaj obejmują: ocenę słuchu, mowy i posługiwania się językiem przez pacjentów w celu określenia charakteru zaburzeń słuchu i komunikacji; przeprowadzanie badań słuchu lub mowy/testów językowych lub innych badań z użyciem specjalistycznych instrumentów diagnostycznych i specjalistycznego sprzętu diagnostycznego oraz interpretowanie wyników badań w świetle innych danych medycznych, społecznych i behawioralnych w celu ustalenia właściwego przebiegu leczenia; planowanie działań doradczych, kontroli, rehabilitacji mowy i innych programów związanych z zagadnieniami słuchu i komunikacji, kierowanie nimi i branie w nich udziału; przepisywanie aparatów słuchowych i innych urządzeń wspomagających zgodnie z potrzebami pacjentów oraz informowanie pacjentów o sposobie ich użycia; planowanie i realizowanie programów wykorzystywanych w leczeniu u pacjentów zaburzeń mowy i połykania, takich jak: jąkanie się, zaburzenia karmienia, itp.; udzielanie porad osobom cierpiącym na zaburzenia słuchu lub upośledzenie językowe, ich rodzinom, nauczycielom i pracodawcom oraz kierowanie nimi; w razie potrzeby kierowanie pacjentów i ich rodzin na dodatkowe leczenie lub zajęcia edukacyjne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',74);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2295','Optometryści','Optometryści zajmują się diagnozowaniem i leczeniem chorób oczu i zaburzeń wzroku; w przypadku spostrzeżenia zmian sugerujących odstępstwa od norm fizjologicznych, kierują pacjentów do lekarza specjalisty. Udzielają porad z zakresu pielęgnacji oczu i przepisują pomoce optyczne lub zalecają stosowanie innych form terapii zaburzeń wzroku.','Zadania wykonywane w grupie optometrystów zazwyczaj obejmują: badanie oczu pacjentów i przeprowadzanie badań diagnostycznych w celu oceny zdrowia narządu wzroku i określenia charakteru i zakresu problemów i nieprawidłowości związanych ze wzrokiem; badanie funkcji wzroku z użyciem wyspecjalizowanych instrumentów i sprzętu do pomiaru ostrości wzroku, funkcji dróg wzrokowych, pola widzenia, ruchu gałki ocznej, swobody widzenia i ciśnienia śródgałkowego; wykrywanie, diagnozowanie i leczenie chorób oczu, w tym przepisywanie leków na choroby oczu; konsultowanie się z oftalmologami lub innymi specjalistami ochrony zdrowia lub kierowanie do nich pacjentów, jeżeli niezbędne jest dodatkowe leczenie; wykrywanie i diagnozowanie zaburzeń ruchu gałki ocznej i wad widzenia dwuocznego oraz planowanie i realizowanie programów leczenia; udzielanie pacjentom porad z zakresu ćwiczenia oczu służącego koordynacji ruchów gałek ocznych i skupieniu wzroku; dobieranie i przepisywanie okularów korekcyjnych, soczewek kontaktowych i innych pomocy wzrokowych oraz sprawdzanie urządzeń optycznych pod względem ich sprawności, bezpieczeństwa, wygody stosowania i wpływu na styl życia; udzielanie porad związanych ze stanem narządów wzroku, takich jak: pielęgnacja soczewek kontaktowych, dbałość o wzrok przez osoby starsze, optyka, ergonomia widzenia oraz bezpieczeństwo oczu w miejscu pracy i w sektorze przemysłowym; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',75);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2299','Specjaliści ochrony zdrowia gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą specjaliści ochrony zdrowia nie zaklasyfikowani do grupy średniej 229 Inni specjaliści ochrony zdrowia. Grupa ta obejmuje osoby zajmujące się: epidemiologią, koordynowaniem badań klinicznych, kosmetologią, psychoterapią, terapią uzależnień.','Zadania wykonywane w grupie specjalistów ochrony zdrowia gdzie indziej niesklasyfikowanych zazwyczaj obejmują: przeprowadzanie wywiadów z pacjentami i wykonywanie badań diagnostycznych w celu określenia stanu zdrowia pacjentów oraz charakteru zaburzeń fizycznych lub psychicznych, chorób lub innych problemów zdrowotnych; opracowywanie i realizowanie planów leczenia urazów, chorób i innych zaburzeń fizycznych i psychicznych; ocenianie i dokumentowanie postępów pacjentów osiąganych dzięki planom leczenia, a w razie konieczności, kierowanie pacjentów i ich rodzin do lekarzy lub innych osób sprawujących opiekę zdrowotną w celu poddania ich zabiegom specjalistycznym lub rehabilitacyjnym; ocenianie ograniczeń funkcjonalnych klientów spowodowanych chorobą, niepełnosprawnością lub innymi zaburzeniami oraz zapewnianie terapii; planowanie i realizowanie programów psychoterapeutycznych i terapeutycznych o zasięgu indywidualnym i grupowym mających na celu poprawę i utrzymanie funkcjonowania w aspekcie fizycznym, poznawczym, emocjonalnym i społecznym, w tym poprzez wykorzystanie sztuki i rzemiosła, tańca i ruchu, muzyki i innych zajęć rekreacyjnych i ruchowych; pełnienie dyżurów specjalistycznych i udzielanie konsultacji za pomocą środków łączności internetowej, telefonicznej i wideofonicznej; branie udziału w doskonaleniu zawodowym (szkoleniu) innych pracowników oraz prowadzenie badań naukowych i publikowanie ich wyników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',76);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2310','Nauczyciele akademiccy','Nauczyciele akademiccy zajmują się prowadzeniem zajęć dydaktycznych; w ramach swojej dyscypliny lub specjalności; przygotowywaniem i wygłaszaniem wykładów, zajęć seminaryjnych, konwersatoriów i ćwiczeń w ramach zalecanego programu zajęć w szkołach wyższych. Prowadzą badania naukowe i przygotowują opracowania i publikacje naukowe.','Zadania wykonywane przez nauczycieli akademickich zazwyczaj obejmują: opracowywanie programów nauczania i wprowadzanie do nich zmian oraz przygotowywanie programu zajęć zgodnie z wymogami; przygotowywanie i wygłaszanie wykładów oraz prowadzenie ćwiczeń, konwersatoriów i seminariów; prowadzenie badań naukowych i przeprowadzanie eksperymentów laboratoryjnych; zachęcanie studentów do prowadzenia dyskusji i do kształtowania niezależnych opinii; nadzorowanie, w stosownych przypadkach eksperymentalnych i praktycznych, działań prowadzonych przez studentów; przeprowadzanie egzaminów ustnych i testowych oraz ich sprawdzanie i ocenianie; kierowanie badaniami prowadzonymi przez doktorantów lub przez innych członków katedry; prowadzenie badań nad koncepcjami, teoriami i metodami operacyjnymi mającymi zastosowanie w przemyśle i innych dziedzinach oraz rozwijanie ich; przygotowywanie publikacji, raportów z badań i artykułów naukowych; uczestniczenie w spotkaniach katedry i wydziału oraz branie udziału w konferencjach i seminariach. Do ich zadań może także należeć nadzorowanie innych pracowników.',77);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2320','Nauczyciele kształcenia zawodowego','Nauczyciele kształcenia zawodowego zajmują się nauczaniem przedmiotów zawodowych w zasadniczych szkołach zawodowych i średnich szkołach zawodowych oraz instytucjach edukacyjnych dla dorosłych oraz w placówkach oferujących edukację na dalszych etapach. Przygotowują uczniów do pracy w określonych zawodach lub grupach zawodów.','Zadania wykonywane przez nauczycieli kształcenia zawodowego zazwyczaj obejmują: opracowywanie programów nauczania oraz metod nauczania; określanie potrzeb szkoleniowych uczniów lub słuchaczy dorosłych; utrzymywanie współpracy z osobami reprezentującymi dane branże i sektory kształcenia w celu zapewnienia właściwego programu kształcenia; prowadzenie zajęć i dyskusji w celu zwiększenia wiedzy i rozwijania kompetencji uczniów; kształtowanie u uczniów umiejętności praktycznych, wymaganych przy wykonywaniu danego zawodu poprzez prowadzanie zajęć praktycznych w warsztatach szkolnych lub w zakładach pracy; obserwowanie i ocenianie pracy uczniów w celu określania postępów, zapewniania informacji zwrotnej; przygotowywanie sprawozdań i prowadzenie wymaganej dokumentacji dotyczącej m.in.: ocen uczniów, obecności na zajęciach; nadzorowanie indywidualnych lub grupowych praktyk zawodowych, pracy w warsztatach szkolnych, laboratoriach lub zakładach pracy; udzielanie indywidualnych konsultacji uczniom lub przeprowadzanie zajęć wyrównawczych; projektowanie, a w razie potrzeby wykonywanie technicznych środków dydaktycznych; pełnienie funkcji wychowawczych i przeprowadzanie lekcji wychowawczych; organizowanie lub promowanie działalności pozalekcyjnej czy pozaszkolnej, takiej jak: uczestnictwo na forach dyskusyjnych, w kołach zainteresowań; uczestniczenie w spotkaniach dotyczących edukacyjnej i organizacyjnej polityki szkoły; przygotowywanie się do spotkań z rodzicami i uczestniczenie w nich w celu przedstawienia postępów uczniów. Do ich zadań może także należeć nadzorowanie innych pracowników.',78);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2330','Nauczyciele gimnazjów i szkół ponadgimnazjalnych (z wyjątkiem nauczycieli kształcenia zawodowego)','Nauczyciele gimnazjów i ponadgimnazjalnych (z wyjątkiem nauczycieli kształcenia zawodowego) zajmują się nauczaniem jednego lub większej liczby przedmiotów w gimnazjach oraz szkołach ponadgimnazjalnych, takich, jak: licea ogólnokształcące, licea profilowane, technika, zasadnicze szkoły zawodowe, uzupełniające szkoły ogólnokształcące, technika uzupełniające i szkoły policealnych, z wyjątkiem przedmiotów przygotowujących uczniów do pracy w określonych sektorach zawodowych.','(z wyjątkiem nauczycieli kształcenia zawodowego) zazwyczaj obejmują: opracowywanie programów nauczania, wprowadzanie do nich zmian oraz przygotowywanie programów zajęć zgodnie z wytycznymi programu nauczania; określanie i wprowadzanie zasad postępowanie i procedur utrzymywania porządku wśród uczniów; przygotowywanie i prowadzenie lekcji, dyskusji i prezentacji z zakresu jednego lub większej liczby przedmiotów; określanie celów lekcji i projektów do realizacji; przygotowywanie materiałów i sal dydaktycznych do przeprowadzenia zajęć; dostosowywanie metod nauczania i materiałów edukacyjnych do potrzeb i zainteresowań uczniów; obserwowanie i ocenianie wyników i zachowania uczniów; przygotowywanie, przeprowadzanie i ocenianie testów, zadań i egzaminów w celu określenia postępów uczniów; przygotowywanie raportów dotyczących pracy uczniów oraz prowadzenie konsultacji z innymi nauczycielami i z rodzicami; sprawowanie wychowawstwa i przeprowadzanie lekcji wychowawczych; uczestniczenie w spotkaniach dotyczących edukacyjnej lub organizacyjnej polityki szkoły; planowanie i organizowanie zajęć szkolnych, takich jak: wycieczki, imprezy sportowe i koncerty oraz uczestniczenie w nich; organizowanie lub promowanie działalności pozalekcyjnej i pozaszkolnej, takiej jak: uczestnictwo na forach dyskusyjnych, w kołach zainteresowań; uczestniczenie w spotkaniach dotyczących edukacyjnej i organizacyjnej polityki szkoły; przygotowywanie się do spotkań z rodzicami i uczestniczenie w nich w celu przedstawienia postępów uczniów. Do ich zadań może także należeć nadzorowanie innych pracowników.',79);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2341','Nauczyciele szkół podstawowych','Nauczyciele szkół podstawowych zajmują się nauczaniem kilku przedmiotów w szkołach podstawowych.','Zadania wykonywane przez nauczycieli szkół podstawowych zazwyczaj obejmują: przygotowywanie dziennych i długoterminowych planów lekcji zgodnie z wytycznymi programu nauczania; prowadzenie zajęć indywidualnych i grupowych z użyciem różnych metod nauczania i materiałów edukacyjnych (np. programów komputerowych, książek, gier) dostosowanych do zróżnicowanych potrzeb dzieci; utrzymywanie dyscypliny i dobrych nawyków pracy w sali lekcyjnej; planowanie i przeprowadzanie z udziałem dzieci zajęć sportowych, koncertów i wycieczek; zadawanie i ocenianie pracy na lekcji i pracy domowej; przygotowywanie, przeprowadzanie i ocenianie testów i zadań w celu określenia postępów dzieci; obserwowanie i ocenianie wyników i zachowania dzieci; sprawowanie nadzoru nad dziećmi podczas lekcji oraz podczas całego czasu przebywania w szkole, w tym podczas przebywania przez dzieci na placu zabaw oraz w czasie przerw; uczestniczenie w radach pedagogicznych oraz konsultowanie się z innymi nauczycielami w kwestiach dotyczących kształcenia; uczestniczenie w spotkaniach dotyczących edukacyjnej i organizacyjnej polityki szkoły; przygotowywanie się do spotkań z rodzicami i uczestniczenie w nich w celu przedstawienia postępów uczniów. Do ich zadań może także należeć nadzorowanie innych pracowników.',80);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2342','Specjaliści do spraw wychowania małego dziecka','Specjaliści do spraw wychowania małego dziecka, do których należą nauczyciele przedszkoli i wychowawcy małego dziecka zajmują się promowaniem społecznego, fizycznego i intelektualnego rozwoju dzieci, które nie osiągnęły wieku szkolnego poprzez prowadzenie zajęć edukacyjnych i wychowawczych.','Zadania wykonywane przez specjalistów do spraw wychowania małego dziecka zazwyczaj obejmują: planowanie i organizowanie zajęć indywidualnych i grupowych mających na celu rozwijanie u dzieci zdolności motorycznych, umiejętności współpracy i umiejętności społecznych, pewności siebie i zrozumienia; rozwijanie umiejętności językowych poprzez opowiadanie historii, odgrywanie scenek i wykorzystywanie piosenek, wierszy oraz swobodnych konwersacji i dyskusji; kierowanie działaniami dzieci umożliwiającymi im wykazanie się kreatywnością przez wykorzystanie sztuki, aktorstwa, muzyki i sprawności fizycznej; obserwowanie dzieci w celu określenia ich postępów oraz wykrywania, we współpracy z rodzicami lub psychologiem, oznak problemów rozwojowych, emocjonalnych i zdrowotnych; obserwowanie i ocenianie kwestii związanych z dożywianiem dzieci, dobrem i ich potrzebami w zakresie bezpieczeństwa; nadzorowanie zabaw dzieci w celu zapewniania ich bezpieczeństwa; udzielenie wskazówek dzieciom i pomaganie im w ukształtowaniu właściwych nawyków związanych z jedzeniem, ubieraniem się, itp.; współpraca z rodzicami i innymi pracownikami merytorycznymi o postępach dzieci lub ich problemach oraz, w razie potrzeby, ustalanie stosownych działań i przekazywanie ich do właściwych służb; uczestniczenie w spotkaniach dotyczących edukacyjnej i organizacyjnej polityki placówki; nawiązywanie i utrzymywanie współpracy z podmiotami pracującymi z małymi dziećmi i świadczącymi inne usługi na rzecz dzieci; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',81);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2351','Wizytatorzy i specjaliści metod nauczania','Wizytatorzy i specjaliści metod nauczania zajmują się analizą i kontrolą pracy nauczycieli, funkcjonowaniem instytucji oświatowych i osiąganymi wynikami przez uczniów oraz zalecają zmiany i udoskonalenia procesu dydaktycznego. Przeprowadzają badania, opracowują nowe metod nauczania oraz pomoce dydaktyczne; udzielają porad z tego zakresu; zajmują się metodyką edukacji multimedialnej oraz edukacji na odległość.','Zadania wykonywane przez wizytatorów i specjalistów metod nauczania zazwyczaj obejmują: regularne wizytowanie szkół i prowadzenie konsultacji z pracownikami administracyjnymi i personelem dydaktycznym na tematy dotyczące programów nauczania, metod nauczania, sprzętu i innych kwestii; wizytowanie sal lekcyjnych w celu obserwacji technik nauczania oraz ocenienia jakości pracy nauczycieli i ich osiągnięć dydaktycznych; przygotowywanie sprawozdań i kierowanie do władz oświatowych zaleceń dotyczących ewentualnych zmian i udoskonaleń w programach i metodach nauczania; ocenianie programów poszczególnych przedmiotów, form egzaminowania oraz udzielanie porad w tym zakresie; zbieranie informacji o pomocach audiowizualnych i innych pomocach dydaktycznych oraz organizowanie wprowadzenia ich w szkołach i innych instytucjach oświatowych; zapewnianie nauczycielom możliwości ciągłego rozwoju zawodowego, dostępu do szkoleń i usług doradczych; organizowanie i przeprowadzanie warsztatów i konferencji mających na celu zapoznanie nauczycieli z nowymi programami i metodami nauczania; opracowywanie struktury, treści i celów nowych programów i metod nauczania, w tym z zakresu edukacji multimedialnej i nauczania na odległość. Do ich zadań może także należeć nadzorowanie innych pracowników.',82);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2352','Nauczyciele szkół specjalnych','Nauczyciele szkół specjalnych pracujący z dziećmi, młodzieżą i osobami dorosłymi o specjalnych potrzebach edukacyjnych i wychowawczych na różnym poziomie nauczania. Zajmują się nauczaniem niepełnosprawnych fizycznie lub umysłowo upośledzonych, osób mających trudności z nauką lub osób o innych specjalnych potrzebach edukacyjnych (głuchych i niedosłyszących, niedostosowanych społecznie, niewidomych i niedowidzących, przewlekle chorych i niepełnosprawnych ruchowo, upośledzonych umysłowo). Promują społeczny, emocjonalny, intelektualny i fizyczny rozwój swoich uczniów.','Zadania wykonywane przez nauczycieli szkół specjalnych ujętych w tej grupie zazwyczaj obejmują: ocenianie możliwości i ograniczeń uczniów związanych z upośledzeniem intelektualnym, fizycznym, społecznym i emocjonalnym lub innymi specyficznymi problemami; opracowywaniem programów nauczania lub wprowadzaniem do nich zmian, przygotowywanie i przeprowadzanie zajęć według tych programów dostosowanych do możliwości i potrzeb uczniów; prowadzenie zajęć indywidualnych lub grupowych z wykorzystaniem specjalnych technik lub pomocy dydaktycznych odpowiadających potrzebom uczniów; stosowanie podczas zajęć specjalnych strategii i technik nauczania w celu poprawy rozwoju zdolności czuciowo-motorycznych i percepcyjno-motorycznych, usprawnienia posługiwania się językiem, poprawy funkcji poznawczych i pamięci; określanie i wprowadzanie zasad postępowania oraz procedur i zasad utrzymywania porządku wśród uczniów; nauczanie praktycznych umiejętności i zaradności wśród uczniów z upośledzeniem słuchu, wzroku oraz z innymi rodzajami upośledzenia; pobudzanie i rozwijanie u uczniów pewności siebie, zainteresowań, zdolności, umiejętności manualnych i koordynacji; prowadzenie konsultacji z innymi pracownikami dydaktycznymi w zakresie planowanie i organizowania lekcji dla uczniów o specjalnych potrzebach edukacyjnych; przygotowywanie danych o uczniach oraz sprawozdań; stosowanie indywidualnych form oceny postępów każdego ucznia; opracowanie indywidualnych planów nauczania uczniów w konsultacji z rodzicami, dyrektorem placówki oraz innymi specjalistami zaangażowanymi w proces edukacyjny i opiekuńczo-wychowawczy, służących promowaniu rozwoju uczniów. Do ich zadań może także należeć nadzorowanie innych pracowników.',83);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2353','Lektorzy języków obcych','Lektorzy języków obcych zajmują się nauczaniem języków obcych dzieci, młodzieży oraz osób dorosłych, którzy uczą się języka, aby ułatwić sobie udział w programach edukacyjnych realizowanych w języku obcym lub spełnić wymagania związane z pracą, dla celów migracyjnych lub dla rozwoju osobistego. Pracują oni poza strukturą systemu szkół podstawowych, średnich i wyższych lub wspierają uczniów i nauczycieli w ramach tego systemu.','Zadania wykonywane przez lektorów języków obcych ujętych w tej grupie zazwyczaj obejmują: ocenianie stopnia umiejętności językowych i zakresu trudności językowych uczniów oraz określanie potrzeb i celów ich kształcenia; planowanie, przygotowywanie i prowadzenie lekcji oraz warsztatów grupowych i indywidualnych, których program i tempo realizacji są dostosowane do możliwości i potrzeb uczniów; opracowywanie i przygotowywanie materiałów dydaktycznych oraz dostosowywanie do potrzeb istniejących materiałów; ocenianie postępów uczniów; udzielanie pomocy uczniom podczas lekcji, w trakcie których przedmioty są nauczane w języku innym niż ich język ojczysty; udzielanie wsparcia innym nauczycielom przez opracowywanie specjalnych programów nauczania przeznaczonych dla uczniów uczących się języka obcego, w którym są prowadzone zajęcia; zadawanie i sprawdzanie prac oraz przygotowywanie i ocenianie wyników egzaminów; ocenianie, dokumentowanie i sporządzanie raportów z postępów uczniów. Do ich zadań może także należeć nadzorowanie innych pracowników.',84);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2354','Nauczyciele muzyki w placówkach pozaszkolnych','Nauczyciele muzyki w placówkach pozaszkolnych zajmują się nauczaniem praktycznych i teoretycznych aspektów muzyki oraz jej wykonywania wśród uczniów poza strukturami systemu szkół podstawowych, średnich i wyższych, ale mogą także prowadzić zajęcia indywidualne lub w małych grupach, w ramach zajęć pozalekcyjnych organizowanych we współpracy z instytucjami i placówkami oświatowymi i kulturalnymi.','Zadania wykonywane przez nauczyciele muzyki w placówkach pozaszkolnych zazwyczaj obejmują: ocenianie zdolności uczniów oraz określanie ich potrzeb i celów nauczania; planowanie, przygotowywanie i realizowanie programów nauczania, planów lekcji i warsztatów przeznaczonych dla uczniów indywidualnych oraz dla grup; przygotowywanie i prezentowanie materiałów dotyczących teorii muzyki i interpretacji muzyki; nauczanie i prezentowanie praktycznych aspektów śpiewu lub gry na określonym instrumencie; rozwijanie u uczniów umiejętności czytania i pisania nut; zadawanie zadań i nauczanie utworów muzycznych na poziomie trudności odpowiadającym umiejętnościom, zainteresowaniom i talentowi uczniów; ocenianie uczniów oraz doradzanie im, przedstawianie uwag i zachęcanie do pracy; udoskonalanie programów nauczania, programów kursów, materiałów kursów i metod nauczania; przygotowywanie uczniów do egzaminów, występów i sprawdzianów; organizowanie wyjazdów na występy muzyczne; organizowanie przesłuchań lub występów z udziałem uczniów oraz udzielanie pomocy przy ich organizacji. Do ich zadań może także należeć nadzorowanie innych pracowników.',85);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2355','Nauczyciele sztuki w placówkach pozaszkolnych','Nauczyciele sztuki w placówkach pozaszkolnych zajmują się nauczaniem praktycznych i teoretycznych aspektów tańca, sztuki aktorskiej, sztuk plastycznych i innych (z wyłączeniem muzyki) oraz jej wykonywania wśród uczniów poza strukturami systemu szkół podstawowych, średnich i wyższych, mogą także prowadzić zajęcia indywidualne lub w małych grupach w ramach zajęć pozalekcyjnych organizowanych we współpracy z instytucjami i organizacjami oświatowymi i kulturalnymi.','Zadania wykonywane przez nauczyciele sztuki w placówkach pozaszkolnych ujętych w tej grupie zazwyczaj obejmują: ocenianie zdolności uczniów oraz określanie ich potrzeb nauczania; planowanie, przygotowywanie i realizowanie programów nauczania, planów lekcji i warsztatów przeznaczonych dla uczniów indywidualnych i dla grup; przygotowywanie i prezentowanie materiałów dotyczących teorii nauczanego przedmiotu; nauczanie i prezentowanie praktycznych aspektów sztuki aktorskiej, tańca, sztuk plastycznych lub innych; zadawanie zadań i prac na poziomie trudności odpowiadającym umiejętnościom, zainteresowaniom i talentowi uczniów; ocenianie uczniów oraz doradzanie im, przedstawianie uwag krytycznych i zachęcanie do pracy; udoskonalanie programów nauczania, programów kursów, materiałów kursów i metod nauczania; przygotowywanie uczniów do egzaminów, występów i sprawdzianów; organizowanie wyjazdów na wystawy i występy; organizowanie występów uczniów lub wystaw ich prac oraz udzielanie pomocy przy ich organizowaniu. Do ich zadań może także należeć nadzorowanie innych pracowników.',86);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2356','Instruktorzy technologii informatycznych','Instruktorzy technologii informatycznych zajmują się opracowywaniem, organizowaniem i realizowaniem programów szkoleń i kursów dla użytkowników komputerów i innych technologii informatycznych poza strukturami systemu szkół podstawowych, średnich i wyższych.','Zadania wykonywane przez instruktorów technologii informatycznych ujętych w tej grupie zazwyczaj obejmują: określanie potrzeb i wymogów szkoleniowych z zakresu technologii informatycznych dotyczących użytkowników indywidualnych, grupowych oraz instytucji i organizacji; przygotowywanie i opracowywanie instruktażowych materiałów szkoleniowych i pomocy dydaktycznych, takich jak: podręczniki, pomoce wizualne, zajęcia dostępne on-line, prototypy oraz dokumenty referencyjne wspierające szkolenie; opracowywanie, koordynowanie, planowanie i realizowanie programów szkolenia, które można przeprowadzić w ramach zajęć indywidualnych oraz warsztatów grupowych, spotkań, pokazów i konferencji; monitorowanie i przeprowadzanie bieżącej oceny jakości i skuteczności szkolenia; posiadanie aktualnych informacji na temat wprowadzania na rynek nowych wersji produktu, rozwoju oprogramowania i ogólnych informacji na temat tendencji technologicznych, sporządzania produktów i materiałów przeznaczanych dla użytkowników, takich jak: podręczniki szkoleń i zajęć, pomoce on-line oraz instrukcje operacyjne i instrukcje dotyczące konserwacji. Do ich zadań może także należeć nadzorowanie innych pracowników.',87);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2359','Specjaliści nauczania i wychowania gdzie indziej niesklasyfikowani','Grupa tej grupy elementarnej należą specjaliści nauczania i wychowania nie zaklasyfikowani do grupy średniej 235 Inni specjaliści nauczania i wychowania. Grupa ta obejmuje na przykład osoby prowadzące zajęcia z przedmiotów innych niż języki obce i sztuka oraz osoby zapewniające uczniom doradztwo z zakresu kształcenia w placówkach szkolnych i pozaszkolnych, kształcenia na odległość, zajmujące się opieką psychologiczną pedagogiczną i wychowaniem w placówkach pozaszkolnych, oświatowych, wychowawczych i opiekuńczych, prowadzeniem bibliotek szkolnych, itp.','Zadania wykonywane przez specjalistów nauczania i wychowania gdzie indziej niesklasyfikowanych ujętych w tej grupie zazwyczaj obejmują: ocenianie zdolności uczniów oraz określanie ich potrzeb nauczania; planowanie, przygotowywanie i realizowanie programów nauczania, planów lekcji i warsztatów przeznaczonych dla osób indywidualnych oraz dla grup; przygotowywanie i prezentowanie materiałów dotyczących teorii nauczanego przedmiotu; nauczanie i prezentowanie praktycznych aspektów dotyczących danego przedmiotu; udoskonalanie i uzupełnianie programów nauczania, programów kursów i metod nauczania; przygotowywanie uczniów do egzaminów i sprawdzianów; doradzanie w kwestiach kształcenia, takich jak: wybór kierunku kształcenia, zawodu, kursu oraz programu szkolenia; organizowanie i prowadzenie zajęć, w tym kształcenie na odległość oraz ocenę postępów uczniów; doradzanie i pomoc w planowaniu kariery zawodowej, przezwyciężeniu problemów osobistych, społecznych lub negatywnych zachowań wpływających na naukę; przygotowywanie uczniów do dalszej nauki poprzez zachęcanie ich do realizacji ambitnych zadań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',88);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2411','Specjaliści do spraw księgowości i rachunkowości','Specjaliści do spraw księgowości i rachunkowości zajmują się planowaniem systemów rachunkowości na potrzeby osób indywidualnych i przedsiębiorstw, organizowaniem ich i administrowaniem nimi. Prowadzą badania i analizy, zajmują się kontrolą ewidencji księgowej i sprawozdań finansowych w celu zapewnienia ich dokładności i zgodności z obowiązującymi standardami rachunkowości i praktykami księgowymi.','Zadania wykonywane przez specjalistów do spraw księgowości i rachunkowości ujętych w tej grupie zazwyczaj obejmują: udzielanie porad z zakresu zasad funkcjonowania systemów budżetowych, kontroli rachunkowości, innych systemów rachunkowości oraz ich planowanie i wdrażanie; przygotowywanie i poświadczanie sprawozdań finansowych przedstawianych zarządowi, udziałowcom i organom ustawowym lub innym; przygotowywanie deklaracji podatkowych, udzielanie porad z zakresu problemów podatkowych i reprezentowanie przedsiębiorstw w urzędach podatkowych w sprawie spornych roszczeń; przygotowywanie prognoz zysków i budżetów lub sporządzanie sprawozdań na ich temat; przeprowadzanie dochodzeń finansowych w takich sprawach jak: podejrzenia o nadużycia finansowe, niewypłacalność i upadłość; poddawanie audytowi rachunków i dokumentacji księgowej; prowadzenie dochodzeń w sprawie finansowych aspektów wydajności pracy, pakietów udziałów, sprzedaży nowych produktów i doradzanie zarządowi w tych kwestiach; opracowywanie i kontrolowanie systemu umożliwiającego określanie kosztów jednostkowych produktów i usług; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',89);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2412','Doradcy finansowi i inwestycyjni','Doradcy finansowi i inwestycyjni zajmują się opracowywaniem planów finansowych dla osób prywatnych i organizacji oraz inwestowaniem funduszy i zarządzaniem nimi w imieniu tych osób oraz organizacji; doradztwem podatkowym, inwestycyjnym i emerytalnym.','Zadania wykonywane przez doradców finansowych i inwestycyjni ujętych w tej grupie zazwyczaj obejmują: tworzenie i utrzymywanie bazy klientów; przeprowadzanie wywiadów z klientami w celu określenia ich statusu finansowego, celów finansowych i stopnia ryzyka oraz uzyskania innych informacji niezbędnych do opracowania planów finansowych i strategii inwestycyjnych; ustalanie celów finansowych oraz opracowywanie i wdrażanie strategii służących osiąganiu tych celów; organizowanie zakupu i sprzedaży akcji i obligacji na rzecz klientów; monitorowanie wyników inwestycji oraz analizowanie i weryfikowanie planów inwestycyjnych opierających się na zmodyfikowanych potrzebach i zmianach na rynkach; rekomendowanie i zapewnianie ubezpieczenia klientów. Do ich zadań może także należeć nadzorowanie innych pracowników.',90);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2413','Analitycy finansowi','Analitycy finansowi (giełdowi, kredytowi, bankowi, ubezpieczeniowi) zajmują się przeprowadzaniem ilościowych analiz informacji wpływających na programy inwestycyjne instytucji publicznych lub osób prywatnych.','Zadania wykonywane przez analityków finansowych ujętych w tej grupie zazwyczaj obejmują: analizowanie informacji finansowych w celu przygotowywania prognoz dotyczących przedsiębiorstwa, branży i warunków gospodarczych, które są wykorzystywane podczas podejmowania decyzji inwestycyjnych; gromadzenie wiedzy i aktualnych informacji na temat osiągnięć w dziedzinie technologii przemysłowej, biznesu, finansów i teorii ekonomicznych; interpretowanie danych wpływających na programy inwestycyjne, takich jak: cena, produkcja, stabilność, przyszłe tendencje w dziedzinie zagrożeń inwestycyjnych i wpływy gospodarcze; monitorowanie osiągnięć gospodarczych, przemysłowych i korporacyjnych poprzez analizowanie informacji pozyskiwanych z publikacji i służb finansowych, firm bankowości inwestycyjnej, agencji rządowych, publikacji handlowych, źródeł przedsiębiorstwa i wywiadów przeprowadzanych osobiście; rekomendowanie inwestycji i terminów inwestowania przedsiębiorstwom, pracownikom firm inwestycyjnych lub osobom zajmującym się inwestowaniem; ustalanie cen, po których papiery wartościowe powinny być sprzedawane i oferowane na rynku; przygotowywanie, w oparciu o analizy finansowe, planów działania dotyczących inwestycji; ocenianie i porównywanie względnej jakości różnych papierów wartościowych w danej branży; przedstawianie ustnych i pisemnych sprawozdań dotyczących ogólnych tendencji gospodarczych, poszczególnych przedsiębiorstw i całej branży; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',91);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2421','Specjaliści do spraw zarządzania i organizacji','Specjaliści do spraw zarządzania i organizacji pomagają organizacjom w osiąganiu większej wydajności i w rozwiązywaniu problemów organizacyjnych. Zajmują się badaniem struktur, metod, systemów i procedur organizacyjnych zwiększających osiąganie zamierzonych celów finansowych, pozyskiwania i wykorzystywania funduszy unijnych oraz sprawnego zarządzania organizacją.','Zadania wykonywane przez specjalistów do spraw zarządzania i organizacji ujętych w tej grupie zazwyczaj obejmują: udzielanie pomocy w określaniu strategii i planów, których celem jest zadowolenie klientów, wydajne wykorzystywanie zasobów organizacji oraz motywowanie do określania celów, strategii i planów; analizowanie i ocenianie systemów zarządzania i struktur organizacyjnych instytucji, przedsiębiorstwa w celu ich doskonalenia biorąc pod uwagę wszystkie poziomy struktury organizacji; analizowanie istniejących i proponowanych metod i procedur, takich jak procedury administracyjne i urzędnicze; dokumentowanie, analizowanie i rekomendowanie propozycji dokonania zmiany metod, procedur i toku pracy, przedefiniowania funkcji pracy i rozwiązania problemów organizacyjnych; wspieranie wdrożenia zatwierdzonych zaleceń, wydawania zmienionych instrukcji dotyczących procedury pozyskiwania funduszy, realizacji projektów oraz innych dokumentów; dokonywanie rewizji procedur operacyjnych i udzielanie porad z zakresu odstępstw od procedur i standardów, itp.; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',92);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2422','Specjaliści do spraw administracji i rozwoju','Specjaliści do spraw administracji i rozwoju zajmują się opracowywaniem i analizowaniem polityki stanowiącej podstawę tworzenia, wdrażania i modyfikacji programów rządowych i handlowych; pełnią funkcje administracyjne i kontrolne wewnątrz służb publicznych (państwowych i samorządowych) oraz w organizacjach zajmujących się rozwojem przemysłu, transportu, usług gastronomicznych, hotelarskich i turystycznych, planowania strategicznego, stosunków międzynarodowych, zarządzania służbą zdrowia, zamówień publicznych oraz zarządzania kryzysowego.','Zadania wykonywane przez specjalistów do spraw administracji i rozwoju ujętych w tej grupie zazwyczaj obejmują: przygotowywania analiz z obszaru i tematyki swojego działania; podejmowanie decyzji administracyjnych w zakresie posiadanych uprawnień; ocenę skuteczności i celowości polityki gospodarczej regionu lub jednostki administracyjnej oraz formułowanie propozycji zmian; dokonywanie przeglądu istniejących polityk i ustawodawstwa w celu wykrycia nieprawidłowości i nieaktualnych przepisów; badanie tendencji społecznych, gospodarczych i przemysłowych oraz oczekiwań klientów związanych z zapewnianymi programami i usługami; formułowanie i analizowanie wariantów polityki, przygotowywanie materiałów informacyjnych i zaleceń dotyczących zmian polityki oraz doradzanie w zakresie preferowanych wariantów; ocenianie wpływów, skutków finansowych, wzajemnych relacji z innymi programami oraz politycznych i administracyjnych aspektów realizacji polityk; przeprowadzanie oceny zagrożeń i ryzyka oraz opracowywanie zaleceń; dokonywanie przeglądu operacji i programów w celu zapewnienia ich zgodności z polityką organizacji; opracowywanie raportów i ekspertyz dla swoich przełożonych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',93);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2423','Specjaliści do spraw zarządzania zasobami ludzkimi','Specjaliści do spraw zarządzania zasobami ludzkimi zajmują się świadczeniem profesjonalnych usług biznesowych związanych z rozwiązywaniem problemów zatrudnionego personelu, takimi jak: rekrutacją i selekcją kandydatów do pracy, zarządzaniem talentami, doradztwem personalnym i zawodowym, kulturą firmy, wynagrodzeniami, analizą zadań wykonywanych przez pracowników na stanowiskach pracy.','Zadania wykonywane przez specjalistów do spraw zarządzania zasobami ludzkimi ujętych w tej grupie zazwyczaj obejmują: doradzanie w kwestiach rekrutacji i zatrudnienia kandydatów do pracy, polityki awansów i wynagrodzeń, stosunków pracowników z pracodawcami lub w innych obszarach polityki kadrowej; badanie i analizowanie pracy na poszczególnych stanowiskach z użyciem różnych metod; przeprowadzanie wywiadów z pracownikami, przełożonymi i kierownictwem oraz sporządzanie na podstawie uzyskanych informacji szczegółowych opisów stanowiska pracy lub zawodu; przygotowywanie informacji zawodowych o zawodach i możliwościach zatrudnienia dla młodzieży, osób poszukujących pracy oraz osób pragnących zmienić pracę; udzielanie indywidualnych i grupowych porad zawodowych; analizowanie możliwości zatrudnienia, wyboru zawodu i możliwości podejmowania dalszego kształcenia lub szkolenia oraz doradzanie w tym zakresie; świadczenie usług z zakresu międzynarodowego pośrednictwa pracy oraz doradztwa z zakresu warunków życia i pracy w krajach Unii Europejskiej i Europejskiego Obszaru Gospodarczego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',94);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2424','Specjaliści do spraw szkoleń zawodowych i rozwoju kadr','Specjaliści do spraw szkoleń i rozwoju kadr zajmują się planowaniem, opracowywaniem, wdrażaniem i ocenianiem programów szkoleń i ścieżek rozwoju zawodowego pracowników, mających na celu kształtowanie umiejętności i kompetencji pracowniczych niezbędnych do osiągnięcia celów organizacyjnych.','Zadania wykonywane przez specjalistów do spraw szkoleń i rozwoju kadr ujętych w tej grupie zazwyczaj obejmują: określanie potrzeb i wymogów szkoleniowych osób prywatnych i organizacji; określanie celów rozwoju kadr i ocenianie efektów uczenia się; przygotowywanie i opracowywanie instruktażowych materiałów szkoleniowych i pomocy, takich jak: podręczniki, pomoce audiowizualne, zajęcia dostępne on-line, oraz dokumenty referencyjne wspierające szkolenie; opracowywanie, koordynowanie, planowanie i realizowanie programów szkoleń i rozwoju zawodowego pracowników; organizowania warsztatów, spotkań, pokazów i konferencji oraz współpraca z firmami szkoleniowymi (zewnętrznymi podmiotami prowadzącymi szkolenia) w tym zakresie; promowanie szkoleń i rozwoju wewnątrz firmy i poza nią oraz dokonywanie oceny działalności promocyjnej; monitorowanie, przeprowadzanie bieżącej oceny jakości i skuteczności szkoleń wewnętrznych i szkoleń organizowanych poza firmą; gromadzenie specjalistycznej literatury, materiałów dydaktycznych i szkoleniowych w celu poznania różnych tematów, metod i systemów prowadzenia szkoleń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć również nadzorowanie innych pracowników.',95);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2431','Specjaliści do spraw reklamy i marketingu','Specjaliści do spraw reklamy i marketingu zajmują się opracowywaniem i koordynowaniem strategii i kampanii reklamowych, ustalaniem rynku dla nowych towarów i usług oraz określaniem i tworzeniem możliwości rynkowych dla nowych i już istniejących towarów i usług.','Zadania wykonywane przez specjalistów do spraw reklamy i marketingu ujętych w tej grupie zazwyczaj obejmują: planowanie, opracowywanie, organizowanie kampanii reklamowych i przyjmowanie strategii kampanii reklamowych; doradzanie menedżerom i klientom, jaką strategie przyjąć, aby umożliwić dotarcie do rynków docelowych; budowanie świadomości konsumentów i skuteczne promowanie zalet towarów i usług; pisanie tekstów reklamowych i scenariuszy medialnych; organizowanie produkcji reklamowych programów telewizyjnych i filmowych oraz zamieszczania informacji o nich w mediach; analizowanie danych dotyczących wzorców zachowań i preferencji konsumentów; interpretowanie i prognozowanie aktualnych i przyszłych tendencji zachowań rynkowych konsumentów; badanie potencjalnego popytu i charakterystyki rynku, które dotyczą nowych towarów i usług oraz gromadzenie i analizowanie danych oraz innych informacji statystycznych; wspieranie rozwoju przedsiębiorstwa poprzez przygotowywanie i realizowanie celów, strategii i programów marketingowych; zlecanie i przeprowadzanie badania rynku w celu określenia możliwości rynkowych dotyczących nowych lub już istniejących towarów i usług; doradzanie w sprawie wszystkich elementów marketingowych, takich jak mix produktów, ustalanie cen, reklama i promocja sprzedaży, sprzedaż i kanały dystrybucji, itp.; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',96);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2432','Specjaliści do spraw public relations','Specjaliści do spraw public relations zajmują się planowaniem, opracowywaniem, wdrażaniem i ocenianiem strategii informacyjno-komunikacyjnych tworzących zrozumienie i korzystny obraz przedsiębiorstw i innych organizacji, ich towarów i usług oraz ich roli w społeczeństwie.','Zadania wykonywane przez specjalistów do spraw public relations ujętych w tej grupie zazwyczaj obejmują: planowanie i organizowanie kampanii reklamowych i strategii komunikacyjnych; doradzanie pracownikom szczebla kierowniczego w sprawie wpływu ich polityki, programów i praktyk na kwestie public relations w zakresie przygotowywania oraz kontrolowania informacji i komunikatów prasowych; przeprowadzanie i zlecanie badań opinii publicznej, analizowanie ich wyników oraz planowanie kampanii public relations i kampanii promocyjnych; organizowanie specjalnych imprez, seminariów, widowisk, konkursów i uroczystości mających na celu promowanie renomy przedsiębiorstwa i zapewnienie korzystnej reklamy; reprezentowanie organizacji i organizowanie wywiadów z przedstawicielami mediów; uczestniczenie w uroczystościach biznesowych, towarzyskich i innych w celu promowania organizacji; zlecanie wykonywania fotografii i innych materiałów ilustracyjnych oraz ich pozyskiwanie; wybieranie, ocenianie i sprawdzanie materiałów dostarczonych przez autorów tekstów reklamowych, fotografów, rysowników i inne osoby w celu stworzenia korzystnych materiałów reklamowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',97);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2433','Specjaliści do spraw sprzedaży (z wyłączeniem technologii informacyjno-komunikacyjnych)','Specjaliści do spraw sprzedaży (z wyłączeniem technologii informacyjno-komunikacyjnych) reprezentują przedsiębiorstwa podczas sprzedaży wielu towarów i usług przemysłowych, medycznych i farmaceutycznych firmom prowadzącym działalność przemysłową, biznesową i inną.','Zadania wykonywane przez specjalistów do spraw sprzedaży (z wyłączeniem technologii informacyjno-komunikacyjnych) ujętych w tej grupie zazwyczaj obejmują: tworzenie wykazów potencjalnych klientów z wykorzystaniem katalogów firm i innych źródeł; pozyskiwanie i uaktualnianie informacji na temat towarów i usług oraz warunków rynkowych; odwiedzanie przedsiębiorstw stałych lub potencjalnych klientów w celu tworzenia możliwości sprzedażowych i korzystania z już istniejących; ocenianie potrzeb i zasobów klientów i rekomendowanie im odpowiednich towarów lub usług; zapewnianie wkładu w projekt produktu w sytuacji, gdy towary i usługi należy dopasować o potrzeb klientów; opracowywanie sprawozdań i propozycji w ramach prezentacji handlowych w celu przedstawienia korzyści płynących z wykorzystywania towarów i usług; szacowanie kosztów instalowania i konserwacji sprzętu lub usługi; monitorowanie zmieniających się potrzeb klientów i działalności konkurentów oraz przekazywanie informacji na ten temat kierownikom do spraw sprzedaży; ustalanie i negocjowanie cen i warunków kredytu oraz przygotowywanie umów sprzedaży i zarządzanie nimi; organizowanie dostawy towarów, instalacji sprzętu i świadczenia usług; składanie kierownikom do spraw sprzedaży sprawozdań dotyczących wyników sprzedaży i pokupności towarów i usług; kontaktowanie się z klientami po dokonaniu sprzedaży w celu upewnienia się, że klient jest zadowolony z zakupu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',98);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2434','Specjaliści do spraw sprzedaży z dziedziny technologii teleinformatycznych','Specjaliści do spraw sprzedaży z dziedziny technologii teleinformatycznych zajmują się sprzedażą hurtową szerokiej gamy sprzętu komputerowego, oprogramowania i innych towarów i usług z dziedziny technologii informacyjno-komunikacyjnych, w tym instalacji oraz zajmują się prezentacją specjalistycznych informacji.','Zadania wykonywane przez specjalistów do spraw sprzedaży z dziedziny technologii teleinformatycznych ujętych w tej grupie zazwyczaj obejmują: pozyskiwanie zamówień i sprzedawanie towarów przedsiębiorstwom przemysłowym, firmom zajmującym się sprzedażą detaliczną lub hurtową; sprzedawanie sprzętu technicznego i zapasów przedsiębiorstwom lub osobom prywatnym; świadczenie usług pokrewnych na ich rzecz; omawianie potrzeb klientów oraz dostarczanie specjalistycznych informacji dotyczących sposobu zaspokojenia potrzeb dzięki poszczególnym elementom sprzętu, zapasom i usługom; ustalanie i negocjowanie cen i warunków kredytu; zawieranie umów i dokumentowanie zamówień; uaktualnianie akt klientów i przygotowywanie raportów sprzedaży; organizowanie dostawy towarów, instalacji sprzętu i świadczenia usług; przedstawianie producentom informacji na temat uwag i wymagań klientów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',99);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2440','Specjaliści do spraw rynku nieruchomości','Specjaliści do spraw rynku nieruchomości organizują sprzedaż, zakup i wynajem nieruchomości, zazwyczaj w imieniu klientów i na zasadzie prowizji.','Zadania wykonywane przez specjalistów do spraw rynku nieruchomości zazwyczaj obejmują: uzyskiwanie informacji dotyczących nieruchomości przeznaczonych do sprzedaży lub pod wynajem, ich właścicieli oraz potrzeb potencjalnych kupców lub najemców; prezentowanie potencjalnym nabywcom lub najemcom nieruchomości przeznaczonych do sprzedaży lub pod wynajem oraz wyjaśnianie warunków ich sprzedaży lub wynajmu; usprawnianie negocjacji między najemcami i właścicielami dotyczących czynszu i opłat za wynajem; sporządzanie umów najmu i sprzedaży oraz szacowanie kosztów; organizowanie podpisania umów najmu i przekazania praw do nieruchomości; pobieranie czynszu i kaucji w imieniu właściciela i wizytowanie nieruchomości przed, podczas i po okresie wynajmu; zatrudnienie pracowników wykonujących prace porządkowe związane z utrzymaniem nieruchomości w odpowiednim stanie; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',100);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2511','Analitycy systemów komputerowych','Analitycy systemów komputerowych zajmują się badaniem, analizowaniem i ocenianiem wymogów, procedur lub problemów klientów dotyczących technologii systemów teleinformatycznych oraz opracowywaniem i wdrażaniem propozycji, zaleceń i planów mających na celu poprawę obecnych lub projektowanych systemów teleinformatycznych.','Zadania wykonywane przez analityków systemów komputerowych zazwyczaj obejmują: prowadzenie konsultacji z użytkownikami, formułowanie i dokumentowanie ich wymagań, w konsultacji z przełożonymi; identyfikowanie i analizowanie procesów biznesowych, procedur i metod pracy; identyfikowanie i ocenianie przypadków niekompetencji i rekomendowanie optymalnych praktyk biznesowych oraz identyfikowanie i ocenianie funkcjonalności i działania systemu; branie odpowiedzialności za rozmieszczenie rozwiązań funkcjonalnych, takich jak: tworzenie, przyjmowanie i wdrażanie planów testów systemu; tworzenie specyfikacji funkcjonalnych wykorzystywanych przez firmy opracowujące systemy teleinformatyczne; zwiększanie zakresu systemów lub ich modyfikowanie w celu usprawnienia toku pracy lub służenia nowym celom; koordynowanie i łączenie systemów teleinformatycznych w ramach organizacji w celu zwiększenia ich kompatybilności. Do ich zadań może także należeć nadzorowanie innych pracowników.',101);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2512','Specjaliści do spraw rozwoju systemów informatycznych','Specjaliści do spraw rozwoju systemów informatycznych zajmują się badaniem, analizowaniem i ocenianiem wymogów dotyczących istniejących lub nowych aplikacji oprogramowania i systemów informatycznych oraz zajmują się projektowaniem, opracowywaniem, testowaniem i utrzymywaniem oprogramowania komputerowego w celu sprostania tym wymogom.','Zadania wykonywane przez specjalistów do spraw rozwoju systemów informatycznych zazwyczaj obejmują: badanie, analizowanie i ocenianie wymogów dotyczących aplikacji oprogramowania i systemów informatycznych; badanie, projektowanie i opracowywanie systemów oprogramowania komputerowego; prowadzenie konsultacji z inżynierami elektronikami w celu dokonania oceny sprzężenia sprzętu komputerowego z oprogramowaniem; opracowywanie i przeprowadzanie procedur testowania oprogramowania i procedur ich zatwierdzania; modyfikowanie istniejącego oprogramowania, dostosowanie oprogramowania do nowego sprzętu lub ulepszenia interfejsów i zwiększenia ich wydajności; kierowanie procesem tworzenia oprogramowania i opracowywania dokumentacji; ocenianie, tworzenie, ulepszanie i dokumentowanie procedur w zakresie obsługi technicznej systemów operacyjnych, środowisk komunikacyjnych i oprogramowania aplikacji; prowadzenie konsultacji z klientami w sprawie utrzymania systemów oprogramowania komputerowego. Do ich zadań może także należeć nadzorowanie innych pracowników.',102);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2513','Projektanci aplikacji sieciowych i multimediów','Projektanci aplikacji sieciowych i multimediów wykorzystują wiedzę z zakresu projektowania komputerowego oraz wiedzę techniczną, aby badać, analizować, oceniać, projektować animacje i gry komputerowe, programować, tworzyć i zmieniać strony internetowe oraz aplikacje łączące tekst, grafikę, animacje, obrazowania, odtwarzanie dźwięku i obrazu i inne media interaktywne; zajmują się rozwojem stron internetowych, Internetu i Intranetu.','Zadania wykonywane przez projektantów aplikacji sieciowych i multimediów zazwyczaj obejmują: analizowanie, projektowanie i tworzenie stron internetowych z wykorzystaniem sztuki i kreatywności w programowaniu i w językach skryptowych; projektowanie i tworzenie animacji cyfrowych, obrazowania, prezentacji, gier, klipów dźwiękowych i wideoklipów oraz aplikacji internetowych z wykorzystaniem oprogramowania multimedialnego, narzędzi i programów, interaktywnej grafiki i języków programowania; kontaktowanie się ze specjalistami do spraw sieci w kwestiach dotyczących stron internetowych, takich jak: bezpieczeństwo i prowadzenie stron internetowych, kontrolowanie i egzekwowanie bezpieczeństwa serwerów internetowych i serwerów stron internetowych, alokacja miejsca, dostęp użytkowników, ciągłość prowadzenia działalności, tworzenie kopii zapasowej strony internetowej i planowanie odzyskiwania jej po awarii; projektowanie, tworzenie i integrowanie kodu komputerowego z innymi wyspecjalizowanymi danymi wejściowymi, takimi jak: pliki obrazu, pliki dźwiękowe, języki skryptowe w celu tworzenia, utrzymania i wspierania stron internetowych; uczestniczenie w analizowaniu, określaniu i opracowywaniu strategii internetowych, metodologii stron internetowych i planów rozwoju. Do ich zadań może także należeć nadzorowanie innych pracowników.',103);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2514','Programiści aplikacji','Programiści aplikacji zajmują się pisaniem i obsługą techniczną kodu programowalnego wskazanego w instrukcjach i specyfikacjach technicznych dotyczących aplikacji oprogramowania komputerowego i systemów operacyjnych.','Zadania wykonywane przez programistów aplikacji zazwyczaj obejmują: pisanie i obsługę techniczną kodu programu wskazanego w instrukcjach i specyfikacjach zgodnie z jakością uznanych standardów; zmienianie, naprawianie lub ulepszanie istniejących programów w celu zwiększenia ich wydajności operacyjnej lub dostosowania ich do nowych wymogów; przeprowadzanie prób programów i aplikacji oprogramowania w celu potwierdzenia, że będą one źródłem wymaganych informacji; kompilowanie i sporządzanie dokumentacji związanej z tworzeniem programów; identyfikowanie problemów, procesów i rozwiązań technicznych oraz informowanie o nich. Do ich zadań może także należeć nadzorowanie innych pracowników.',104);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2519','Analitycy systemów komputerowych i programiści gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą analitycy systemowi i specjaliści do spraw rozwoju aplikacji komputerowych nie zaklasyfikowani do grupy średniej 251 Analitycy systemów komputerowych i programiści. Grupa ta obejmuje na przykład osoby zajmujące się informatyką medyczną, zastosowaniami informatycznymi, testowaniem oprogramowania komputerowego i systemów teleinformatycznych.','Zadania wykonywane przez analityków systemów komputerowych i programistów gdzie indziej niesklasyfikowanych zazwyczaj obejmują: opracowywanie i dokumentowanie planów testowania oprogramowania; instalowanie oprogramowania i sprzętu komputerowego oraz konfigurowanie operacyjnego oprogramowania systemu w ramach przygotowania do testów; weryfikowanie, czy program działa zgodnie z wymaganiami użytkowników i określonymi wytycznymi; przeprowadzanie testów aplikacji oprogramowania i testów systemów informacyjnych i telekomunikacyjnych oraz analizowanie i dokumentowanie ich wyników; opracowywanie i wdrażanie polityki, procedur i skryptów testowania oprogramowania i systemu informacyjnego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',105);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2521','Projektanci i administratorzy baz danych','Projektanci i administratorzy baz danych zajmują się projektowaniem, tworzeniem, kontrolowaniem, utrzymywaniem i wspieraniem optymalnej wydajności i bezpieczeństwa baz danych.','Zadania wykonywane przez projektantów i administratorów baz danych ujętych w tej grupie zazwyczaj obejmują: projektowanie i tworzenie architektury baz danych, struktur danych, tabel, słowników i konwencji nazewniczych dotyczących projektów systemów informacyjnych; projektowanie, konstruowanie, modyfikowanie, integrowanie, wdrażanie i testowanie systemów zarządzania bazami danych; przeprowadzanie badań i udzielanie porad z zakresu wyboru, stosowania i wdrażania narzędzi zarządzania bazami danych; tworzenie i wdrażanie polityki, standardów i modeli administrowania danymi; opracowywanie polityk i procedur dotyczących dostępu do baz danych; realizowanie planów operacyjnych i zapobiegawcze zachowywanie kopii zapasowych, stosowanie procedur odzyskiwania oraz przeprowadzanie kontroli bezpieczeństwa i integralności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',106);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2522','Administratorzy systemów komputerowych','Administratorzy systemów komputerowych zajmują się tworzeniem, kontrolowaniem, utrzymywaniem i wspieraniem optymalnej wydajności i bezpieczeństwa systemów technologii informacyjnych.','Zadania wykonywane przez administratorów systemów komputerowych ujętych w tej grupie zazwyczaj obejmują: utrzymywanie sieci komputerowych i powiązanych środowisk komputerowych i administrowanie nimi, w tym sprzętem komputerowym, oprogramowaniem systemów oraz oprogramowaniem aplikacji; rekomendowanie zmian w celu usprawnienia konfiguracji systemów i sieci oraz określanie związanych z tymi zmianami wymogów dotyczących sprzętu i oprogramowania; diagnozowanie problemów ze sprzętem i z oprogramowaniem; przeprowadzanie operacji związanych z tworzeniem kopii zapasowych i z odzyskiwaniem danych; operowanie konsolami głównymi w celu monitorowania wydajność systemów i sieci komputerowych oraz koordynowania dostępem do sieci komputerowej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników. Nazwa: Specjaliści do spraw sieci komputerowych Kod: 2523 Synteza: Specjaliści do spraw sieci komputerowych zajmują się badaniem, analizowaniem i rekomendowaniem strategii dotyczących architektury oraz rozwoju sieci, wdrażaniem, zarządzaniem, utrzymywaniem i konfigurowaniem sprzętu i oprogramowania sieci, monitorowaniem i optymalizowaniem wydajności sieci komputerowych. Zadania Zadania wykonywane przez specjalistów do spraw sieci komputerowych ujętych w tej grupie zazwyczaj obejmują: analizowanie, opracowywanie, interpretowanie i ocenianie złożonego projektu systemu i specyfikacji jego budowy, modeli i diagramów danych w ramach rozwijania, konfigurowania i integrowania systemów komputerowych; badanie, analizowanie, ocenianie i monitorowanie infrastruktury sieci w celu zapewnienia konfiguracji sieci umożliwiającej ich optymalne funkcjonowanie; ocenianie i zalecanie ulepszeń w ramach operacji sieci i zintegrowanego sprzętu, oprogramowania, komunikacji i systemów operacyjnych; zapewnianie specjalistycznego wsparcia oraz pomocy w rozwiązywania problemów z siecią i w usuwaniu skutków awarii; instalowanie, konfigurowanie, testowanie i obsługiwanie nowych i ulepszonych sieci, aplikacji, serwerów i stacji roboczych baz danych oprogramowania oraz administrowanie nimi; przygotowywanie i prowadzenie instrukcji utrzymania, procedur i dokumentacji dotyczących zasobów sieci oraz dokumentowanie wykrywania i usuwania wad sieci, ulepszeń i zmian wprowadzonych w sieci; monitorowanie komunikacji w sieci, aktywności, możliwości i wykorzystania sieci w celu zapewnienia trwałej integralności i optymalnej wydajności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',107);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2529','Specjaliści do spraw baz danych i sieci komputerowych gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą specjaliści do spraw baz danych i sieci nie zaklasyfikowanych do grupy średniej 252 Specjaliści do spraw baz danych i sieci komputerowych. Grupa ta obejmuje na przykład osoby zajmujące się bezpieczeństwem oprogramowania komputerowego.','Zadania wykonywane przez specjalistów do spraw baz danych i sieci gdzie indziej niesklasyfikowanych ujętych w tej grupie zazwyczaj obejmują: opracowywanie planów mających na celu ochronę plików komputerowych przed przypadkowymi lub nie zatwierdzonymi zmianami, zniszczeniem lub ujawnieniem oraz mających na celu sprostanie wymogom awaryjnego przetwarzania danych; szkolenie użytkowników i promowanie świadomości w zakresie bezpieczeństwa w celu zapewnienia bezpieczeństwa systemu i zwiększenia wydajności serwerów i sieci; utrzymywanie kontaktów z użytkownikami w celu poznania ich potrzeb i opinii dotyczących takich problemów jak: dostęp do danych komputerowych, naruszenia bezpieczeństwa i zmian w oprogramowaniu; monitorowania aktualnych raportów dotyczących wirusów komputerowych, aby określić czas aktualizowania systemów ochrony antywirusowej; wprowadzanie zmian do komputerowych plików bezpieczeństwa, aby dodać nowe oprogramowanie, usunąć błędy lub zmienić indywidualny status dostępu; monitorowanie wykorzystania plików danych i regulowanie dostępu w celu ochrony informacji w plikach komputerowych; przeprowadzanie oceny ryzyka i testów systemów przetwarzania danych w celu zapewnienia realizacji działań dotyczących przetwarzania danych i wprowadzenia środków zabezpieczających poufne informacje podczas ich transmisji i zapobieganie występowaniu zakłóconych transferów cyfrowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',108);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2611','Adwokaci, radcy prawni i prokuratorzy','Adwokaci, radcy prawni i prokuratorzy udzielają klientom porad prawnych z zakresu różnych dziedzin, przygotowują dokumenty prawne, reprezentują klientów przed sądem w sprawach cywilnych i karnych; występują w roli oskarżyciela przed sądem, strzegą praworządności, wszczynają i prowadzą postępowania przygotowawcze.','Zadania wykonywane przez adwokatów, radców prawnych i prokuratorów zazwyczaj obejmują: udzielanie klientom porad prawnych z zakresu różnych dziedzin oraz podejmowanie czynności prawnych w imieniu klientów; wyszukiwanie zasad prawnych, ustaw i wcześniejszych orzeczeń sądów związanych z konkretnymi sprawami; zbieranie materiału dowodowego umożliwiającego przygotowywanie obrony lub wystąpienie na drogę sądową poprzez przesłuchiwanie klientów i świadków w celu ustalenia stanu faktycznego sprawy; ocenianie uzyskanych informacji i opracowywanie strategii i argumentacji podczas przygotowań do przedstawienia sprawy; reprezentowanie klientów w sądach, trybunałach i organach administracyjnych lub instruowanie adwokatów co do sposobu występowania do sądu wyższej instancji; podejmowanie się obrony i występowanie w sądzie wyższej instancji; występowanie w roli prokuratora w imieniu rządu; negocjowanie ugody w kwestiach dotyczących sporu prawnego; przygotowywanie projektów ustaw i rządowych aktów wykonawczych w oparciu o istniejące przepisy; sporządzanie dokumentów prawnych, takich jak umowy, dokumenty dotyczące nieruchomości i testamenty oraz przygotowywanie ekspertyz prawnych; redagowanie dokumentów prawnych, pism procesowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',109);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2612','Sędziowie','Sędziowie przewodniczą posiedzeniom w sprawach cywilnych i karnych. Wydają wyroki w sprawach karnych, orzeczenia w sprawach cywilnych oraz nakazy sądowe.','Zadania wykonywane przez sędziów zazwyczaj obejmują: przewodniczenie w trakcie rozpraw i przesłuchań; interpretowanie i stosowanie przepisów procesowych oraz orzekanie w kwestii dopuszczalności dowodu; określanie praw i obowiązków zaangażowanych stron oraz praw i obowiązków w sprawach z udziałem ławy przysięgłych; instruowanie członków ławy przysięgłych co do kwestii prawnych mających zastosowanie w danej sprawie; określanie wagi i znaczenia materiału dowodowego na rozprawach prowadzonych bez udziału ławy przysięgłych oraz orzekanie o winie, niewinności lub stopniu odpowiedzialności oskarżonego lub pozwanego; wydawanie wyroków wobec osób skazanych w sprawach karnych, określanie wysokości odszkodowania lub innego stosownego zadośćuczynienia w sprawach cywilnych i wydawanie nakazów sądowych; badanie kwestii prawnych i publikowanie na ich temat opinii. Do ich zadań może także należeć nadzorowanie innych pracowników.',110);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2619','Specjaliści z dziedziny prawa gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą specjaliści z dziedziny prawa nie zaklasyfikowani do grupy średniej 261 Specjaliści z dziedziny prawa. Grupa ta obejmuje na przykład osoby pełniące inne funkcje prawne niż bronienie pozwanych, oskarżanie lub przewodniczenie posiedzeniom sądowym. Są to m.in.: asystenci prawni, asystenci prokuratorów i sędziów, prawnicy legislatorzy, komornicy, notariusze, referendarze sądowi.','Zadania wykonywane przez specjalistów z dziedziny prawa gdzie indziej niesklasyfikowani ujętych w tej grupie zazwyczaj obejmują: udzielanie porad prawnych dotyczących różnych problemów związanych ze sprawami osobistymi, prowadzeniem działalności gospodarczej oraz sprawami administracyjnymi; przygotowywanie dokumentów prawnych i umów, aktów notarialnych, świadczenie pomocy w prowadzeniu spraw sądowych przez sędziów i prokuratorów, prowadzenie postępowań komorniczych, przygotowywanie ustaw prawnych i nadzorowanie procesu legislacyjnego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',111);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2621','Archiwiści i muzealnicy','Archiwiści i muzealnicy zajmują się gromadzeniem, ocenianiem i zapewnianiem ochrony i konserwacji zbiorów archiwalnych, artefaktów i archiwów o znaczeniu historycznym, kulturalnym, administracyjnym i artystycznym, dzieł sztuki i innych przedmiotów; planowaniem, opracowywaniem i wdrażaniem systemów ochrony archiwów i cennych dokumentów historycznych oraz dzieł sztuki w muzeach i galeriach.','Zadania wykonywane przez archiwistów i muzealników zazwyczaj obejmują: ocenianie i konserwowanie archiwów dla celów administracyjnych, historycznych, prawnych, dowodowych i innych; kierowanie przygotowywaniem indeksów, bibliografii, kopii mikrofilmów i innych pomocy odnoszących się do zgromadzonego materiału lub samodzielne ich opracowywanie oraz udostępnianie ich użytkownikom; badanie pochodzenia, dystrybucja i wykorzystywanie materiałów i przedmiotów o znaczeniu kulturalnym i historycznym; organizowanie, tworzenie i utrzymywanie zbiorów przedmiotów o znaczeniu artystycznym, kulturalnym, naukowym lub historycznym; kierowanie procesem klasyfikowania i katalogowania zbiorów muzealnych i zbiorów galerii sztuki oraz organizowanie wystaw; badanie, ocenianie oraz tworzenie, organizowanie i konserwowanie cennych dokumentów historycznych, takich jak dokumenty rządowe, dokumenty prywatne, fotografie, mapy, rękopisy i materiały audiowizualne; przygotowywanie opracowań naukowych i sprawozdań; planowanie i wdrażanie skomputeryzowanego zarządzania archiwami i rejestrami elektronicznymi; organizowanie wystaw w muzeach i w galeriach sztuki, reklamowanie wystaw oraz organizowanie specjalnych pokazów o charakterze ogólnym, specjalistycznym lub edukacyjnym; ocenianie i pozyskiwanie materiałów archiwalnych w celu stworzenia i zwiększenia zbiorów archiwalnych dla celów badawczych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',112);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2622','Bibliotekoznawcy i specjaliści zarządzania informacją','Bibliotekoznawcy i specjaliści zarządzania informacją zajmują się gromadzeniem, tworzeniem, organizowaniem i prowadzeniem zbiorów bibliotecznych i innych zbiorów informacji, organizowaniem i kontrolowaniem innych usług bibliotecznych oraz dostarczaniem informacji użytkownikom.','Zadania wykonywane przez bibliotekoznawców i specjalistów zarządzania informacją zazwyczaj obejmują: organizowanie, tworzenie i prowadzenie usystematyzowanego zbioru książek, czasopism i innych materiałów drukowanych, audiowizualnych i rejestrowanych cyfrowo; wybieranie i rekomendowanie pozyskiwania zbiorów bibliotecznych; organizowanie, klasyfikowanie i katalogowanie materiałów bibliotecznych; zarządzanie wypożyczeniami bibliotecznymi, jednostkami prowadzącymi wypożyczenia międzybiblioteczne i sieciami informacyjnymi; odzyskiwanie materiałów i prezentowanie informacji przedsiębiorstwom i innym użytkownikom w oparciu o zbiory lub o systemy biblioteczne i systemy sieci informacyjnych; prowadzenie badań i analizowanie lub modyfikowanie usług bibliotecznych i informacyjnych zgodnie ze zmianami potrzeb użytkowników; opracowywanie i wdrażanie systemów i modeli pojęciowych służących gromadzeniu, organizowaniu, klasyfikowaniu i odzyskiwaniu informacji; przygotowywanie opracowań naukowych i sprawozdań; wyszukiwanie materiałów referencyjnych w sposób tradycyjny i z użyciem mediów interaktywnych (on-line), dokonywanie wypożyczeń międzybibliotecznych oraz pełnienie innych funkcji mających na celu wspieranie dostępu użytkowników do materiałów bibliotecznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',113);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2631','Ekonomiści','Ekonomiści zajmują się przeprowadzaniem badań, monitorowaniem danych, analizowaniem informacji, przygotowywaniem sprawozdań i planów mających na celu rozwiązywanie problemów gospodarczych oraz problemów przedsiębiorstw, tworzeniem modeli służących analizowaniu, wyjaśnianiu i przewidywaniu zachowań i wzorów gospodarczych. Udzielają porad przedsiębiorstwom, grupom interesów i rządom w celu opracowywania rozwiązań obecnych lub przewidywanych problemów gospodarczych oraz problemów ekonomicznych przedsiębiorstw.','Zadania wykonywane przez ekonomistów zazwyczaj obejmują: przewidywanie zmian gospodarczych mających znaczenie dla krótkoterminowego planowania wydatków, dla planowania długoterminowego i dla oceny inwestycji; opracowywanie zaleceń, polityk i planów dotyczących gospodarki, strategii korporacyjnych i inwestycji oraz przeprowadzanie analiz dotyczących wykonalności projektów; monitorowanie danych gospodarczych w celu dokonywania oceny wydajności oraz udzielanie porad co do stosowności polityki pieniężnej i fiskalnej; przewidywanie poziomu produkcji i konsumpcji określonych produktów i usług w oparciu o dane dotyczące wcześniejszych poziomów produkcji i konsumpcji oraz o ogólne warunki gospodarcze i przemysłowe; przygotowywanie prognoz dotyczących dochodów i wydatków, stóp procentowych i kursów walutowych; analizowanie czynników wpływających na udział siły roboczej, zatrudnienie, wynagrodzenia, bezrobocie i inne zjawiska na rynku pracy; stosowanie wzorów matematycznych i technik statystycznych oraz testowanie teorii ekonomicznych i opracowywanie rozwiązań problemów gospodarczych; kompilowanie, analizowanie i interpretowanie danych gospodarczych z wykorzystaniem teorii ekonomicznych i różnych technik statystycznych; ocenianie skutków decyzji politycznych dotyczących gospodarki i finansów publicznych, udzielanie porad z zakresu polityki gospodarczej oraz w sprawie możliwych sposobów działania w świetle dawnych, obecnych i przyszłych czynników i tendencji gospodarczych; przygotowywanie opracowań naukowych i sprawozdań; analizowanie problemów związanych z działalnością gospodarczą poszczególnych przedsiębiorstw; przeprowadzanie badań dotyczących warunków rynkowych o zasięgu lokalnym, regionalnym i krajowym w celu ustalania poziomu sprzedaży i poziomu cen towarów i usług; dokonywania oceny potencjalnych i przyszłych tendencji strategii rozwojowych biznesowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',114);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2632','Archeolodzy, socjolodzy i specjaliści dziedzin pokrewnych','Archeolodzy, socjolodzy i specjaliści dziedzin pokrewnych pokrewni zajmują się badaniem i opisywaniem struktur społecznych, pochodzenia i ewolucji społeczeństw oraz wzajemnych zależności między warunkami środowiskowymi a działalnością ludzką. Udzielają porad z zakresu praktycznego stosowania swoich odkryć dając podstawy do formułowania polityki gospodarczej i społecznej danego kraju.','Zadania wykonywane przez archeologów, socjologów i specjalistów dziedzin pokrewnych ujętych w tej grupie zazwyczaj obejmują: prowadzenie badań dotyczących pochodzenia społeczeństw, ich rozwoju, struktury, wzorów społecznych, organizacji i współzależności występujących w społeczeństwie; ustalanie pochodzenia i ewolucji ludzkości poprzez badanie zmieniających się cech charakterystycznych, instytucji kulturalnych i społecznych; ustalanie rozwoju ludzkości na podstawie materialnych pozostałości z przeszłości, takich jak: domy, świątynie, narzędzia, wyroby ceramiczne, monety, broń lub rzeźby; studiowanie fizycznych i klimatycznych aspektów obszarów i regionów oraz wiązanie poczynionych ustaleń z działalnością gospodarczą, społeczną i kulturalną; opracowywanie teorii, modeli i metod interpretowania oraz opisywania zjawisk społecznych; ocenianie skutków decyzji politycznych dotyczących polityki społecznej; analizowane i ocenianie danych dotyczących społeczeństwa; udzielanie porad z zakresu praktycznego stosowania odkryć podczas formułowania polityki gospodarczej i społecznej na rzecz grup ludności i regionów oraz na rzecz rozwoju rynków; przygotowywanie opracowań naukowych i sprawozdań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',115);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2633','Filozofowie, historycy i politolodzy','Filozofowie, historycy i politolodzy zajmują się badaniem natury doświadczenia ludzkiego oraz istnienia, opisywanie historii określonego okresu, kraju czy regionu a także instytucji politycznych, ich struktur, ruchów i zachowań politycznych. Dokumentują swoje ustalenia i sporządzają dotyczące ich sprawozdania. Udostępniają uzyskaną wiedzę jako podstawę do podejmowania decyzji i działań w sferze polityki, dyplomacji i w podobnych dziedzinach.','Zadania wykonywane przez filozofów, historyków i politologów ujętych w tej grupie zazwyczaj obejmują: badanie głównie w drodze rozumowania, ogólnych przyczyn, zasad i sensu świata, działań ludzkich, doświadczeń, istnienia, interpretowania i rozwijania koncepcji oraz teorii filozoficznych; uwzględnianie i porównywanie źródeł pierwotnych, takich jak: oryginalne lub współczesne zapisy dawnych wydarzeń i źródeł wtórnych, znaleziska archeologiczne i antropologiczne; uzyskiwanie odpowiednich materiałów, sprawdzanie ich autentyczności i prowadzenie badań w tym zakresie oraz opisywanie historii określonej epoki, kraju, regionu lub określonego aspektu jego historii - na przykład gospodarczego, społecznego lub politycznego; przeprowadzanie badań w takich dziedzinach jak: filozofia polityczna lub dawna obecna teoria i praktyka systemów, instytucji lub zachowań politycznych; obserwowanie współczesnych instytucji i opinii politycznych, gromadzenie danych na ich temat z wykorzystaniem różnych źródeł, w tym wywiadów z przedstawicielami rządu, partii politycznych i innych znaczących osób; opracowywanie teorii, modeli i metod interpretowania i opisywania natury doświadczenia ludzkiego oraz wydarzeń i zachowań historycznych i politycznych; publikowanie odkryć i wniosków lub udostępnianie ich do wykorzystania przez rząd, partie polityczne lub inne organizacje i zainteresowane osoby; przygotowywanie opracowań naukowych, referatów, raportów i sprawozdań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',116);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2634','Psycholodzy i pokrewni','Psycholodzy i pokrewni zajmują się badaniem i studiowaniem procesów umysłowych i zachowań ludzi jako jednostek lub członków grupy oraz stosowaniem tej wiedzy do bardziej sprawnego przystosowania jednostki lub grup do życia społecznego oraz rozwoju osobistego, edukacyjnego i zawodowego.','Zadania wykonywane przez psychologów i pokrewnych ujętych w tej grupie zazwyczaj obejmują: planowanie i przeprowadzanie testów w celu pomiarów psychicznych, fizycznych i innych cech, takich jak: inteligencja, zdolności, umiejętności; interpretowanie i ocenianie wyników testów oraz udzielanie porad; analizowanie wpływu dziedziczności, czynników społecznych, zawodowych i innych na sposób myślenia i zachowania jednostki; udzielanie pomocy psychologicznej lub prowadzenie terapii indywidualnych i grupowych oraz świadczenie usług uzupełniających; utrzymywanie wymaganych kontaktów, np. z członkami rodziny, władzami oświatowymi lub pracodawcami i zalecanie możliwych rozwiązań problemów oraz postępowań; badanie znaczenia czynników psychologicznych w diagnozie, leczeniu i profilaktyce chorób psychicznych i zaburzeń emocjonalnych lub zaburzeń osobowości oraz konsultowanie się ze specjalistami w dziedzinach pokrewnych; przygotowywanie opracowań naukowych, referatów, raportów i sprawozdań; opracowywanie testów osiągnięć, testów diagnostycznych i testów predyktywnych wykorzystywanych przez nauczycieli podczas planowania metod nauczania i treści zajęć; przeprowadzanie badań dotyczących organizacji pracy, grup roboczych, morale, motywacji, nadzoru i zarządzania; opracowywanie teorii, modeli i metod interpretowania i opisywania zachowań ludzkich; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',117);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2635','Specjaliści do spraw społecznych','Specjaliści do spraw społecznych udzielają porad osobom indywidualnym, rodzinom, grupom, społecznościom i organizacjom w reakcji na ich problemy społeczne i osobiste. Pomagają klientom w rozwijaniu umiejętności oraz w dostępie do zasobów i usług i wsparcia niezbędnych do poradzenia sobie z problemami związanymi z bezrobociem, ubóstwem, niepełnosprawnością, uzależnieniami, przestępczością, problemami rodzinnymi; wykonują zadania o charakterze wychowawczo-resocjalizacyjnym i profilaktycznym w stosunku do osób oddanych pod nadzór przez orzeczenie sądowe i osób przebywających w zakładach karnych oraz pomagają stronom znajdującym się w konflikcie w osiągnięciu porozumienia przez mediacje.','Zadania wykonywane przez specjalistów do spraw społecznych ujętych w tej grupie zazwyczaj obejmują: prowadzenie rozmów z poszczególnymi osobami, rodzinami lub grupami w celu oceny ich sytuacji i problemów oraz określenia rodzaju wymaganych usług bądź wsparcia; analizowanie sytuacji klientów i prezentowanie alternatywnego podejścia do kwestii rozwiązywania problemów; inspirowanie działań w zakresie zaspokajania niezbędnych potrzeb życiowych jednostek i grup społecznych; planowanie, ocenianie, doskonalenie i rozwijanie usług pomocy społecznej; prowadzenie działań zapobiegających przestępczości nieletnich lub zapewniających rehabilitację młodocianych przestępców; prowadzenie zajęć wychowawczych i sprawowanie opieki nad osobami przebywającymi w areszcie śledczym lub w zakładzie karnym; pomaganie stronom znajdującym się w konflikcie w rozwiązaniu go bez przemocy i osiągnięciu ugody; przygotowywanie akt sprawy lub raportów na potrzeby sądów i postępowań prawnych; świadczenie usług doradczych i mediacyjnych oraz organizowanie sesji grupowych w celu pomagania klientom w rozwijaniu umiejętności i zrozumienia niezbędnego do radzenia sobie z problemami społecznymi i osobistymi oraz do ich rozwiązywania; planowanie i wdrażanie programów pomocy dla klientów w zakresie pomocy finansowej, prawnej lub świadczących inne usługi; prowadzenie dochodzenia w sprawach o nadużycia lub zaniedbania oraz podejmowanie działań mających na celu ochronę dzieci i innych zagrożonych osób; pracowanie z osobami łamiącymi prawo podczas odbywania przez nie wyroku oraz po tym okresie, celem pomocy w integracji społecznej oraz zmiany ich zachowania; pełnienie funkcji przedstawiciela grup klientów w społeczeństwie i lobbowanie na rzecz rozwiązywania ich problemów; opracowywanie programów prewencyjnych i interwencyjnych mających na celu zaspokojenie potrzeb społeczeństwa; utrzymywanie kontaktu ze związanymi z klientami innymi organami pomocy socjalnej, instytucjami oświatowymi, podmiotami zapewniającymi opiekę zdrowotną w celu przedstawienia im informacji i otrzymania informacji zwrotnej na temat ogólnej sytuacji klientów oraz ich postępów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',118);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2636','Duchowni i osoby konsekrowane','Duchowni i osoby konsekrowane różnych religii i wyznań oraz zakonnicy kultywują święte tradycje, praktyki i wierzenia. Odprawiają nabożeństwa, celebrują obrzędy o znaczeniu religijnym; udzielają porad duchowych i moralnych oraz pełnią inne funkcje związane z praktykowaniem religii.','Zadania wykonywane przez duchownych i osoby konsekrowane ujętych w tej grupie zazwyczaj obejmują: kultywowanie świętych tradycji, praktyk i wierzeń; odprawianie nabożeństw, obrzędów i ceremonii religijnych; wypełnianie różnych obowiązków administracyjnych i społecznych, w tym uczestniczenie w spotkaniach komitetów i w posiedzeniach organizacji religijnych; udzielanie porad duchowych i moralnych zgodnych z wyznawaną religią; głoszenie doktryn religijnych we własnym kraju lub za granicą; przygotowywanie kazań religijnych; opracowywanie i realizowanie kursów i programów edukacji religijnej; doradzanie jednostkom w kwestii problemów międzyludzkich, zdrowotnych, finansowych i religijnych; planowanie specjalnych uroczystości takich jak: obozy, konferencje, seminaria, rekolekcje oraz uczestniczenie w nich; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',119);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2641','Literaci i inni autorzy tekstów','Literaci i inni autorzy tekstów zajmują się planowaniem, zbieraniem informacji i pisaniem utworów literackich: książek, scenariuszy, scenopisów, sztuk, esejów, przemówień, podręczników i artykułów o charakterze innym niż dziennikarski (z wyłączeniem materiałów dla gazet, magazynów i innych czasopism) w celu ich opublikowania lub prezentacji określonemu środowisku.','Zadania wykonywane przez literatów i innych autorów tekstów ujętych w tej grupie zazwyczaj obejmują: tworzenie, pisanie i redagowanie powieści, sztuk, scenariuszy, wierszy i innych materiałów w celu ich opublikowania lub przedstawienia; ocenianie zalet dzieł literackich i innych dzieł sztuki; pisanie scenariuszy oraz przygotowywanie programów produkcji scenicznych, filmowych, radiowych i telewizyjnych; analizowanie materiału, takiego jak: specyfikacje, uwagi i rysunki oraz pisanie specjalistycznych podręczników użytkownika, broszur, instrukcji obsługi i podobnych publikacji technicznych w celu jasnego zwięzłego wyjaśnia kwestii instalowania, i obsługiwania sprzętu elektronicznego, mechanicznego i innego; wybieranie materiału do publikacji, sprawdzanie stylu, gramatyki i poprawności treści, wprowadzanie wszelkich niezbędnych zmian i dokonywanie korekt przed drukiem; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',120);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2642','Dziennikarze','Dziennikarze zbierają, badają, interpretują i przekazują wiadomości, opinie i komentarze oraz prezentują sprawy publiczne za pośrednictwem gazet, telewizji, radia oraz innych mediów; oceniają wartość dzieł sztuki i produkcji artystycznej, filmowej, pisarskiej; selekcjonują, aranżują i redagują teksty, ilustrują je rysunkami i fotografiami.','Zadania wykonywane przez dziennikarzy zazwyczaj obejmują: zbieranie wiadomości lokalnych, z kraju i ze świata przy pomocy wywiadów, śledztw dziennikarskich i obserwacji, uczestniczenie w wydarzeniach publicznych, wyszukiwanie zapisów, recenzowanie wydawnictw, uczestniczenie w projekcjach filmów i występach scenicznych; zbieranie, przekazywanie i komentowanie wiadomości i spraw publicznych do publikacji w gazetach i czasopismach, transmisji radiowej, telewizyjnej lub za pośrednictwem innych mediów służących do transmisji internetowej; otrzymywanie, analizowanie i weryfikowanie wiadomości i innych tekstów pod względem dokładności; przeprowadzanie wywiadów z politykami i innymi osobami publicznymi w czasie konferencji prasowych i przy innych okazjach, obejmujących wywiady indywidualne nagrywane na potrzeby radia, telewizji lub mediów; zbieranie i przekazywanie informacji na temat osiągnięć w dziedzinach specjalistycznych takich, jak: medycyna, nauka, technologia; pisanie artykułów wstępnych i komentarzy na bieżące tematy w celu zwiększenia zainteresowania daną kwestią wśród ludzi i wyrażenia poglądów reprezentowanych przez daną publikację lub stację nadawczą; pisanie recenzji utworów literackich, muzycznych i innych utworów artystycznych w oparciu o wiedzę, osąd oraz doświadczenie na potrzeby prasy, telewizji, radia lub innych mediów; dokonywanie wyboru materiału do publikacji, weryfikacja poprawności stylistycznej, gramatycznej, dokładności i zgodności danych treści z prawem oraz organizowanie koniecznej korekty; współpraca z pracownikami działu produkcji w zakresie weryfikacji ostatecznej wersji odbitek korektorskich bezpośrednio przed drukiem; wybór, gromadzenie i przygotowanie materiałów reklamowych poświęconych określonym przedsiębiorstwom lub organizacjom przeznaczonych do publikacji w prasie, radiu, telewizji oraz innych mediach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników',121);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2643','Filolodzy i tłumacze','Filolodzy i tłumacze tekstowi i ustni studiują pochodzenie, rozwój i strukturę języków i dokonują pisemnego lub ustnego przekładu z jednego języka na inny oraz badają pochodzenie, rozwój i strukturę języków.','Zadania wykonywane przez filologów i tłumaczy zazwyczaj obejmują: badanie związku między starożytnymi prajęzykami a współczesnymi grupami języków, śledzenie pochodzenia i ewolucji słów, zasad gramatycznych i form językowych oraz prezentowanie wyników przedmiotowych prac; przygotowywanie lub doradztwo w zakresie systemów klasyfikacji języków, gramatyk, słowników i podobnych materiałów względnie ich przygotowywanie; dokonywanie przekładu pisemnego z jednego języka na inny przy zapewnieniu, że zostało zachowane właściwe znaczenie oryginału, że teksty prawne, techniczne lub naukowe zostały właściwie oddane oraz że frazeologia, terminologia, duch i styl utworu literackiego zostały w miarę możliwości zachowane; opracowywanie metod zastosowania komputerów oraz innych narzędzi w celu poprawy wydajności i jakości tłumaczenia; dokonywanie przekładu ustnego z języka mówionego lub języka migowego na inny język mówiony lub migowy, zwłaszcza w czasie konferencji, spotkań lub przy innych podobnych okazjach z przekazywaniem możliwie najdokładniej znaczenia oraz sensu oryginalnej wypowiedzi; przegląd i korekta tłumaczonych materiałów; przygotowywanie naukowych opracowań, referatów i raportów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',122);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2651','Artyści plastycy','Artyści plastycy tworzą i wykonują dzieła sztuki rzeźbiąc, malując, rysując, tworząc komiksy, rysunki animowane, grafiki, stosując rytownictwo lub różne techniki pokrewne.','Zadania wykonywane przez artystów plastyków zazwyczaj obejmują: formułowanie i rozwój pomysłów, projektów i styli malarstwa, rysunku i rzeźby; aranżowanie przedmiotów, ustawianie modeli oraz dobór krajobrazów lub innych form wizualnych w zależności od wybranego tematu; dobór środków artystycznych, metod i materiałów; tworzenie figuratywnych lub abstrakcyjnych form trójwymiarowych lub płaskorzeźb przez kształtowanie, rycie i obróbkę oraz łączenie materiałów takich, jak: drewno, kamień, glina, metal, tkanina, lód lub papier; tworzenie figuratywnych lub abstrakcyjnych rysunków lub malowideł przy pomocy ołówków, tuszu, kredy, farb olejnych, akwareli lub przez zastosowanie innych technik; tworzenie rysunków i wykonywanie ich techniką akwafortową lub rytowniczą na metalu, drewnie lub innych materiałach; tworzenie komiksów przedstawiających osoby i wydarzenia, często w formie karykaturalnej; tworzenie i wykonywanie projektów ilustracji do książek, czasopism, reklam i dla innych podobnych celów; konserwowanie lub restaurowanie uszkodzonych, zabrudzonych lub wyblakłych malowideł lub innych przedmiotów sztuki; malowanie miniatur; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',123);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2652','Kompozytorzy, artyści muzycy i śpiewacy','Kompozytorzy, artyści muzycy i śpiewacy piszą, aranżują, komponują i adaptują utwory muzyczne, kierują ich wykonaniem (dyrygują) lub uczestniczą w ich wykonaniu.','Zadania wykonywane przez kompozytorów, artystów muzyków i śpiewaków zazwyczaj obejmują: tworzenie struktur melodycznych, harmonicznych i rytmicznych w celu wyrażenia idei i emocji w formie muzyki; przekładanie idei i pojęć na standardowe znaki i symbole muzyczne do celów ich odtwarzania i wykonania; adaptowanie i aranżowanie muzyki na potrzeby określonych grup instrumentalnych lub wokalnych, instrumentów lub na określone okazje; dyrygowanie grupami instrumentalnymi lub wokalnymi; dokonywanie wyboru muzyki na potrzeby występów i przydzielanie partii instrumentalnych poszczególnym muzykom; granie na jednym lub większej liczbie instrumentów muzycznych w charakterze solisty, członka orkiestry lub zespołu muzycznego; śpiewanie w charakterze solisty lub członka grup wokalnych lub innych zespołów; ćwiczenie i uczestniczenie w próbach w celu utrzymania wysokiego standardu występu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',124);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2653','Choreografowie i tancerze','Choreografowie i tancerze komponują, tworzą lub wykonują tańce artystyczne w teatrach, widowiskach, filmie.','Zadania wykonywane przez choreografów i tancerzy zazwyczaj obejmują: komponowanie i opracowywanie choreograficzne tańców, które często przekazują opowieści, temat, ideę lub nastrój za pomocą kroków, ruchów lub gestów; wykonywanie utworów choreograficznych (tańców) w charakterze solisty, z partnerem lub w roli członka grupy tanecznej, na żywo z udziałem publiczności i na potrzeby filmu lub innych mediów; trenowanie, ćwiczenie w celu utrzymania odpowiedniego poziomu umiejętności i formy; reżyserowanie i uczestniczenie w próbach w celu ćwiczenia choreografii i technik wymaganych do występu; prowadzenie przesłuchań do ról w spektaklach tanecznych lub na członkowstwo w zespołach tanecznych; koordynowanie produkcji muzyki we współpracy z dyrektorami muzycznymi; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',125);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2654','Producenci filmowi, reżyserzy i pokrewni','Producenci filmowi, reżyserzy i pokrewni nadzorują i kontrolują techniczne i artystyczne aspekty produkcji filmowych, telewizyjnych i radiowych oraz występów scenicznych. Podejmują inicjatywę produkcji filmu, pozyskują środki na jego realizację oraz organizują i nadzorują proces produkcji a także podejmują działania związane z promocją i dystrybucją filmu.','Zadania wykonywane przez producentów filmowych, reżyserów i pokrewnych zazwyczaj obejmują: nadzorowanie prac nad scenariuszem lub podejmowanie decyzji o zakupie praw do gotowego scenariusza; poszukiwanie koproducentów i inwestorów w celu zabezpieczenia budżetu filmu oraz zawieranie umów z podmiotami finansującymi film; dobieranie głównych współpracowników, nadzorowanie wyboru obsady aktorskiej oraz formowanie ekipy produkcyjnej filmu; dokonywanie wyboru scenarzystów; studiowanie scenariuszy w celu ustalenia interpretacji artystycznej oraz instruowanie aktorów w zakresie metod odgrywania ról; reżyserowanie wszystkich aspektów produkcji dramatycznych zarówno scenicznych, jak i telewizyjnych, radiowych lub filmowych z uwzględnieniem doboru aktorów oraz ostatecznych decyzji dotyczących kostiumów, scenografii, efektów dźwiękowych i świetlnych; planowanie, organizowanie i kontrolowanie poszczególnych etapów produkcji oraz prowadzenie terminarza zdjęć; prowadzenie archiwum produkcji i negocjowanie tantiem; tworzenie, planowanie, pisanie scenariuszy na potrzeby nagrań audio i wideo oraz montowanie programów; zawieranie umów ubezpieczeniowych związanych z produkcją filmu; sprawowanie nadzoru nad realizacją filmu i wydatkowaniem środków przeznaczonych na jego produkcję oraz przedkładanie sprawozdań koproducentom i inwestorom; pozyskiwanie dystrybutora dla filmu, negocjowanie z nim sposobu promocji, skali eksploatacji i podziału wpływów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',126);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2655','Aktorzy','Aktorzy uprawiają zawodowo sztukę aktorską grając role w przedstawieniach teatralnych (teatr dramatyczny, teatr lalek, pantomima), estradowych, w filmie, radio i telewizji.','Zadania wykonywane przez aktorów zazwyczaj obejmują: pamięciowe przyswajanie roli (tekstu, melodii, tańca itp.); opanowanie kwestii, sygnałów i ról w scenicznej produkcji dramatycznej, w reklamie, na potrzeby telewizji, radia lub filmu; wcielanie się w postacie stworzone przez dramaturga lub autora i przekazanie ich publiczności; opowiadanie historii i czytanie utworów literackich w celu edukowania lub zabawiania słuchaczy; branie udziału w przesłuchaniach i castingach w celu otrzymania roli; przygotowanie się do występu poprzez uczestnictwo w próbach i wypełnianie poleceń i podążanie za wskazówkami reżyserów produkcji; czytanie scenariuszy i gromadzenie informacji w celu zrozumienia poszczególnych ról, motywów i cech charakterystycznych granych postaci; odgrywanie ról i uosabianie postaci w sposób ustalony w toku prób w filmie oraz produkcjach telewizyjnych, radiowych i scenicznych; stałe doskonalenie sprawności warsztatowo-głosowej, psychomotorycznej, fizycznej, przez uprawianie odpowiednich ćwiczeń; opanowywanie różnych umiejętności zawodowych (np. taniec w określonym stylu, pantomima) w zakresie potrzebnym do wykonywanych zadań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',127);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2656','Prezenterzy radiowi, telewizyjni i pokrewni','Prezenterzy radiowi, telewizyjni i pokrewni odczytują wiadomości oraz różnego rodzaju teksty i komentarze, przeprowadzają wywiady, wygłaszają inne komunikaty i prezentacje w radio, telewizji, teatrach, dyskotekach, klubach oraz innych placówkach lub mediach; zapowiadają i prowadzą koncerty, imprezy i festiwale muzyczne i inne widowiska oraz zajmują się stroną organizacyjno-techniczną realizacji przedstawień teatralnych lub telewizyjnych.','Zadania wykonywane przez prezenterów radiowych, telewizyjnych i pokrewnych zazwyczaj obejmują: prezentowanie programów radiowych i telewizyjnych; odczytywanie wiadomości i wygłaszanie innych komunikatów w radio i telewizji; prezentowanie artystów zajmujących się sztuką widowiskowa, sportem, itp. (wykonawców koncertów, widowisk estradowych, pokazów mody lub innych imprez artystycznych, komentowanie imprez sportowych, itp.); prezentowanie muzyki rozrywkowej oraz ich wykonawców w radiu, telewizji, dyskotekach, w klubach muzycznych lub na innych imprezach estradowych; czytanie list dialogowych do filmów; obcojęzycznych; przeprowadzanie publicznych wywiadów w radio i telewizji; zapoznawanie się z informacjami dotyczącymi danej kwestii w celu przygotowania się do programu lub wywiadu; komentowanie muzyki i innych tematów takich, jak: pogoda lub warunki na drogach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',128);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('2659','Twórcy i artyści gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą twórcy i artyści nie zaklasyfikowani do grupy średniej 263 Twórcy i artyści. Grupa ta obejmuje osoby zajmujące się twórczością ludową, tworzących oryginalne i indywidualne dzieła w oparciu o regionalne tradycje ludowe, na podstawie umiejętności nabytych w drodze bezpośredniego przekazu ludowej twórczości oraz innych artystów zajmujących się sztuką.','Zadania wykonywane przez twórców i artystów gdzie indziej niesklasyfikowanych zazwyczaj obejmują: tworzenie i upowszechnianie sztuki wyobrażeniowej: malarstwa i rzeźby ludowej, itp.; tworzenie i upowszechnianie artystycznego rękodzieła ludowego w zakresie: tkactwa, hafciarstwa, garncarstwa, plecionkarstwa, kowalstwa, zabawkarstwa, zdobnictwa bibułkowego, plastyki obrzędowej, ludowych instrumentów muzycznych, itp.; tworzenie i upowszechnianie muzyki, tańca i śpiewu ludowego oraz literatury ludowej; uczestniczenie w wystawach, targach i innych ekspozycjach sztuki ludowej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',129);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3111','Technicy nauk chemicznych, fizycznych i pokrewni','Technicy nauk chemicznych, fizycznych i pokrewni wykonują prace techniczne mające na celu pomoc w prowadzeniu badań w dziedzinie chemii, fizyki, geologii, geofizyki, meteorologii, astronomii oraz w rozwoju praktycznego zastosowania wyników przedmiotowych badań w przemyśle, medycynie, wojsku i innych obszarach.','Zadania wykonywane przez techników nauk chemicznych, fizycznych i pokrewnych zazwyczaj obejmują: pobieranie próbek i przygotowanie materiałów i sprzętu do eksperymentów, badań i analiz; przeprowadzanie rutynowych badań laboratoryjnych i wykonywanie rozmaitych prac technicznych mających na celu wspieranie naukowców w dziedzinie chemii i fizyki w prowadzeniu badań i analiz; kontrolowanie ilości i jakości materiałów laboratoryjnych przez badanie próbek i kontrolę użycia; przygotowanie szczegółowych kalkulacji ilości i kosztów materiałów i pracy wymaganych do realizacji poszczególnych zadań, zgodnie z określonymi specyfikacjami; ustawianie, obsługiwanie i konserwacja przyrządów i sprzętu laboratoryjnego, kontrolowanie przebiegu eksperymentów, dokonywanie obserwacji oraz obliczanie i prowadzenie zapisu wyników; przygotowanie materiałów do eksperymentów, przykładowo: mrożenie, cięcie próbek i mieszanie substancji chemicznych; pobieranie i badanie próbek gleby i wody, prowadzenie zapisu wyników oraz analizowanie danych w ramach wsparcia geologów i geofizyków; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',130);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3112','Technicy budownictwa','Technicy budownictwa wykonują prace techniczne w zakresie badań w dziedzinie budownictwa, projektowania, budowania, użytkowania, konserwacji i naprawy budynków i innych konstrukcji takich, jak: systemy doprowadzające wodę i systemy uzdatniania wody, mosty, drogi, tamy i lotniska.','Zadania wykonywane przez techników budownictwa zazwyczaj obejmują: prowadzenie lub asystowanie w terenowych lub laboratoryjnych badaniach gleby i materiałów budowlanych; projektowanie samodzielne lub pod nadzorem architektów obiektów budowlanych nieskomplikowanych konstrukcyjnie, architektonicznie i funkcjonalnie; wykonywanie plansz oraz modeli architektonicznych i urbanistycznych; reprezentowanie architektów, inżynierów budownictwa oraz inżynierii środowiska na terenie budowy, celem zapewnienia zgodności wykonawstwa z dokumentacją projektową oraz wymaganych standardów materiałowych i wykonawczych; kierowanie robotami budowlanymi i prowadzenie dokumentacji budowy; nadzór nad wykonawstwem i eksploatacją instalacji i urządzeń sanitarnych stanowiących wyposażenie budynków; opracowywanie w ramach posiadanych uprawnień projektów tych instalacji; świadczenie pomocy technicznej przy wznoszeniu budynków wraz z wykonywaniem pomiarów technicznych i przygotowywaniem raportów pomiarowych; zapewnienie wsparcia technicznego związanego z budową budynków i innych konstrukcji oraz w zakresie przeprowadzania inspekcji i przygotowywania zestawień wyników z inspekcji; zapewnienie zgodności wykonawstwa ze specyfikacjami zwartymi w projekcie, wymaganymi przepisami prawa i z zachowanie pożądanych standardów materiałów i pracy; zastosowanie wiedzy technicznej w zakresie zasad i praktyk budownictwa w celu identyfikacji i rozwiązywania pojawiających się problemów; kierowanie robotami budowlanymi, prowadzenie dokumentacji budowy; określanie na podstawie otrzymanej dokumentacji projektowej kosztów i ilości materiałów potrzebnych do realizacji określonych projektów oraz pracochłonności projektowanych zadań; organizowanie i nadzorowanie prac konserwatorskich i napraw; prowadzenie inspekcji budynków i innych konstrukcji w czasie i po zakończeniu budowy w celu zapewnienia zgodności z przepisami prawa i normami dotyczącymi klasyfikacji, przeznaczenia i bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',131);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3113','Technicy elektrycy','Technicy elektrycy wykonują prace techniczne mające na celu wsparcie badań w zakresie inżynierii elektrycznej oraz projektowania i konstruowaniu, produkcji, montażu, eksploatacji, obsługi, konserwacji, naprawy i utrzymania w ruchu aparatury, maszyn i urządzeń elektrycznych, obiektów, systemów zasilania energią elektryczną oraz systemów dystrybucji energii elektrycznej.','Zadania wykonywane przez techników elektryków zazwyczaj obejmują: zapewnianie wsparcia technicznego w zakresie badań i rozwoju sprzętu elektrycznego i obiektów lub testowanie prototypów; projektowanie i przygotowanie planów instalacji elektrycznej oraz zespołów obwodów elektrycznych w określonych urządzeniach zgodnie z odpowiednimi specyfikacjami; przygotowanie szczegółowych kalkulacji ilości, kosztów materiałów i pracy wymaganych do produkcji lub wykonania odpowiednich urządzeń czy instalacji, zgodnie z określonymi specyfikacjami; kontrolowanie technicznych aspektów produkcji, instalacji, użytkowania, konserwacji i napraw systemów elektrycznych i sprzętu elektrycznego w celu zapewnienia satysfakcjonującej wydajności i zgodności z określonymi specyfikacjami i przepisami; planowanie metod instalacji, sprawdzanie bezpieczeństwa i sprawności sterowania ukończonej instalacji lub uruchamianie po raz pierwszy nowego sprzętu lub systemów elektrycznych; montaż, instalacja, sprawdzanie, kalibrowanie, modyfikowanie, naprawa systemów i urządzeń elektrycznych w celu zapewnienia zgodności z przepisami i wymogami w zakresie bezpieczeństwa; wykorzystywanie wiedzy technicznej z zakresu elektrotechniki i doświadczeń praktycznych do identyfikowania i rozwiązywania problemów ujawniających się podczas pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',132);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3114','Technicy elektronicy i pokrewni','Technicy elektronicy wykonują prace techniczne mające na celu wsparcie badań w zakresie inżynierii elektronicznej oraz projektowania, konstruowaniu, produkcji, montażu, budowy, eksploatacji, konserwacji i naprawy urządzeń i systemów elektronicznych oraz telekomunikacyjnych.','Zadania wykonywane przez techników elektroników i pokrewnych zazwyczaj obejmują: zapewnianie wsparcia technicznego w zakresie badań i rozwoju sprzętu elektronicznego lub testowania prototypów; projektowanie i przygotowanie planów układów elektronicznych zgodnie z odpowiednimi specyfikacjami; przygotowanie szczegółowych kalkulacji ilości, kosztów materiałów i pracy wymaganych do produkcji oraz instalacji sprzętu elektronicznego zgodnie z określonymi specyfikacjami; sprawowanie technicznego nadzoru na produkcją instalowaniem, eksploatacją, utrzymaniem w ruchu i naprawami urządzeń i sprzętu elektronicznego w celu zapewnienia satysfakcjonującej wydajności oraz zgodności z określonymi normami i przepisami; planowanie metod instalacji, sprawdzanie bezpieczeństwa i sprawności sterowania ukończonej instalacji lub uruchamianie po raz pierwszy nowego sprzętu lub systemów elektronicznych; przeprowadzanie prób systemów elektronicznych, gromadzenie i analizowanie danych oraz pomoc inżynierom elektrykom w montażu układów elektronicznych; wykorzystywanie wiedzy technicznej i doświadczeń praktycznych z zakresu elektroniki do identyfikacji i rozwiązywania problemów ujawniających się podczas pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',133);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3115','Technicy mechanicy','Technicy mechanicy wykonują prace techniczne mające na celu wsparcie badań w zakresie inżynierii mechanicznej oraz projektowania, produkcji, montażu, budowy, obsługi, eksploatacji, konserwacji i naprawy maszyn, podzespołów mechanicznych, urządzeń i instalacji mechanicznych.','Zadania wykonywane przez techników mechaników zazwyczaj obejmują: zapewnianie wsparcia technicznego w zakresie badań i rozwoju maszyn i instalacji, obiektów i podzespołów mechanicznych lub testowanie prototypów; projektowanie, przygotowanie i wykonywanie, na podstawie otrzymanych założeń, dokumentacji technicznej maszyn, urządzeń i instalacji mechanicznych oraz obiektów i podzespołów; określanie, na podstawie otrzymanej dokumentacji szczegółowych kalkulacji ilości i kosztu materiałów potrzebnych do wyprodukowania i zainstalowania odpowiednich maszyn i urządzeń oraz pracochłonności zadań, zgodnie z określonymi specyfikacjami; kontrolowanie technicznych aspektów produkcji, użytkowania, konserwacji i napraw maszyn i instalacji mechanicznych, obiektów i podzespołów w celu zapewnienia satysfakcjonującej wydajności i zgodności z określonymi normami i przepisami; opracowywanie, kontrolę i wdrażanie norm i procedur bezpieczeństwa w zakresie ekspertyz w transporcie samochodowym; montaż i instalowanie nowych i zmodyfikowanych zespołów mechanicznych, podzespołów, obrabiarek i urządzeń sterowniczych oraz hydraulicznych systemów napędowych; przeprowadzanie prób systemów mechanicznych, gromadzenie i analizowanie danych oraz pomoc inżynierom mechanikom w montażu i instalacji zespołów mechanicznych; zapewnianie zgodności planów konstrukcji mechanicznych i ukończonych prac z określonymi specyfikacjami i przepisami; wykorzystywanie wiedzy technicznej z zakresu technologii budowy maszyn i mechaniki do identyfikacji i rozwiązywania problemów powstałych w trakcie pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',134);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3116','Technicy technologii chemicznej i pokrewni','Technicy technologii chemicznej i pokrewni wykonują prace techniczne mające na celu wsparcie badań w zakresie technologii chemicznej oraz projektowania, produkcji, budowy, obsługi, konserwacji i naprawy instalacji chemicznych. Wykonują zadania techniczne związane z projektowaniem, konstruowaniem, wykonawstwem, naprawami i utrzymaniem w ruchu zakładów chemicznych oraz zakładach przetwórstwa papierniczego, tworzyw sztucznych, środków farmaceutycznych, środków kosmetycznych, itp.','Zadania wykonywane przez techników technologii chemicznej i pokrewnych zazwyczaj obejmują: wspieranie badań i rozwoju przemysłowych procesów chemicznych, instalacji i sprzętu lub testowanie prototypów; projektowanie i przygotowanie planów zakładów chemicznych zgodnie z odpowiednimi specyfikacjami; przygotowanie szczegółowych kalkulacji ilości i kosztów materiałów oraz pracochłonności produkcji zakładów chemicznych, zgodnie z określonymi normami i przepisami; sprawowanie technicznego nadzoru nad projektowaniem, wykonawstwem, eksploatacją, utrzymaniem w ruchu i naprawami zakładów chemicznych, przetwórstwa papierniczego, tworzyw sztucznych, produkcji środków farmaceutycznych i kosmetycznych, itp. W celu zapewnienia sprawnej pracy zgodnie z normami i przepisami; prowadzenie badań laboratoryjnych w dziedzinie chemii i fizyki w ramach wspierania inżynierów w przygotowaniu analiz jakościowych i ilościowych materiałów w stałym, ciekłym lub gazowym stanie skupienia; podejmowanie samodzielnych decyzji w przypadku zakłóceń produkcji lub zgłaszanie nieprawidłowości przełożonym; określanie metod i narzędzi pomiarowych oraz dokonywanie pomiarów parametrów fizykochemicznych wraz z opracowaniem statystycznym wyników pomiarów i obserwacji; prowadzenie ewidencji oraz dokumentacji wyników oznaczeń laboratoryjnych, zabezpieczanie próbek analizowanych materiałów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',135);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3117','Technicy górnictwa, metalurgii i pokrewni','Technicy górnictwa i metalurgii i pokrewni wykonują prace techniczne mające na celu wsparcie badań i eksperymentów nad rozwojem i ulepszaniem metod wydobycia minerałów stałych, ropy naftowej i gazu ziemnego oraz metod wytopu metali z rud i ich dalszej przeróbki. Kierują procesami wydobywania minerałów, wytopu rud metali; obsługują, konserwują i dokonują napraw urządzeń wydobywczych w kopalniach i instalacji kopalnianych, systemów transportu i składowania ropy naftowej i gazu ziemnego oraz urządzeń metalurgicznych.','Zadania wykonywane przez techników górnictwa, metalurgii i pokrewnych zazwyczaj obejmują: świadczenie pomocy technicznej w pracach badawczo-rozwojowych dotyczącymi procesów wydobywania minerałów oraz wytopu i dalszej przeróbki metali oraz ustalania właściwości metali i nowych stopów; zapewnienie wsparcia technicznego w zakresie pomiarów geologicznych i topograficznych; opracowywanie planów technicznych systemów wydobycia i transportu ropy naftowej, gazu ziemnego, rud mineralnych oraz instalacji przetwarzania i rafinowania minerałów i metali; przygotowanie szczegółowych kalkulacji ilości i kosztów materiałów i pracy wymaganych do realizacji projektów w zakresie poszukiwania, wydobycia przetwarzania i transportu ropy naftowej i gazu ziemnego; kontrolowanie aspektów technicznych, prawnych dotyczących bezpieczeństwa budowy, instalacji, użytkowania, konserwacji i napraw instalacji poszukiwania, wydobycia, transportu i składowania rud mineralnych, ropy naftowej, gazu ziemnego oraz zakładów przetwarzania minerałów; zapewnienie wsparcia w zakresie planowania i projektowania kopalni, szybów kopalnianych, tuneli oraz podziemnej infrastruktury w zakresie udzielania pierwszej pomocy; pobieranie i przygotowanie próbek skał, minerałów i metali, prowadzenie badań laboratoryjnych w celu ustalenia ich właściwości, analizowanie i przygotowywanie sprawozdań dotyczących wyników badań oraz konserwacja sprzętu badawczego; posługiwanie się mikroskopami, urządzeniami wykorzystującymi promieniowanie elektromagnetyczne, spektrometrami, spektrografami, densytometrami oraz urządzeniami badającymi ciśnienie; wspieranie naukowców w korzystaniu z elektrycznych, akustycznych i jądrowych instrumentów pomiarowych, zarówno w czynnościach laboratoryjnych, jak i produkcyjnych w celu uzyskania danych pozwalających na identyfikację potencjalnych źródeł rud metali, gazu ziemnego lub ropy naftowej; nadzorowanie pracy urządzeń sterujących procesami hutniczymi, z zachowaniem wymogów dyscypliny technologicznej i eksploatacyjnej; kierowanie pracą zespołów lub brygad, wykonujących określone czynności na poszczególnych etapach procesu produkcyjnego oraz w wydzielonych obszarach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',136);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3118','Kreślarze','Kreślarze przygotowują rysunki techniczne, mapy i ilustracje na podstawie szkiców, wyników pomiarów geodezyjnych i siatek kartograficzno-geograficznych oraz innych danych, a także przenoszą ostateczne wersje rysunków i obrazów na klisze drukarskie wykorzystując tradycyjne urządzenia i przybory kreślarskie oraz urządzenia i programy komputerowe.','Zadania wykonywane przez kreślarzy zazwyczaj obejmują: przygotowanie i korektę rysunków roboczych na podstawie szkiców i specyfikacji przygotowanych przez inżynierów i projektantów dla celów produkcji, instalacji i ustawiania maszyn, sprzętu lub ich budowy, modyfikacji, konserwacji i naprawy budynków, tam, mostów, dróg oraz innych projektów architektonicznych i budowlanych; obsługę wspieranego komputerowo sprzętu umożliwiającego projektowanie w celu stworzenia, modyfikowania i uzyskania wydruków i wersji cyfrowych rysunków roboczych; obsługę tabletu i podobnego sprzętu w celu digitalizacji wydruków rysunków roboczych, map i innych krzywizn; przygotowanie i korektę ilustracji na potrzeby opracowań, broszur i instrukcji technicznych dotyczących: montażu, instalacji, obsługi, konserwacji i naprawy maszyn oraz innego sprzętu i towarów; kopiowanie rysunków i obrazów na tablice kamienne i metalowe do druku; przygotowanie schematów instalacji elektrycznej, schematów montażu płytek drukowanych, rysunków technicznych wykorzystywanych przy produkcji; tworzenie schematów roboczych maszyn i urządzeń mechanicznych obejmujących: wymiary, metody mocowania i inne informacje techniczne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',137);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3119','Technicy nauk fizycznych i technicznych gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą technicy nauk fizycznych i technicznych nie zaklasyfikowani do grupy średniej 311 Technicy nauk chemicznych i fizycznych. Grupa ta obejmuje na przykład osoby zaangażowane w prace nad opracowaniem procedur i prowadzenie badań w zakresie bezpieczeństwa lub inżynierii produkcji biomedycznej, środowiskowej lub przemysłowej.','Zadania wykonywane przez techników nauk fizycznych i technicznych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: gromadzenie danych i zapewnianie wsparcia technicznego w zakresie: skutecznego, bezpiecznego i ekonomicznego wykorzystania personelu, materiałów i sprzętu; metod pracy i sekwencji czynności i nadzoru ich wykonania; pomoc w identyfikacji potencjalnych zagrożeń, wprowadzanie procedur i urządzeń zabezpieczających; modyfikowanie i testowanie sprzętu i urządzeń wykorzystywanych w produkcji różnych urządzeń technicznych, w bardzo wielu dziedzinach: budownictwa okrętowego, akustyki, automatyki, w spawalnictwie, pożarnictwie, technologii drewna, technologii szkła, w transporcie drogowym, kolejowym i innych; wspieranie inżynierów w tworzeniu i testowaniu zautomatyzowanego sprzętu; obsługę urządzeń i aparatury pomiarowej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',138);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3121','Osoby dozoru ruchu w górnictwie','Osoby dozoru ruchu w górnictwie nadzorują czynności górnicze i związane z obróbką kamienia oraz bezpośrednio nadzorują i koordynują prace prowadzone przez górników pracujących w kopalniach podziemnych i odkrywkowych oraz kamieniołomach.','Zadania wykonywane przez osoby dozoru ruchu w górnictwie zazwyczaj obejmują: sprawowanie bezpośredniego nadzoru i koordynowanie prac prowadzonych przez pracowników wydobywających z ziemi minerały i inne złoża naturalne, obsługiwanie pojazdów podziemnych lub ciężkiego sprzętu w kopalniach odkrywkowych i kamieniołomach; opracowanie metod wypełniania planów pracy oraz zalecanie kierownikom w przemyśle górniczym środków mających na celu poprawę wydajności; prowadzenie współpracy z personelem kierowniczym i technicznym oraz innymi działami i wykonawcami w celu rozwiązywania problemów dotyczących działania przedsiębiorstwa i koordynowania prac; dostarczanie sprawozdań kierownikom w przemyśle górniczym dotyczących wszelkich aspektów prac górniczych oraz związanych z obróbką kamienia; określanie potrzeb danej kopalni lub kamieniołomu w zakresie personelu i materiałów; zapewnienie zgodności prac z wymogami w zakresie bhp i ppoż.; wykonywanie innych zadań pokrewnych.',139);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3122','Mistrzowie produkcji w przemyśle przetwórczym','Mistrzowie produkcji w przemyśle przetwórczym koordynują i nadzorują prace prowadzone przez techników produkcji przemysłowej, operatorów maszyn i innych pracowników produkcji.','Zadania wykonywane przez mistrzów produkcji w przemyśle przetwórczym zazwyczaj obejmują: koordynowanie i nadzorowanie prac techników kontroli procesu przemysłowego, operatorów maszyn, monterów, i innych pracowników produkcji; organizowanie i codzienne planowanie prac z uwzględnieniem planów, ekonomii, personelu i środowiska; przygotowywanie kosztorysów, protokołów i sprawozdań; identyfikowanie braków kadrowych i braków komponentów; zapewnienie bezpieczeństwa pracowników; instruowanie i szkolenie nowych pracowników; zapewnienie zgodności prac z wymogami w zakresie bhp i ppoż.; wykonywanie innych zadań pokrewnych.',140);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3123','Mistrzowie produkcji w budownictwie','Mistrzowie produkcji w budownictwie kierują, koordynują, nadzorują i planują prace wykonywane przez pracowników pracujących przy budowie i renowacji budynków i konstrukcji budowlanych.','Zadania wykonywane przez mistrzów produkcji w budownictwie zazwyczaj obejmują: zapoznawanie się z dokumentacją projektu budowli w celu ustalenia wymogów i procedur planowania prac budowlanych; określanie na podstawie otrzymanej dokumentacji kosztu, ilości materiałów budowlanych oraz potrzebnych pracowników niezbędnych do wykonania prac; nadzorowanie i sprawdzanie postępu prac; badanie sprzętu i placów budowy w celu zapewnienia zgodności prac z wymogami w zakresie bhp i ppoż.; nadzorowanie placów budowy i koordynowanie prac przy innych projektach budowlanych; nadzorowanie, instruowanie i szkolenie nowych pracowników; wykonywanie innych zadań pokrewnych.',141);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3131','Operatorzy urządzeń energetycznych','Operatorzy urządzeń energetycznych obsługują, kontrolują i dokonują konserwacji urządzeń elektroenergetycznych elektrowni wodnych i cieplnych: tablic rozdzielczych i podobnych urządzeń w zakładach energetycznych, które kontrolują produkcję i dystrybucję energii elektrycznej oraz innych rodzajów energii w sieciach transmisyjnych. Operatorzy urządzeń energetycznych obsługują reaktory, turbiny, generatory i inne urządzenia pomocnicze wykorzystywane w zakładach produkcji energii elektrycznej.','Zadania wykonywane przez operatorów urządzeń energetycznych zazwyczaj obejmują: obsługę, kontrolę i sprawdzanie rozmaitych rodzajów urządzeń wykorzystywanych do produkcji energii elektrycznej; obsługę i kontrolę systemów i urządzeń do produkcji energii elektrycznej z uwzględnieniem kotłów parowych, turbin, generatorów, kondensatorów i reaktorów w elektrowniach wodnych, termicznych, węglowych, gazowych i atomowych w celu wytwarzania i dystrybucji energii elektrycznej; kontrolowanie uruchamiania i wyłączania urządzeń energetycznych; kontrolowanie zmian, regulowanie poziomu wody i komunikowanie się z operatorami poszczególnych systemów w celu koordynacji obciążenia linii transmisyjnych, częstotliwości oraz napięcia na liniach; prowadzenie odczytów ze schematów, liczników i wskaźników w określonych odstępach czasu; rozwiązywanie problemów w zależności od potrzeb i podejmowanie działań korekcyjnych; uzupełnianie i prowadzenie protokołów, rejestrów i sprawozdań poszczególnych stacji oraz komunikowanie się z innymi pracownikami w celu dokonania oceny sprawności sprzętu; czyszczenie i konserwacja urządzeń takich, jak generatory, kotły parowe, turbiny, pompy i kompresory w celu zapobiegania awariom urządzeń lub pogorszeniu ich sprawności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',142);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3132','Operatorzy urządzeń do spalania odpadów, uzdatniania wody i pokrewni','Operatorzy urządzeń do spalania odpadów, uzdatniania wody i pokrewni obsługują skomputeryzowane systemy kontroli i podobne urządzenia w zakładach utylizacji odpadów stałych i płynnych w celu regulowania utylizacji oraz usuwania ścieków i odpadów w zakładach filtracji i uzdatniania wody w celu regulowania procesu uzdatniania i dystrybucji wody.','Zadania wykonywane przez operatorów urządzeń do spalania odpadów, uzdatniania wody i pokrewnych zazwyczaj obejmują: obsługę i kontrolowanie skomputeryzowanych systemów kontroli, maszyn i podobnych urządzeń w oczyszczalniach ścieków, zakładach uzdatniania wody i zakładach utylizacji odpadów płynnych w celu regulacji przepływu, utylizacji, usuwania ścieków i odpadów oraz w zakładach filtracji i uzdatniania wody w celu regulowania procesu uzdatniania i dystrybucji wody przeznaczonej do konsumpcji i późniejszego wprowadzenia do naturalnych systemów wodnych; kontrolowanie działania wielkich pieców i podobnych urządzeń wykorzystywanych w spalarniach do spalania osadów kanalizacyjnych i odpadów stałych w zakładach utylizacji odpadów; sprawdzanie urządzeń i kontrolowanie warunków ich działania: liczników, filtrów, chloratorów i mierników w centralnej sterowni w celu ustalenia wymogów w zakresie obciążenia, sprawdzenia zgodności przepływów, ciśnień i temperatur ze specyfikacjami oraz w celu wykrycia ewentualnych awarii; monitorowanie i regulowanie urządzeń sterowniczych oraz urządzeń pomocniczych takich, jak: płuczka wieżowa do oczyszczania spalin oraz jednostki odzyskiwania ciepła w spalarniach; pobieranie i przeprowadzanie analizy chemicznej bakteriologicznej próbek wody i ścieków z wykorzystaniem sprzętu do badań oraz norm analizy kolorystycznej; analizowanie wyników badań w celu dokonania korekty ustawień urządzeń i systemów dezynsekcji i deratyzacji wody i innych cieczy; przeprowadzanie kontroli bezpieczeństwa w zakładach i w ich otoczeniu; uzupełnianie i prowadzenie rejestrów i sprawozdań dotyczących danego zakładu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',143);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3133','Kontrolerzy (sterowniczy) procesów w przemyśle chemicznym','Kontrolerzy (sterowniczy) procesów w przemyśle chemicznym obsługują i kontrolują urządzenia w procesach chemicznych oraz regulują i dokonują konserwacji jednostek i urządzeń przetwórczych, w których prowadzona jest destylacja, filtracja, oddzielanie, ogrzewanie lub rafinacja substancji chemicznych służących do produkcji nawozów sztucznych, włókien chemicznych.','Zadania wykonywane przez kontrolerów (sterowniczych) procesów w przemyśle chemicznym zazwyczaj obejmują: obsługę skomputeryzowanego elektronicznego panelu sterowania z centralnej sterowni w celu kontroli i optymalizacji procesów chemicznych i fizycznych zachodzących w kilku jednostkach przetwarzających; regulację urządzeń, zaworów, pomp, urządzeń sterowniczych i urządzeń przetwarzających; kontrolowanie przygotowania, odmierzanie i doprowadzanie surowców i środków przetwórczych takich, jak: katalizatory oraz wyposażanie urządzeń w środki filtrujące; kontrolowanie uruchamiania i zakończenia procesów chemicznych; rozwiązywanie problemów i kontrola zewnętrznych urządzeń przetwórczych; sprawdzanie urządzeń w celu wykrycia ewentualnych awarii, przeprowadzanie rutynowych testów działania oraz organizowanie konserwacji; analizowanie próbek produktów, prowadzenie badań, prowadzenie zapisu danych i prowadzenie rejestru produkcji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',144);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3134','Kontrolerzy (sterowniczy) procesów przeróbki ropy naftowej i gazu','Kontrolerzy (sterowniczy) procesów przeróbki ropy naftowej i gazu obsługują i kontrolują urządzenia oraz regulują i dokonują konserwacji jednostek przetwórczych i urządzeń umożliwiających rafinację, destylację i obróbkę ropy naftowej, produktów ropopochodnych i produktów ubocznych oraz gazu ziemnego.','Zadania wykonywane przez kontrolerów (sterowniczych) procesów przeróbki ropy naftowej i gazu zazwyczaj obejmują: obsługę skomputeryzowanego elektronicznego panelu sterowania z centralnej sterowni w celu kontroli i optymalizacji procesów chemicznych i fizycznych zachodzących w kilku jednostkach przetwarzających; regulację urządzeń, zaworów, pomp, urządzeń sterowniczych i urządzeń przetwarzających; kontrolowanie uruchamiania i zakończenia procesów chemicznych, oraz zewnętrznych urządzeń przetwórczych; sprawdzanie urządzeń w celu wykrycia awarii, sprawdzanie rur w celu wykrycia ewentualnych nieszczelności i przeprowadzenia ich konserwacji; analizowanie próbek produktów, prowadzenie badań, zapisu danych i wpisywanie ich do rejestru produkcji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',145);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3135','Kontrolerzy (sterowniczy) procesów metalurgicznych','Kontrolerzy (sterowniczy) procesów metalurgicznych obsługują i kontrolują wielofunkcyjne urządzenia kontrolujące przebieg procesów przemysłowych i sprzęt kontrolujący pracę pieców konwertorowych i rafinacyjnych, walcarek, urządzeń obróbki cieplnej metali lub urządzeń przy pomocy, których przeprowadza się proces obróbki cieplnej metali.','Zadania wykonywane przez kontrolerów (sterowniczych) procesów metalurgicznych zazwyczaj obejmują: koordynowanie i kontrolowanie przebiegu określonego etapu procesu obróbki cieplnej metali przy pomocy paneli kontrolnych, terminali komputerowych i innych systemów kontroli z centralnej sterowni; obsługę wielofunkcyjnych centralnych urządzeń kontrolnych nadzorujących kruszenie, oddzielanie, filtrowanie, topienie, prażenie, obrabianie, rafinowanie lub inną obróbkę metali; kontrolowanie wydruków komputerowych, monitorów wideo i mierników w celu sprawdzenia określonych warunków przebiegu procesu oraz dokonania niezbędnej korekty; koordynowanie i prowadzenie nadzoru nad personelem produkcyjnym takim, jak: operatorzy maszyn i procesów metalurgicznych oraz asystenci i pomocnicy; uruchamianie i zatrzymywanie procesu obróbki cieplnej metali w przypadku niebezpieczeństwa lub zgodnie z określonym terminarzem; zapewnianie i organizowanie szkoleń dla członków personelu produkcyjnego; prowadzenie rejestru poszczególnych zmian produkcji i gromadzenie innych danych oraz sporządzanie sprawozdań z produkcji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',146);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3139','Kontrolerzy (sterowniczy) procesów przemysłowych gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą kontrolerzy (sterowniczy) procesów przemysłowych nie zaklasyfikowani do grupy średniej 313 Kontrolerzy (sterowniczy) procesów przemysłowych. Grupa ta obejmuje na przykład osoby zajmujące się kontrolą robotów przemysłowych, urządzeń do produkcji papieru, operatorów robotów i manipulatorów przemysłowych, operatorów zautomatyzowanej linii produkcyjnej w przemyśle elektromaszynowym.','Zadania wykonywane przez kontrolerów (sterowniczych) procesów przemysłowych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: obsługiwanie i nadzorowanie bieżącej pracy robotów i manipulatorów przemysłowych; programowanie i przeprogramowywanie robotów przemysłowych do pełnienia określonych funkcji; uaktywnianie robotów przemysłowych; kontrolowanie i zapewnianie ich ciągłej pracy; przewidywanie i zapobieganie, na podstawie wniosków z kontroli, niewłaściwej pracy robotów, ich konserwowanie i naprawa; wykonywanie koniecznych korekt i dostosowań mechanizmów robotów do zleconych zadań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',147);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3141','Technicy nauk biologicznych (z wyłączeniem nauk medycznych)','Technicy nauk biologicznych (z wyłączeniem nauk medycznych) zapewniają wsparcie techniczne badaczom w dziedzinie nauk biologicznych w prowadzeniu badań, dokonywaniu analiz i badań organizmów żywych oraz opracowywaniu i stosowaniu produktów i procesów na podstawie wyników badań laboratoryjnych prowadzonych w obszarach takich jak: zarządzanie zasobami naturalnymi, ochrona środowiska, biologia roślin i zwierząt, mikrobiologia, biologia komórkowa i molekularna oraz biochemia.','Zadania wykonywane przez techników nauk biologicznych (z wyłączeniem nauk medycznych) zazwyczaj obejmują: udzielanie wsparcia w zakresie projektowania, organizowania i prowadzenia eksperymentów laboratoryjnych; ustawianie, kalibrowanie, obsługiwanie i konserwacja przyrządów i sprzętu laboratoryjnego; pobieranie i przygotowywanie okazów i próbek, roztworów chemicznych, preparatów i hodowla kultur do użytku w eksperymentach; prowadzenie rutynowych badań laboratoryjnych i terenowych; kontrolę przebiegu eksperymentów w celu zapewnienia zgodności z odpowiednimi laboratoryjnymi procedurami kontroli jakości i wytycznymi w zakresie bezpieczeństwa i higieny pracy; prowadzenie obserwacji badań oraz analizowanie, obliczanie, zapisywanie i tworzenie sprawozdań z wyników badań przy zastosowaniu właściwych metod naukowych; zabezpieczanie, klasyfikowanie i katalogowanie okazów i próbek; prowadzenie szczegółowych zapisów wykonywanych prac; posługiwanie się programami komputerowymi przy opracowywaniu modeli i analizowaniu danych; uczestnictwo w badaniach, rozwoju i produkcji produktów i procedur; porządkowanie i magazynowanie materiałów laboratoryjnych; prowadzenie określonych baz danych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',148);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3142','Technicy rolnictwa i pokrewni','Technicy rolnictwa i pokrewni organizują, nadzorują i wykonują prace związane z hodowlą roślin, nasiennictwem oraz produkcją roślin w gospodarstwach rolnych. Uczestniczą w badaniach i eksperymentach, udzielają wsparcia technicznego badaczom i specjalistom zajmujących się rolnictwem, rolnikom indywidualnym oraz kierownikom gospodarstw rolnych.','Zadania wykonywane przez techników rolnictwa i pokrewnych zazwyczaj obejmują: przygotowanie materiałów i sprzętu do eksperymentów, badań i analiz; pobieranie i przygotowanie próbek gleb, komórek roślinnych i zwierzęcych, tkanek, fragmentów lub całych organów zwierzęcych do wykorzystania w eksperymentach, badaniach i analizach; udzielanie wsparcia i prowadzenie eksperymentów, badań i analiz przy zastosowaniu takich technik, jak: mikroskopia, histochemia, chromatografia, elektroforeza i spektroskopia; identyfikowanie patogenicznych mikroorganizmów i insektów, pasożytów, grzybów i chwastów szkodliwych dla upraw i hodowli oraz udzielanie wsparcia przy opracowywaniu metod ich kontroli; analizowanie produktów w celu wyznaczenia norm jakości i ich egzekwowania; prowadzenie i sprawowanie nadzoru nad programami operacyjnymi takimi, jak: wylęgarnie ryb, programy produkcji szklarniowej i hodowli; analizowanie próbek nasion w celu oceny ich jakości, czystości i wskaźnika kiełkowania; gromadzenie danych i przygotowanie kalkulacji ilości i kosztów materiałów i pracy wymaganych do realizacji projektów; organizowanie konserwacji i naprawy sprzętu badawczego; wykonywanie innych zadań pokrewnych. Do zadań może także należeć nadzorowanie innych pracowników.',149);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3143','Technicy leśnictwa','Technicy leśnicy wykonują prace techniczne i nadzorcze mające na celu wspieranie badań w dziedzinie leśnictwa i zarządzania zasobami leśnymi, nasiennictwa, szkółkarstwa, hodowli zwierząt, zbioru plonów, ochrony zasobów oraz użytkowania lasów.','Zadania wykonywane przez techników leśnictwa zazwyczaj obejmują: prowadzenie inwentaryzacji lasów, badań i pomiarów terenowych zgodnie z przyjętymi procedurami naukowymi i operacyjnymi; udzielanie wsparcia w wykonywaniu prac technicznych w zakresie przygotowania planów zarządzania zasobami leśnymi i zbioru plonów z wykorzystaniem technik fotogrametrycznych i kartograficznych oraz komputerowych systemów informatycznych; udzielanie wsparcia w planowaniu i sprawowaniu nadzoru nad budową dróg dojazdowych i dróg leśnych; wdrażanie, nadzór i wykonywanie prac technicznych w zakresie prac leśniczych obejmujących przygotowanie, sadzenie i doglądanie szkółek leśnych; koordynowanie działań takich, jak: wycinanie drzew, tłumienie pożarów lasów, kontrola w zakresie chorób i szkodników oraz przerzedzanie skupisk leśnych; prowadzenie nadzoru i wykonywanie prac technicznych związanych ze zbieraniem plonów leśnych; zapewnianie zgodności z przepisami i polityką dotyczącą ochrony środowiska, korzystania z zasobów, bezpieczeństwa pożarowego i zapobiegania wypadkom; prowadzenie nadzoru nad działaniami dotyczącymi szkółek leśnych; udzielanie wsparcia technicznego w zakresie programów badań w leśnictwie w obszarach takich, jak: poprawa jakości drzew, zakładanie i prowadzenie sadów nasiennych, badań na obecność insektów i chorób lub leśnictwa eksperymentalnego oraz badań w zakresie inżynierii leśnictwa; przygotowanie planów obsadzania i wycinki; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',150);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3144','Technicy technologii żywności','Technicy technologii żywności uczestniczą w badaniach i opracowywaniu nowych produktów żywnościowych i technologii oraz organizują i nadzorują procesy pracy i produkcji produktów żywnościowych na powierzonym im odcinku produkcji. Dokonują rozliczeń produkcyjnych, prowadzą remonty aparatury technologicznej oraz urządzeń produkcyjnych.','Zadania wykonywane przez techników technologii żywności zazwyczaj obejmują: świadczenie pomocy technicznej związanej z badaniami w zakresie nowoczesnych technik i technologii oraz opracowywaniem nowych asortymentów produktów żywnościowych; współuczestniczenie w opracowywaniu instrukcji technologicznych oraz normatywów zużycia surowców i materiałów; nadzorowanie prawidłowości przebiegu procesów technologicznych oraz właściwej eksploatacji maszyn i linii produkcyjnych; dokonywanie kontroli pomiędzy poszczególnymi fazami produkcji w zakresie zgodności z obowiązującymi wymaganiami jakościowymi, instrukcjami i recepturami; nadzorowanie warunków higieniczno-sanitarnych produkcji oraz przestrzegania przez pracowników przepisów bhp i ppoz.; organizowanie dobowego zaopatrzenia działu w niezbędne surowce, materiały i opakowania oraz przekazywanie wyrobów do magazynu; podejmowanie samodzielnych decyzji w przypadku zakłóceń produkcji lub zgłaszanie nieprawidłowości przełożonym; prowadzenie dokumentacji produkcyjnej; dokonywanie remontów aparatury technologicznej oraz urządzeń produkcyjnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',151);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3151','Pracownicy służb technicznych żeglugi','Oficerowie służb technicznych i żeglugi uczestniczą w obsłudze, konserwacji i naprawach urządzeń i maszyn mechanicznych, elektrycznych i elektronicznych na pokładzie statków lub pełnią podobne funkcje pomocnicze na lądzie.','Zadania wykonywane przez pracowników służb technicznych żeglugi zazwyczaj obejmują: kontrolę i uczestnictwo w obsłudze, konserwacji i naprawach urządzeń i maszyn mechanicznych, elektrycznych i elektronicznych na pokładzie statków; uzupełnianie zapasów paliwa i innych zasobów potrzebnych w przedziale maszynowym oraz prowadzenie związanej z tym ewidencji; sprawowanie technicznego nadzoru nad instalacją, konserwacją i naprawami maszyn i urządzeń na statku w celu zapewnienia zgodności z obowiązującymi instrukcjami i uregulowaniami; przeprowadzanie inspekcji oraz konserwacja i awaryjne naprawy silników, maszyn i urządzeń pomocniczych; pełnienie warty w przedziale maszynowym oraz monitorowanie i rejestrowanie działania silników, maszyn i urządzeń pomocniczych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',152);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3152','Oficerowie pokładowi, piloci żeglugi i pokrewni','Oficerowie pokładowi i piloci żeglugi dowodzą i prowadzą nawigację statków i podobnych jednostek pływających oraz sprawują nadzór nad bezpieczeństwem żeglugi oraz pełnią podobne funkcje na lądzie.','Zadania wykonywane przez oficerów pokładowych, pilotów żeglugi i pokrewnych zazwyczaj obejmują: dowodzenie i nawigowanie statkami oraz podobnymi jednostkami na morzu lub na wodach śródlądowych; kontrolę i uczestnictwo w działaniach związanych z wachtą na pokładzie i na mostku; nawigację jednostek pływających z portu, do portu oraz przez kanały, cieśniny i inne wody, na których konieczne są specjalne umiejętności; zapewnienie bezpiecznego załadowania i wyładowania towarów oraz nadzorowanie stosowania przepisów i procedur bezpieczeństwa przez załogę i pasażerów; sprawowanie technicznego nadzoru nad naprawami i utrzymaniem statku w ruchu w celu zapewnienia zgodności z instrukcjami i przepisami; wykorzystywanie wiedzy i doświadczeń praktycznych z zakresu eksploatacji i nawigacji statku do identyfikowania i rozwiązywania problemów powstałych w czasie pracy; uzupełnianie zapasów okrętowych i rekrutacja w razie potrzeby załogi oraz prowadzenie związanej z tym ewidencji; wymiana zwyczajnych i alarmowych komunikatów ze stacjami na lądzie i innymi statkami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',153);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3153','Piloci statków powietrznych i personel pokrewny','Piloci statków powietrznych i personel pokrewny kontrolują pracę urządzeń mechanicznych, elektrycznych i elektronicznych celem pilotowania i prowadzenia nawigacji statków powietrznych, przewożących pasażerów, pocztę i towar, wykonują związane z tym zadania przed lotem i podczas lotu. Wykonują loty próbne, szkolą i doszkalają pilotów nie mających dostatecznego przygotowania zawodowego.','Zadania wykonywane przez pilotów statków powietrznych i personel pokrewny zazwyczaj obejmują: pilotowanie i prowadzenie nawigacji statków powietrznych zgodnie z obowiązującymi procedurami kontrolnymi i operacyjnymi; przygotowywanie i przedkładanie planu lotu lub weryfikowanie standardowego planu lotu; kontrolowanie pracy urządzeń mechanicznych, elektrycznych i elektronicznych w celu upewnienia się, że wszystkie urządzenia i instrumenty pomiarowe pracują właściwie; wykorzystywanie wiedzy i doświadczeń praktycznych z zakresu lotnictwa do identyfikowania i rozwiązywania problemów powstałych w czasie pracy; badanie rejestrów obsługi i przeprowadzanie inspekcji w celu upewnienia się, że statek powietrzny jest w dobrym stanie technicznym; prowadzenie rejestrów lotów; uczestniczenie w odprawach przed lotem oraz utrzymywanie łączności podczas lotu z kontrolą ruchu lotniczego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',154);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3154','Kontrolerzy ruchu lotniczego i personel pokrewny','Kontrolerzy ruchu lotniczego personel pokrewny kierują ruchem statków powietrznych w powietrzu i na ziemi, wykorzystując radio, radar i systemy świetlne. Przyjmują i przekazują informacje o sprawności urządzeń nawigacyjnych, warunków pogodowych i zmianach stanu lotnisk niezbędnych do bezpiecznego przeprowadzenia operacji lotniczych','Zadania wykonywane przez kontrolerów ruchu lotniczego i personelu pokrewnego zazwyczaj obejmują: kierowanie i kontrolowanie statku powietrznego zbliżającego się do lotniska, w czasie ruchu po płycie lotniska oraz opuszczającego lotnisko; kierowanie i kontrolowanie statku powietrznego zmierzającego do wyznaczonego sektora lotniska; sprawdzanie i zatwierdzanie planów lotów; informowanie załogi statku powietrznego i personelu operacyjnego o warunkach pogodowych, urządzeniach operacyjnych, planach lotów i ruchu w powietrzu; wykorzystywanie wiedzy i doświadczeń praktycznych związanych z kontrolą i bezpieczeństwem ruchu lotniczego do identyfikacji i rozwiązywania problemów powstałych w czasie pracy; organizowanie działań i operacji awaryjnych, poszukiwawczych i ratunkowych; kierowanie działaniami wszystkich statków powietrznych i pojazdów obsługi na drogach startowych lotniska lub w ich pobliżu; utrzymywanie łączności radiowej i telefonicznej z wieżami kontroli lotniska, jednostkami kontroli terminali i innymi centrami kontroli; koordynowanie ruchu statków powietrznych do obszarów przyległych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',155);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3155','Technicy urządzeń ruchu lotniczego','Technicy urządzeń ruchu lotniczego wykonują zadania związane z projektowaniem, instalowaniem, zarządzaniem, obsługą, konserwacją i naprawami systemów kontroli ruchu lotniczego i nawigacji lotniczej.','Zadania wykonywane przez techników urządzeń ruchu lotniczego zazwyczaj obejmują: wykonywanie zadań technicznych związanych z rozwijaniem elektronicznych i skomputeryzowanych systemów i urządzeń nawigacji lotniczej oraz testowanie prototypów; świadczenie pomocy technicznej przy projektowaniu i trasowaniu współpracujących obwodów systemów nawigacji lotniczej i napowietrznych systemów wykrywania i naprowadzania; przygotowywanie i udział w przygotowywaniu kosztorysów oraz instrukcji technicznych i treningowych dla ruchu powietrznego i urządzeń zabezpieczających; sprawowanie nadzoru technicznego lub uczestniczenie w nadzorze technicznym nad tworzeniem, instalowaniem i eksploatacją naziemnych urządzeń nawigacji lotniczej oraz ich utrzymaniem w dobrym stanie technicznym; wykorzystywanie wiedzy i doświadczeń praktycznych związanych z zasadami bezpieczeństwa inżynierii ruchu lotniczego do identyfikacji i rozwiązywania problemów powstałych w czasie pracy; opracowywanie i modyfikowanie oprogramowania systemowego oraz usuwanie występujących w nim błędów; modyfikowanie istniejących naziemnych systemów i urządzeń nawigacji lotnicze w celu dostosowania ich do wymagań nowych procedur kontroli ruchu powietrznego, zwiększenia ich wydajność, niezawodności i integralności; kontrolowanie, monitorowanie i certyfikowanie urządzeń tworzących zintegrowany system zarządzania podsystemami nawigacji i łączności oraz kalibrowanie naziemnych urządzeń nawigacji lotniczej dla zapewnienia maksymalnego bezpieczeństwa i dokładności lotu, operacji startu i lądowania; prowadzenie szkoleń technicznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',156);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3211','Operatorzy aparatury medycznej','Operatorzy aparatury medycznej testują i obsługują radiologiczne, ultrasonograficzne elektrokardiograficzne, radiologiczne, audiometryczne, tomograficzne i inne medyczne urządzenia obrazujące w celu otrzymywania obrazów struktur ciała na potrzeby diagnozowania i leczenia urazów, chorób i innych upośledzeń. Mogą przeprowadzać radioterapię pacjentów pod nadzorem radiologa lub innego specjalisty ochrony zdrowia.','Zadania wykonywane przez operatorów aparatury medycznej zazwyczaj obejmują: obsługiwanie i nadzorowanie obsługi radiologicznych, ultrasonograficznych i magnetycznych urządzeń obrazujących służących do otrzymywania obrazów ciała do celów diagnostycznych; wyjaśnianie pacjentom przebiegu zabiegów oraz obsługiwanie urządzeń ochronnych w celu zapewnienia pacjentowi bezpieczeństwa i komfortu podczas badania, wykonywanie materiałów fotograficznych; ustawianie urządzeń obrazujących; obserwacja monitorów obrazowych, regulowanie ustawień i urządzeń sterowniczych zgodnie z wymaganiami technicznymi; przeglądanie i ocenianie otrzymanych zdjęć rentgenowskich, nagrań lub informacji wygenerowanych komputerowo w celu stwierdzenia, czy są one odpowiednie do celów diagnostyki i rejestrowania wyników zabiegów; monitorowanie stanów i reakcji pacjentów; dokonywanie pomiaru, zapisywanie dawek promieniowania lub leków przeciwdziałających promieniowaniu radiologicznemu zgodnie z zaleceniem lekarzy; wykrywanie i śledzenie obiegu leków lub promieniowania w ciałach pacjentów przy użyciu radioizotopu, kamery lub innych urządzeń do celów diagnozowania i leczenia chorób; ewidencjonowanie i pozbywanie się materiałów radioaktywnych oraz przechowywanie leków przeciwdziałających promieniowaniu radiologicznemu zgodnie z obowiązującymi procedurami bezpieczeństwa dotyczącymi promieniowania; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',157);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3212','Technicy analityki medycznej','Technicy analityki medycznej wykonują badania kliniczne próbek płynów ustrojowych (krwi i wydzielin) oraz tkanek ciała ludzkiego na potrzeby diagnostyczne w celu uzyskania informacji o stanie zdrowia pacjenta.','Zadania wykonywane przez techników analityki medycznej zazwyczaj obejmują: przeprowadzanie analizy chemicznej płynów ustrojowych, w tym krwi, moczu i płynu rdzeniowego, materiałów biopsyjnych w celu stwierdzenia obecności ich prawidłowych lub nieprawidłowych składników; obsługiwanie, kalibrowanie i konserwowanie spektrofotometrów, kalorymetrów, fotometrów płomieniowych i komputerowo sterowanych analizatorów; wprowadzanie danych uzyskanych w drodze analizy testów laboratoryjnych i wyników klinicznych do systemów prowadzenia rejestrów oraz przedstawianie wyników lekarzom i innym specjalistom ochrony zdrowia; analiza próbek materiału biologicznego pod kątem składu chemicznego lub reakcji; obsługiwanie, czyszczenie i konserwowanie aparatury laboratoryjnej; analizowanie wyników testów laboratoryjnych w celu sprawdzenia prawidłowości wyników; ustanawianie i monitorowanie programów zapewniających prawidłowość wyników testów laboratoryjnych oraz rozwijanie, standaryzowanie, ocenianie i modyfikowanie procedur, technik i testów wykorzystywanych do analizy próbek; pobieranie próbek oraz namnażanie, wyodrębnianie i identyfikowanie drobnoustrojów do analizy; posiewanie ustrojów na zapłodnione komórki jajowe, pożywkę bulionową lub inne podłoża bakteriologiczne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',158);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3213','Technicy farmaceutyczni','Technicy farmaceutyczni wykonują zadania techniczne, analityczne i administracyjne związane z produkcją leków, ich analizą i kontrolą oraz obrotem środkami farmaceutycznymi, artykułami sanitarnymi i sprzętem medycznym. Sprawują nadzór lub prowadzą procesy technologiczne produkcji leków w przemyśle farmaceutycznym. Sporządzają i przygotowują lekarstwa w aptekach, szpitalach i przychodniach pod kierunkiem farmaceuty lub innego specjalisty ochrony zdrowia.','Zadania wykonywane przez techników farmaceutycznych zazwyczaj obejmują: przygotowywanie leków i innych złożonych substancji farmaceutycznych pod kierunkiem farmaceuty lub innego specjalisty ochrony zdrowia; nadzorowanie i prowadzenie procesów technologicznych produkcji leków w przemyśle farmaceutycznym, wydawanie leków klientom i dawanie pisemnych lub ustnych instrukcji dotyczących ich użycia zgodnie z zaleceniami lekarzy, weterynarzy lub innych specjalistów ochrony zdrowia; przyjmowanie recept i sprawdzanie, czy zamieszczone na nich informacje są kompletne i prawidłowe w odniesieniu do standardów prowadzenia dokumentacji medycznej; zapewnianie odpowiednich warunków przechowywania i zabezpieczania leków; napełnianie i opatrywanie etykietą pojemników z lekami; udzielanie klientom odpowiedzi na pytania, dotyczące leków, wyszukiwanie leków lub kierowanie do farmaceutów w celu uzyskania dodatkowych informacji na temat leków; wycenianie, ewidencjonowanie i realizowanych recept; zamawianie, opatrywanie etykietami, podliczanie zapasów leków, chemikaliów i sprzętu oraz wprowadzanie danych inwentarzowych do systemów prowadzenia rejestrów; czyszczenie i przygotowywanie urządzeń oraz pojemników stosowanych do przygotowywania złożonych substancji farmaceutycznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',159);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3214','Technicy medyczni i dentystyczni','Technicy medyczni i dentystyczni projektują, dopasowują, obsługują i naprawiają narzędzia oraz urządzenia medyczne i dentystyczne zgodnie z zaleceniami lub instrukcjami specjalisty ochrony zdrowia. Korygują problemy fizyczne w zakresie protetyki słuchu, protetyki dentystycznej oraz ortopedycznej. Projektują, wykonują i dopasowują protezy zębowe, słuchowe oraz sprzęt ortopedyczno-rehabilitacyjny taki jak: kołnierze ortopedyczne, szyny ortopedyczne, protezy kończyn, aparaty słuchowe, korony i mosty dentystyczne itp.','Zadania wykonywane przez techników medycznych i dentystycznych zazwyczaj obejmują: badanie pacjentów, przeprowadzanie z nimi wywiadów, pobieranie miary w celu określenia potrzeb protetycznych pacjentów oraz zidentyfikowania czynników, które mogą mieć wpływ na ich dopasowanie; konsultowanie z lekarzami ortopedami, audiologami i dentystami instrukcji i zaleceń dotyczących aparatów i przyrządów; interpretowanie zaleceń lub instrukcji w celu określenia rodzaju produktu lub aparatu, jaki należy wykonać oraz potrzebnych materiałów i narzędzi; wykonywanie lub przyjmowanie odlewów i odcisków według których będą wytwarzane aparaty i przyrządy; projektowanie i wykonywanie protetycznych lub prostetycznych urządzeń przy wykorzystaniu materiałów, takich jak: tworzywa termoplastyczne lub termoutwardzalne, stopy metali, skóra przy użyciu narzędzi ręcznych oraz z napędem mechanicznym; dopasowywanie aparatów i przyrządów, testowanie i ocenianie ich, dokonywanie korekt ich ustawień w celu zapewnienia prawidłowego dopasowania oraz komfortu pacjentów; naprawianie, modyfikowanie i konserwowanie medycznych i dentystycznych urządzeń prostetycznych i podtrzymujących ciało zgodnie z instrukcjami; wyginanie, formowanie i kształtowanie materiałów lub tworzyw tak, aby były one dopasowane do zalecanych form potrzebnych do wykonania części tworzących strukturę protezy; wykonywanie pełnych lub częściowych protez zębowych, ochraniaczy na zęby, koron, metalowych klamer, plomb, mostów i innych; instruowanie pacjentów w zakresie użytkowania i dbania o aparaty prostetyczne lub protetyczne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',160);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3220','Dietetycy i żywieniowcy','Dietetycy i żywieniowcy oceniają, planują i wdrażają programy mające na celu poprawę skutków wpływu żywności i żywienia na zdrowie ludzkie. Planują i nadzorują przygotowywanie diet leczniczych lub innych dla poszczególnych osób lub grup ludzi, uczestniczą w programach edukacji żywieniowej oraz prowadzą doradztwo w zakresie problemów żywienia.','Zadania wykonywane przez dietetyków i żywieniowców zazwyczaj obejmują: udzielanie porad dotyczących żywienia, planowanie diet i przygotowywanie żywności w celu zwiększenia korzyści zdrowotnych i ograniczenie potencjalnego zagrożenia dla zdrowia; planowanie diet i menu, nadzorowanie przygotowania i podawania posiłków oraz monitorowanie spożycia i jakości pożywienia w celu zapewnienia opieki w zakresie żywienia w miejscach, w których oferowane są usługi gastronomiczne; gromadzenie i dokonywanie oceny danych związanych ze stanem zdrowia i wyżywienia osób indywidualnych, grup i społeczności w oparciu o wartości odżywcze podawanej i konsumowanej żywności; planowanie i dokonywanie oceny żywienia, programów interwencyjnych oraz kształcenia i szkolenia w celu podniesienia poziomów wyżywienia osób indywidualnych i społeczności; konsultacje z innymi specjalistami ochrony zdrowia i specjalistami w zakresie opieki w celu zaspokajania potrzeb związanych z dietą i żywieniem pacjentów; opracowywanie i ocenianie produktów spożywczych w celu spełnienia wymogów dotyczących żywienia; prowadzenie badań w zakresie żywienia i udostępnianie ich wyników między innymi na konferencjach naukowych, w raportach i w prasie; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',161);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3230','Praktykujący niekonwencjonalne lub komplementarne metody terapii','Praktykujący niekonwencjonalne lub komplementarne metody terapii rozpoznają i eliminują zaburzenia bioenergetyczne organizmu w celach relaksacyjnych, profilaktycznych i biostymulujących; wykonują różnego rodzaju zabiegi z zakresu rehabilitacji, zajmują się profilaktyką, opieką i terapią w przypadku chorób, zaburzeń i urazów z zastosowaniem terapii ziołowych, z udziałem zwierząt (hipoterapia, kynoterapia) lub innych terapii (akupunktura), chiropraktyka, homeopatia, refleksologia, naturopatia, osteopatia, muzykoterapia) opartych na teoriach, wierzeniach i doświadczeniach właściwych dla poszczególnych kultur.','metody terapii zazwyczaj obejmują: przeprowadzanie szczegółowego wywiadu dotyczącego stanu zdrowia klienta; informowanie klienta o istocie i spodziewanych efektach stosowanej metody terapii; rozpoznawanie zmian biopola i ustalanie obszarów zakłóconych z wykorzystaniem wyćwiczonych możliwości własnego organizmu oraz sprawdzonych metod i środków; zalecanie oraz prowadzenie opieki i terapii w przypadku chorób lub innych dolegliwości z zastosowaniem tradycyjnych technik i środków leczniczych takich jak: fizyczne oddziaływanie na ciało i ćwiczenia fizyczne, wykorzystywanie preparatów zawierających: ekstrakty z ziół, roślin, owadów i zwierząt; świadczenie opieki i terapii w przypadku urazów fizycznych obejmujące nastawianie złamanych kości lub zwichniętych stawów przy wykorzystaniu metod naturalnych polegających na fizycznym oddziaływaniu na ciało; wykonywanie zabiegów z zastosowaniem różnych metod: masażu, bioenergoterapii i innych podobnych metod; prowadzenie dokumentacji swojej działalności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',162);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3240','Technicy weterynarii','Technicy weterynarii wykonują zadania doradcze, diagnostyczne, zapobiegawcze i terapeutyczne w dziedzinie weterynarii o zakresie i kompleksowości ograniczonej w stosunku do tych, jakie wykonują lekarze weterynarii oraz jakie wykonuje się pod kierunkiem lekarzy weterynarii. Sprawują opiekę nad zwierzętami poddawanymi leczeniu i pozostającymi tymczasowo w placówkach weterynaryjnych, przeprowadzają badania laboratoryjne; pomagają lekarzom weterynarii w przeprowadzaniu zabiegów i operacji, wykonują zabiegi sanitarno-weterynaryjne i zabiegi unasieniania zwierząt.','Zadania wykonywane przez techników weterynarii zazwyczaj obejmują: udzielanie porad dotyczących pielęgnacji zwierząt oraz ich chorób i urazów; przeprowadzanie badań zwierząt w celu postawienia diagnozy i przekazywanie trudniejszych przypadków lekarzom weterynarii, jeśli zajdzie taka potrzeba; leczenie chorych lub rannych zwierząt, w szczególności w przypadku częstych chorób i zaburzeń; czyszczenie i wyjaławianie stołów i instrumentów do badania oraz przygotowywanie materiałów wykorzystywanych do badania zwierząt i przeprowadzania zabiegów; wykonywanie zadań technicznych związanych ze sztucznym unasiennianiem zwierząt; przygotowywanie zwierząt do badania lub zabiegu; pomaganie lekarzom weterynarii w podawaniu środków znieczulających i tlenu podczas zabiegu; opiekę nad zwierzętami podczas rekonwalescencji, po operacji i monitorowanie ich stanu zdrowia; wykonywanie obrazów radiologicznych, pobieranie próbek oraz przeprowadzanie testów laboratoryjnych w ramach pomocy w diagnozowaniu problemów zdrowotnych zwierząt; przeprowadzanie rutynowych zabiegów dentystycznych na zwierzętach i pomaganie lekarzom weterynarii w zakresie stomatologii zwierzęcej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',163);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3251','Asystenci dentystyczni','Asystenci dentystyczni sprawują podstawową opiekę w zakresie profilaktyki i leczenia chorób i zaburzeń uzębienia i jamy ustnej zgodnie z planami i procedurami ustanowionymi przez lekarza dentystę lub innego specjalistę ochrony zdrowia. Przygotowują materiały i instrumenty dentystyczne dla lekarza oraz pacjentów do badań i zabiegów leczniczych.','Zadania wykonywane przez asystentów dentystycznych zazwyczaj obejmują: udzielanie porad w zakresie higieny uzębienia, diety oraz innych środków zapobiegawczych służących obniżaniu ryzyka dla zdrowia jamy ustnej; przeprowadzanie badania fizykalnego jamy ustnej, zębów i struktur powiązanych w celu oceny stanu zdrowia jamy ustnej pacjenta; identyfikowanie przypadków złego stanu zdrowia lub chorób jamy ustnej wymagających skierowania pacjenta do lekarza dentysty lub innego specjalisty ochrony zdrowia; pomaganie lekarzom dentystom w przeprowadzaniu skomplikowanych zabiegów dentystycznych; przeprowadzanie zabiegów fluorowania i czyszczenia zębów; przygotowywanie, czyszczenie i wyjaławianie narzędzi, sprzętu i materiałów dentystycznych wykorzystywanych do badania i leczenia pacjentów; przygotowywanie pacjentów do zabiegu lub leczenia; wykonywanie innych zadań pokrewnych.',164);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3252','Środowiskowi pracownicy ochrony zdrowia','Środowiskowi pracownicy ochrony zdrowia pełnią istotną rolę we wszystkich tych strukturach, które uznają potrzebę oddziaływań leczniczo-terapeutycznych w środowisku osoby chorej. Utrzymują kontakty z osobami chorymi i ich rodzinami. Udzielają pomocy terapeutycznej, wsparcia psychologicznego, koordynują pomoc lekarską lub socjalną oraz motywują do podjęcia leczenia lub rehabilitacji.','Zadania wykonywane przez środowiskowych pracowników ochrony zdrowia zazwyczaj obejmują: budowania i utrzymania relacji terapeutycznej z osobą chorą i jej rodziną; utrzymanie relacji, udzielenia pomocy terapeutycznej oraz wsparcia psychologicznego; koordynowanie pomocy lekarskiej lub socjalnej; motywowania do podjęcia leczenia lub rehabilitacji; interweniowanie w sytuacji szeroko rozumianego kryzysu chorego jego rodziny; sprawne komunikowanie się i koordynowanie współpracy z różnymi ośrodkami leczenia oraz wsparcia socjalnego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',165);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3253','Optycy okularowi','Optycy okularowi zajmują się projektowaniem i dopasowywaniem szkieł okularowych lub soczewek kontaktowych przeznaczonych do korygowania obniżonej ostrości wzroku na podstawie recept od lekarzy oftalmologów lub optometrystów; mierzą pole widzenia i siłę wzroku (bez leczenia farmakologicznego); wykonują usługi związane z okularami korekcyjnymi, soczewkami kontaktowymi, pomocami i przyrządami optycznymi takimi jak: dobieranie szkieł do opraw okularowych lub soczewki kontaktowe do gałki ocznej .','Zadania wykonywane przez optyków okularowych zazwyczaj obejmują: wykonywanie badań oczu, dopasowanie szkieł okularowych, soczewek kontaktowych i innych przyrządów optycznych korygujących wady wzroku, a w koniecznych przypadkach kierowanie pacjenta do lekarza okulisty; udzielanie porad dotyczących doboru okularów, oprawek, rodzajów soczewek kontaktowych i innych przyrządów optycznych oraz właściwego ich używania; realizacja recept na przyrządy optyczne i określanie trybu pracy laboratorium optycznego w zakresie szlifowania i mocowania soczewek w oprawkach, przygotowywania soczewek kontaktowych oraz innych koniecznych prac; sprawdzanie zgodności wykonanych przyrządów i urządzeń optycznych z receptą i dopasowywanie ich do gałki ocznej klienta; dokonywanie wszelkich napraw różnych typów okularów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',166);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3254','Technicy fizjoterapii i masażyści','Technicy fizjoterapii i masażyści prowadzą terapię pacjentów, których sprawność ruchowa jest ograniczona w wyniku: urazu, choroby lub upośledzenia, stosując ćwiczenia rehabilitacyjne, metody manualne, ultradźwiękowe, cieplne, laserowe itp. Posługują się technikami terapeutycznymi w celu uzupełnienia kompleksowego postępowania leczniczego; wykonują zabiegi z zakresu kinezyterapii, fizykoterapii, balneologii i masażu leczniczego w celu zahamowania i likwidacji procesu chorobowego, kompensacji ubytków funkcji organizmu lub jego adaptacji do nowych warunków wynikających z uszkodzenia lub utraty części ciała.','Zadania wykonywane przez techników fizjoterapii i masażystów zazwyczaj obejmują: współpracę z pacjentem i jego rodziną z wykorzystaniem metod psychologicznych i pedagogicznych pod kątem czynnego uczestniczenia w procesie profilaktyki, leczenia i rehabilitacji; wykonywanie specjalistycznych zabiegów fizykoterapeutycznych, w celu profilaktyki, leczenia i rehabilitacji pacjentów; prowadzenie terapii manualnej, takiej jak: terapia masażem i wykonywanie różnymi technikami i metodami masażu: leczniczego, rehabilitacyjnego, sportowego, kosmetycznego w celu poprawy krążenia, samopoczucia, zmniejszenia bolesności stawów i mięśni albo ich usprawnienia; prowadzenie terapii elektrycznością, ultradźwiękami lub innymi metodami fizycznymi przy wykorzystaniu specjalistycznych technik i sprzętu, w tym lamp na podczerwień, mokrych kompresów, ziół i minerałów; instruowanie, motywowanie i pomaganie pacjentom w wykonywaniu ćwiczeń fizycznych oraz stosowaniu technik relaksacji; współpraca z lekarzem i całym zespołem terapeutycznym; monitorowanie i rejestrowanie postępów pacjentów podczas leczenia, włączając pomiar zakresu ruchu stawów oraz funkcji życiowych; kontrolą i oceną reakcji pacjenta na stosowane zabiegi, dokonywanie korekt w postępowaniu leczniczym; przestrzeganie przepisów bhp obowiązujących przy określonych rodzajach zajęć w celu zapewnienia choremu maksymalnego bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',167);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3255','Średni personel ochrony środowiska, medycyny pracy i bhp','Średni personel ochrony środowiska, medycyny pracy i bhp bada przestrzeganie zasad i przepisów dotyczących czynników środowiskowych, jakie mogą mieć wpływ na ludzkie zdrowie, bezpieczeństwo w miejscu pracy oraz bezpieczeństwo procesów produkcji dóbr i świadczenia usług. Kontrolują miejsca pracy z punktu widzenia bezpieczeństwa i higieny pracy. Wdrażają i oceniają programy mające przywrócić lub poprawić bezpieczeństwo i warunki sanitarne pod nadzorem specjalisty ochrony zdrowia. Propagują w różnych środowiskach konieczność ochrony przyrody i przestrzegania zasad i przepisów dotyczących bezpieczeństwa i higieny pracy.','i bhp zazwyczaj obejmują: udzielanie pracodawcom i przedstawicielom pracowników porad w zakresie wdrażania państwowych i innych zasad i przepisów dotyczących bezpieczeństwa zawodowego i środowiska pracy; przeprowadzanie inspekcji w miejscach pracy w celu sprawdzenia, czy środowisko pracy oraz wykorzystywane w nim maszyny i urządzenia są zgodne z obowiązującymi przepisami i standardami dotyczącymi higieny lub zdrowia oraz bezpieczeństwa zawodowego i środowiskowego; udzielanie porad w zakresie problemów dotyczących higieny i środowiska pracy oraz sposobów ich rozwiązywania; przeprowadzanie kontroli inspekcji w miejscach pracy i uzyskiwanie w wyniku przeprowadzonych wywiadów, obserwacji i innych działań, informacji na temat praktyk i wypadków w miejscu pracy w celu określenia stopnia zgodności bezpieczeństwa z obowiązującymi zasadami i przepisami; przeprowadzanie inspekcji w miejscach produkcji przetwarzania, transportowania, obsługi, przechowywania i sprzedaży wyrobów i produktów w celu sprawdzenia stopnia zgodności z obowiązującymi przepisami i standardami; udzielanie przedsiębiorcom oraz pracownikom porad w zakresie przestrzegania przepisów dotyczących bezpieczeństwa i higieny pracy; przeprowadzanie inspekcji w przedsiębiorstwach w celu sprawdzenia, czy przestrzegają one obowiązujących przepisów dotyczących emisji zanieczyszczeń oraz utylizacji niebezpiecznych odpadów; podejmowanie działań mających służyć utrzymaniu i poprawie poziomu czystości wody, powietrza, żywności, gleby oraz zapobieganiu ich zanieczyszczaniu; promowanie środków zapobiegawczych i naprawczych takich jak: kontrola organizmów przenoszących choroby oraz substancji szkodliwych znajdujących się w powietrzu, higieniczne obchodzenie się z żywnością, prawidłowe pozbywanie się odpadów oraz czyszczenie przestrzeni publicznej; szacowanie ilości i kosztów materiałów i pracy koniecznych do realizacji zadań dotyczących zapewniania zdrowia, bezpieczeństwa i higieny pracy; prowadzenie dokumentacji swojej działalności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',168);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3256','Ratownicy medyczni','Ratownicy medyczni udzielają szybkiej i sprawnej pomocy o charakterze ratowniczym w nagłych stanach zagrożenia życia, wypadkach i katastrofach; sprawują opiekę nad pacjentami, którzy ulegli urazom, są chorzy, niepełnosprawni, upośledzeni ruchowo lub umysłowo, przed oraz podczas transportu do szpitali lub innych placówek ochrony zdrowia','Zadania wykonywane przez ratowników medycznych zazwyczaj obejmują: ocenianie stanu zdrowia osób uczestniczących w wypadkach, katastrofach naturalnych lub innych nagłych zdarzeniach i ustalanie, czy potrzebna jest natychmiastowa lub doraźna pomoc medyczna; wykonywanie zabiegów i podawanie leków oraz stosowanie innych metod leczenia zgodnie z obowiązującymi przepisami w zakresie udzielania pomocy medycznej w nagłych wypadkach; monitorowanie zmian w stanie zdrowia pacjentów podczas transportu z i do szpitali lub innych ośrodków medycznych i rehabilitacyjnych; informowanie i szkolenie organizacji społecznych oraz pracowników służb użyteczności publicznej w zakresie udzielania pierwszej pomocy w przypadku chorób lub urazów; rejestrowanie informacji dotyczących stanu pacjentów i zastosowanego leczenia w systemach prowadzenia rejestrów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',169);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3259','Średni personel do spraw zdrowia gdzie indziej niesklasyfikowany','Do tej grupy elementarnej należy średni personel ochrony zdrowia nie zaklasyfikowany do grupy średniej 325 Inny średni personel do spraw zdrowia. Grupa ta obejmuje osoby zajmujące się: edukacją zdrowotną i podstawową opieką profilaktyczną, higieną szkolną, terapią uzależnień, koordynowaniem pobierania i przeszczepiania tkanek i narządów, terapią zajęciową .','Zadania wykonywane przez średni personel do spraw zdrowia gdzie indziej niesklasyfikowany zazwyczaj obejmują: przeprowadzanie wywiadów z pacjentami i badanie ich w celu uzyskania informacji dotyczących stanu zdrowia, zakresu obrażeń, choroby lub innych fizycznych lub psychicznych problemów zdrowotnych; sprawowanie opieki terapeutycznej nad pacjentami; monitorowanie postępów leczenia pacjentów oraz identyfikowanie objawów i symptomów wymagających skierowania do lekarza lub innego specjalisty ochrony zdrowia; rejestrowanie informacji dotyczących stanu zdrowia pacjentów i ich reakcji na leczenie w bazach danych o pacjentach; przekazywanie informacji innym usługodawcom z zakresu ochrony zdrowia, gdy jest to konieczne, do zapewnienia ciągłej i kompleksowej opieki; pozyskiwanie dawców tkanek i narządów (przeprowadzanie rozmowy z rodziną potencjalnego, zmarłego dawcy); wykonywanie badań laboratoryjnych w odniesieniu do dawców tkanek i narządów, przestrzeganie procedur pobierania komórek i tkanek oraz ich przyjmowanie do banku komórek i tkanek; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',170);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3311','Dealerzy i maklerzy aktywów finansowych','Dealerzy i maklerzy aktywów finansowych kupują i sprzedają papiery wartościowe, akcje, obligacje i inne instrumenty finansowe oraz biorą udział w obrocie na rynku walut, rynku transakcji natychmiastowych lub rynku transakcji terminowych w imieniu firmy, w której są zatrudnieni lub klientów na zasadzie prowizji. Polecają transakcje klientom, doradzają i udzielają informacji w zakresie obrotu papierami wartościowymi.','Zadania wykonywane przez dealerów i maklerów aktywów finansowych zazwyczaj obejmują: uzyskiwanie informacji o stanie finansów klientów oraz firm, których mogą dotyczyć inwestycje; analizowanie tendencji rynkowych w przypadku papierów wartościowych, akcji, obligacji i innych instrumentów finansowych, włączając waluty; informowanie potencjalnych klientów o warunkach i perspektywach rynków finansowych; doradzanie i uczestniczenie w negocjacjach dotyczących warunków i struktury kredytów oraz lokowania akcji i obligacji na rynku finansowym w celu powiększania kapitału klientów; rejestrowanie i przekazywanie zleceń kupna i sprzedaży papierów wartościowych, akcji, obligacji, innych instrumentów finansowych oraz walut dostarczanych w określonym terminie lub natychmiast; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',171);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3312','Pracownicy do spraw kredytów, pożyczek i pokrewni','Pracownicy do spraw kredytów analizują i oceniają informacje finansowe zawarte we wnioskach kredytowych, zatwierdzają lub odrzucają wnioski kredytowe klientów oraz polecają kadrze kierowniczej zatwierdzenie lub odrzucenie wniosków.','Zadania wykonywane przez pracowników do spraw kredytów, pożyczek i pokrewnych zazwyczaj obejmują: przeprowadzanie z klientami wywiadów dotyczących zaciągniętych przez nich kredytów osobistych, hipotecznych, studenckich lub biznesowych; poszukiwanie informacji o stanie finansów wnioskujących o kredyt, opinii o zaciągniętych przez nich kredytach oraz dokonywanie oceny wnioskujących o kredyt; przedkładanie wniosków kredytowych kadrze kierowniczej z zaleceniem ich zatwierdzenia lub odrzucenia; zatwierdzanie lub odrzucanie wniosków kredytowych w ramach przysługującego zakresu upoważnień przy zapewnieniu przestrzegania obowiązujących standardów; prowadzenie rejestru spłat oraz przygotowywanie zwyczajowych listów z wezwaniem do spłaty zaległych należności oraz przekazywanie ich do celów wszczęcia kroków prawnych; uzupełnianie dokumentacji kredytowej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',172);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3313','Księgowi','Księgowi prowadzą pełne rejestry transakcji finansowych przedsiębiorstwa i sprawdzają prawidłowość dokumentów i rejestrów dotyczących tych transakcji, odpowiadając za rzetelność danych i zgodność z prawem zawieranych transakcji.','Zadania wykonywane przez księgowych zazwyczaj obejmują: prowadzenie pełnych rejestrów wszystkich transakcji finansowych przedsiębiorstwa zgodnie z głównymi zasadami księgowości; sprawdzanie prawidłowości dokumentacji i rejestrów dotyczących wypłat, przychodów i innych transakcji finansowych; przygotowywanie okresowych sprawozdań finansowych i raportów; stosowanie wiedzy i doświadczeń praktycznych związanych z zasadami księgowości do identyfikowania i rozwiązywania problemów powstałych w czasie pracy; używanie standardowych pakietów oprogramowania komputerowego do wykonywania obliczeń księgowych i innych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',173);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3314','Średni personel do spraw statystyki i dziedzin pokrewnych','Średni personel do spraw statystyki i dziedzin pokrewnych pomaga w planowaniu zbierania, przetwarzania i prezentacji danych statystycznych oraz w wykonywaniu tych czynności, zazwyczaj pod nadzorem statystyków, matematyków lub ekonomistów.','Zadania wykonywane przez średni personel do spraw statystyki i dziedzin pokrewnych zazwyczaj obejmują: udzielanie pomocy w planowaniu i wykonywaniu obliczeń statystycznych, matematycznych i innych oraz wykonywanie tych czynności; przygotowywanie szczegółowych szacunków ilości i kosztów materiałów i pracy koniecznych do przeprowadzenia czynności związanych ze spisem i badaniami statystycznymi; wykonywanie zadań technicznych związanych z ustanawianiem, prowadzeniem i wykorzystywaniem rejestrów i dokonywanie doboru próby badawczej na potrzeby czynności związanych ze spisami i badaniami statystycznymi; wykonywanie zadań technicznych związanych ze zbieraniem danych i kontrolą jakości w ramach spisów i badań statystycznych; używanie standardowych pakietów oprogramowania komputerowego do wykonywania obliczeń matematycznych, statystycznych, księgowych i innych; przygotowywanie wyników statystycznych, matematycznych i księgowych na potrzeby ich prezentacji w formie graficznej lub tabelarycznej; wykorzystywanie wiedzy i doświadczeń praktycznych związanych z zasadami statystyki, matematyki, ekonomi oraz dziedzin pokrewnych do identyfikowania i rozwiązywania problemów powstałych w czasie pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',174);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3315','Rzeczoznawcy (z wyłączeniem majątkowych)','Rzeczoznawcy (z wyłączeniem majątkowych) oceniają wartość nieruchomości i różnych dóbr oraz oceniają straty pokrywane z polis ubezpieczeniowych.','Zadania wykonywane przez rzeczoznawców (z wyłączeniem majątkowych) zazwyczaj obejmują: określanie jakości lub wartości surowców, artykułów rolno-spożywczych, nieruchomości, samochodów i innych urządzeń technicznych, sprzętu przemysłowego, majątku ruchomego gospodarstwa domowego lub osobistego, dzieł sztuki, kamieni szlachetnych, biżuterii i innych cennych przedmiotów; ocenianie zakresu zniszczeń lub strat i zobowiązań firm ubezpieczeniowych i specjalistów do spraw ryzyka ubezpieczeniowego w związku ze stratami pokrywanymi z polis ubezpieczeniowych; uzyskiwanie informacji na temat sprzedaży i wartości podobnych przedmiotów i nieruchomości; badanie przedmiotów lub nieruchomości w celu oceny ich stanu, rozmiaru i konstrukcji; przygotowywanie raportów na temat wartości, które zawierają opis czynników i metod wykorzystanych w szacunkach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',175);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3321','Agenci ubezpieczeniowi','Agenci ubezpieczeniowi doradzają i sprzedają ubezpieczenia; zawierają umowy w imieniu i na rachunek ubezpieczyciela (towarzystwa ubezpieczeniowego) z nowymi oraz stałymi klientami w zakresie ubezpieczeń majątkowych i osobowych, w tym: ubezpieczenia na życie, od nieszczęśliwych wypadków, samochodowe, od odpowiedzialności cywilnej, na dożycie, emerytalne, od pożaru, morskie oraz innego typu.','Zadania wykonywane przez agentów ubezpieczeniowych zazwyczaj obejmują: uzyskiwanie informacji dotyczących klienta koniecznych do określenia odpowiedniego typu ubezpieczenia i jego warunków; negocjowanie z klientami typu ubezpieczenia w celu określenia rodzaju i stopnia ryzyka, jakiego ma dotyczyć ubezpieczenie; objaśnianie szczegółowych zagadnień związanych z ubezpieczeniem i jego warunkami, ryzykiem, którego dotyczy ubezpieczenie oraz świadczeniami dla klientów; pomaganie klientom w wyborze typu i zakresu ubezpieczenia, obliczanie składek i określanie metody płatności; negocjowanie i zwieranie umów reasekuracji; doradzanie i negocjowanie w sprawie warunków zawarcia umów ubezpieczeniowych; wykonywanie innych zadań przydzielonych przez ubezpieczyciela, a związanych z zawartymi przez niego umowami ubezpieczenia, zwłaszcza z zakresu ustalania zasadności roszczeń i przysługujących odszkodowań. Do ich zadań może także należeć nadzorowanie innych pracowników.',176);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3322','Przedstawiciele handlowi','Przedstawiciele handlowi reprezentują firmy w celu sprzedaży różnych dóbr i usług przedsiębiorstwom i innym organizacjom; dostarczają informacji o produktach, jeśli zajdzie taka potrzeba.','Zadania wykonywane przez przedstawicieli handlowych zazwyczaj obejmują: ubieganie się o zamówienia i sprzedaż towarów; zbieranie zamówień i sprzedaż dóbr i usług placówkom detalicznym, hurtowym, przemysłowym i innym; sprzedaż sprzętu, materiałów i związanych z nimi usług jednostkom biznesowym lub osobom prywatnym; uzyskiwanie i aktualizowanie informacji dotyczących warunków rynkowych oraz dóbr i usług swojego pracodawcy i konkurencji; dostarczanie potencjalnym klientom informacji dotyczących właściwości i funkcji sprzedawanych produktów i sprzętu oraz przedstawianie sposobu ich użytkowania; przedstawianie cen i warunków kredytowych, rejestrowanie zamówień i uzgadnianie warunków dostaw; przekazywanie odpowiedzi klientów i ich wymagań dostawcom oraz wytwórcom; monitorowanie poziomu zadowolenia klientów z zakupionych produktów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',177);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3323','Zaopatrzeniowcy','Zaopatrzeniowcy kupują dobra i usługi (półfabrykaty, wyposażenie techniczne, części, materiały i surowce) w celu ich wykorzystania lub ponownej sprzedaży w imieniu podmiotów i organizacji przemysłowych, handlowych, rządowych i innych poszukując dogodnych warunków i źródeł zakupów.','Zadania wykonywane przez zaopatrzeniowców zazwyczaj obejmują: określanie lub negocjowanie warunków umów, zawieranie kontraktów na dostawę lub zalecanie, przyznania kontraktów na zakup sprzętu, surowców, produktów, usług oraz nabywanie towarów w celu ich ponownej sprzedaży; uzyskiwanie informacji o potrzebach i zapasach oraz opracowywanie specyfikacji ilościowych i jakościowych dotyczących koniecznych zakupów, określanie kosztów, terminów dostawy i innych warunków umów; nabywanie ogólnego i specjalistycznego sprzętu, materiałów lub usług biznesowych do wykorzystania lub późniejszego przetworzenia w przedsiębiorstwie; ogłaszanie przetargów, konsultowanie się z dostawcami i dokonywanie przeglądu ofert; nabywanie towarów w celu ich ponownej sprzedaży przez placówki detaliczne lub hurtowe; zapoznawanie się z raportami rynkowymi, prasą handlową i materiałami promocyjnymi dotyczącymi handlu oraz odwiedzanie targów, salonów wystawienniczych, placówek handlowych i imprez poświęconych promocji towarów i produktów; wybieranie towarów lub produktów, które najlepiej odpowiadają potrzebom przedsiębiorstwa; kontaktowanie się z dostawcami i negocjowanie cen, rabatów, warunków kredytu oraz uzgodnień dotyczących transportu; nadzorowanie dystrybucji towarów pomiędzy punkty sprzedaży i utrzymywanie odpowiedniego poziomu zapasów; ustalanie harmonogramów dostaw, monitorowanie postępów i kontaktowanie się z klientami i dostawcami w celu rozwiązywania problemów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',178);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3324','Pośrednicy handlowi','Pośrednicy handlowi kupują i sprzedają towary i usługi transportowe, zazwyczaj w dużej ilości, na licytacjach lub na giełdach towarowych, w imieniu firmy, w której są zatrudnieni lub klientów indywidualnych na zasadzie prowizji','Zadania wykonywane przez pośredników handlowych zazwyczaj obejmują: kontaktowanie kupców ze sprzedawcami towarów; omawianie wymagań klientów dotyczących sprzedaży i kupna oraz udzielanie stosownych porad; kupowanie i sprzedawanie przestrzeni ładunkowej na statkach; negocjowanie warunków zakupu lub sprzedaży towarów i terminowych kontraktów handlowych towarowych; wyszukiwanie przestrzeni ładunkowej lub magazynowej na towary oraz negocjowanie opłat za transport, spedycję i ich przechowywanie; monitorowanie i analizowanie tendencji rynkowych oraz innych czynników mających wpływ na podaż i popyt towarów oraz usług transportowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',179);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3331','Spedytorzy i pokrewni','Spedytorzy i pokrewni przeprowadzają procedury związane z rozliczeniami celnymi i zapewniają prawidłowość licencji importowych i eksportowych oraz dopełnienie wszelkich formalności związanych z transportem towarów drogą morską, lotniczą, kolejową i samochodową.','Zadania wykonywane przez spedytorów i pokrewnych zazwyczaj obejmują: przeprowadzanie procedur związanych z rozliczeniami celnymi w przypadku importu i eksportu; załatwianie właściwych formalności i zezwoleń na eksport i import towarów; zapewnienie ważności ubezpieczenia, prawidłowości licencji importowych i eksportowych oraz dopełnianie wszelkich formalności związanych ze spedycją załadunku towarów; podpisywanie i wystawianie konosamentów; sprawdzanie dokumentacji eksportowej i importowej w celu stwierdzenia zawartości ładunku, klasyfikowanie przewożonego towaru do różnych kategorii pod względem stosowanych od nich opłat lub taryf przy wykorzystaniu systemu oznaczania i kodowania towarów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',180);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3332','Organizatorzy konferencji i imprez','Organizatorzy konferencji i imprez organizują i koordynują świadczenie usług organizacji kongresów, konferencji i seminariów, targów, imprez rozrywkowych i okolicznościowych, sportowych, bankietów, itp. Planują, programują, zlecają i nadzorują obsługę konferencji i imprez. Pozyskują zlecenia indywidualne i zbiorowe na sprzedaż biletów na imprezy i widowiska. Prezentują i promują imprezy zapewniając pełne wykorzystanie miejsc na widowni.','Zadania wykonywane przez organizatorów konferencji i imprez zazwyczaj obejmują: promowanie konferencji, imprez i targów; udzielanie odpowiedzi na pytania dotyczące świadczonych usług oraz kosztów wynajmu: sali, sprzętu, cateringu i usług pokrewnych; spotykanie się z klientami w celu omówienia ich potrzeb oraz określenia pakietów oferowanych; organizowanie i koordynowanie świadczonych usług związanych z centrami konferencyjnymi, cateringiem, oznakowaniem, planszami informacyjnymi, sprzętem audiowizualnym i komputerowym, zakwaterowaniem, transportem oraz wydarzeniami towarzyskimi dla uczestników; uzgadnianie logistyki wystąpień prelegentów; negocjowanie kosztów usług świadczonych w ramach ustalonego budżetu; nadzorowanie pracy podwykonawców; pozyskiwanie zleceń indywidualnych i zbiorowych na sprzedaż biletów na imprezy i widowiska, prezentowanie i promowanie imprez, zapewnianie pełnego wykorzystanie miejsc na widowni; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',181);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3333','Pośrednicy pracy i zatrudnienia','Pośrednicy pracy i zatrudnienia pozyskują miejsca pracy dla osób bezrobotnym lub poszukujących pracy; znajdują pracowników dla pracodawców i zawierają kontrakty pracy zgodnie z potrzebami przedsiębiorstw i innych instytucji na podstawie otrzymanych zleceń; poszukują pracowników dla pracodawców w przypadkach sezonowych wzrostów sprzedaży, postojów urlopowych lub przy dużej rotacji personelu.','Zadania wykonywane przez pośredników pracy i zatrudnienia zazwyczaj obejmują: wyszukiwanie miejsc pracy osobom jej poszukującym; wyszukiwanie pracowników mogących zająć wolne stanowiska pracy na zlecenie pracodawcy lub pracownika, uzgadnianie z pracodawcą wymaganych od kandydatów do pracy kwalifikacji zawodowych: formalnego poziomu wykształcenia, kompetencji, umiejętności i cech osobowości zgodnie z krajowymi lub międzynarodowymi uregulowaniami i wymogami; gwarantowanie zgodności kontraktów pracy z wymogami prawa i ich podpisywanie; doradzanie w kwestii programów kursów i szkoleń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',182);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3334','Agenci do spraw rynku nieruchomości','Agenci do spraw rynku nieruchomości organizują sprzedaż, zakup i wynajem nieruchomości zazwyczaj w imieniu klientów i na zasadzie prowizji. Zajmują się również pozyskiwaniem gruntów pod nieruchomości.','Zadania wykonywane przez agentów do spraw rynku nieruchomości zazwyczaj obejmują: uzyskiwanie informacji dotyczących nieruchomości przeznaczonych do sprzedaży lub pod wynajem, ich właścicieli oraz potrzeb potencjalnych nabywców lub najemców; prezentowanie potencjalnym nabywcom lub najemcom nieruchomości przeznaczonych do sprzedaży lub pod wynajem oraz wyjaśnianie warunków sprzedaży lub wynajmu; usprawnianie negocjacji między najemcami i właścicielami dotyczących czynszu i opłat za wynajem; wstępne przygotowywanie dokumentów do sporządzenia umów najmu i sprzedaży oraz szacowanie kosztów; organizowanie podpisania umów najmu i przekazania praw do nieruchomości; pobieranie czynszu i kaucji w imieniu właściciela i wizytowanie nieruchomości przed, podczas i po okresie wynajmu; pozyskiwanie gruntów pod nieruchomości w celu wybudowania obiektów mieszkalnych lub użyteczności publicznej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników oraz współpraca z biurami notarialnymi.',183);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3339','Pośrednicy usług biznesowych gdzie indziej niesklasyfikowani','Do tej grupy elementarnej należą pośrednicy usług biznesowych nie zaklasyfikowani do grupy średniej 333 Pośrednicy usług biznesowych. Grupa ta obejmuje na przykład osoby zawierające kontakty biznesowe oraz zajmujące się usługami artystycznymi, handlowymi, marketingowymi, licytacjami, promocją marki handlowej, sprzedażą reklam internetowych, organizacją reklamy.','Zadania wykonywane przez pracowników tej grupy zazwyczaj obejmują: pozyskiwanie informacji o sprzedawanych usługach oraz potrzebach potencjalnych klientów; negocjowanie umów w imieniu sprzedających lub kupujących oraz objaśnianie klientom warunków sprzedaży i płatności; podpisywanie umów w imieniu sprzedających lub kupujących oraz zapewnienie honorowania umów; dbałość o udostępnienie kupującemu zakupionej usługi biznesowej w uzgodnionej formie i w uzgodnionym czasie; prowadzenie sprzedaży aukcyjnej różnych rodzajów nieruchomości, samochodów, towarów, zwierząt, dzieł sztuki, biżuterii i innych przedmiotów; kontrolowanie jakości oferowanych wyrobów i usług oraz obsługi klientów przez firmy handlowe, gastronomiczne i inne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',184);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3341','Kierownicy biura','Kierownicy biura nadzorują i koordynują i kontrolują działania średniego personelu do spraw biznesu i administracji.','Zadania wykonywane przez kierowników biura zazwyczaj obejmują: koordynowanie, zlecanie i kontrolowanie pracy pracowników zajmujących się edycją tekstów, prowadzeniem rejestrów i archiwizowaniem dokumentów, obsługą telefonu i centrali telefonicznej, wprowadzaniem danych, przygotowywaniem dokumentów w postaci elektronicznej oraz innymi czynnościami wymagającymi posiadania umiejętności sprawnego posługiwania się urządzeniami technicznymi; przygotowywanie harmonogramów zadań i procedur organizacyjnych pracy biura oraz koordynowanie jej z innymi jednostkami, wydziałami lub departamentami organizacji; przygotowywanie i przedstawianie raportów o realizacji zadań oraz sprawozdań z wykonania planu; szkolenie i instruowanie pracowników w zakresie zadań pracowniczych, procedur bezpieczeństwa i polityki firmy lub zlecanie przeprowadzenia takich szkoleń; ocenianie pracowników pod względem wyników pracy i zgodności sposobu jej wykonywania z obowiązującymi przepisami oraz zalecanie odpowiednich działań personalnych; udzielanie pomocy w rekrutacji kandydatów do pracy, przeprowadzanie wstępnych rozmów z kandydatami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',185);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3342','Sekretarze prawni','Sekretarze prawni wykonują czynności administracyjne związane z biegiem spraw sądowych. Wykorzystują wiedzę specjalistyczną z zakresu terminologii i procedur prawnych do pomagania prawnikom w wykonywaniu czynności związanych z obowiązującymi procedurami oraz z wewnętrzną koordynacją na szczeblu kierowniczym w kancelariach prawnych, działach prawnych dużych firm i organów państwa.','Zadania wykonywane przez sekretarzy prawnych zazwyczaj obejmują: prowadzenie repertoriów (ksiąg) sądowych przez dokonywanie w nich wpisów obrazujących bieg sprawy; przygotowywanie dokumentów prawnych takich jak: akty, testamenty, pisma procesowe i ich streszczenia, itp.; dokonywanie korekty dokumentów i korespondencji w celu zapewnienia zgodności z obowiązującymi wymogami procedur prawnych; wysyłanie do klientów, świadków i urzędników sądowych listów, faksów, itp., stanowiących korespondencję prawną lub zlecanie jej dostarczenia; segregowanie i przechowywanie dokumentów, akt spraw, wydawnictw prawnych, itp.; wyznaczanie terminów spotkań klientów i ich organizowanie; protokołowanie na rozprawach i posiedzeniach sądowych; pomaganie w przygotowaniu budżetów, monitorowanie wydatków, tworzenie projektów umów i zleceń zakupu lub nabycia; kontrolowanie przygotowania sali na posiedzenie sądu; sporządzanie i wydawanie odpisów orzeczeń i innych dokumentów sądowych; prowadzenie terminarzy oraz księgi należności sądowych, spraw związanych z obrotem zagranicznym itp.; pobieranie i kasowanie znaczków opłaty sądowej za określone czynności sądowe i kancelaryjne; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',186);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3343','Pracownicy administracyjni i sekretarze biura zarządu','Pracownicy administracyjni i sekretarze biura zarządu gromadzą informację i dokumentację biura oraz organizują przepływ informacji pomiędzy różnymi jednostkami instytucji lub organizacji; przygotowują korespondencję, raporty i protokoły ze spotkań oraz inne specjalistyczne dokumenty. Prowadzą obsługę kancelaryjno-biurową zarządu, spółki, partii, urzędu, itp. na podstawie obowiązujących przepisów administracyjnych i prawnych.','Zadania wykonywane przez pracowników administracyjnych i sekretarzy biura zarządu zazwyczaj obejmują: wykonywanie czynności administracyjnych i dokumentacyjnych; tworzenie korespondencji administracyjnej, protokołów, itp.; ustalanie, proponowanie i przestrzeganie terminów podjęcia działań następczych; ocenianie próśb o spotkanie, wyznaczanie terminów oraz organizowanie spotkań i podróży służbowych; pomaganie w przygotowaniu budżetów, monitorowanie wydatków, tworzenie projektów umów i zleceń zakupu lub nabycia; kontaktowanie się z innymi pracownikami w sprawach dotyczących działań organizacji; pisanie listów biznesowych, technicznych i innych oraz odpowiadanie na korespondencję; przygotowywanie protokołów z posiedzeń zarządu, zgromadzeń ustawodawczych, sądów i innych organizacji przy wykorzystaniu stenograficznych lub innych specjalistycznych urządzeń biurowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',187);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3344','Sekretarze medyczni i pokrewni','Sekretarze medyczni i pokrewni zajmują się gromadzeniem, segregowaniem i udostępnianiem informacji o stanie pacjenta i środowiska wykorzystując specjalistyczną wiedzę z zakresu terminologii medycznej i procedur świadczenia usług opieki zdrowotnej; przygotowują dane o pacjentach do komputerowych baz danych; zbierają, porządkują i udostępniają materiały dotyczących chorób i ich zapobieganiu; przygotowują wnioski ubezpieczeniowe.','Zadania wykonywane przez sekretarzy medycznych i pokrewnych zazwyczaj obejmują: wyznaczanie terminów i potwierdzanie wizyt pacjentów; sporządzanie, ewidencjonowanie i sprawdzanie kart pacjentów, raportów, dokumentów i korespondencji; przeprowadzanie wywiadów z pacjentami w celu wypełnienia formularzy, dokumentów i historii chorób; wypełnianie wniosków ubezpieczeniowych i innych dokumentów; prowadzenie dokumentacji medycznej oraz biblioteki specjalistycznej; przygotowywanie sprawozdań finansowych i dokumentacji związanej z przygotowywaniem rachunków za świadczone usługi medyczne; pomaganie w przygotowaniu budżetów, sporządzaniu projektów umów zleceń zakupu lub nabycia potrzebnych materiałów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',188);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3351','Funkcjonariusze celni i ochrony granic','Funkcjonariusze celni i ochrony granic kontrolują osoby i pojazdy przekraczające granice państwowe, egzekwując stosowanie ustalonych przez państwo przepisów i zarządzeń.','Zadania wykonywane przez funkcjonariuszy celnych i ochrony granic zazwyczaj obejmują: patrolowanie granic państwa i wód przybrzeżnych w celu zatrzymywania osób, które nielegalnie przyjeżdżają lub opuszczają kraj, nielegalnie przywożą lub wywożą walutę lub towary; kontrolowanie dokumentów przewozowych osób przekraczających granice państwa w celu upewnienia się, czy osoby te posiadają wymagane zezwolenia, upoważnienia; kontrolowanie bagaży osób przekraczających granice państwa w celu upewnienia się, czy ich zawartość jest zgodna z obowiązującymi przepisami i zarządzeniami dotyczącymi przewozu lub wywozu towarów i walut; kontrolowanie dokumentów transportowych i przewozowych pojazdów przekraczających granice państwa w celu upewnienia się, czy są zgodne z zasadami i przepisami krajowymi dotyczącymi tranzytu, importu i eksportu dóbr oraz sprawdzenia, czy zostały dokonane wymagane opłaty; zatrzymywanie osób, które naruszyły przepisy imigracyjne i rekwirowanie zakazanych i nie zadeklarowanych dóbr, które uznano za niezgodne z przepisami celnymi; koordynowanie działań z innymi agencjami zaangażowanymi w egzekwowanie prawa, deportacje i ściganie oraz współpraca z nimi; wykonywanie czynności administracyjnych dotyczących rejestrowania decyzji, transakcji, naruszenia prawa i dochodzeń; w koniecznych przypadkach składanie przed sądem zeznań dotyczących okoliczności i wyników przeprowadzonych dochodzeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',189);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3352','Urzędnicy do spraw podatków','Urzędnicy do spraw podatków badają deklaracje podatkowe, rachunki, akty kupna-sprzedaży i inne dokumenty w celu określenia rodzaju podatku i kwoty, cła i innych opłat należnych od osób prywatnych oraz przedsiębiorstw; konsultują się w wyjątkowych lub ważnych przypadkach z przełożonymi wyższej instancji.','Zadania wykonywane przez urzędników do spraw podatków zazwyczaj obejmują: doradzanie i pomoc organizacjom, przedsiębiorstwom i obywatelom w sprawach prawidłowej interpretacji przepisów prawnych, zasad i zarządzeń dotyczących płatności podatków, ceł i innych państwowych opłat; badanie i analizowanie deklaracji podatkowych, rachunków sprzedaży i innych dokumentów w celu określenia rodzaju i wysokości należnych podatków, ceł i innych płatności; badanie kompleksowych zeznań podatkowych oraz dokumentacji organizacji: rejestrów, systemów i sprawozdań z wewnętrznych kontroli księgowych w celu sprawdzenia zgodności deklaracji podatkowych z przepisami i zasadami dotyczących płatności podatków; wykonywanie czynności administracyjnych związanych z udokumentowaniem decyzji, prowadzeniem rejestrów i sporządzaniem sprawozdań z działań podejmowanych w poszczególnych przypadkach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',190);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3353','Urzędnicy do spraw świadczeń społecznych','Urzędnicy do spraw świadczeń społecznych przyjmują i analizują wnioski i podania o prawo do uzyskania świadczeń społecznych takich jak: zasiłki, renty i emerytury; ustalają, czy ubiegający się o świadczenia społeczne spełnia wymagane warunki oraz określają kwoty świadczeń konsultując się w spornych lub ważnych przypadkach z przełożonymi wyższych instancji.','Zadania wykonywane przez urzędników do spraw świadczeń społecznych zazwyczaj obejmują: doradzanie indywidualnym osobom lub organizacjom w prawidłowej interpretacji przepisów prawnych dotyczących przyznawania rent, emerytur i państwowych zasiłków; badanie podań i wniosków oraz załączonych dokumentów w celu określenia rodzaju i kwoty przysługującego świadczenia; dokonywanie oceny dokumentacji i przeprowadzanie wywiadów z beneficjentami świadczeń w celu sprawdzenia, czy kwalifikują się oni do dalszego otrzymywania świadczeń lub dostępu do usług; wykonywanie czynności administracyjnych związanych z prowadzeniem dokumentacji klientów oraz przygotowywanie sprawozdań dotyczących zasadności przyznania świadczeń; podejmowanie decyzji dotyczących kierowania osób potrzebujących pomocy do odpowiednich służb, zakończenia przyznawania świadczeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',191);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3354','Urzędnicy organów udzielających licencji','Urzędnicy organów udzielających licencji badają wnioski o udzielenie licencji na eksport lub import dóbr, założenie przedsiębiorstwa, budowę domu lub innej budowli, wydanie paszportu; określają zasadność wydawania licencji czy paszportu, wskazują warunki lub ograniczenia, jakie powinny dotyczyć wydawania poszczególnych licencji i pozwoleń oraz przekazują wyjątkowe lub szczególnie ważne przypadki swoim przełożonym wyższej instancji.','Zadania wykonywane przez urzędników organów udzielających licencji zazwyczaj obejmują: doradzanie zainteresowanym osobom w prawidłowej interpretacji przepisów prawnych i zarządzeń dotyczących rodzaju wymaganych licencji oraz warunków, z jakimi związane jest wydanie licencji oraz praw i obowiązków obywateli w tym zakresie; badanie wniosków, załączonych dokumentów oraz określanie, czy licencja może być wydana ; badanie wniosków i wydawanie decyzji o wydaniu paszportu; badanie wniosków i wydawanie pozwoleń na budowę domu lub innych budowli; badanie wniosków i wydawanie pozwoleń na prowadzenie działalności gospodarczej; wykonywanie powiązanych zadań czynności administracyjnych związanych z przetwarzaniem wniosków, obsługą dokumentacji, dokonywaniem ocen, podejmowaniem decyzji oraz przygotowywaniem korespondencji; wykonywanie innych zadań pokrewnych. Do ich zadań urzędników udzielających licencji może także należeć nadzorowanie innych pracowników.',192);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3355','Policjanci','Policjanci prowadzą działania mające na celu ochronę życia, zdrowia i mienia obywateli; badają fakty i okoliczności związane z popełnionymi przestępstwami w celu identyfikowania podejrzanych i uzyskiwania informacji początkowo niedostępnych lub nieoczywistych dotyczących przestępstw lub osób i ich działań, najczęściej w ramach przeciwdziałania przestępczości.; rozwiązują sytuacje kryzysowe. Wykrywają przestępstwa kryminalne i zapobiegają im; poszukują osób zaginionych i ścigają osoby poszukiwane; zwalczają terroryzm, unieszkodliwiają osoby i grupy terrorystyczne.','Zadania wykonywane przez policjantów obejmują: pozyskiwanie kontaktów i źródeł informacji o planowanych lub popełnionych przestępstwach w celu zapobiegania przestępczości lub identyfikowania podejrzanych; zdobywanie i sprawdzanie dowodów poprzez badanie miejsc zbrodni i wypadków w celu znalezienia dowodów poszlakowych i rzeczowych; przesłuchiwanie świadków i podejrzanych oraz analizowanie dokumentów i plików komputerowych; analizowanie dowodów w celu rozwiązywania spraw kryminalnych, wykrywania działalności przestępczej i gromadzenia informacji na potrzeby procesów sądowych; pozyskiwanie kontaktów i źródeł informacji początkowo niedostępnych lub nieoczywistych dotyczących przedsiębiorstw lub osób poszukiwanych i ich działań, w ramach przeciwdziałania przestępczości; dokonywanie aresztowań; składanie zeznań w sądach oraz składanie raportów przełożonym na temat okoliczności i wyników dochodzeń; rozwiązywanie sytuacji kryzysowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',193);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3356','Funkcjonariusze służby więziennej','Funkcjonariusze Służby Więziennej nadzorują w zakładach karnych osoby odbywające karę pozbawienia wolności a w aresztach śledczych osoby tymczasowo aresztowane oraz sprawują nad nimi nadzór i opiekę wychowawczą.','Zadania funkcjonariuszy służby więziennej zazwyczaj obejmują: przyjmowanie tymczasowo aresztowanych i skazanych do grupy więziennej; składanie ich mienia do magazynu depozytów oraz eskortowanie ich do miejsc pobytu; legitymowanie osób przybywających do więzień i aresztów śledczych; zapewnianie bezpieczeństwa osobom osadzonym w zakładach karnych i aresztach śledczych oraz innym osobom tam przebywającym; przeprowadzanie osadzonych w obrębie zakładu karnego lub aresztu i pełnienie nad nimi nadzoru podczas pracy, widzeń, posiłków, spaceru itp.; dokonywanie okresowych inspekcji cel i sprawdzanie zabezpieczeń zamków, okien, drzwi i bram; nadzór nad korespondencja osadzonych; planowanie działań wychowawczych w stosunku do osób skazanych i osadzonych tymczasowo w areszcie, organizowanie i nadzorowanie więźniów podczas: pracy, nauki, rekreacji, sportu i wypoczynku; wdrażanie skazanych do przestrzegania dyscypliny i porządku; kształtowanie środowiska więziennego, doradzanie i udzielanie pomocy w trudnych sytuacjach i problemach wynikających z codziennego życia więziennego; zapobieganie aktom samoagresji i demoralizacji; obserwowanie postępowania i zachowania więźniów, aby zapobiec zakłóceniom porządku i ucieczkom; patrolowanie terenu więzienia; udzielanie pomocy we wdrażaniu programów rehabilitacyjnych; dokonywanie okresowej oceny osób skazanych i prowadzenie odpowiedniej dokumentacji; opiniowanie i wnioskowanie w sprawie nagradzania i karania skazanych; eskortowanie więźniów podczas transportu i na okres przepustek; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',194);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3357','Funkcjonariusze służb specjalnych','Funkcjonariusze służb specjalnych rozpoznają, zapobiegają i zwalczają zagrożenia godzące w bezpieczeństwo państwa, jego obronność i niepodległość oraz przestępstwa godzące w podstawy ekonomiczne państwa.','Zadania wykonywane przez funkcjonariuszy służb specjalnych zazwyczaj obejmują: wykonywanie czynności operacyjno-rozpoznawczych i dochodzeniowo-śledczych w celu rozpoznawania, zapobiegania i wykrywania przestępstw szpiegostwa, terroryzmu, naruszenia tajemnicy państwowej i innych przestępstw godzących w bezpieczeństwo państwa oraz ścigania ich sprawców; wykonywanie czynności operacyjno-rozpoznawczych i dochodzeniowo-śledczych w celu rozpoznawania, zapobiegania, wykrywania i ścigania sprawców przestępstw dotyczących nielegalnego wytwarzania, posiadania i obrotu bronią, amunicją i materiałami wybuchowymi, bronią masowej zagłady oraz środkami odurzającymi i substancjami psychotropowymi w obrocie międzynarodowym; wykonywanie czynności operacyjno-rozpoznawczych i analityczno-informacyjnych w celu uzyskiwania i przetwarzania informacji istotnych dla ochrony bezpieczeństwa państwa i jego porządku konstytucyjnego; wykonywanie czynności operacyjno-rozpoznawczych i analityczno-informacyjnych w celu uzyskiwania i przetwarzania informacji istotnych dla zwalczania korupcji w instytucjach państwowych i samorządzie terytorialnym oraz działalności godzącej w interesy ekonomiczne państwa; wykonywanie, na polecenie sądu lub prokuratora, czynności w zakresie określonym w Kodeksie postępowania karnego oraz Kodeksie karnym wykonawczym; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',195);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3359','Urzędnicy państwowi do spraw nadzoru gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje urzędników państwowych do spraw nadzoru niesklasyfikowanych w innych podgrupach grupy 3 Technicy i inny średni personel. Grupa ta obejmuje na przykład inspektorów do spraw nadzoru nad przestrzeganiem prawa budowlanego, przepisów ochrony przeciwpożarowej, przepisów w zakresie transportu drogowego, wymogów sanitarnych w produkcji i handlu, przepisów obowiązujących w obrocie handlowym i przy wykonywaniu usług, a także inspektorów do spraw obrony cywilnej.','indziej niesklasyfikowanych zazwyczaj obejmują: nadzorowanie przestrzegania prawa budowlanego w zakresie zgodności projektów budowlanych, obiektów inżynierskich i dróg z planami zagospodarowania przestrzennego, wymaganiami ochrony środowiska, spełniania warunków bezpieczeństwa ludzi i mienia w projektach budowlanych oraz zgodności rozwiązań architektoniczno-budowlanych z przepisami; prowadzenie inspekcji zakładów przemysłowych, hoteli, kin i innych budynków lub konstrukcji w celu wykrycia ewentualnych zagrożeń pożarowych i udzielenia porad dotyczących sposobów ich usunięcia, w tym uczestniczenie w odbiorach technicznych nowych obiektów pod kątem ochrony przeciwpożarowej; dokonywanie kontroli przestrzegania przepisów w zakresie transportu drogowego i niezarobkowego krajowego i międzynarodowego przewozu samochodowego; przeprowadzanie inspekcji w miejscach produkcji, przetwarzania, transportowania, obsługi, przechowywania i sprzedaży produktów celem sprawdzenia stopnia zgodności z normami, przepisami i standardami; prowadzenie kontroli na rzecz prawidłowego działania podmiotów gospodarczych i ochrony interesu indywidualnych nabywców towarów i usług przez ujawnianie naruszenia obowiązujących przepisów i eliminowanie z obrotu towarowego oraz z działalności usługowej niewłaściwej jakości towarów i usług; badanie miejsc prowadzenia działalności gospodarczej celem zapewnienia stosowania prawidłowych miar i wag w handlu; przygotowywanie protokółów pokontrolnych; planowanie i organizowanie szkoleń ludności i załóg zakładów pracy w zakresie ratownictwa medycznego, działań przeciwpożarowych oraz postępowania w przypadku klęsk żywiołowych, katastrof i napadu powietrznego; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',196);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3411','Średni personel z dziedziny prawa i pokrewny','Średni personel z dziedziny prawa i pokrewny pełni funkcje pomocnicze w kancelariach prawnych, świadczy usługi związane z zagadnieniami prawnymi lub prowadzi dochodzenia na zlecenie klientów.','Zadania wykonywane przez średni personel z dziedziny prawa i pokrewnych pracowników zazwyczaj obejmują: pomoc w przygotowywaniu dokumentów prawnych, w tym: apelacji, testamentów, umów oraz pism zawierających opisy sytuacji prawnych, określających warunki udzielania kredytów lub zapewniania ubezpieczenia; pomoc w przygotowywaniu dokumentów dotyczących przekazania nieruchomości lub akcji oraz innych działań wymagających urzędowej rejestracji; prowadzenie repertoriów spraw prowadzonych w kancelariach prawnych i notarialnych oraz prowadzenie korespondencji; sporządzanie i wydawanie wypisów aktów notarialnych; udzielanie interesantom podstawowych informacji dotyczących trybu czynności notarialnych, orientacyjnych kosztów, niezbędnych dokumentów potrzebnych do dokonania czynności notarialnych; badanie przypadków kradzieży mienia, pieniędzy lub informacji z placówek biznesowych oraz innych przypadków możliwego popełnienia przez klientów lub pracowników czynów niezgodnych z prawem; badanie przedsiębiorstw lub osób i ich działań w imieniu klientów; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',197);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3412','Pracownicy wsparcia rodziny, pomocy społecznej i pracy socjalnej','Pracownicy wsparcia rodziny, pomocy społecznej i pracy socjalnej tworzą i wdrażają programy pomocy społecznej i prac społecznych oraz pomagają klientom w postępowaniu w przypadku problemów osobistych i społecznych.','Zadania wykonywane przez pracowników wsparcia rodziny, pomocy społecznej i pracy socjalnej zazwyczaj obejmują: gromadzenie informacji ważnych z punktu widzenia potrzeb klientów oraz ocenianie istotnych umiejętności oraz mocnych i słabych stron klientów; pomaganie osobom niepełnosprawnym lub starszym w uzyskiwaniu dostępu do usług i w zwiększaniu ich możliwości funkcjonowania w społeczeństwie; pomaganie klientom w ustalaniu możliwości i opracowywaniu planów działania przy jednoczesnym udzielaniu niezbędnego wsparcia i pomocy; wspieranie klientów w dostępie do usług placówek świadczących pomoc medyczną, prawną, finansową, opiekuńczą, wychowawczą oraz świadczenie usług doradczych w codziennych sprawach życiowych, takich jak: możliwość uzyskania mieszkania, pracy, opieki domowej, wymagających skierowania do odpowiednich instytucji pomocy społecznej; doradzanie klientom przebywającym w placówkach resocjalizacyjnych, monitorowanie ich działań oraz pomaganie w planowaniu zwolnienia i wcześniejszego zwolnienia; uczestniczenie w selekcji i włączaniu klientów do odpowiednich programów; świadczenie usług w zakresie interwencji kryzysowych i udzielania schronienia w nagłych wypadkach; prowadzenie warsztatów w zakresie umiejętności życiowych, programów leczenia uzależnień, programów kształtowania zachowań, programów adresowanych do młodzieży, itp., pod nadzorem specjalistów w dziedzinie pracy socjalnej i ochrony zdrowia; pomaganie w dokonywaniu oceny skuteczności interwencji i programów poprzez monitorowanie postępów klientów i sporządzanie odpowiednich raportów; świadczenie usług opiekuńczych w odniesieniu do dzieci, osób starszych, niedołężnych i niepełnosprawnych; współpracę z innymi agencjami pomocy społecznej, szkołami i usługodawcami z dziedziny ochrony zdrowia, mającymi kontakt z klientami w celu przekazywania informacji i otrzymywania opinii zwrotnych dotyczących ogólnej sytuacji życiowej i postępów klientów; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',198);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3421','Sportowcy i dżokeje','Sportowcy i dżokeje trenują i biorą udział w zawodach sportowych, indywidualnie lub jako członkowie drużyn w wybranej przez siebie dyscyplinie sportu.','Zadania wykonywane przez sportowców i dżokejów zazwyczaj obejmują: uczestniczenie w imprezach sportowych o charakterze zawodów; uczestniczenie w regularnych sesjach treningowych oraz trenowanie we własnym zakresie w celu utrzymania odpowiedniego poziomu sprawności fizycznej i umiejętności; podejmowanie działań promujących sport oraz uczestniczenie w wywiadach dla mediów; utrzymywanie szerokiej wiedzy fachowej na temat danej dyscypliny sportu; podejmowanie decyzji o przyjęciu określonej strategii w porozumieniu z trenerami; ocenę możliwości innych zawodników i warunków w obiektach sportowych; uczestniczenie w wydarzeniach sportowych; przestrzeganie zasad i przepisów dotyczących danej dyscypliny sportu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',199);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3422','Trenerzy, instruktorzy i działacze sportowi','Trenerzy, instruktorzy i działacze sportowi pracują z zawodowymi sportowcami i amatorami w celu poprawy ich wyników, zachęcają do większego udziału w sporcie oraz organizują i prowadzą imprezy sportowe zgodnie z przyjętymi zasadami.','Zadania wykonywane przez trenerów, instruktorów i działaczy sportowych zazwyczaj obejmują: identyfikowanie mocnych i słabych stron sportowców lub drużyn; planowanie, rozwój i realizację sesji treningowych; opracowanie, planowanie i koordynację terminarzy i programów zawodów; motywowanie i przygotowywanie sportowców lub drużyn do udziału w zawodach lub meczach; opracowywanie strategii na potrzeby udziału w zawodach, przygotowywanie planów gry oraz kierowanie sportowcami lub drużynami w czasie trwania meczów i innych imprez sportowych; analizowanie i ocenę osiągnięć sportowców i drużyn oraz modyfikowanie programów treningowych; monitorowanie i analizowanie techniki i wyników sportowych oraz ustalanie sposobów osiągnięcia postępów w przyszłości; prowadzenie imprez sportowych i zawodów sportowych z zachowaniem standardów gry oraz zapewnianie zgodności z zasadami gry i przepisami bezpieczeństwa; rejestrowanie czasu i odnotowywanie wyników spotkań lub zawodów; ocenę osiągnięć zawodników, przyznawanie punktów, nakładanie kar za naruszenia oraz ustalanie wyników; odnotowywanie wyników i innych danych dotyczących wydarzeń sportowych: wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',200);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3423','Instruktorzy fitness i rekreacji ruchowej','Instruktorzy fitness i rekreacji ruchowej zajmują się prowadzeniem zajęć indywidualnych i grupowych w pomieszczeniach zamkniętych i w plenerze, mających na celu aktywizację i rekreację ruchową klientów oraz odnowę biologiczną.','Zadania wykonywane przez instruktorów fitness i rekreacji ruchowej zazwyczaj obejmują: planowanie i prowadzenie zajęć rekreacyjnych lub zajęć fitness; monitorowanie zajęć rekreacyjnych, sportowych lub zajęć fitness w celu zapewnienia bezpieczeństwa oraz w razie zaistnienia takiej potrzeby, udzielenia pierwszej pomocy w nagłych wypadkach; ocenę i kontrolę zdolności i sprawności klientów i zalecanie odpowiednich zajęć; demonstrowanie i nauczanie ruchów ciała, pojęć i umiejętności stosowanych w ćwiczeniach fitness i zajęciach rekreacyjnych; prowadzenie różnego rodzaju gier ruchowych, w tym organizowanie turniejów sportowych; prowadzenie lub organizowanie (przy udziale instruktorów sportu) nauki narciarstwa, saneczkarstwa, tenisa stołowego i ziemnego, jeździectwa, sztuk walki i innych form sportowych atrakcyjnych dla uczestników zajęć; wykonywanie masaży i prowadzenie ćwiczeń relaksacyjnych w celu odnowy biologicznej klientów; udzielanie instrukcji w zakresie korzystania ze sprzętu; wyjaśnianie i egzekwowanie przestrzegania procedur bezpieczeństwa, zasad i przepisów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',201);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3431','Fotografowie','Fotografowie obsługują aparaty fotograficzne w celu fotografowania ludzi, wydarzeń, miejsc, materiałów, produktów i innych tematów.','Zadania wykonywane przez fotografów zazwyczaj obejmują: robienie zdjęć dla celów reklamowych lub innych celów komercyjnych, przemysłowych lub naukowych oraz w celu zilustrowania opowieści i artykułów w gazetach, czasopismach i innych publikacjach; fotografowanie osób i grup osób; zapoznanie się z wymogami danego zadania i podejmowanie decyzji w zakresie doboru aparatu, kliszy, oświetlenia i scenografii tła; ustalanie kompozycji zdjęć, wprowadzanie odpowiednich poprawek technicznych w zakresie sprzętu i fotografowanego obiektu; obsługę skanerów w celu przenoszenia fotografii do pamięci komputera; stosowanie komputera do obróbki zdjęć; przystosowywanie istniejących fotografii w celu stworzenia nowych obrazów cyfrowych do wykorzystania w produktach multimedialnych; wykorzystanie aerografu, komputera lub innych technik w celu osiągnięcia pożądanego efektu wizualnego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',202);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3432','Plastycy, dekoratorzy wnętrz i pokrewni','Plastycy, dekoratorzy wnętrz i pokrewni projektują dekoracje wnętrz i urządzanie budynków handlowych, przemysłowych, publicznych, usługowych i mieszkalnych w celu stworzenia otoczenia dopasowanego do przeznaczenia danej przestrzeni, biorąc pod uwagę względy estetyczne i czynniki, które poprawiają warunki mieszkalne i warunki pracy lub wpływają na zwiększenie sprzedaży. Zajmują się także oprawą plastyczną widowisk, wystaw, uroczystości i imprez, pracami konserwatorskimi przedmiotów o walorach artystycznych oraz wytwarzają wyroby dekoracyjne z materiałów sztukatorskich, kamieniarskich i podobnych.','Zadania wykonywane przez plastyków, dekoratorów wnętrz i pokrewnych pracowników zazwyczaj obejmują: konsultacje z klientami i ustalenie celu i zakresu prac; uzyskiwanie i analizowanie informacji dotyczących wymogów w zakresie organizacji przestrzennej, funkcjonalności, wydajności, bezpieczeństwa i wymogów estetycznych; formułowanie koncepcji wystroju wnętrza budynków oraz przygotowanie szkiców, schematów, ilustracji i planów w celu przekazania zamysłu projektu; negocjowanie stosowanych rozwiązań w ramach danego projektu z klientami, kierownictwem, dostawcami materiałów i pracownikami budowlanymi; dobór, określanie i zalecanie wyboru materiałów o walorach funkcjonalnych i estetycznych, mebli i produktów do wystroju wnętrz; określanie szczegółów i dokumentowanie projektu wybranego do realizacji; koordynowanie prac budowlanych i realizacji wystroju wnętrz; projektowanie i dekorowanie witryn wystawowych i innych przestrzeni wystawowych w celu promocji produktów i usług; projektowanie i wykonywanie elementów dekoracji teatralnych i rekwizytów; projektowanie i wykonywanie dekoracji wnętrz przy zastosowaniu, świeżych roślin ozdobnych oraz roślin suchych i sztucznych; wykonywanie prac konserwatorskich przedmiotów o walorach artystycznych; projektowanie oraz wykonywanie do celów dekoracyjnych obiektów małej architektury z materiałów kamieniarskich i sztukatorskich oraz rekonstrukcję i odtwarzanie elementów sztukatorskich i kamieniarskich o wartości historycznej i artystycznej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie i koordynowanie pracy innych pracowników.',203);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3433','Pracownicy bibliotek, galerii, muzeów, informacji naukowej i pokrewni','Pracownicy bibliotek, galerii, muzeów, informacji naukowej i pokrewni wspierają specjalistów w zakresie bibliotekarstwa i informacji naukowej w gromadzeniu, opracowywaniu i udostępnianiu zbiorów bibliotecznych i informacji naukowej, przygotowują dzieła sztuki, okazy i artefakty w celu włączenia ich do kolekcji, aranżują i tworzą ekspozycje w galeriach.','Zadania wykonywane przez pracowników bibliotek, galerii, muzeów, informacji naukowej i pokrewnych zazwyczaj obejmują: zamawianie nowych materiałów bibliotecznych, prowadzenie rejestru bibliotecznego i obsługę systemów obiegu materiałów; katalogowanie materiałów drukowanych i zarejestrowanych; wyszukiwanie i weryfikację danych bibliograficznych; pozyskiwanie i gromadzenie informacji naukowej, technicznej i ekonomicznej oraz opracowywanie zbiorów; wprowadzanie danych do baz danych i edycję danych komputerowych; obsługę sprzętu audiowizualnego i reprograficznego; udostępnianie zbiorów bibliotecznych oraz udostępnianie i rozpowszechnianie informacji; ustawianie i przygotowanie eksponatów do wystawienia; projektowanie i aranżowanie wystawy eksponatów, gablot wystawowych i przestrzeni wystawowych; pomoc w ustawianiu oświetlenia i sprzętu wystawowego; odbiór, wysyłanie, pakowanie i rozpakowywanie eksponatów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',204);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3434','Szefowie kuchni i organizatorzy usług gastronomicznych','Szefowie kuchni i organizatorzy usług gastronomicznych projektują menu, tworzą potrawy, planują, organizują i nadzorują przygotowanie posiłków i obsługę konsumentów w restauracjach i innych miejscach żywienia zbiorowego lub w ramach świadczenia usług cateringowych.','Zadania wykonywane przez szefów kuchni i organizatorów usług gastronomicznych zazwyczaj obejmują: planowanie i opracowanie przepisów i menu, szacowanie kosztów żywności i kosztów pracy, a także zamawianie dostaw żywności; monitorowanie jakości potraw na wszystkich etapach przygotowania i prezentacji; omawianie kwestii dotyczących przygotowania posiłków z kierownictwem, dietetykami, personelem pracującym w kuchni i obsługującym klientów; nadzorowanie i koordynowanie pracy kucharzy i innych pracowników biorących udział w przygotowaniu potraw; kontrolowanie zapasów, sprzętu i stanowisk pracy w celu zapewnienia zgodności z ustalonymi normami; ustalanie sposobu prezentacji potraw i tworzenie dekoracyjnych ekspozycji żywności; instruowanie kucharzy i innych pracowników w zakresie przygotowania, gotowania, garnirowania i prezentacji potraw; udział w rekrutacji pracowników kuchni i obsługi konsumentów oraz nadzorowanie ich pracy; przygotowanie, doprawianie i gotowanie dań specjalnych i skomplikowanych potraw; organizowanie i nadzorowanie realizacji usług cateringowych; wyjaśnianie i egzekwowanie przestrzegania przepisów dotyczących higieny i bezpieczeństwa żywności; prowadzenie dokumentacji dotyczącej rozliczeń produkcji gastronomicznej; wykonywanie innych zadań pokrewnych.',205);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3435','Aktorzy cyrkowi i pokrewni','Aktorzy cyrkowi i pokrewni wykonują numery akrobatyczne, ewolucje na różnych przyrządach, prowadzą pokazy iluzjonistyczne, przedstawiają scenki komiczne, żonglują różnymi przedmiotami lub popisują się tresurą różnorodnych zwierząt podczas przedstawień cyrkowych lub innych występów scenicznych.','Zadania wykonywane przez aktorów cyrkowych i im pokrewnych zazwyczaj obejmują: odgrywanie zabawnych skeczy i opowiadanie śmiesznych historyjek; wykonywanie sztuczek iluzjonistycznych i zręcznościowych, pokazów hipnozy; wykonywanie trudnych i widowiskowych popisów akrobatycznych i gimnastycznych lub pokazów żonglerki; tresowanie i prezentowanie występów z udziałem zwierząt; ćwiczenie umiejętności i uczestniczenie w próbach w celu utrzymania wysokiego standardu występu. Do ich zadań może także należeć nadzorowanie innych pracowników.',206);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3436','Muzycy i pokrewni','Muzycy i pokrewni grają na instrumentach muzycznych, wykonują piosenki, występują na scenie teatralnej i estradzie w roli muzyka, aktora-wokalisty, wykonują oprawę muzyczną nabożeństw religijnych.','Zadania wykonywane przez muzyków i pokrewnych wykonawców zazwyczaj obejmują: granie na jednym lub większej liczbie instrumentów muzycznych w charakterze solisty, członka orkiestry lub zespołu muzycznego; śpiewanie w charakterze solisty, członka grup wokalnych lub innych zespołów; występowanie na scenie teatralnej i estradzie w roli aktora, tancerza i wokalisty; uświetnianie nabożeństw i uroczystości religijnych śpiewem i grą na organach lub innych instrumentach oraz prowadzenie chóru kościelnego; ćwiczenie umiejętności i uczestniczenie w próbach w celu utrzymania wysokiego standardu występu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',207);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3437','Tancerze','Tancerze uczestniczą w widowiskach teatralnych, operowych i estradowych wykonując różne rodzaje tańca scenicznego, takie jak: taniec klasyczny, współczesny, wyrazisty, charakterystyczny (stylizowany ludowy) i historyczny.','Zadania wykonywane przez tancerzy zazwyczaj obejmują: wykonywanie tańca w charakterze solisty, z partnerem lub jako członek grupy tanecznej, na żywo z udziałem publiczności lub na potrzeby filmu i innych mediów wizualnych; uczestniczenie w systematycznych treningach i ćwiczeniach kondycyjnych w celu utrzymania sprawności motorycznej i doskonalenia umiejętności zawodowych; trenowanie: uczestniczenie w konkursach i pokazach tańca towarzyskiego oraz prowadzenie nauki tańca; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',208);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3439','Średni personel w zakresie działalności artystycznej i kulturalnej gdzie indziej niesklasyfikowany','Ta grupa elementarna obejmuje średni personel w zakresie działalności artystycznej i kulturalnej niesklasyfikowany w innych podgrupach grupy 343 Średni personel w zakresie działalności artystycznej, kulturalnej i kulinarnej. Grupa ta obejmuje na przykład osoby, które wspierają reżyserów, kierowników produkcji i aktorów w realizacji produkcji teatralnych, filmowych, telewizyjnych lub komercyjnych, animatorów kultury, menedżerów klubów muzycznych, itp.','Zadania wykonywane przez średni personel w zakresie działalności artystycznej i kulturalnej gdzie indziej niesklasyfikowany zazwyczaj obejmują: uczestniczenie w pracach pionu kierownika produkcji, takich jak: opracowanie harmonogramu prac, planu zajęć dla poszczególnych stanowisk, organizowanie i koordynowanie przebiegu produkcji filmowej i telewizyjnej, przygotowanie scenopisu do zdjęć i wyciągów dla aktorów i członków grupy zdjęciowej; organizowanie, koordynowanie i monitorowanie przebiegu produkcji filmowej i telewizyjnej; zajmowanie się stroną organizacyjno-techniczną realizacji przedstawienia teatralnego, odpowiadając za stworzenie warunków do jego przygotowania oraz za prawidłowy przebieg spektaklu; dostarczanie aktorom kostiumów i pomaganie w ich zakładaniu; podpowiadanie aktorom tekstu; zastępowanie aktorów w scenach niebezpiecznych, zwłaszcza w zagrażających zdrowiu lub życiu aktora, czy w scenach trudnych, w których potrzeba wysokiej sprawności fizycznej lub specjalnych umiejętności; dostarczanie na miejsce spektaklu teatralnego, filmowego, telewizyjnego, itp. gotowych rekwizytów lub montowanie ich z elementów; planowanie, instalowanie i nadzorowanie oświetlenia planów filmowych podczas realizacji programów w studiach telewizyjnych, w plenerach i innych miejscach; opracowanie i przygotowanie scen filmowych z użyciem broni i materiałów pirotechnicznych oraz realizowanie pokazów pirotechnicznych z użyciem materiałów i wyrobów pirotechniki widowiskowej, z zachowaniem wymogów bezpieczeństwa; inicjowanie, organizowanie i upowszechnianie kultury i sztuki w publicznych palcówkach kulturalno-oświatowych oraz w środowisku lokalnym; prowadzenie klubu muzycznego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',209);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3511','Operatorzy urządzeń teleinformatycznych','Operatorzy urządzeń teleinformatycznych wspierają codzienne podtrzymywanie, eksploatację i monitoring systemów urządzeń informacyjno-komunikacyjnych, urządzeń zewnętrznych, sprzętu, oprogramowania i pokrewnego sprzętu komputerowego w celu zapewnienia optymalnego działania oraz identyfikacji wszelkich problemów.','Zadania wykonywane przez operatorów urządzeń teleinformatycznych zazwyczaj obejmują: obsługę i kontrolę zewnętrznych i pokrewnych urządzeń komputerowych; wprowadzanie poleceń, korzystanie z terminala komputerowego oraz uruchamianie urządzeń kontrolnych w komputerach i urządzeniach zewnętrznych w celu integrowania i eksploatacji sprzętu; monitorowanie systemów ostrzegających o wystąpieniu awarii sprzętu lub błędów w działaniu; powiadamianie technika do spraw nadzoru i monitorowania o awariach sprzętu; odpowiadanie na wiadomości informujące o wystąpieniu błędów w programie przez wyszukiwanie i rozwiązywanie problemów, delegowanie rozwiązania problemów do innych pracowników lub kończenie pracy programu; zapoznawanie się z instrukcjami konfiguracji zadań w celu doboru odpowiedniego sprzętu, kolejności zastosowania przedmiotowych urządzeń, rodzaju materiałów, które należy załadować, np. płyt, papieru oraz ustawieniami w zakresie kontroli; odzyskiwanie, wyszukiwanie, oddzielanie i sortowanie w zależności od potrzeby, wyników pracy programu oraz przesyłanie danych do określonych użytkowników; ładownie odpowiednich materiałów eksploatacyjnych do urządzeń zewnętrznych, takich jak drukarki, a także nadzorowanie ich ładowania przez operatorów urządzeń zewnętrznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',210);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3512','Technicy wsparcia informatycznego i technicznego','Technicy wsparcia informatycznego i technicznego zapewniają, bezpośrednio lub za pośrednictwem telefonu, wsparcie techniczne użytkownikom poczty elektronicznej lub innych elektronicznych środków komunikacji w zakresie rozwiązywania problemów dotyczących oprogramowania, sprzętu, komputerowych urządzeń zewnętrznych, sieci, baz danych oraz Internetu, a także zapewniają poradnictwo i wsparcie w zakresie rozmieszczenia, instalacji i konserwacji systemów.','Zadania wykonywane przez techników wsparcia informatycznego i technicznego zazwyczaj obejmują: udzielanie odpowiedzi na zapytania użytkowników dotyczące rozwiązywania problemów w zakresie eksploatacji sprzętu lub oprogramowania; wprowadzanie poleceń i obserwowanie działania systemu w celu sprawdzenia poprawności działania i wykrycia błędów; instalowanie i dokonywanie drobnych napraw sprzętu, oprogramowania i urządzeń zewnętrznych zgodnie ze schematami i instrukcjami w zakresie instalacji; nadzorowanie codziennej pracy systemów komunikacyjnych i komputerowych; przygotowanie sprzętu do użytkowania przez pracownika, dokonywanie lub zapewnianie prawidłowej instalacji przewodów, obsługę systemów lub odpowiedniego sprzętu; prowadzenie rejestru codziennych operacji w zakresie komunikacji danych, problemów i podejmowanych działań naprawczych lub prac instalacyjnych; emulowanie i odtwarzanie problemów technicznych napotkanych przez użytkowników; korzystanie z przewodników dla użytkowników, instrukcji technicznych i innej dokumentacji w celu wyszukania i zastosowania rozwiązań; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',211);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3513','Operatorzy sieci i systemów komputerowych','Operatorzy sieci i systemów komputerowych tworzą, obsługują i konserwują sieci lub inne systemy komunikacyjne.','Zadania wykonywane przez operatorów sieci i systemów komputerowych zazwyczaj obejmują: obsługę, konserwację i rozwiązywanie problemów związanych z systemami sieciowymi; obsługę i konserwację systemów komunikacji danych innych niż sieci; identyfikowanie obszarów wymagających aktualizacji sprzętu lub oprogramowania; instalowanie sprzętu komputerowego, oprogramowania sieciowego, systemów operacyjnych i aplikacji; prowadzenie prac związanych z uruchamianiem i zamykaniem sieci komputerowych oraz tworzeniem kopii zapasowych i odzyskiwaniem danych po awarii; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',212);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3514','Technicy sieci internetowych','Technicy sieci internetowych konserwują, monitorują i udzielają wsparcia w zakresie utrzymania optymalnego funkcjonowania stron internetowych i intranetowych oraz sprzętu i oprogramowania sieciowego.','Zadania wykonywane przez techników sieci internetowych zazwyczaj obejmują: instalowanie, monitorowanie i udzielanie wsparcia w zakresie niezawodności i użyteczności internetowych i intranetowych stron sieciowych oraz sprzętu i oprogramowania serwera sieciowego; przygotowanie i prowadzenie dokumentacji, polityki i instrukcji, rejestrowanie procedur operacyjnych i dziennika systemowego; opracowywanie, koordynowanie, wdrażanie i monitorowanie środków bezpieczeństwa; analizowanie i formułowanie zaleceń w celu zwiększenia wydajności, z uwzględnieniem aktualizacji i nabywania nowych systemów; prowadzenie współpracy z klientami i użytkownikami, a także zapewnianie im wsparcia; modyfikowanie stron internetowych; prowadzenie prac związanych z tworzeniem kopii zapasowych i odzyskiwaniem danych z serwerów sieciowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',213);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3521','Operatorzy urządzeń do rejestracji i transmisji obrazu i dźwięku','Operatorzy urządzeń do rejestracji i transmisji obrazu i dźwięku obsługują, kontrolują stan techniczny sprzętu do rejestracji, edycji obrazu i dźwięku oraz transmisji obrazu i dźwięku w ramach programów radiowych i telewizyjnych, a także innych rodzajów sygnałów telekomunikacyjnych na lądzie, morzu i pokładzie statków powietrznych; obsługują projekcyjną aparaturę kinową.','Zadania wykonywane przez operatorów urządzeń do rejestracji i transmisji obrazu i dźwięku zazwyczaj obejmują: ustawianie i operowanie kamerami filmowymi, kamerami wideo i innymi specjalistycznymi kamerami; przygotowanie i obsługę sprzętu zdjęciowego; kontrolowanie sprzętu do rejestracji dźwięku; kontrolowanie sprzętu do edycji i łączenia zarejestrowanych obrazów i dźwięków w celu zapewnienia zadowalającej jakości oraz stworzenia wizualnych i dźwiękowych efektów specjalnych; stosowanie wiedzy i zasad oraz praktyk rejestracji i edycji obrazu oraz dźwięku w celu identyfikacji i rozwiązywania pojawiających się problemów; kontrolowanie systemów transmisji i przesyłania danych oraz systemów satelitarnych obsługujących programy telewizyjne i radiowe; kontrolowanie systemów komunikacji radiowej, usług satelitarnych i systemów multipleksów na lądzie, morzu i pokładach statków powietrznych; stosowanie wiedzy i zasad oraz praktyk nadawania, terminali telekomunikacyjnych i systemów transmisyjnych w celu identyfikacji i rozwiązywania problemów; przeprowadzanie awaryjnych napraw sprzętu; obsługę aparatury projekcyjno-dźwiękowej oraz technicznych urządzeń kina; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',214);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('3522','Operatorzy urządzeń telekomunikacyjnych','Operatorzy urządzeń telekomunikacyjnych wykonują prace techniczne związane z badaniami w dziedzinie inżynierii telekomunikacyjnej oraz projektowaniem, produkcją, montażem, budową, obsługą konserwacją i naprawą systemów telekomunikacyjnych.','Zadania wykonywane przez operatorów urządzeń telekomunikacyjnych zazwyczaj obejmują: udzielanie wsparcia technicznego w zakresie badań i rozwoju sprzętu telekomunikacyjnego lub testowanie prototypów; studiowanie materiałów technicznych takich, jak projekty i szkice w celu ustalenia metody pracy; przygotowanie szczegółowych kalkulacji ilości i kosztów materiałów i pracy wymaganych do produkcji i instalacji sprzętu telekomunikacyjnego; zapewnianie nadzoru technicznego produkcji, użytkowania, konserwacji i naprawy sprzętu telekomunikacyjnego w celu zapewnienia satysfakcjonującej pracy i zgodności z określonymi instrukcjami i przepisami; stosowanie wiedzy technicznej z zakresu zasad i praktyk stosowanych w dziedzinie inżynierii telekomunikacji w celu identyfikacji i rozwiązywania problemów pojawiających się w toku pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',215);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4110','Pracownicy obsługi biurowej','Pracownicy obsługi biurowej wykonują szereg biurowych i administracyjnych obowiązków zgodnie z obowiązującymi procedurami.','Zadania wykonywane przez pracowników obsługi biurowej zazwyczaj obejmują: zapisywanie, przygotowywanie, organizację i klasyfikację informacji oraz prowadzenie dokumentacji; prowadzenie ewidencji korespondencji, przyjmowanie i nadawanie korespondencji pocztowej; odbieranie telefonów i korespondencji elektronicznej i przekazywanie ich do właściwych pracowników; przygotowywanie sprawozdań i korespondencji; prowadzenie rejestru urządzeń wydawanych pracownikom; rejestrowanie dokonanych transakcji finansowych, sprawdzanie danych, przygotowywanie faktur; wprowadzanie informacji do komputera, ich weryfikowanie i dokonywanie korekty; przyjmowanie i realizowanie wniosków dotyczących zameldowania na pobyt stały lub czasowy, wymeldowania oraz wydania dowodu osobistego; prowadzenie w systemie informatycznym i kartotecznym gminnych zbiorów meldunkowych, zawierających dane osób zameldowanych na pobyt stały, na pobyt czasowy i osób wymeldowanych; wykonywanie czynności związanych z administrowaniem nieruchomością wspólnoty mieszkaniowej, w tym m.in. prowadzenie książki obiektu, teczek właścicieli oraz innej dokumentacji, prowadzenie odczytów wodomierzy i liczników zużycia ciepła w budynku oraz w lokalach i rozliczanie opłat za zużytą wodę, przyjmowanie zgłoszeń, interwencji i wniosków właścicieli lokali, doręczanie właścicielom książeczek opłat, organizowanie działań związanych z likwidacją przyczyn awarii instalacji i urządzeń, sprawowanie nadzoru nad pracą dozorcy i kontrolę stanu sanitarno-porządkowego posesji, zgłaszanie zarządowi wspólnoty potrzebę drobnych napraw i prac remontowych, przygotowywanie zebrań wspólnoty. Zadania wykonywane przez pracowników obsługi biurowej zazwyczaj obejmują: zapisywanie, przygotowywanie, organizację i klasyfikację informacji oraz prowadzenie dokumentacji; prowadzenie ewidencji korespondencji, przyjmowanie i nadawanie korespondencji pocztowej; odbieranie telefonów i korespondencji elektronicznej i przekazywanie ich do właściwych pracowników; przygotowywanie sprawozdań i korespondencji; prowadzenie rejestru urządzeń wydawanych pracownikom; rejestrowanie dokonanych transakcji finansowych, sprawdzanie danych, przygotowywanie faktur; wprowadzanie informacji do komputera, ich weryfikowanie i dokonywanie korekty; przyjmowanie i realizowanie wniosków dotyczących zameldowania na pobyt stały lub czasowy, wymeldowania oraz wydania dowodu osobistego; prowadzenie w systemie informatycznym i kartotecznym gminnych zbiorów meldunkowych, zawierających dane osób zameldowanych na pobyt stały, na pobyt czasowy i osób wymeldowanych; wykonywanie czynności związanych z administrowaniem nieruchomością wspólnoty mieszkaniowej, w tym m.in. prowadzenie książki obiektu, teczek właścicieli oraz innej dokumentacji, prowadzenie odczytów wodomierzy i liczników zużycia ciepła w budynku oraz w lokalach i rozliczanie opłat za zużytą wodę, przyjmowanie zgłoszeń, interwencji i wniosków właścicieli lokali, doręczanie właścicielom książeczek opłat, organizowanie działań związanych z likwidacją przyczyn awarii instalacji i urządzeń, sprawowanie nadzoru nad pracą dozorcy i kontrolę stanu sanitarno-porządkowego posesji, zgłaszanie zarządowi wspólnoty potrzebę drobnych napraw i prac remontowych, przygotowywanie zebrań wspólnoty; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',216);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4120','Sekretarki (ogólne)','Sekretarki (ogólne) korzystają z maszyn do pisania, komputerów oraz innych urządzeń do edycji tekstu w celu przepisywania treści korespondencji i innych dokumentów, sprawdzania i formatowania dokumentów przygotowanych przez innych pracowników, zajmują się zarządzaniem przychodzącą i wychodzącą korespondencją pocztową, sprawdzaniem wniosków o spotkanie lub wizyty oraz wykonywaniem szeregu różnych zadań administracyjnych.','Zadania wykonywane przez sekretarki (ogólne) zazwyczaj obejmują: sprawdzanie, formatowanie i przepisywanie korespondencji, protokołów i sprawozdań ze słuchu, dokumentów elektronicznych lub wersji roboczych tak, by odpowiadały one standardom biura, z wykorzystaniem maszyny do pisania, komputera lub innych urządzeń do edycji tekstu; wykorzystywanie różnego oprogramowania komputerowego, w tym arkuszy kalkulacyjnych w celu zapewnienia wsparcia administracyjnego; zarządzanie przychodzącą i wychodzącą korespondencją; skanowanie, zapisywanie i rozdzielanie poczty, korespondencji i dokumentów; sprawdzanie wniosków o spotkania i wizyty oraz pomoc w organizacji spotkań; kontrolę i prowadzenie rejestru urlopów i wykorzystania innych uprawnień przysługujących pracownikom; organizowanie i nadzorowanie systemów przechowywania dokumentów; zarządzanie regularną korespondencją z własnej inicjatywy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',217);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4131','Maszynistki i operatorzy edytorów tekstu','Maszynistki i operatorzy edytorów tekstu wpisują, edytują i drukują za pomocą maszyny do pisania, komputera lub innego edytora tekstu oraz zapisują teksty mówione lub pisane w formie stenografii.','Zadania wykonywane przez maszynistki i operatorów edytorów tekstu zazwyczaj obejmują: tworzenie pisemnych dokumentów z wersji roboczych, wersji skorygowanych, nagrań dźwiękowych oraz stenografii za pomocą komputera, procesora tekstu lub maszyny do pisania; sprawdzanie gotowych dokumentów pod kątem błędów w ortograficznych, gramatycznych, interpunkcyjnych oraz błędów w formatowaniu; gromadzenie i porządkowanie materiałów, które mają być przepisane, zgodnie z instrukcjami; archiwizowanie i przechowywanie dokumentów na dysku twardym komputera lub na dysku przenośnym, zarządzanie systemem komputerowym służącym do przechowywania, odzyskiwania lub aktualizowania dokumentów; sporządzanie notatek ze słuchu metodą stenografowania; przedstawianie słowa mówionego, słów piosenek, itp. w formie napisów w filmach i programach telewizyjnych; odtwarzanie stenogramu i nagranych tekstów, dokonywanie ich weryfikacji i korekty; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',218);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4132','Operatorzy wprowadzania danych','Operatorzy wprowadzania danych wprowadzają zakodowane, statystyczne, finansowe i inne liczbowe dane do urządzeń elektronicznych, komputerowych baz danych, arkuszy kalkulacyjnych oraz innych zbiorów danych za pomocą klawiatury, skanera optycznego, oprogramowania służącego do rozpoznawania głosu lub innych narzędzi do wprowadzania danych. Wprowadzają dane do urządzeń mechanicznych lub elektronicznych, które służą przeprowadzaniu matematycznych obliczeń.','Zadania wykonywane przez operatorów wprowadzania danych zazwyczaj obejmują: przyjmowanie i rejestrowanie faktur, formularzy, rejestrów oraz innych dokumentów służących do gromadzenia danych; wprowadzania danych liczbowych, kodów i tekstu z materiałów źródłowych do komputerowych urządzeń służących do przechowywania i przetwarzania danych; weryfikowanie zgodności i kompletności danych oraz w razie konieczności, korygowanie wprowadzonych danych; obsługę urządzeń wykorzystywanych w księgowości i do obliczeń; przenoszenie danych pomiędzy różnymi systemami i różnym oprogramowaniem baz danych; wykonywanie innych zadań pokrewnych.',219);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4211','Kasjerzy bankowi i pokrewni','Kasjerzy bankowi i pokrewni zajmują się bezpośrednią obsługą klienta w bankach i urzędach pocztowych w zakresie pobierania, wymiany i wypłacania pieniędzy lub świadczenia usług pocztowych i telekomunikacyjnych, wykonują czynności związane z nadzorem prawidłowości obsługi klientów oraz przechowywaniem w skarbcu zapasów pieniężnych i depozytów klientów.','Zadania wykonywane przez kasjerów bankowych i pokrewnych pracowników zazwyczaj obejmują: obsługę depozytów pieniężnych i wypłat gotówki, czeków, przelewów, rachunków, płatności kartą kredytową, przekazów pieniężnych, czeków potwierdzonych oraz innych powiązanych transakcji bankowych; zapisywanie wpływów i obciążeń kredytowych na rachunkach bankowych klientów; opłacanie rachunków i dokonywanie przelewów na życzenie klienta; prowadzenie rejestru wszystkich transakcji i zestawianie ich z saldem rachunku; dokonywanie wymiany walut zgodnie z poleceniem klienta; przyjmowanie i wydawanie przesyłek pocztowych, sprzedaż znaczków pocztowych i świadczenie innych typowych usług pocztowych, takich jak: opłaty rachunków, przelewy pieniężne, itd.; przyjmowanie opłat i wykonywanie usług telekomunikacyjnych; organizowanie i nadzorowanie pracy kasjerów i kontrolowanie zgodności i prawidłowości rozliczeń; informowanie klientów o nowych usługach bankowych i pocztowych; przechowywanie i zabezpieczanie w skarbcu wartości pieniężnych i depozytów klientów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',220);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4212','Bukmacherzy, krupierzy i pokrewni','Bukmacherzy, krupierzy i pokrewni zajmują się szacunkiem szans na wygraną oraz pobieraniem i wypłacaniem pieniędzy obstawianych na mecze sportowe i inne wydarzenia, prowadzą kolektury gier liczbowych lub organizują gry losowe w lokalach gier losowych.','Zadania wykonywane przez bukmacherów, krupierów i pokrewnych pracowników zazwyczaj obejmują: określanie ryzyka, by oszacować prawdopodobieństwo wygranej i ograniczyć lub wykluczyć przyjmowanie zakładów; przygotowywanie i publikowanie list zakładów; objaśnianie i interpretację zasad działania lokalu gier losowych; rozdawanie kart, rzucanie kośćmi lub obsługa koła ruletki w kasynach gry; ogłaszanie zwycięzców, wypłacanie wygranej i zbieranie płatności od pozostałych uczestników; przyjmowanie zakładów gier liczbowych i wypłacanie wygranych niższego stopnia; wykonywanie innych zadań pokrewnych.',221);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4213','Pracownicy lombardów i instytucji pożyczkowych','Pracownicy lombardów i instytucji pożyczkowych pożyczają pieniądze pod zastaw przedmiotów, nieruchomości lub innych zabezpieczeń.','Zadania wykonywane przez pracowników lombardów i instytucji pożyczkowych zazwyczaj obejmują: szacowanie wartości zastawionych przedmiotów, obliczanie odsetek i pożyczanie pieniędzy; zwracanie przedmiotów po spłacie pożyczki lub w przypadku braku spłaty, sprzedaż zastawionych przedmiotów; pożyczanie pieniędzy pod zastaw przyszłych zysków przedsięwzięć; odbieranie kwot pożyczonych w sytuacji zagrożenia zastawu w postaci przyszłych zysków; prowadzenie rejestru otrzymanych w zastaw przedmiotów oraz otrzymanych i wypłaconych pieniędzy; wykonywanie innych zadań pokrewnych.',222);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4214','Windykatorzy i pokrewni','Windykatorzy i pokrewni zajmują się ściąganiem zaległości podatkowych, płatności z tytułu zadłużenia konta, czeków bez pokrycia lub roszczeń wierzycieli, jak również pobieraniem opłat za zużycie mediów energetycznych.','Zadania wykonywane przez windykatorów i pokrewnych pracowników zazwyczaj obejmują: odszukiwanie i lokalizację dłużników; nawiązywanie kontaktu telefonicznego, osobistego i korespondencyjnego z dłużnikami w celu odzyskania długu lub uzgodnienia późniejszej płatności; sporządzanie sprawozdań obejmujących kwotę odzyskanych pieniędzy i prowadzenie rejestru i dokumentacji dotyczących działalności windykacyjnej; zalecanie wystąpienia na drogę sądową lub odstąpienie od czynności windykacyjnych w przypadkach, gdy dług z różnych powodów nie może zostać odzyskany; kontrolowanie wielkości zużycia energii elektrycznej, gazu, ciepła lub wody przez odbiorców oraz pobieranie z tego tytułu należności; wykonywanie innych zadań pokrewnych.',223);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4221','Konsultanci i inni pracownicy biur podróży','Konsultanci i inni pracownicy biur podróży udzielają informacji, opracowują plan podróży, są odpowiedzialni za rezerwację środków transportu i zakwaterowania oraz organizację wycieczek grupowych.','Zadania wykonywane przez konsultantów i innych pracowników biur podróży zazwyczaj obejmują: zdobywanie informacji dotyczących dostępności, ceny i standardu różnych rodzajów środków transportu i zakwaterowania, zapewnianie zgodności proponowanych rozwiązań z oczekiwaniami klientów i doradzanie im odnośnie organizacji podróży; opracowywanie planów podróży; dokonywanie i potwierdzanie rezerwacji; wydawanie biletów i voucherów; pomaganie klientom w zdobyciu niezbędnych dokumentów podróżnych, takich jak: wizy; wystawianie rachunków i przyjmowanie płatności; organizowanie wycieczek grupowych, biznesowych i wypoczynkowych dla grup i klientów indywidualnych; sprawowanie w imieniu biura podróży opieki nad turystami w miejscu pobytu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',224);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4222','Pracownicy centrów obsługi telefonicznej (pracownicy call center)','Pracownicy centrów obsługi telefonicznej (pracownicy call center) świadczą usługi konsultacyjne i informacyjne dla klientów w zakresie produktów, usług i polityki przedsiębiorstwa lub organizacji, obsługują transakcje finansowe za pośrednictwem telefonu lub mediów elektronicznych. Pracują oni w miejscach oddalonych od klientów danej organizacji lub przedsiębiorstwa, których udzielone informacje dotyczą.','Zadania wykonywane przez pracowników centrów obsługi telefonicznej (pracowników call center) zazwyczaj obejmują: obsługę połączeń telefonicznych od klientów poprzez udzielanie im informacji, świadczenie usługi lub reagowanie na skargi; rozpoznawanie potrzeb i wprowadzanie informacji na ten temat do systemu komputerowego; przekazywanie zadań w stosownych przypadkach, do innych jednostek; wystawianie faktur i obsługę płatności; wysyłanie do klientów listów, formularzy informacyjnych i innych dokumentów; doradzanie klientom w zakresie dodatkowych produktów lub usług; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',225);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4223','Operatorzy centrali telefonicznych','Operatorzy centrali telefonicznych obsługują urządzenia centrali telefonicznej w celu realizacji połączeń telefonicznych, odebrania połączeń klientów pytających o informacje lub zgłaszających problem, nagrywania i przekazywania wiadomości innym pracownikom lub klientom.','Zadania wykonywane przez operatorów centrali telefonicznych zazwyczaj obejmują: obsługę łącznicy telefonicznej w celu nawiązania, wstrzymania, przełączenia lub odrzucenia połączeń telefonicznych; realizowanie połączeń wychodzących; udzielanie informacji i nagrywanie wiadomości; przekazywanie wiadomości innym pracownikom lub klientom; identyfikacja problemów w systemie operacyjnym i zgłaszanie awarii służbom serwisowym.',226);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4224','Recepcjoniści hotelowi','Recepcjoniści hotelowi rejestrują gości, przydzielają pokoje, wydają klucze, udzielają informacji o usługach oferowanych w hotelu, dokonują rezerwacji, prowadzą rejestr pokoi dostępnych dla gości, informują gości o kosztach pobytu i przyjmują płatności.','Zadania wykonywane przez recepcjonistów hotelowych zazwyczaj obejmują: prowadzenie wykazu pokoi dostępnych dla gości, możliwych do zarezerwowania lub przydzielenia; rejestrację przybyłych gości, przydzielanie pokojów, weryfikowanie wiarygodności klientów i wydawanie kluczy; udzielanie informacji na temat dostępnych pokojów i dokonywanie ich rezerwacji; udzielanie informacji o usługach świadczonych w hotelu i dostępnych w okolicy; reagowanie na skargi gości oraz zgodnie z zapotrzebowaniem, organizowanie obsługi pokojowej lub obsługi technicznej; kontaktowanie się z pracownikami obsługi pokojowej lub technicznej w sprawach zgłaszanych przez gości hotelowych; obciążanie i kontrolowanie rachunków gości za pomocą systemów komputerowych lub ręcznie; przyjmowanie i przekazywanie gościom wiadomości osobiście lub telefonicznie; weryfikację rachunków wystawianych gościom opuszczającym hotel i przyjmowanie płatności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',227);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4225','Pracownicy biur informacji','Pracownicy biur informacji odpowiadają na osobiste, pisemne, elektroniczne lub telefoniczne pytania dotyczące produktów i usług, udzielają informacji lub odsyłają do innych źródeł informacji. Pracują oni w miejscach umożliwiających bezpośredni kontakt z klientem, miejscem produkcji lub świadczenia usług.','Zadania wykonywane przez pracowników biur informacji zazwyczaj obejmują: odpowiadanie na pytania dotyczące produktów lub usług oraz udzielanie informacji odnośnie ich dostępności, lokalizacji, ceny i powiązanych z tym kwestii; odpowiadanie na pytania w przypadku powstania problemów oraz udzielanie konsultacji, informacji i wsparcia; zapisywanie informacji dotyczących pytań i skarg; przekazywanie złożonych pytań do kierowników zespołów lub doradców-ekspertów; wydawanie zainteresowanym stosownych formularzy, pakietów informacyjnych i broszur; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',228);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4226','Recepcjoniści (z wyłączeniem hotelowych)','Recepcjoniści (z wyłączeniem hotelowych) przyjmują i witają odwiedzających, klientów i gości, udzielają informacji oraz na prośbę klientów, ustalają termin spotkania.','Zadania wykonywane przez recepcjonistów (z wyłączeniem hotelowych) zazwyczaj obejmują: przyjmowanie i witanie odwiedzających, gości lub klientów; wyznaczanie terminów spotkań dla klientów; telefoniczne udzielanie informacji i ustalanie terminów; kierowanie klientów to odpowiednich miejsc lub osób; wystawianie przepustek uprawniających do wstępu na teren zamknięty; udostępnianie pakietów informacyjnych, broszur lub formularzy; wykonywanie innych zadań pokrewnych.',229);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4227','Ankieterzy','Ankieterzy przeprowadzają badania ankietowe na różne tematy i zapisują odpowiedzi respondentów.','Zadania wykonywane przez ankieterów zazwyczaj obejmują: nawiązywanie kontaktu telefonicznego lub osobistego z badanymi i wyjaśnianie celu przeprowadzanej ankiety; zadawanie pytań po przedstawieniu zarysu kwestionariusza lub ankiety; zapisywanie odpowiedzi na papierze lub wprowadzanie ich bezpośrednio do komputerowej bazy danych, z wykorzystaniem komputerowego systemu ankietowego; znajdowanie i usuwanie niespójności w odpowiedziach; udzielanie opinii zwrotnej zleceniodawcy w zakresie problemów z pozyskaniem właściwych danych; wykonywanie innych zadań pokrewnych.',230);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4311','Pracownicy do spraw rachunkowości i księgowości','Pracownicy do spraw rachunkowości i księgowości wprowadzają do komputera, klasyfikują i prowadzą rejestr danych liczbowych uzupełniających rejestr finansowy. Wykonują oni szeroki zakres obowiązków związanych z obliczeniami, zapisami księgowymi i weryfikacją w celu pozyskania zasadniczych danych finansowych wykorzystywanych w prowadzeniu księgowości.','zazwyczaj obejmują: weryfikację danych, zapisów i dokumentów pod kątem poprawności wprowadzenia, poprawności obliczeń i poprawności wprowadzonych kodów; obsługę komputerów wyposażonych w oprogramowanie księgowe do prowadzenia rejestru, magazynowania i analizowania informacji; klasyfikowanie, zapisywanie oraz podsumowywanie danych liczbowych i finansowych w celu opracowania i prowadzenia rejestru finansowego przy wykorzystaniu ksiąg głównych oraz komputerów; obliczanie, przygotowywanie i wystawianie rachunków, faktur, wyciągów z kont i innych sprawozdań finansowych zgodnie z przyjętą procedurą; przygotowywanie statystycznych, finansowych, księgowych i kontrolnych raportów i wykresów dotyczących takich kwestii, jak: rachunki za transakcje gotówkowe, wierzytelności i zadłużenie oraz zyski i straty; wykonywanie innych zadań pokrewnych.',231);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4312','Pracownicy do spraw statystyki, finansów i ubezpieczeń','Pracownicy do spraw statystyki, finansów i ubezpieczeń pozyskują, opracowują i wprowadzają do komputera dane statystyczne i aktuarialne lub wykonują zadania biurowe związane z transakcjami przedsiębiorstw ubezpieczeniowych, banków i innych instytucji.','Zadania wykonywane przez pracowników do spraw statystyki, finansów i ubezpieczeń zazwyczaj obejmują: przetwarzanie zgłoszeń o objęcie ubezpieczeniem lub anulowanie ubezpieczenia, roszczeń odszkodowawczych, zmian warunków ubezpieczenia i dokonywanie płatności; pozyskiwanie i opracowywanie danych statystycznych i aktuarialnych w oparciu o standardowe lub specjalne źródła informacji; obliczanie wartości ogólnych, średnich, odsetek i innych szczegółów oraz przedstawianie ich zgodnie z wymaganiami w formie graficznej; przygotowywanie dokumentów finansowych i obliczanie należnego oprocentowania, opłat maklerskich oraz opłat skarbowych; prowadzenie rejestru obligacji, akcji i innych papierów wartościowych nabytych lub sprzedanych w imieniu klientów oraz pracodawcy; wykonywanie innych zadań pokrewnych.',232);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4313','Pracownicy obsługi płacowej','Pracownicy obsługi płacowej zbierają, weryfikują i przetwarzają informacje dotyczące listy płac oraz wprowadzają do komputera informacje o wynagrodzeniu i świadczeniach przysługujących pracownikom w dziale, przedsiębiorstwie lub innej instytucji.','Zadania wykonywane przez pracowników obsługi płacowej zazwyczaj obejmują: prowadzenie rejestru frekwencji pracowników, dni wolnych i nadgodzin w celu obliczenia przysługującego wynagrodzenia i świadczeń; sporządzanie listy płac z tytułu wynagrodzeń pracowników i innych świadczeń, dokonywanie przelewów na rachunek osobisty pracownika lub przekazywanie wypłat gotówkowych do realizacji w kasie jednostki; sprawdzanie list obecności, grafików pracy, wyliczonych płac i innych informacji w celu wykrycia niezgodności; weryfikowanie frekwencji i przepracowanych godzin, dokonywanie korekty dochodów i umieszczanie informacji w odpowiednich rejestrach; opracowywanie i weryfikowanie zaświadczeń o zarobkach pracowników, zawierających informacje o pensji brutto i netto oraz kwotach potrącanych, takich jak: podatki, składki na związki zawodowe, zadłużenie, składki ubezpieczeniowe i emerytalne; wykonywanie innych zadań pokrewnych.',233);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4321','Magazynierzy i pokrewni','Magazynierzy i pokrewni prowadzą ewidencję gotowych produktów, surowców i materiałów produkcyjnych przyjętych, zważonych, wydanych, wysłanych i zmagazynowanych, prowadzą punkty skupu surowców i wypożyczalnie towarów oraz przeprowadzają inwentaryzację magazynowanych towarów lub inwentaryzację środków gospodarczych przedsiębiorstwa czy instytucji.','Zadania wykonywane przez magazynierów i pokrewnych pracowników zazwyczaj obejmują: porządkowanie i kontrolowanie przyjętych i wysyłanych towarów oraz prowadzenie właściwej ewidencji; prowadzenie ewidencji magazynowej, weryfikowanie wydanych towarów, szacowanie potrzeb i składanie zapotrzebowania; odbieranie, magazynowanie i wydawanie narzędzi, części zamiennych i innego sprzętu oraz prowadzenie właściwej ewidencji; ważenie towarów przyjmowanych, wydanych, wyprodukowanych i wysłanych oraz prowadzenie ewidencji; prowadzenie inwentaryzacji przedmiotów przyjętych do magazynu; wykonywanie czynności związanych ze skupem surowców rolnych; wykonywanie czynności związanych z odpłatnym wypożyczaniem różnych towarów; ewidencjonowanie i weryfikowanie stanu majątku przedsiębiorstwa lub instytucji oraz przeprowadzanie inwentaryzacji w celu ustalenia rzeczywistego stanu środków gospodarczych; wykonywanie innych zadań pokrewnych.',234);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4322','Planiści produkcyjni','Planiści produkcyjni obliczają ilości materiałów potrzebnych do produkcji przemysłowej, budownictwa i innych planów produkcyjnych, przygotowują i sprawdzają harmonogramy produkcyjne.','Zadania wykonywane przez planistów produkcyjnych zazwyczaj obejmują: obliczanie ilości, określanie jakości i rodzajów materiałów na podstawie planów produkcyjnych; przygotowywanie harmonogramów dostaw materiałowych w sposób dostosowany do potrzeb produkcyjnych oraz prowadzenie odpowiednich zapisów; przygotowywanie lub pomoc w przygotowaniu harmonogramów produkcji na podstawie zamówień klientów oraz zgodnie z możliwościami i wydajnością produkcyjną; weryfikowanie stanu zapasów w magazynie, organizowanie dostaw i badanie przyczyn opóźnień; prowadzenie ewidencji i koordynowanie przepływu pracy i materiałów pomiędzy oddziałami przedsiębiorstwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',235);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4323','Pracownicy do spraw transportu','Pracownicy do spraw transportu rejestrują i kontrolują działalność operacyjną transportu, koordynują towarowy i pasażerski transport kolejowy, drogowy i powietrzny oraz przygotowują sprawozdania dla kierownictwa.','obejmują: prowadzenie rejestru działalności operacyjnej i koordynację kolejowego transportu pasażerskiego i towarowego; zarządzanie rozkładem jazdy pociągów w danym okręgu komunikacyjnym i prowadzenie odpowiedniego rejestru; zarządzanie, kontrolowanie i rejestrowanie załadunku i rozładunku towarów na rampach kolejowych; koordynowanie i rejestrowanie działalności operacyjnej transportu drogowego, takiej jak: rozkłady jazdy pojazdów i grafiki kierowców, załadunek i rozładunek pojazdów i składowanie towarów tranzytowych; przyjmowanie zleceń telefonicznych na samochodowe usługi przewozowe, pasażerskie lub pasażersko-towarowe i koordynowanie ich realizacji; koordynowanie i rejestrowanie działalności operacyjnej pasażerskiego i towarowego transportu powietrznego, takiej jak: prowadzenie list pasażerów i wykazów ładunku; przygotowywanie sprawozdań dla kierownictwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',236);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4411','Pomocnicy biblioteczni','Pomocnicy biblioteczni wydają i odbierają materiały, sortują i układają na półkach książki, materiały audio i wideo, czasopisma, dzienniki, magazyny i gazety oraz udzielają ogólnych informacji.','Zadania wykonywane przez pomocników bibliotecznych zazwyczaj obejmują: wydawanie i odbieranie książek i innych materiałów bibliotecznych; odkładanie na półki książek i innych materiałów bibliotecznych; wykonywanie zadań biurowych, takich jak: tradycyjna lub elektroniczna ewidencja, obróbka tekstu oraz pisanie na maszynie lub komputerze; prowadzenie prenumeraty prasowej; pomaganie użytkownikom biblioteki w uzyskaniu dostępu do podstawowych materiałów bibliotecznych oraz w wypożyczaniu międzybibliotecznym; prowadzenie rejestru biblioteki w odniesieniu do nabytych, wydanych i zwróconych książek i innych materiałów; uczestniczenie w skontrum zbiorów bibliotecznych w celu ustalenia ich aktualnego stanu i zgodności z ewidencją inwentarzową; wykonywanie innych zadań pokrewnych.',237);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4412','Listonosze i pokrewni','Listonosze i pokrewni zajmują się sortowaniem, rejestrowaniem, dostarczaniem przesyłek z urzędów pocztowych i innych podobnych organizacji, jak również wewnątrz przedsiębiorstwa oraz wykonują inne obowiązki związane z usługami pocztowymi.','Zadania wykonywane przez listonoszy i pokrewnych pracowników zazwyczaj obejmują: gromadzenie i przygotowywanie przesyłek pocztowych do wyekspediowania, załadunek i rozładunek przesyłek w urzędach pocztowych lub prywatnych przedsiębiorstwach kurierskich; sortowanie i doręczanie przesyłek do domów prywatnych i przedsiębiorstw oraz doręczanie zawiadomień o przesyłkach; przyjmowanie w rejonie przesyłek pocztowych do realizacji i na życzenie klienta udostępnianie potwierdzenia dostarczenia przesyłki; sortowanie i prowadzenie prostych rejestrów korespondencji przychodzącej i wychodzącej oraz wysyłanie poczty wychodzącej w różnych przedsiębiorstwach; wykonywanie innych zadań pokrewnych.',238);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4413','Kodowacze, korektorzy i pokrewni','Kodowacze, korektorzy i pokrewni zamieniają informacje na kody, dokonują weryfikacji i korekty tekstów oraz wykonują szereg innych zadań biurowych.','Zadania wykonywane przez kodowaczy, korektorów i pokrewnych pracowników zazwyczaj obejmują: kodowanie informacji i klasyfikowanie informacji według kodów pod kątem przetwarzania danych; porównywanie przygotowanych do druku materiałów z oryginałami, dokonywanie koniecznych korekt tekstu, zgodnie z ustalonymi zasadami; sortowanie matryc i oznakowanie ich numerami identyfikacyjnymi; sortowanie dokumentów lub sprawdzanie porządku stron w zbiorze; ręczne adresowanie okólników i kopert; wykonywanie innych zadań pokrewnych.',239);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4414','Technicy archiwiści i pokrewni','Technicy archiwiści i pokrewni zajmują się przejmowaniem, przechowywaniem, zabezpieczaniem, ewidencjonowaniem, porządkowaniem i udostępnianiem dokumentacji powstającej w toku działalności jednostek organizacyjnych.','Zadania wykonywane przez techników archiwistów i pokrewnych pracowników zazwyczaj obejmują: prowadzenie archiwum zakładowego zgodnie z instrukcją archiwalną; przejmowanie z poszczególnych komórek jednostki organizacyjnej teczek spraw przygotowanych na podstawie jednolitego rzeczowego wykazu akt; zabezpieczanie i przechowywanie akt oraz prowadzenie ewidencji archiwalnej; brakowanie dokumentacji niearchiwalnej po upływie czasokresu jej przechowywania w archiwum zakładowym; przekazywanie materiałów archiwalnych (dokumentów przechowywanych wieczyście) do archiwum państwowego; wyszukiwanie akt i ich udostępnianie; wydawanie odpisów i kopii przechowywanych materiałów oraz wystawianie zaświadczeń na ich podstawie; skanowanie dokumentów papierowych i umieszczanie ich w archiwum elektronicznym; obsługiwanie komputerowego systemu informacji archiwalnej; uczestniczenie, pod kierunkiem doświadczonego archiwisty, w pracach związanych z przejmowaniem, przechowywaniem, porządkowaniem, zabezpieczaniem i udostępnianiem materiałów archiwalnych archiwów państwowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',240);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4415','Pracownicy działów kadr','Pracownicy działów kadr prowadzą i aktualizują rejestr pracowników, w tym informacje dotyczące przeniesień, awansów, oceny, urlopów, wynagrodzenia, kwalifikacji i szkoleń, oraz prowadzą sprawy związane z działalnością socjalną w jednostce organizacyjnej.','Zadania wykonywane przez pracowników działów kadr zazwyczaj obejmują: prowadzenie rejestru pracowników zatrudnionych, zwolnionych, kompletowanie dokumentów osobowych i zakładanie akt osobowych; aktualizowanie informacji dotyczących historii zatrudnienia, wynagrodzenia, oceny pracownika, kwalifikacji i szkolenia oraz wykorzystanych i niewykorzystanych urlopów; sporządzanie umów o pracę i angaży; prowadzenie ewidencji czasu pracy pracowników; udział w czynnościach związanych z organizacją i przebiegiem okresowej oceny pracowników; wystawianie pracownikom skierowań na badania wstępne, okresowe i kontrolne; monitorowanie stażu pracy zatrudnionych pod kątem uprawnień do nagrody jubileuszowej i sporządzanie wniosków o ich wypłatę; przyjmowanie wniosków urlopowych i prowadzenie ewidencji urlopów wypoczynkowych, bezpłatnych, szkoleniowych, wychowawczych; przygotowywanie i kompletowanie materiałów, opisów stanowisk oraz sporządzanie ogłoszeń dotyczących naboru na wolne stanowiska; uczestnictwo w pracach komisji rekrutacyjnej, powiadamianie zainteresowanych o wynikach rekrutacji; współpracę z komórką do spraw płac w zakresie informacji o przysługujących pracownikom wynagrodzeniach i innych świadczeniach; sporządzanie zaświadczeń o zatrudnieniu i wynagrodzeniu oraz legitymacji ubezpieczeniowych; wystawianie świadectw pracy; przygotowywanie wniosków emerytalno-rentowych pracowników; prowadzenie bieżącej ewidencji komputerowej danych osobowych; przygotowywanie sprawozdań statystycznych dotyczących zatrudnienia; prowadzenie spraw związanych z działalnością socjalną na rzecz pracowników, emerytów i rencistów oraz członków ich rodzin; prowadzenie dokumentacji funduszu socjalnego; wykonywanie innych zadań pokrewnych.',241);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('4419','Pracownicy obsługi biura gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje pracowników obsługi biura niesklasyfikowanych w innych podgrupach grupy 4 Pracownicy biurowi. Grupa ta obejmuje na przykład asystentów do spraw wydawniczych, pracowników biur ogłoszeń, asystentów w biurach prasowych.','Zadania wykonywane przez pracowników obsługi biura gdzie indziej niesklasyfikowanych zazwyczaj obejmują: współdziałanie z komórkami wydawnictwa i kooperantami oraz monitorowanie procesu wydawniczego książek, publikacji prasowych i mediów elektronicznych; prezentowanie produktów wydawniczych na targach, wystawach, kongresach; organizowanie i koordynowanie procesu dystrybucji; przygotowywanie projektów umów wydawniczych o dzieło: z autorami, tłumaczami tekstów oraz autorami ilustracji i zdjęć; przyjmowanie zgłoszeń klientów o umieszczenie w publikacjach prasowych reklamy lub ogłoszenia, obliczanie ceny usługi i wystawianie rachunków; pomoc w przygotowaniu materiałów reklamowych, katalogów i innych materiałów przeznaczonych do publikacji; czytanie prasy i innych publikacji w celu znalezienia informacji interesującej pracowników danej instytucji lub klientów; wykonywanie innych zadań pokrewnych.',242);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5111','Stewardzi','Stewardzi dbają o komfort i bezpieczeństwo pasażerów, serwują posiłki i napoje oraz świadczą inne usługi pokrewne, zwykle na pokładzie samolotu lub statku.','Zadania wykonywane przez stewardów zazwyczaj obejmują: witanie pasażerów wchodzących na pokład samolotu lub statku, kontrolowanie biletów i kart pokładowych, wskazywanie miejsc; ogłaszanie, wyjaśnianie i prezentowanie przepisów bezpieczeństwa i procedur awaryjnych, takich jak: użycie masek tlenowych, pasów bezpieczeństwa i kamizelek ratunkowych; przygotowywanie i serwowanie wcześniej ugotowanych posiłków i napojów; prowadzenie sprzedaży bezcłowej; dbanie o ogólne potrzeby i komfort pasażerów, udzielanie informacji, pilnowanie porządku w kabinach; udzielanie instrukcji i pomocy pasażerom oraz przestrzeganie wyznaczonych procedur w przypadku zagrożenia, np. podczas ewakuacji samolotu po lądowaniu awaryjnym; sprawdzanie stanu i funkcjonowania apteczek pierwszej pomocy i wyposażenia awaryjnego; udzielanie pierwszej pomocy pasażerom w potrzebie; uczestniczenie w odprawach dotyczących warunków pogodowych, wysokości lotu, trasy, procedur awaryjnych, koordynacji pracy załogi, czasu lotu, posiłków i napojów, które będą serwowane oraz liczby pasażerów; przygotowywanie pasażerów i samolotu do startu lub lądowania; określenie szczególnych potrzeb pasażerów, takich jak: małe dzieci, osoby starsze lub niepełnosprawne; wykonują inne zadania pokrewne.',243);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5112','Konduktorzy i pokrewni','Konduktorzy i pokrewni kontrolują i wydają bilety oraz dbają o bezpieczeństwo i komfort pasażerów pociągów, tramwajów, autobusów i innych publicznych środków komunikacji.','Zadania wykonywane przez konduktorów i pokrewnych pracowników zazwyczaj obejmują: kontrolowanie i wydawanie biletów lub sprawdzanie ważności poprzednio wydanych biletów; obsługę pasażerów podróżujących wagonami specjalnymi, kuszetkami i sypialnymi; udzielanie pomocy w trakcie wchodzenia do pojazdu, znajdowania miejsca, umieszczania bagażu, szczególnie w przypadku osób starszych, chorych lub niepełnosprawnych; otwieranie i zamykanie drzwi dla pasażerów; sprawdzanie stanu pojazdu przed rozpoczęciem podróży; sygnalizowanie prowadzącemu pojazd potrzeby wstrzymania lub wznowienia podróży; reagowanie na prośby i skargi pasażerów, udzielanie informacji na temat trasy, przystanków i czasu trwania podróży lub możliwości kontynuowania podróży innymi środkami transportu; nadzorowanie przestrzegania zasad bezpieczeństwa; podejmowanie odpowiednich działań w razie zagrożenia lub wypadku; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników',244);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5113','Przewodnicy turystyczni i piloci wycieczek','Przewodnicy turystyczni i piloci wycieczek towarzyszą osobom indywidualnym lub grupom w czasie wizyt i wycieczek turystycznych w miejscach zainteresowania, takich jak: miejsca o znaczeniu historycznym, zakłady przemysłowe czy parki rozrywki. Opisują oni miejsca będące przedmiotem zainteresowania i udzielają dodatkowych informacji.','Zadania wykonywane przez przewodników turystycznych i pilotów wycieczek zazwyczaj obejmują: towarzyszenie turystom i udzielanie informacji podczas rejsów i wycieczek krajoznawczych; towarzyszenie osobom odwiedzającym takie miejsca, jak muzea, wystawy, parki rozrywki, zakłady przemysłowe; opisywanie i udzielanie informacji dotyczących przedmiotów zainteresowania lub eksponatów i udzielanie odpowiedzi na pytania zwiedzających; organizowanie zajęć edukacyjnych dla dzieci w wieku szkolnym; nadzorowanie przestrzegania przez zwiedzających odpowiednich przepisów i wymogów bezpieczeństwa; witanie i rejestrowanie gości i uczestników wycieczek, wydawanie niezbędnych plakietek identyfikacyjnych i wyposażenia zabezpieczającego; rozdawanie broszur, prowadzenie prezentacji audiowizualnych, objaśnianie procedur i zasad funkcjonowania w miejscach docelowych wycieczek; dbanie o bezpieczeństwo grup i wykonywanie takich czynności, jak udzielanie pierwszej pomocy lub prowadzenie ewakuacji w razie zagrożenia; rozwiązywanie wszelkich problemów dotyczących trasy wycieczki, obsługi lub zakwaterowania; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',245);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5120','Kucharze','Kucharze planują, przygotowują i gotują posiłki w hotelach, restauracjach i innych lokalach gastronomicznych, na pokładzie statków, w pociągach pasażerskich oraz w prywatnych gospodarstwach domowych.','Zadania wykonywane przez kucharzy zazwyczaj obejmują: planowanie posiłków, przygotowywanie i gotowanie potraw; planowanie, nadzorowanie i koordynowanie pracy w kuchni; kontrolowanie jakości żywności; ważenie, mierzenie i mieszanie składników zgodnie z przepisami lub własnym uznaniem; użytkowanie urządzeń i sprzętu kuchennego, regulowanie temperatury piekarników, rusztów, opiekaczy i innych sprzętów służących do gotowania; kontrolowanie i sprzątanie kuchni, wyposażenia kuchennego, miejsc przeznaczonych dla gości, itp. w celu zapewnienia odpowiedniego poziomu bezpieczeństwa i higieny; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',246);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5131','Kelnerzy','Kelnerzy serwują posiłki i napoje w lokalach gastronomicznych, klubach, instytucjach i stołówkach, na pokładzie statków i w pociągach pasażerskich.','Zadania wykonywane przez kelnerów zazwyczaj obejmują: zastawianie stołu czystymi obrusami, sztućcami, naczyniami i szkłem; witanie klientów, przynoszenie menu i karty napojów; doradzanie klientom przy wyborze posiłków i napojów; przyjmowanie zamówień na posiłki i napoje oraz przekazywanie ich do kuchni lub do baru; serwowanie posiłków i napojów klientom do stołów; sprzątanie stołów; wystawianie rachunków, przyjmowanie płatności, obsługiwanie urządzeń płatniczych i kasy fiskalnej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',247);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5132','Barmani','Barmani przygotowują, mieszają i serwują napoje alkoholowe i bezalkoholowe klientom przebywającym bezpośrednio przy barze lub za pośrednictwem kelnerów.','zamówień na napoje od innych pracowników lub bezpośrednio od klientów; przygotowanie i serwowanie w barze napojów alkoholowych lub bezalkoholowych; mieszanie składników w celu przygotowania koktajli i innych napojów; obsługę urządzenia do serwowania piwa z kegów, wymianę pustych kegów na nowe; serwowanie klientom przekąsek i innych artykułów spożywczych; przyjmowanie płatności za sprzedane produkty, obsługiwanie kasy fiskalnej i wydawanie paragonów; mycie brudnych szklanek i kieliszków i utrzymywanie baru w porządku, w tym ekspresów do przygotowywania kawy i herbaty; pomoc w utrzymaniu odpowiedniego wyposażenia baru, ustawianie butelek i kieliszków; potwierdzanie pełnoletniości klientów poprzez sprawdzenie dowodu tożsamości przed serwowaniem alkoholu; podejmowanie kroków w celu ograniczenia problemów związanych z nadmiernym piciem alkoholu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',248);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5141','Fryzjerzy','Fryzjerzy strzygą, stylizują, farbują, prostują lub ondulują włosy, golą lub przystrzygają zarost oraz leczą dolegliwości skóry głowy oraz wykonują peruki.','Zadania wykonywane przez fryzjerów zazwyczaj obejmują: strzyżenie, mycie, farbowanie, kręcenie włosów i modelowanie fryzury; golenie i przystrzyganie brody lub wąsów; leczenie dolegliwości skóry głowy; udzielanie porad dotyczących pielęgnacji włosów, produktów kosmetycznych i fryzur; stylizowanie włosów w formie dredów i warkoczy oraz przedłużanie włosów; przygotowywanie i dopasowywanie peruk do oczekiwań klientów oraz wykonywanie peruk do charakteryzacji aktorów; umawianie się na spotkanie i przyjmowanie płatności; sprzątanie miejsca pracy i dezynfekowanie przyrządów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',249);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5142','Kosmetyczki i pokrewni','Kosmetyczki i pokrewni pracownicy zajmują się wykonywaniem zabiegów pielęgnacyjnych twarzy i ciała, stosowaniem kosmetyków i wykonywaniem makijażu oraz innymi zabiegami upiększającymi, a także zajmują się charakteryzują aktorów lub doradzają klientom w zakresie stylu i kolorystyki ubierania się, makijażu i fryzury.','Zadania wykonywane przez kosmetyczki i pokrewnych pracowników zazwyczaj obejmują: oczyszczanie skóry twarzy i ciała i stosowanie kremów, balsamów lub produktów pokrewnych; wykonywanie masażu twarzy i ciała; wykonywanie makijażu klientkom salonów piękności lub aktorom i innym artystom; wykonywanie charakteryzacji aktorów; oczyszczanie, kształtowanie i polerowanie paznokci u rąk i stóp oraz leczenie drobnych dolegliwości stóp, takich jak: odciski, modzele i zdeformowania paznokci u stóp; obsługiwanie klientów biorących kąpiel pielęgnacyjną i wykonywanie podstawowego masażu; usuwanie zbędnego owłosienia ciała za pomocą wosku, depilacji lub innych sposobów; doradzają klientom w zakresie stylu i kolorystyki ubierania się, makijażu i fryzury; umawianie się na spotkanie i przyjmowanie płatności; wykonywanie zabiegów z zachowaniem zasad higieny i bezpieczeństwa pracy; mycie i wyjaławianie narzędzi oraz konserwowanie aparatów zabiegowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',250);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5151','Pracownicy obsługi technicznej biur, hoteli i innych obiektów','Pracownicy obsługi technicznej biur, hoteli i innych obiektów organizują i nadzorują prace związane z utrzymaniem porządku i czystości w pomieszczeniach hoteli, biur i innych obiektów oraz zajmują się ich wyposażaniem w sprzęt, meble, pościel, materiały biurowe, środki czystości i inne niezbędne materiały.','Zadania wykonywane przez pracowników obsługi technicznej biur, hoteli i innych obiektów zazwyczaj obejmują: szkolenie, organizowanie i nadzorowanie pracy pracowników obsługi pokojowej i innego personelu sprzątającego; organizowanie i nadzorowanie zakupów w celu zaopatrzenia hoteli, biur, statków i innych obiektów w sprzęt, meble, pościel i podobne wyposażenie oraz w materiały biurowe, środki czystości i inne niezbędne materiały; prowadzenie zaopatrzenia stołówek szkolnych i podobnych w artykuły żywnościowe i gospodarcze oraz kierowanie żywieniem; nadzorowanie przechowywania i wydawanie materiałów; czuwanie nad bezpieczeństwem, stanem sanitarnym, estetyką i prawidłowym wyposażeniem pokoi hotelowych; przygotowywanie lub nadzór nad przygotowaniem sal na konferencje; prowadzenie wewnętrznej ewidencji majątku, udział w pracach komisji inwentaryzacyjnych i likwidacyjnych; wykonywanie innych zadań pokrewnych.',251);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5152','Pracownicy usług domowych','Pracownicy usług domowych organizują, nadzorują i wykonują prace związane z utrzymaniem prywatnych gospodarstw domowych, w tym małych podmiotów gospodarczych świadczących usługi hotelowe, gastronomiczne i agroturystyczne z pomocą dodatkowych pracowników lub bez.','Zadania wykonywane przez pracowników usług domowych zazwyczaj obejmują: nadzorowanie pracowników zatrudnionych w domach; kupowanie lub nadzorowanie zaopatrzenia; nadzorowanie przechowywania towarów i stanu zapasów; udzielanie pomocy w przypadkach drobnych urazów lub chorób w formie takich czynności, jak: mierzenie temperatury, podawanie lekarstw, zakładanie opatrunków; zamiatanie lub odkurzanie, mycie i polerowanie podłogi, mebli i elementów wyposażenia; ścielenie łóżek, czyszczenie łazienek, wymianę ręczników, mydła itp.; opiekę nad domowymi zwierzętami i roślinami, przyjmowanie gości, odbieranie telefonów i przekazywanie wiadomości; przygotowywanie i gotowanie posiłków, zastawanie i sprzątanie stołów oraz serwowanie posiłków i napojów; sprzątanie kuchni i ogólna pomoc w pracach kuchennych, w tym w zmywaniu naczyń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',252);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5153','Gospodarze budynków','Gospodarze budynków zajmują się budynkami mieszkalnymi, hotelowymi, biurowymi, kościołami oraz innymi obiektami wraz z przyległymi terenami, utrzymując je w czystości i należytym stanie. Mogą oni nadzorować pracę innych pracowników lub wykonawców w zależności od rozmiaru i charakteru budynku.','Zadania wykonywane przez gospodarzy budynków zazwyczaj obejmują: nadzorowanie pracowników sprzątających, pracowników gospodarczych i konserwatorów; wykonywanie lub uczestniczenie w pracach porządkowych, prostych naprawach i utrzymaniu wnętrza budynku w odpowiednim stanie; sprzątanie terenu wokół budynków; obsługę pieców i bojlerów w celu zapewnienia ogrzewania i gorącej wody; wpływanie na zachowanie lokatorów i gości, np. w zakresie hałaśliwego zachowania lub niewłaściwego użytkowania budynku; dostarczanie lokatorom i użytkownikom budynków istotnych dla nich informacji lub zamieszczanie informacji na tablicach ogłoszeń; świadczenie drobnych usług dla nieobecnych lokatorów, takich jak odbieranie przesyłek w ich imieniu lub odbieraniu telefonów i udzielaniu informacji; powiadamianie zarządców lub właścicieli budynków o niezbędnych pracach remontowych; patrolowanie budynków i zapewnianie bezpieczeństwa; wypełnianie formularzy rejestracyjnych i udostępnianie lokatorom egzemplarzy przepisów obowiązujących w budynku; wykonywanie innych zadań pokrewnych.',253);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5161','Astrolodzy, wróżbici i pokrewni','Astrolodzy, wróżbici i pokrewni badają wpływ poszczególnych planet układu słonecznego i gwiazd stałych na środowisko ziemskie, opowiadają o wydarzeniach z przeszłości i przewidują przyszłe zdarzenia w życiu osobistym, korzystając z metod astrologicznych, w oparciu o cechy charakterystyczne dłoni klienta, wylosowane karty lub inne techniki oraz ostrzegają i dają rady dotyczące przyszłości.','Zadania wykonywane przez astrologów, wróżbitów i osoby praktykujące pokrewne techniki zazwyczaj obejmują: studiowanie wpływu konstelacji gwiazd i innych zjawisk na życie ludzkie; sporządzanie horoskopu urodzeniowego, stanowiącego psychofizyczny portret człowieka w celu samopoznania, określenia pola możliwości i ukierunkowania w życiu; opracowywanie prognozy w zakresie wybranych spraw, problemów, predyspozycji i zdarzeń odnoszących się do jednostki bądź ogółu, danej chwili, roku lub wieku; przewidywanie przyszłych zdarzeń poprzez interpretację cech charakterystycznych dłoni lub układu kart; określanie charakteru i przeznaczenia człowieka poprzez interpretację liczb wynikających z ważnych dla niego dat, imienia i innych danych; określanie korzystnego czasu dla różnych ludzkich wydarzeń, takich jak: zawarcie małżeństwa, wyruszenie w podróż, zawarcie transakcji, objęcie stanowiska; udzielanie porad dotyczących zaginionych osób lub rzeczy; przestrzeganie i doradzanie w zakresie możliwego rozwoju wydarzeń; doradzanie klientom różnych środków ostrożności celem uniknięcia nieszczęśliwych przypadków, itp.',254);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5162','Osoby do towarzystwa','Osoby do towarzystwa zapewniają pracodawcy towarzystwo i spełniają jego różne potrzeby osobiste.','Zadania wykonywane przez osoby do towarzystwa zazwyczaj obejmują: towarzyszenie pracodawcy w różnych miejscach, czytanie, prowadzenie konwersacji i uczestniczenie w zajęciach sportowych; pomaganie w zapewnianiu rozrywki gościom pracodawcy; utrzymywanie w dobrym stanie garderoby i innych przedmiotów osobistych pracodawcy; wykonywanie innych zadań pokrewnych.',255);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5163','Pracownicy zakładów pogrzebowych','Pracownicy zakładów pogrzebowych zajmują się organizacją uroczystości pogrzebowych oraz wykonują różne zadania związane z przygotowaniem ciała zmarłych do pogrzebu, kremacją zwłok i grzebaniem zmarłych.','Zadania wykonywane przez pracowników zakładów pogrzebowych zazwyczaj obejmują: organizowanie i prowadzanie uroczystości pogrzebowych, kremacji i pochówku; prowadzenie konsultacji z rodziną lub organizatorem pogrzebu dotyczących przygotowania nekrologu, wyboru trumny lub urny, zamawiania wieńców i wiązanek, ustalenia miejsca i daty ceremonii pogrzebu lub kremacji oraz innych spraw związanych z pogrzebem; wykonywanie kosmetyki pośmiertnej, ubieranie ciał zmarłych i umieszczanie ich w trumnach; wykonywanie rekonstrukcji powypadkowej zniekształconych lub okaleczonych części ciała; balsamowanie ciał zmarłych w celu opóźnienia lub wstrzymania procesu rozkładu; zapewnienie zgodności z wymogami sanitarnymi oraz przepisami prawnymi dotyczącymi balsamowania; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',256);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5164','Opiekunowie zwierząt domowych i pracownicy zajmujący się zwierzętami','Opiekunowie zwierząt domowych i pracownicy zajmujący się zwierzętami opiekują się zwierzętami w schroniskach, hotelach dla zwierząt, ośrodkach jeździeckich i podobnych obiektach, cyrkach, ogrodach zoologicznych lub sklepach zoologicznych; karmią zwierzęta i je pielęgnują, zajmują się tresurą zwierząt, trenowaniem koni wyścigowych, wykonują zabiegi fizjoterapeutyczne i pielęgnacyjne; pomagają przy zabiegach lekarzom lub technikom weterynarii oraz pomagają w tworzeniu właściwych relacji pomiędzy zwierzęciem towarzyszącym i jego właścicielem.','Zadania wykonywane przez opiekunów zwierząt domowych i pracowników zajmujących się zwierzętami zazwyczaj obejmują: pielęgnowanie zwierząt, np. poprzez wykonywanie takich czynności, jak mycie, czesanie, upinanie i przycinanie sierści, obcinanie pazurów i czyszczenie uszu; karmienie zwierząt oraz czyszczenie, porządkowanie i dezynfekowanie pomieszczeń, w których są trzymane; opiekę nad zwierzętami domowymi podczas nieobecności właściciela, w domu właściciela lub w domu opiekuna; opiekę nad porzuconymi zwierzętami w schroniskach; opiekę nad zwierzętami dzikimi w cyrkach, ogrodach zoologicznych i rezerwatach; tresowanie zwierząt w celu wykształcenia i podtrzymania żądanego zachowania, umiejętności i nawyków, w kontekście konkursów, rozrywki, posłuszeństwa, bezpieczeństwa, itp.; trenowanie koni wyścigowych; wykonywanie zabiegów fizjoterapeutycznych, takich jak: masaż, kinezyterapia, fizykoterapia w celu poprawy stanu zdrowia, kondycji i samopoczucia zwierząt towarzyszących; gromadzenie i zapisywanie informacji o zwierzętach, jak np.: wagi, rozmiaru, kondycji fizycznej oraz o wykonanych zabiegach, podanych lekach i pokarmie; prowadzenie lub przenoszenie zwierząt do sal zabiegowych oraz trzymanie ich podczas zabiegów; pomaganie właścicielowi zwierzęcia towarzyszącego tworzyć satysfakcjonującą dla obu stron relację pomiędzy nim a zwierzęciem, usunąć lub zredukować niepożądane, niepokojące zachowania zwierzęcia, na które skarży się właściciel; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',257);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5165','Instruktorzy nauki jazdy','Instruktorzy nauki jazdy prowadzą naukę kierowania pojazdami samochodowymi i tramwajami.','Zadania wykonywane przez instruktorów nauki jazdy zazwyczaj obejmują: nauczanie w zakresie przepisów ruchu drogowego, zasad budowy i eksploatacji pojazdów oraz udzielania pierwszej pomocy w nagłych wypadkach; nauczanie sztuki prowadzenia pojazdu i bezpiecznej jazdy; objaśnianie i demonstrowanie działania i sposobu prowadzenia pojazdów za pomocą diagramów i materiałów audiowizualnych; ocenę postępów kursantów w nabywaniu umiejętności kierowania pojazdami i doradzanie im w kwestii gotowości przystąpienia do egzaminu; doradzanie i nauczanie bardziej złożonych technik prowadzenia pojazdu w sytuacjach awaryjnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',258);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5169','Pracownicy usług osobistych gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje pracowników usług osobistych niesklasyfikowanych w innych podgrupach grupy 51 Pracownicy usług osobistych. Grupa ta obejmuje na przykład osoby świadczące usługi w zakresie radiestezji, lub które oferują swoje usługi i towarzystwo w różnych imprezach.','Zadania wykonywane przez pracowników usług osobistych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: wykonywanie usług radiestezyjnych, jak np.: układanie w mieszkaniach i strojenie odpromienników w celu ochrony organizmów żywych przed szkodliwymi rodzajami promieniowania otoczenia, wskazywanie miejsc nienapromieniowanych pod lokalizację domów i obiektów publicznych, wskazywanie miejsc bicia studzien; towarzyszenie klientom w wyjściach do restauracji lub w innych celach; towarzyszenie klientom jako partner do tańca; witanie gości w klubie nocnym i zapewnianie im odpowiedniej rozrywki; wykonywanie innych zadań pokrewnych.',259);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5211','Sprzedawcy na targowiskach i bazarach','Sprzedawcy na targowiskach i bazarach sprzedają różne towary na stoiskach, rynkach i innych przestrzeniach otwartych lub prowadzą sprzedaż obwoźną z samochodu.','Zadania wykonywane przez sprzedawców na targowiskach i bazarach zazwyczaj obejmują: pozyskiwanie pozwolenia na rozstawienie stoiska w określonym miejscu na ulicy, targu lub innej przestrzeni; podejmowanie decyzji dotyczących asortymentu produktów, zaopatrzenia i ceny towarów przeznaczonych na sprzedaż; nabywanie zaopatrzenia lub zlecanie regularnego zaopatrzenia towarów od sprzedawców hurtowych lub bezpośrednio od producentów; rozstawianie i składanie stoisk, transportowanie, przechowywanie, rozładowywanie i pakowanie towarów przeznaczonych na sprzedaż; przyjmowanie, bezpośrednio lub telefonicznie, zamówień na towary od nabywców indywidualnych lub prowadzących sklepy i dostarczanie im towarów samochodem; prezentowanie i sprzedaż towarów, przyjmowanie płatności; ustawianie i eksponowanie towarów przeznaczonych na sprzedaż, pakowanie sprzedanych towarów; prowadzenie rejestru poziomu zapasów i transakcji finansowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',260);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5212','Uliczni sprzedawcy żywności','Uliczni sprzedawcy żywności przygotowują i sprzedają, lub sprzedają wcześniej przygotowane, gorące i zimne posiłki oraz napoje przeznaczone do bezpośredniej konsumpcji na ulicy lub w miejscach publicznych, takich jak: dworce, kina, teatry.','Zadania wykonywane przez ulicznych sprzedawców żywności zazwyczaj obejmują: pozyskiwanie pozwolenia, jeśli jest ono wymagane, na sprzedaż żywności i napojów na ulicy lub w miejscu publicznym; organizowanie zaopatrzenia w napoje i żywność do późniejszej sprzedaży; przygotowywanie, wcześniej lub na miejscu, żywności i napojów przeznaczonych do sprzedaży; załadowywanie i rozładowywanie żywności i napojów, ich przewożenie lub przenoszenie do miejsca, gdzie będzie odbywać się sprzedaż, tj. na ulicę lub do miejsc publicznych, takich jak: dworce, kina, teatry, itp.; eksponowanie i sprzedaż żywności i napojów oraz przyjmowanie płatności; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',261);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5221','Właściciele sklepów','Właściciele sklepów prowadzą małe sklepy, samodzielnie lub z pomocą ograniczonej liczby pracowników.','Zadania wykonywane przez właścicieli sklepów zazwyczaj obejmują: podejmowanie decyzji dotyczących asortymentu produktów, zaopatrzenia i ceny sprzedawanych towarów; uzupełnianie zaopatrzenia poprzez zakup produktów na targach, od sprzedawców hurtowych i innych dostawców; określanie cen i eksponowanie towarów przeznaczonych na sprzedaż; sprzedaż towarów klientom i doradzanie im w zakresie ich użytkowania; prowadzenie rejestru poziomu zapasów i transakcji finansowych; rozpatrywanie reklamacji zakupionych towarów i podejmowanie decyzji o dalszym działaniu; przeprowadzanie inwentaryzacji towarów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',262);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5222','Kierownicy sprzedaży w marketach','Kierownicy sprzedaży w marketach nadzorują i koordynują pracę ekspedientów, kasjerów i innych pracowników dużych sklepów detalicznych i hurtowych, takich jak: supermarkety i domy towarowe.','Zadania wykonywane przez kierowników sprzedaży w marketach zazwyczaj obejmują: planowanie i przygotowywanie grafików pracy oraz przydzielanie pracownikom określonych obowiązków; objaśnianie pracownikom procedur dotyczących sprzedaży, w tym zasad postępowania w trudnych i skomplikowanych przypadkach; zapewnianie sprawnej obsługi klientów; uczestniczenie i doradzanie w trakcie rekrutacji, zatrudniania, szkolenia, oceny, awansowania oraz zwalniania pracowników oraz w rozpatrywaniu ich zażaleń; rozpatrywanie reklamacji zakupionych towarów i podejmowanie stosownych działań; przeprowadzanie inwentaryzacji towarów wystawionych do sprzedaży i ich zapasów; zapewnianie właściwych cen towarów i usług oraz odpowiedniego eksponowania towarów; nadzorowanie przestrzegania zasad bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',263);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5223','Sprzedawcy sklepowi (ekspedienci)','Sprzedawcy sklepowi (ekspedienci) sprzedają różne towary i usługi bezpośrednio odbiorcom lub w detalicznych i hurtowych przedsiębiorstwach oraz objaśniają funkcje i cechy tych towarów i usług.','Zadania wykonywane przez sprzedawców sklepowych (ekspedientów)zazwyczaj obejmują: określanie oczekiwań klientów i przedstawianie oferty produktów, cen, możliwości dostawy, zasad gwarancji oraz użytkowania produktu; objaśnianie i prezentowanie towarów i usług klientom; sprzedawanie towarów i usług, przyjmowanie różnych form płatności, wystawianie rachunków lub faktur i rejestrowanie sprzedaży przy zastosowaniu kasy fiskalnej; uczestniczenie w bieżącym zarządzaniu zaopatrzeniem i inwentaryzacji; ustawianie i eksponowanie towarów przeznaczonych na sprzedaż, pakowanie sprzedanych towarów.',264);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5230','Kasjerzy i sprzedawcy biletów','Kasjerzy i sprzedawcy biletów obsługują kasy fiskalne, skanery optyczne ceny, komputery i inne elementy wyposażenia służące do rejestrowania sprzedaży i przyjmowania płatności za zakup towarów i usług lub wstęp w takich miejscach jak: sklepy, restauracje czy kasy biletowe, a także wykonują czynności związane z przyjmowaniem i wypłatą wartości pieniężnych w przedsiębiorstwie.','Zadania wykonywane przez kasjerów i sprzedawców biletów zazwyczaj obejmują: przyjmowanie i weryfikację płatności gotówką, czekiem, kartą kredytową lub debetową w sklepach, kasach biletowych itp.; wydawanie reszty i paragonu; wydawanie biletów wstępu na wydarzenia sportowe i kulturalne, biletów komunikacji kolejowej lub miejskiej; obsługę kasy fiskalnej w celu obliczenia należności klienta lub należnej reszty; skanowanie, ważenie i rejestrowanie cen towarów; liczenie i rejestrowanie otrzymanej lub wypłaconej gotówki i porównywanie tych sum z zarejestrowanymi transakcjami; przyjmowanie gotówki, porównywanie jej z wystawionymi rachunkami, przygotowanie wpłaty do banku; dokonywanie wpłat i wypłat gotówki w przedsiębiorstwie, np. z tytułu rozliczeń z kontrahentami lub wynagrodzeń.',265);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5241','Modelki i modele','Modelki i modele zakładają i prezentują odzież i dodatki oraz pozują do fotografii, filmów, reklam i projektów artystycznych.','Zadania wykonywane przez modelki i modeli zazwyczaj obejmują: prezentowanie ubrań z nowej lub aktualnej kolekcji lub ubrań wytypowanych przez klientów; demonstrowanie stylu i charakterystycznych elementów ubrań, dodatków i innych towarów; pozowanie do rzeźb, obrazów i innych typów sztuki wizualnej; pozowanie do fotografii w magazynach i innych mediach reklamowych; pozowanie w programach telewizyjnych, filmach i reklamach oraz innych pokrewnych produkcjach.',266);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5242','Demonstratorzy wyrobów','Demonstratorzy wyrobów demonstrują towary w lokalach handlowych, na pokazach oraz w domach prywatnych.','Zadania wykonywane przez demonstratorów wyrobów zazwyczaj obejmują: rozstawianie ekspozycji i demonstrowanie towarów przeznaczonych na sprzedaż, informowanie klientów o cechach charakterystycznych i sposobie eksploatacji, wzbudzanie zainteresowania klientów; udzielanie informacji i doradzanie w zakresie korzystania z produktów; sprzedaż towarów lub kierowanie klientów do pracowników odpowiedzialnych za sprzedaż; przyjmowanie zamówień i przygotowywanie płatności, dostawy i odbioru towarów; oferowanie próbek i dystrybucja katalogów i materiałów promocyjnych.',267);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5243','Agenci sprzedaży bezpośredniej','Agenci sprzedaży bezpośredniej sprzedają towary i usługi w imieniu przedsiębiorstwa, zazwyczaj poprzez odwiedzanie potencjalnych klientów w ich domach','Zadania wykonywane przez agentów sprzedaży bezpośredniej zazwyczaj obejmują: udzielanie szczegółowych informacji dotyczących towarów lub usług oraz warunków sprzedaży, osobiście odwiedzając potencjalnych klientów; demonstrowanie lub opisywanie oferowanych towarów lub usług; rejestrowanie zamówień i transakcji oraz składanie zamówień u dostawców; przygotowywanie faktur i umów sprzedaży oraz przyjmowanie płatności; wysyłanie listów, arkuszy informacyjnych i innych dokumentów do klientów; tworzenie list potencjalnych klientów i kontaktowanie się z nimi telefonicznie w celu zawarcia nowych transakcji; podróżowanie pomiędzy różnymi obszarami i klientami, przewożenie próbek towarów oraz towarów przeznaczonych na sprzedaż.',268);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5244','Sprzedawcy (konsultanci) w centrach sprzedaży telefonicznej/internetowej','Sprzedawcy (konsultanci) w centrach sprzedaży telefonicznej/internetowej kontaktują się z obecnymi i przyszłymi klientami za pomocą telefonu lub innych mediów elektronicznych w celu promowania i sprzedaży towarów i usług oraz organizowania wizyt handlowych. Sprzedawcy ci pracują w centrum kontaktowym z klientami lub poza jego siedzibą.','Zadania wykonywane przez sprzedawców (konsultantów) w centrach sprzedaży telefonicznej/internetowej zazwyczaj obejmują: promowanie towarów i usług przez telefon lub pocztą elektroniczną (e-mailem), zgodnie z procedurami i listą kontaktów; rozbudzanie zainteresowania towarami i usługami, dążenie do sprzedaży lub zorganizowanie spotkania z przedstawicielem handlowym; przetwarzanie zamówień i organizowanie wysyłki towarów i usług, zestawów informacyjnych i broszur; organizowanie spotkań dla przedstawicieli handlowych; sporządzanie notatek z kontaktów posprzedażowych, aktualizowanie marketingowej bazy danych klientów; przygotowywanie raportów dla kierownictwa dotyczących działalności konkurencji oraz występujących problemów; prowadzenie statystyk wykonanych telefonów, korespondencji elektronicznej oraz osiągniętych rezultatów; składanie okresowych raportów dotyczących działań telemarketingowych i ich rezultatów.',269);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5244','Sprzedawcy (konsultanci) w centrach sprzedaży telefonicznej/internetowej','Sprzedawcy (konsultanci) w centrach sprzedaży telefonicznej/internetowej kontaktują się z obecnymi i przyszłymi klientami za pomocą telefonu lub innych mediów elektronicznych w celu promowania i sprzedaży towarów i usług oraz organizowania wizyt handlowych. Sprzedawcy ci pracują w centrum kontaktowym z klientami lub poza jego siedzibą.','Zadania wykonywane przez sprzedawców (konsultantów) w centrach sprzedaży telefonicznej/internetowej zazwyczaj obejmują: promowanie towarów i usług przez telefon lub pocztą elektroniczną (e-mailem), zgodnie z procedurami i listą kontaktów; rozbudzanie zainteresowania towarami i usługami, dążenie do sprzedaży lub zorganizowanie spotkania z przedstawicielem handlowym; przetwarzanie zamówień i organizowanie wysyłki towarów i usług, zestawów informacyjnych i broszur; organizowanie spotkań dla przedstawicieli handlowych; sporządzanie notatek z kontaktów posprzedażowych, aktualizowanie marketingowej bazy danych klientów; przygotowywanie raportów dla kierownictwa dotyczących działalności konkurencji oraz występujących problemów; prowadzenie statystyk wykonanych telefonów, korespondencji elektronicznej oraz osiągniętych rezultatów; składanie okresowych raportów dotyczących działań telemarketingowych i ich rezultatów.',270);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5245','Sprzedawcy w stacji paliw','Sprzedawcy w stacji paliw przyjmują pojazdy silnikowe do przeglądu lub naprawy w stacjach obsługi pojazdów, sprzedają paliwo, smary i inne produkty samochodowe w stacjach paliw oraz świadczą usługi, takie jak: tankowanie, czyszczenie, smarowanie i wykonywanie drobnych napraw pojazdów silnikowych.','Zadania wykonywane przez sprzedawców w stacji paliw zazwyczaj obejmują: przyjmowanie pojazdu silnikowego w stacji obsługi, uzgadnianie zakresu prac oraz terminu odbioru pojazdu; wystawianie klientowi faktury za usługę oraz wydawanie mu pojazdu po uiszczeniu należności; napełnianie zbiorników paliwa i kanistrów do poziomu wskazanego przez klienta; sprawdzanie i wyrównywanie ciśnienia w oponach pojazdu oraz sprawdzanie poziomu oleju i innych płynów; mycie przedniej szyby i innych szyb pojazdu; wykonywanie drobnych napraw, takich jak: wymiana opon, żarówek, piór wycieraczek przedniej szyby, itp.; obsługiwanie i prowadzenie automatycznych myjni samochodowych; pobieranie od klientów płatności za dokonane zakupy; czyszczenie dystrybutorów paliwa i sprzątanie podjazdów, sklepów i innych obiektów; przeprowadzanie inwentaryzacji i przygotowywanie sprawozdań dotyczących ilości sprzedanego paliwa, oleju, akcesoriów i innych przedmiotów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',271);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5246','Wydawcy posiłków','Wydawcy posiłków zajmują się obsługiwaniem klientów przy bufecie i końcowymi etapami przygotowywania prostych potraw w restauracjach, kawiarniach, hotelach, barach szybkiej obsługi, stołówkach, szpitalach i w innych obiektach.','Zadania wykonywane przez wydawców posiłków zazwyczaj obejmują: serwowanie posiłków przy bufecie; pomaganie klientom w wyborze potraw i przyjmowanie zamówień; czyszczenie, obieranie i krojenie produktów spożywczych ręcznie lub z wykorzystaniem urządzeń elektrycznych; przygotowywanie prostych potraw i odgrzewanie wcześniej przygotowanych posiłków; porcjowanie i pakowanie żywności lub umieszczanie jej bezpośrednio na talerzach serwowanych klientom; pakowanie posiłków na wynos; uzupełnianie lodówek, barów sałatkowych i bufetów oraz prowadzenie dokumentacji dotyczącej ilości wykorzystywanego jedzenia; przyjmowanie płatności za zakupione produkty spożywcze.',272);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5249','Pracownicy sprzedaży i pokrewni gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje pracowników sprzedaży i pokrewnych pracowników niesklasyfikowanych w innych podgrupach grupy 52 Sprzedawcy i pokrewni. Grupa ta obejmuje na przykład bukieciarzy, doradców klienta, ekspedientów w punktach usługowych, hostessy.','Zadania wykonywane przez pracowników sprzedaży i pokrewnych pracowników gdzie indziej niesklasyfikowanych zazwyczaj obejmują: wykonywanie na zamówienie wiązanek, wieńców i innych kompozycji kwiatowych; doradzanie klientom przy nabywaniu produktów lub usług, z uwzględnieniem ich potrzeb i możliwości finansowych; przyjmowanie zleceń na wykonanie usługi, określanie kosztów, wystawianie rachunku lub faktury i przyjmowanie zapłaty; oprowadzanie gości na targach i wystawach, zajmowanie się promocją produktów w placówkach handlowych lub wykonywanie części obowiązków organizatorów bankietów, konferencji i targów, takich jak: witanie gości, wskazywanie im drogi, dbanie o ich dobre samopoczucie; wykonywanie innych zadań pokrewnych.',273);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5311','Opiekunowie dziecięcy','Opiekunowie dziecięcy sprawują opiekę i nadzór nad dziećmi w warunkach domowych lub w placówkach opiekuńczo-wychowawczych oraz zapewniają opiekę przed zajęciami szkolnymi, po nich lub podczas wakacji.','Zadania wykonywane przez opiekunów dziecięcych zazwyczaj obejmują: pomaganie dzieciom podczas mycia, ubierania i jedzenia; odprowadzanie dzieci do szkoły i odbieranie ich po zajęciach lub organizowanie im zajęć edukacyjnych, rekreacyjnych i sportowych; asystowanie w przygotowywaniu materiałów i sprzętów wykorzystywanych podczas zajęć edukacyjnych i rekreacyjnych dla dzieci; kontrolowanie zachowań dzieci i kierowanie ich rozwojem społecznym; przywoływanie dzieci do porządku i zalecanie lub inicjowanie stosowania innych środków kontroli zachowań, takich jak: dbanie o ubranie, sprzątanie zabawek i książek; obserwowanie i monitorowanie zabaw dzieci; prowadzenie dokumentacji dotyczącej każdego dziecka, w tym codziennych obserwacji i informacji o działaniach, serwowanych posiłkach i podanych lekach; wykonywanie innych zadań pokrewnych.',274);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5312','Asystenci nauczycieli','Asystenci nauczycieli zajmują się czynnościami niezwiązanymi z nauczaniem i polegającymi na asystowaniu nauczycielom, zajmują się sprawowaniem opieki i nadzoru nad dziećmi w szkołach i przedszkolach.','Zadania wykonywane przez asystentów nauczycieli zazwyczaj obejmują: prezentowanie i nadzorowanie działań, które wspierają fizyczny, społeczny, emocjonalny i intelektualny rozwój dziecka w szkole i przedszkolu oraz uczestniczenie w tych działaniach; przygotowywanie miejsc wewnątrz i na zewnątrz budynków do przeprowadzania zajęć lekcyjnych i rekreacyjnych oraz pomoc w organizacji zabaw edukacyjnych; pomaganie dzieciom z intelektualnymi, fizycznymi, behawioralnymi, językowymi i innymi problemami związanymi z nauką; pomaganie dzieciom w nabywaniu umiejętności społecznych; asystowanie w przygotowywaniu materiałów dydaktycznych oraz kopiowanie i segregowanie materiałów pisanych i drukowanych; obsługiwanie sprzętu audiowizualnego, komputerów i innych pomocy dydaktycznych; rozdawanie i zbieranie materiałów lekcyjnych; wykonywanie innych zadań pokrewnych.',275);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5321','Pomocniczy personel medyczny','Pomocniczy personel medyczny świadczy bezpośrednią opiekę i wsparcie w codziennych czynnościach pacjentom oraz osobom przebywającym w zakładach opieki zdrowotnej, takich jak szpitale, zakłady opiekuńczo – lecznicze lub pielęgnacyjno-opiekuńcze, sanatoria, hospicja itp., w oparciu o określone plany opieki oraz pod bezpośrednim nadzorem specjalistów opieki zdrowotnej.','Zadania wykonywane przez pomocniczy personel medyczny zazwyczaj obejmują: świadczenie opieki ustalonej przez lekarza i innych specjalistów opieki zdrowotnej; pomaganie podopiecznemu przy zabiegach higienicznych, ubieraniu się i rozbieraniu oraz w zaspokajaniu potrzeb związanych z odżywianiem i załatwianiem potrzeb fizjologicznych; utrzymywanie higieny i estetyki w otoczeniu chorego; sadzanie, podnoszenie i obracanie pacjentów oraz ich przenoszenie na wózki inwalidzkie lub na łóżka mobilne; pomaganie podopiecznemu utrzymać aktywność ruchową, użytkować sprzęt ortopedyczny i rehabilitacyjny; podawanie choremu leków doustnych na polecenie pielęgniarki lub lekarza; dokonywanie pomiarów podstawowych parametrów życiowych pacjenta, takich jak temperatura, tętno, ciśnienie tętnicze krwi itp.; przygotowywanie pacjenta do wybranych badań diagnostycznych i zabiegów; pomaganie podopiecznemu w adaptacji do warunków życia w szpitalu oraz zmian związanych z przewlekłą chorobą lub starością, a także w komunikowaniu się z rodziną, zespołem opiekuńczym i terapeutycznym oraz z innymi pacjentami; udzielanie wsparcia emocjonalnego osobie chorej i niesamodzielnej oraz jej rodzinie; współpracę z pielęgniarką w planowaniu i realizowaniu opieki, pomaganie pielęgniarce podczas zabiegów pielęgniarskich; obserwację stanu, reakcji i zachowania pacjenta oraz zgłaszanie zmian specjalistom z zakresu opieki zdrowotnej; wykonywanie innych zadań pokrewnych.',276);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5322','Pracownicy domowej opieki osobistej','Pracownicy domowej opieki osobistej świadczą standardową opiekę i wsparcie przy czynnościach życia codziennego osobom wymagającym takiej opieki z powodu wieku, choroby, urazu, złego stanu fizycznego lub umysłowego, w ich domach lub w niepublicznych domach opieki.','Zadania wykonywane przez pracowników domowej opieki osobistej zazwyczaj obejmują: pomoc w ramach opieki osobistej i terapeutycznej w zakresie higieny osobistej, karmienia, ubierania i poruszania się, ćwiczeń, komunikacji, przyjmowania leków doustnych oraz zmiany opatrunków, w oparciu o plany opieki ustalone przez specjalistów opieki zdrowotnej; prowadzenie rejestrów z opieki nad pacjentem, zmian jego stanu i reakcji na opiekę i leczenie oraz zgłaszanie problemów i wystawianie skierowań do specjalistów opieki zdrowotnej lub spraw socjalnych; przenoszenie i zmienianie pozycji pacjentów z trudnościami w poruszaniu się i pomoc w przenoszeniu ich na wózki inwalidzkie i do pojazdów mechanicznych; zapewnianie pacjentom i ich rodzinom wsparcia emocjonalnego i dostarczanie im informacji i porad w takich dziedzinach jak żywienie, higiena, ćwiczenia, opieka nad niemowlętami, lub też przystosowania do potrzeb niepełnosprawności lub choroby; utrzymywanie higieny w środowisku pacjenta, w tym zmianę pościeli, pranie ubrań, mycie naczyń i sprzątanie pomieszczeń mieszkalnych; zapewnianie wsparcia psychologicznego pacjentom, np. przez rozmowy lub czytanie na głos; planowanie i dokonywanie zakupów, przygotowanie lub podawanie posiłków z uwzględnieniem wymogów żywieniowych i zalecanej diety; zapewnianie wsparcia rodzicom i pomocy przy opiece nad niemowlętami w okresie poporodowym; umawianie pacjentów na wizyty u lekarzy lub innych specjalistów medycznych i towarzyszenie im w wizytach; wykonywanie innych zadań pokrewnych.',277);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5329','Pracownicy opieki osobistej w ochronie zdrowia i pokrewni gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje pracowników opieki osobistej w ochronie zdrowia i pokrewnych pracowników niesklasyfikowanych w innych podgrupach grupy 532 Pracownicy opieki osobistej w ochronie zdrowia i pokrewni. Grupa ta obejmuje na przykład takie zawody, jak: pomoc apteczna, pomoc dentystyczna, preparator medyczny, sanitariusz szpitalny, zabiegowy balneologiczny, itp.','Zadania wykonywane przez pracowników opieki osobistej w ochrony zdrowia ujętych w tej grupie zazwyczaj obejmują: czyszczenie i sterylizację instrumentów, pojemników, zlewek oraz innego sprzętu; etykietowanie leków, preparatów chemicznych oraz innych preparatów farmaceutycznych i ustawianie towarów na półkach; przenoszenie i zmienianie pozycji pacjentów i przewożenie ich na wózkach inwalidzkich lub mobilnych łóżkach; przygotowywanie pacjentów do badania lub leczenia; przygotowywanie zestawu instrumentów, przygotowywanie materiałów i asystowanie dentystom podczas zabiegów; przygotowywanie i wykonywanie zabiegów balneologicznych, jak: bicze wodne, kąpiele lecznicze mineralne i borowinowe, inhalacje, itp.; przygotowywanie i konserwowanie preparatów z tkanek ludzkich lub zwierzęcych do badań naukowych; wykonywanie innych zadań pokrewnych.',278);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5411','Strażacy','Strażacy zapobiegają, zwalczają i gaszą pożary oraz pomagają w sytuacjach kryzysowych, chronią życie i mienie oraz przeprowadzają akcje ratunkowe.','Zadania wykonywane przez strażaków zazwyczaj obejmują: reagowanie na alarmy pożarowe oraz inne wezwania, takie jak: do wypadków samochodowych i przemysłowych, ataków bombowych oraz innych sytuacji kryzysowych; kontrolę i gaszenie pożarów przy użyciu sprzętu ręcznego, mechanicznego oraz substancji chemicznych; zwalczanie specjalnych typów pożarów oraz stosowanie specjalnego sprzętu w obiektach przemysłowych; ratowanie ludzi z płonących budynków oraz miejsc wypadków oraz osób znajdujących się w zagrożeniu; zapobieganie lub ograniczanie rozprzestrzeniania się niebezpiecznych substancji w przypadku pożarów lub wypadków; informowanie społeczeństwa o działaniach przeciwpożarowych; wykonywanie innych zadań pokrewnych.',279);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5412','Strażnicy w zakładach dla nieletnich','Strażnicy w zakładach dla nieletnich zapewniają porządek i bezpieczeństwo w zakładach poprawczych oraz schroniskach dla nieletnich.','Zadania wykonywane przez strażników w zakładach dla nieletnich zazwyczaj obejmują: legitymowanie osób przybywających i opuszczających zakłady poprawcze lub schroniska dla nieletnich; zapewnianie bezpieczeństwa osobom osadzonym oraz innym osobom tam przebywającym; zabezpieczanie zakładu przed osobami niepowołanymi i strzeżenie mienia zakładu; ochranianie zakładu przed zagrożeniami wewnętrznymi i zapewnianie prawidłowego rozmieszczenia nieletnich przebywających w zakładzie; konwojowanie nieletnich osadzonych w zakładzie; współdziałanie z wyspecjalizowanymi pracownikami zakładu w zakresie wychowania i nauczania nieletnich w celu ich resocjalizacji i readaptacji społecznej; wykonywanie innych zadań pokrewnych.',280);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5413','Pracownicy ochrony osób i mienia','Pracownicy ochrony osób i mienia wykonują zadania związane z ochroną osób i obiektów, zapewnianiem spokoju i porządku w obiektach i miejscach publicznych oraz zapobieganiem przestępstwom i wykroczeniom.','Zadania wykonywane przez pracowników ochrony osób i mienia zazwyczaj obejmują: planowanie zabezpieczenia osób, obiektów i urządzeń; wykonywanie bezpośredniej ochrony życia i zdrowia powierzonych osób; ochronę mienia jednostek organizacyjnych lub osób fizycznych przed kradzieżą, rabunkiem lub dewastacją; kontrolowanie dostępu do obiektów, monitoring i zezwalanie na wejście lub wyjście pracowników i gości, wydawanie przepustek; legitymowanie osób w celu ustalenia ich tożsamości; dokonywanie kontroli osobistej, a także przeglądanie zawartości bagaży oraz sprawdzanie ładunków i pomieszczeń dla zapewnienia bezpieczeństwa ochranianych osób, obiektów i urządzeń; zapewnianie porządku w miejscach publicznych oraz wewnątrz obiektów, w których odbywają się imprezy masowe; patrolowanie wyznaczonego obszaru w celu zapobiegania przestępczości, wykroczeniom i innym zdarzeniom zakłócającym bezpieczeństwo i porządek; sprawowanie nadzoru nad przestrzeganiem przepisów o bezpieczeństwie i porządku ruchu na drogach publicznych; zatrzymywanie osób stwarzających bezpośrednie zagrożenie dla życia lub zdrowia ludzkiego oraz dla mienia, a także osób rażąco naruszających porządek publiczny; powiadamianie organów prokuratury lub policji o zdarzeniach noszących znamiona przestępstw lub wykroczeń; konwojowanie wartości pieniężnych oraz innych przedmiotów wartościowych lub niebezpiecznych; reagowanie na alarmy, badanie przyczyn zakłóceń porządku oraz, zależnie od okoliczności, nawiązywanie kontaktu z przełożonymi, policją lub strażą pożarną; informowanie odpowiednich władz, instytucji lub służb publicznych o dostrzeżonych nieprawidłowościach, stanowiących zagrożenie dla życia i zdrowia obywateli oraz mienia, a zwłaszcza o zagrożeniach bezpieczeństwa i porządku publicznego, w tym o zagrożeniach ekologicznych; montaż, eksploatację, konserwację i naprawę elektronicznych urządzeń i systemów alarmowych, które sygnalizują zagrożenia osób i mienia; montaż, konserwację i naprawę urządzeń i środków mechanicznych mających służyć ochronie oraz awaryjne otwieranie urządzeń w miejscach zainstalowania; wykonywanie innych zadań pokrewnych.',281);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('5419','Pracownicy usług ochrony gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje pracowników usług ochrony niesklasyfikowanych w innych podgrupach grupy 541 Pracownicy usług ochrony. Grupa ta obejmuje na przykład pracowników obsługi monitoringu, ratowników wodnych, morskich, górskich, pokładowych i górniczych, strażników leśnych, łowieckich i rybackich, pracowników wyłapujących bezdomne zwierzęta.','Zadania wykonywane przez pracowników usług ochrony gdzie indziej niesklasyfikowanych zazwyczaj obejmują: obsługę i obserwację kamer systemu monitoringu wizyjnego obiektów lub terenu i powiadamianie odpowiednich służb, np. policji, straży pożarnej czy pogotowia ratunkowego, w sytuacjach zaobserwowanych zagrożeń, wypadków, zdarzeń czy też klęsk żywiołowych; monitorowanie plaż i basenów w celu zapobiegania wypadkom i ratowania kąpiących się przed utonięciem; uczestniczenie w akcjach związanych z poszukiwaniem i ratowaniem osób znajdujących się w niebezpieczeństwie na morzu oraz zwalczaniem zagrożeń i zanieczyszczeń na morzu; reagowanie na sygnały wzywania pomocy i uczestniczenie w górskich akcjach ratowniczych; branie udziału w akcjach ratowniczych osób lub mienia znajdujących się w trudno dostępnych miejscach na ziemi lub wodzie, działając z pokładu śmigłowca lub po desantowaniu z niego, lub też podczas zawieszenia na linie dźwigu pokładowego; branie udziału w akcjach ratowniczych i przeciwpożarowych w kopalniach; patrolowanie powierzonego terenu w celu zwalczania szkodnictwa leśnego, łowieckiego lub rybackiego; monitoring ruchu drogowego w celu zidentyfikowania momentów, w których piesi mogą przejść przez ulicę; reagowanie na skargi mieszkańców i przejmowanie zagubionych, bezdomnych lub niebezpiecznych zwierząt; patrolowanie przypisanego obszaru w celu egzekwowania przepisów dotyczących parkowania; wykonywanie innych zadań pokrewnych.',282);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6111','Rolnicy upraw polowych','Rolnicy upraw polowych planują, organizują i wykonują prace związane z uprawą i zbiorem płodów rolnych, takich jak zboża, rzepak, tytoń, buraki ziemniaki, kapusta lub inne uprawy, na sprzedaż lub w dostawach stałych do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez rolników upraw polowych zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości uprawianych roślin, planowanie i koordynację produkcji; ręczne lub maszynowe przygotowanie gleby, nawożenie sztuczne i naturalne; wybór i wysiew ziaren i sadzenie sadzonek; pielęgnowanie upraw poprzez kultywację gleby, przesadzanie, przycinanie lub przerzedzanie roślin oraz instalację i stosowanie urządzeń nawadniających; niszczenie chwastów, szkodników i chorób poprzez stosowanie herbicydów i pestycydów; zbieranie płodów i niszczenie płodów zarażonych lub odrzuconych; sprawdzanie, czyszczenie, klasyfikację, pakowanie, magazynowanie i załadunek płodów na sprzedaż lub w ramach dostaw na rynek; pielęgnowanie zwierząt gospodarskich i utrzymywanie w dobrym stanie budynków, pomieszczeń, sprzętu gospodarczego oraz systemów zasilania w wodę; magazynowanie produktów i wykonywanie niektórych podstawowych czynności przetwórczych; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',283);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6112','Sadownicy','Sadownicy planują, organizują i wykonują prace związane z uprawą drzew i krzewów owocowych, winorośli i chmielu na sprzedaż, w stałych dostawach do odbiorców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','oraz określanie rodzajów i ilości uprawianych roślin, planowanie i koordynację produkcji; ręczne lub maszynowe przygotowanie gleby, nawożenie sztuczne i naturalne; wybór i sadzenie sadzonek lub drzewek; pielęgnowanie upraw poprzez kultywację gleby, przesadzanie, przycinanie lub przerzedzanie roślin oraz instalację i stosowanie urządzeń nawadniających; niszczenie chwastów, szkodników i chorób poprzez stosowanie herbicydów i pestycydów; zbieranie płodów i niszczenie płodów zarażonych lub odrzuconych; sprawdzanie, czyszczenie, klasyfikację, pakowanie, magazynowanie i załadunek płodów na sprzedaż lub w ramach dostaw na rynek; pielęgnowanie zwierząt gospodarskich i utrzymywanie w dobrym stanie budynków, pomieszczeń, sprzętu gospodarczego oraz systemów zasilania w wodę; magazynowanie produktów i wykonywanie niektórych podstawowych czynności przetwórczych; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',284);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6113','Ogrodnicy','Ogrodnicy planują, organizują i wykonują prace związane z uprawą i pielęgnacją drzew, krzewów, kwiatów i innych roślin w parkach i ogrodach prywatnych, produkują drzewka i krzewy do nasadzeń, sadzonki, cebulki i nasiona lub uprawiają warzywa, kwiaty i grzyby w gruncie i pod osłonami na sprzedaż lub w stałych dostawach do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez ogrodników zazwyczaj obejmują: monitoring rynku oraz określanie rodzaju i ilości produkowanych warzyw, materiału ogrodniczego i szkółkarskiego, planowanie i koordynację produkcji; przygotowanie gleby poprzez nawożenie, plantowanie oraz instalację i stosowanie systemu nawadniania i melioracji; sadzenie drzew, żywopłotów, roślin ogrodowych i trawy; cięcie, sadzenie przesadzanie i leczenie drzew, krzewów i żywopłotów, wkopywanie podpórek roślin i ich ochronę, sadzenie trawników z rolki, koszenie, aerację i przycinanie trawników; tworzenie małej architektury ogrodowej, takiej jak: ścieżki i nawierzchnie utwardzone, murki oporowe, skalniaki, stawy ogrodowe i obiekty wodne, ogrodzenia i płoty; kontrolę zdrowia roślin i drzew, identyfikację i niszczenie chwastów, szkodników i chorób, ściółkowanie i nawożenie; produkcję sadzonek, cebulek i nasion i uprawę warzyw i kwiatów z nasion lub sadzonek; produkcję grzybów jadalnych w pomieszczeniach na różnych podłożach; zbiór płodów, kontrolę, czyszczenie, klasyfikację, pakowanie, magazynowanie i załadunek produktów na sprzedaż lub w ramach dostaw na rynek; magazynowanie produktów i wykonywanie niektórych podstawowych czynności przetwórczych; utrzymywanie w dobrym stanie budynków, szklarni i innych konstrukcji oraz sprzętu i systemów nawadniania; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',285);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6114','Rolnicy upraw mieszanych','Rolnicy upraw mieszanych planują, organizują i wykonują prace związane z uprawą i zbiorem różnych płodów z upraw polowych, warzyw, sadów, materiału roślinnego, ogrodniczego i szkółkarskiego, na sprzedaż lub w dostawie do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez rolników upraw mieszanych zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości uprawianych roślin, planowanie i koordynację produkcji; ręczne lub maszynowe przygotowanie gleby, nawożenie sztuczne i naturalne; wybór i wysiew ziaren lub sadzenie sadzonek i drzewek; pielęgnowanie upraw poprzez kultywację gleby, wysadzanie, przycinanie lub przerzedzanie roślin oraz instalację i stosowanie urządzeń nawadniających; niszczenie chwastów, szkodników i chorób poprzez stosowanie herbicydów i pestycydów; zbieranie płodów i niszczenie płodów zarażonych lub odrzuconych; sprawdzanie, czyszczenie, klasyfikację, pakowanie, magazynowanie i załadunek płodów na sprzedaż lub w ramach dostaw na rynek; pielęgnowanie zwierząt gospodarskich i utrzymywanie w dobrym stanie budynków, pomieszczeń, sprzętu gospodarczego oraz systemów zasilania w wodę; magazynowanie produktów i wykonywanie niektórych podstawowych czynności przetwórczych; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',286);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6121','Hodowcy zwierząt gospodarskich i domowych','Hodowcy zwierząt gospodarskich i domowych planują, organizują i prowadzą działania w zakresie hodowli zwierząt udomowionych (z wyłączeniem drobiu), takich jak bydło, owce, świnie, kozy, konie w celu produkcji mięsa, mleka i innych produktów mlecznych, skór, wełny lub hodowli zwierząt sportowych, roboczych lub rekreacyjnych, na sprzedaż lub dostawy do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez hodowców zwierząt gospodarskich i domowych zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości hodowanych zwierząt, planowanie i koordynację produkcji; utrzymywanie pastwisk, dostarczanie i monitoring paszy i wody w celu zapewnienia stosownego odżywiania i stanu zwierząt; nadzorowanie i badanie zwierząt w celu wykrycia chorób, urazów lub zakażeń oraz sprawdzanie stanu fizycznego, np. poprzez kontrolę przyrostu masy; oporządzanie, znakowanie, kolczykowanie, mycie oraz/lub kastrację zwierząt i strzyżenie w celu otrzymania włosia lub wełny; wypuszczanie zwierząt gospodarskich na pastwiska w celu wypasu, ważenie, zwierząt, przetransportowywanie do obór/pomieszczeń gospodarczych, pojazdów lub innych obiektów; ręczne lub maszynowe dojenie zwierząt; mieszanie pasz, dodatków paszowych i leków w przepisanych proporcjach oraz napełnianie zbiorników na paszę i wodę lub ręczne karmienie zwierząt; wykonywanie czynności związanych z reprodukcją zwierząt, takich jak: rozmnażanie, sztuczne zapłodnienie oraz pomoc przy porodach; czyszczenie i utrzymywanie w dobrym stanie budynków gospodarczych, obiektów, sprzętu i maszyn; ubój i skórowanie zwierząt i przygotowanie ich na rynek; magazynowanie i wykonywanie podstawowej obróbki produkcyjnej; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',287);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6122','Hodowcy drobiu','Hodowcy drobiu planują, organizują i prowadzą działania w zakresie hodowli kurcząt, indyków, gęsi, kaczek oraz innego drobiu w celu produkcji mięsa, jaj i niosek na sprzedaż lub dostawy do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez hodowców drobiu zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości hodowanego drobiu, planowanie i koordynację produkcji; dostarczanie i monitoring paszy i wody w celu zapewnienia stosownego odżywiania i stanu drobiu; monitoring i badanie drobiu w celu wykrycia chorób, urazów lub zakażeń oraz sprawdzanie stanu fizycznego, np. poprzez kontrolę przyrostu masy oraz eliminację słabych, chorych i martwych osobników ze stada; mieszanie pasz i dodatków paszowych oraz napełnianie zbiorników na paszę i wodę; szczepienie drobiu poprzez wodę pitną, zastrzyki lub drogą powietrzną; zbieranie i przechowywanie jaj i pakowanie ich na sprzedaż i w celu dostarczenia na rynek; ułatwianie zapłodnienia, sztucznych zapłodnień i wykluwania piskląt oraz określanie płci kurcząt; wynajmowanie lub inwestowanie oraz utrzymywanie w dobrym stanie i sprzątanie budynków gospodarczych, maszyn, sprzętu i innych obiektów; ubój i oprawianie drobiu na sprzedaż lub w celu dostarczenia na rynek; magazynowanie i wykonywanie podstawowej obróbki produktów; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',288);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6123','Pszczelarze i hodowcy jedwabników','Pszczelarze i hodowcy jedwabników planują, organizują i prowadzą działania w zakresie hodowli owadów, takich jak; pszczoły, jedwabniki lub inne gatunki w celu produkcji miodu, wosku pszczelego, jedwabiu oraz innych produktów na sprzedaż lub dostawy do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez pszczelarzy i hodowców jedwabników zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości hodowanych owadów, planowanie i koordynację produkcji; zakup owadów i ich hodowlę oraz zakup pasz i innych produktów; rozmnażanie owadów i gromadzenie ich produktów; wynajmowanie lub inwestowanie oraz utrzymywanie i sprzątanie budynków gospodarczych, maszyn, sprzętu i innych obiektów; magazynowanie i wykonywanie podstawowej obróbki produktów; organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',289);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6129','Hodowcy zwierząt gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje hodowców zwierząt niesklasyfikowanych w innych podgrupach grupy 612 Hodowcy zwierząt. Grupa ta obejmuje na przykład hodowców zwierząt laboratoryjnych, hodowców inwentarza mieszanego, hodowców ptaków (z wyjątkiem drobiu), na sprzedaż lub dostawy do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez hodowców zwierząt gdzie indziej niesklasyfikowanych zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości hodowanych zwierząt, planowanie i koordynację produkcji; hodowlę i żywienie zwierząt; ubój i skórowanie zwierząt, przygotowywanie zwierząt lub produktów na rynek; monitoring i badanie zwierząt w celu wykrycia chorób, urazów lub zakażeń oraz sprawdzanie stanu fizycznego, np. poprzez kontrolę przyrostu masy; wykonywanie czynności związanych z reprodukcją zwierząt, takich jak: rozmnażanie, sztuczne zapłodnienie, pomoc przy porodach; oporządzanie, znakowanie, kolczykowanie, mycie strzyżenie w celu otrzymania włosia, wełny, itp.; wypożyczanie lub inwestowanie i utrzymywanie w dobrym stanie, a także czyszczenie budynków, sprzętu, maszyn i obiektów; magazynowanie i wykonywanie podstawowej obróbki produkcyjnej; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków i zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',290);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6130','Rolnicy produkcji roślinnej i zwierzęcej','Rolnicy produkcji roślinnej i zwierzęcej planują, organizują i wykonują prace związane z uprawami polowymi, sadownictwem i ogrodnictwem oraz hodowlą zwierząt i wytwarzaniem produktów pochodzenia zwierzęcego na sprzedaż w dostawie do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez rolników produkcji roślinnej i zwierzęcej zazwyczaj obejmują: monitoring rynku oraz określanie rodzajów i ilości uprawianych roślin i hodowanych zwierząt, planowanie i koordynację produkcji; zakup nasion, nawozów oraz innych produktów; realizację takich zadań, jak: przygotowanie gleby, wysiew, sadzenie, uprawę i zbiór plonów; produkcję lub zakup paszy i dodatków paszowych; hodowlę i zajmowanie się zwierzętami; ubój i skórowanie zwierząt, przygotowanie zwierząt lub produktów zwierzęcych na rynek; wypożyczanie lub inwestowanie i utrzymywanie w dobrym stanie, a także czyszczenie budynków gospodarczych, sprzętu, maszyn i obiektów; magazynowanie i wykonywanie podstawowej obróbki produkcyjnej; promocję i marketing produktów, organizowanie sprzedaży, zakupu i transportu produktów i materiałów oraz prowadzenie rejestrów z działalności gospodarstwa i transakcji; szkolenie i nadzorowanie pracowników zatrudnionych przy produkcji, przestrzegania przez nich obowiązków oraz zachowania środków ostrożności i bezpieczeństwa; wykonywanie innych zadań pokrewnych.',291);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6210','Robotnicy leśni i pokrewni','Robotnicy leśni i pokrewni wykonują zadania związane z uprawą, pielęgnacją i eksploatacją lasów naturalnych i nasadzeń leśnych.','Zadania wykonywane przez robotników leśnych i pokrewnych pracowników zazwyczaj obejmują: wykonywanie prac przy zakładaniu szkółek, takich jak: przygotowanie gleby, wysiew nasion, pielęgnację siewek i wyjmowanie sadzonek; sadzenie sadzonek, ręczne lub przy użyciu sadzarek, przy zalesianiu i odnawianiu powierzchni zrębowych, pielęgnowanie gleby i młodników; zakładanie na drzewka osłon plastikowych w uprawach i młodnikach; grodzenie mrowisk, upraw i młodników w celu ochrony przed zwierzyną; opylanie i opryskiwanie sprzętem ręcznym szkółek, upraw, drzewostanów i drewna w celu ochrony przed owadami, grzybami i chwastami; prowadzenie obserwacji przeciwpożarowych, powiadamianie o pożarach i gaszenie pożarów w zarodku; zbieranie owoców i szyszek drzew, wyłuszczanie i oczyszczanie nasion, pozyskiwanie żywicy i innych użytków ubocznych; stosowanie pił mechanicznych i innych pił do przerzedzania młodego drzewostanu, przycinania i wycinki drzew i przygotowywania kłód; wstępną obróbkę kłód w miejscu wycinki; dokonywanie zrywki i wywozu kłód przy użyciu zaprzęgów konnych lub ciągników; układanie i załadunek kłód na pochylniach lub spławianie ich rzeką; konserwowanie sprzętu, naprawę i konserwację dróg leśnych, kopanie rowów granicznych i ustawianie znaków ostrzegawczych; wykonywanie innych prac związanych z uprawą, pielęgnacją i eksploatacją lasów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',292);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6221','Hodowcy ryb','Hodowcy ryb rozmnażają i hodują ryby na sprzedaż lub w dostawie do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez hodowców ryb zazwyczaj obejmują: wynajmowanie lub inwestowanie w budynki i sprzęt oraz kupowanie paszy i innych materiałów eksploatacyjnych; rozmnażanie i hodowlę narybku na sprzedaż lub w celu zarybienia zbiorników wodnych; kierowanie i monitoring odłowu ryb i tarła, inkubację jaj i wychów narybku, stosowanie wiedzy w zakresie zarządzania i technik hodowli ryb; przygotowywanie pasz, żywienie i pielęgnowanie ryb; gromadzenie i rejestrowanie danych dotyczących wzrostu, produkcji i danych środowiskowych; kontrolowanie stanu zdrowotnego ryb; odławianie ryb i ich przechowywanie oraz przygotowywanie do sprzedaży; realizowanie dostaw lub marketing produktów; prowadzenie dokumentacji produkcji i kalkulacji; utrzymywanie we właściwym stanie budynków, zbiorników wodnych, maszyn, łodzi i innego sprzętu; nadzorowanie i szkolenie pracowników pomocniczych zatrudnionych w wylęgarni ryb oraz przy wychowie i odłowie ryb; wykonywanie innych zadań pokrewnych.',293);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6222','Rybacy śródlądowi','Rybacy śródlądowi samodzielnie lub jako członkowie załóg łowią ryby lub inne formy organizmów wodnych na wodach śródlądowych lub przybrzeżnych, na sprzedaż w dostawie do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez rybaków śródlądowych zazwyczaj obejmują: przygotowywanie i naprawę sieci i innego sprzętu lub narzędzi połowowych; wybór łowisk, wyznaczanie kursów i nawigowanie przy użyciu kompasu, map i innych narzędzi; sterowanie kutrem na teren połowu i z powrotem oraz na terenie łowiska; zarzucanie przynęty, ustawianie, operowanie i transport sprzętu połowowego; gromadzenie różnych organizmów z brzegów i płycizn; czyszczenie, rozładunek sprzętu połowowego, dokonywanie pomiaru ryb w celu zapewnienia zgodności z przepisami, wyrzucanie niepożądanych lub nielegalnych połowów do wody; sortowanie i magazynowanie połowów w ładowniach w soli i lodzie; bezpośrednie uczestniczenie w połowach oraz nadzór nad członkami załogi; wykonywanie innych zadań pokrewnych.',294);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6223','Rybacy morscy','Rybacy morscy, tacy jak szyprowie lub członkowie załogi statku rybackiego, łowią ryby morskie na sprzedaż lub w dostawie do nabywców hurtowych, organizacji rynkowych lub bezpośrednio na rynki.','Zadania wykonywane przez rybaków morskich zazwyczaj obejmują: przygotowywanie i naprawę sieci i innego sprzętu lub narzędzi połowowych; dowodzenie i sterowanie statkami rybackimi na teren połowu i z powrotem oraz na terenie łowisk; wybór łowisk, wyznaczanie kursów i nawigowanie przy użyciu kompasu, map i innych narzędzi; posługiwanie się instrumentami nawigacyjnymi i elektronicznymi narzędziami połowowymi; kierowanie rybołówstwem i nadzorowanie działań załogi; rejestrowanie postępów w połowach, działań, warunków pogodowych i morskich w dzienniku pokładowym; zarzucanie przynęty, ustawianie i transport sprzętu połowowego; czyszczenie, mrożenie, obkładanie lodem lub solenie połowów morskich; rekrutację, nadzór i szkolenie członków załogi; wykonywanie innych zadań pokrewnych.',295);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6310','Rolnicy produkcji roślinnej pracujący na własne potrzeby','Rolnicy produkcji roślinnej pracujący na własne potrzeby uprawiają i zbierają plony z pól lub sadów, uprawiają warzywa i owoce w celu zapewnienia sobie i swym gospodarstwom domowym żywności, miejsca zamieszkania i dochodów.','Zadania wykonywane przez rolników produkcji roślinnej pracujących na własne potrzeby zazwyczaj obejmują: przygotowanie gleby; wysiew, sadzenie, uprawę i zbiór plonów; uprawę warzyw, drzew i krzewów owocowych; przechowywanie produktów do celów późniejszego wykorzystania oraz obróbkę produktów; budowę i utrzymanie budynków i innych obiektów; sprzedaż produktów na rynkach lokalnych; wytwarzanie narzędzi, ubrań i przedmiotów gospodarstwa domowego; wykonywanie innych zadań pokrewnych.',296);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6320','Hodowcy zwierząt pracujący na własne potrzeby','Hodowcy zwierząt pracujący na własne potrzeby rozmnażają i hodują zwierzęta gospodarcze w celu zapewnienia sobie i swym gospodarstwom domowym żywności, miejsca zamieszkania i dochodów.','Zadania wykonywane przez hodowców zwierząt pracujących na własne potrzeby zazwyczaj obejmują: kultywację pastwisk lub zarządzanie łąkami kośnymi, monitoring pasz i wody niezbędnych do utrzymania zwierząt gospodarczych w dobrym stanie; monitoring i badanie zwierząt w celu wykrycia chorób, urazów lub zakażeń oraz kontrolę ich stanu fizycznego; oporządzanie i znakowanie zwierząt, strzyżenie w celu zebrania włosia lub wełny; wypas lub wyganianie stada na pastwiska, koszenie łąk i dostarczani wody; wychów, karmienie i dojenie zwierząt; rozmnażanie zwierząt i pomoc przy porodach; ubój i skórowanie zwierząt i przygotowanie produktów do spożycia lub sprzedaży; przetwarzanie produktów pochodzenia zwierzęcego; budowę i utrzymanie budynków oraz innych obiektów; kupowanie, prowadzenie handlu wymiennego i sprzedaż zwierząt i produktów pochodzenia zwierzęcego; wytwarzanie narzędzi, ubrań i przedmiotów gospodarstwa domowego; wykonywanie innych zadań pokrewnych.',297);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6330','Rolnicy produkcji roślinnej i zwierzęcej pracujący na własne potrzeby','Rolnicy produkcji roślinnej i zwierzęcej pracujący na własne potrzeby uprawiają pola lub sady, warzywa i owoce, zbierają dzikie owoce, rośliny lecznicze lub inne rośliny, chowają zwierzęta i drób oraz/lub łowią ryby i zbierają różne formy organizmów wodnych w celu zapewnienia sobie i swym gospodarstwom domowym żywności, miejsca zamieszkania i dochodów.','Zadania wykonywane przez rolników produkcji roślinnej i zwierzęcej pracujących na własne potrzeby zazwyczaj obejmują: przygotowanie gleby, wysiew, sadzenie, uprawę i zbiór plonów; uprawę warzyw i owoców; zbieranie dzikich owoców, roślin leczniczych lub innych roślin; hodowlę, chów i karmienie zwierząt i drobiu głównie w celu otrzymania mięsa, jaj, mleka, włosia, skóry lub innych produktów; przechowywanie produktów do celów późniejszego wykorzystania oraz obróbkę produktów; sprzedaż produktów na rynkach lokalnych; budowę i utrzymanie budynków i innych obiektów; wytwarzanie narzędzi, ubrań i przedmiotów gospodarstwa domowego; wykonywanie innych zadań pokrewnych.',298);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('6340','Rybacy i zbieracze pracujący na własne potrzeby','Rybacy i zbieracze pracujący na własne potrzeby zbierają dzikie owoce, rośliny lecznicze lub inne rośliny, łowią ryby i zbierają grzyby lub inne organizmy w celu zapewnienia sobie i swym gospodarstwom domowym żywności, miejsca zamieszkania i dochodów.','Zadania wykonywane przez rybaków i zbieraczy pracujących na własne potrzeby zazwyczaj obejmują: zbieranie dzikich owoców, korzeni, roślin leczniczych lub innych roślin; zbieranie grzybów, ślimaków lub innych organizmów oraz łowienie ryb i zbieranie innych form organizmów wodnych; przechowywanie produktów do celów późniejszego wykorzystania oraz obróbkę produktów; sprzedaż produktów na rynkach lokalnych; budowę i utrzymanie budynków i innych obiektów; wytwarzanie narzędzi, ubrań i przedmiotów gospodarstwa domowego; wykonywanie innych zadań pokrewnych.',299);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7111','Monterzy konstrukcji budowlanych i konserwatorzy budynków','Monterzy konstrukcji budowlanych i konserwatorzy budynków wznoszą, konserwują i remontują domy i podobne niewielkie budynki przy użyciu tradycyjnych lub nowoczesnych technik lub materiałów.','Zadania wykonywane przez monterów konstrukcji budowlanych i konserwatorów budynków zazwyczaj obejmują: przygotowanie gruntu pod budynek lub inny obiekt; wznoszenie konstrukcji wspierających dachy i budynek i pokrycie ścian odpowiednimi materiałami; naprawę więźb dachowych i pokrycie dachu materiałami; wykonanie niwelacji podłogi; konserwację i naprawę istniejących konstrukcji, w tym renowację zabytków architektury; aranżację prac specjalistycznych, takich jak: murarstwo, malowanie, hydraulika i okablowanie, wykonywanych przez podwykonawców; koordynację i nadzór nad działaniami podwykonawców, robotników oraz innych pracowników.',300);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7112','Murarze i pokrewni','Murarze i pokrewni układają cegły, obrobione kamienie oraz inne typy materiału murarskiego w zaprawie murarskiej w celu budowy i naprawy ścian, murów działowych, konstrukcji łukowych oraz innych struktur.','Zadania wykonywane przez murarzy i pokrewnych pracowników zazwyczaj obejmują: układanie kamienia, cegieł i prefabrykatów w celu budowy lub naprawy ścian, murów działowych, kominków oraz innych konstrukcji, takich jak: kominy fabryczne, piece, konwertery, piece przemysłowe, filary i wsporniki; kładzenie cegieł lub innych materiałów murarskich w celu budowy patio, murków ogrodowych oraz innych konstrukcji ozdobnych. wykonywanie z cegły, wykładzin kamionkowych, rur i kształtek kamionkowych: kanałów ściekowych, przepustów, tuneli i studni rewizyjnych; przygotowywanie zapraw murarskich; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',301);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7113','Robotnicy obróbki kamienia','Robotnicy obróbki kamienia tną i kształtują kamień twardy i miękki, bloki i odłamki skalne na potrzeby budowy i konserwacji konstrukcji i budowli kamiennych oraz rzeźbią wzory i figury w kamieniu.','Zadania wykonywane przez robotników obróbki kamienia zazwyczaj obejmują: rozbijanie klinami materiału z kamieniołomów na bloki lub odłamki; selekcjonowanie i sortowanie płyt i bloków granitu, marmuru lub innych kamieni; cięcie, kształtowanie i wykańczanie dla budownictwa i celów monumentalnych, za pomocą ręcznych narzędzi, takich kamieni, jak: granity czy marmury; tworzenie wzorów i kształtów na kamieniu na potrzeby dalszego cięcia, wygładzania, wiercenia oraz innych form obróbki; wycinanie i rzeźbienie znaków, figur lub wzorów w blokach kamienia stosowanego na pomniki lub tablice pamiątkowe; zestawianie kamieni przy wznoszeniu pomników lub tablic pamiątkowych; remont i wymiana elewacji kamiennych na starych budynkach, kościołach i pomnikach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',302);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7114','Betoniarze, betoniarze zbrojarze i pokrewni','Betoniarze, betoniarze zbrojarze i pokrewni wznoszą konstrukcje i fundamenty z betonu zbrojonego, produkują prefabrykaty betonowe, wykonują nawierzchnie żelbetonowe, betonują szczeliny w ścianach lub wykonują cembrowiny studni oraz wykańczają i remontują powierzchnie betonowe.','Zadania wykonywane przez betoniarzy, betoniarzy zbrojarzy i pokrewnych pracowników zazwyczaj obejmują: ręczne i mechaniczne przygotowywanie mieszanki betonowej na podstawie receptury; budowę i remont podłóg, ścian, zbiorników, silosów i innych konstrukcji z betonu zbrojonego; przygotowywanie stali zbrojeniowej oraz montowanie siatki i szkieletów zbrojeniowych w warsztacie zbrojarskim i na budowie; montowanie oraz demontowanie form i deskowań do wykonywania elementów betonowych i żelbetowych; produkcję lub montaż prefabrykatów betonowych; betonowanie szczelin w ścianach lub wykonywanie cembrowin studni; wykańczanie i wygładzanie powierzchni betonowych; wykonywanie podłóg o trwałej i gładkiej powierzchni złożonej z cementu, piasku i marmuru (lastryko); wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',303);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7115','Cieśle i stolarze budowlani','Cieśle i stolarze budowlani wykonują, konserwują i remontują różne konstrukcje i rusztowania drewniane i z innych materiałów.','Zadania wykonywane przez cieśli i stolarzy budowlanych zazwyczaj obejmują: budowę, przeróbki i naprawy stolarki budowlanej w warsztacie lub na placu budowy; konstrukcję, budowę i instalację ciężkich konstrukcji drewnianych na placach budowy; wykonywanie form i deskowań do konstrukcji betonowych i żelbetonowych; montaż i przeróbki wewnętrznych i zewnętrznych elementów wykończeniowych budynków, takich jak: ściany, drzwi, okiennice i ramy okienne, okładziny i panele; budowę, montaż, przeróbki i naprawa konstrukcji drewnianych w pociągach, samolotach, na statkach, łodziach, platformach i innych pojazdach; wykonywanie tymczasowych drewnianych obiektów budowlanych na placu budowy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',304);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7116','Robotnicy budowy dróg','Robotnicy budowy dróg wykonują i naprawiają nawierzchnie ulic, chodników, placów i dróg oraz wykonują podstawowe prace przy budowie i naprawie elementów nawierzchni kolejowej.','Zadania wykonywane przez robotników budowy dróg zazwyczaj obejmują: przygotowywanie koryta drogi pod wszelkiego rodzaju nawierzchnie; ręczne lub mechaniczne rozściełanie kolejnych warstw podbudowy i ich zagęszczanie; ręczne lub mechaniczne rozściełanie kruszywa z lepiszczem bitumicznym, nawierzchni betonowych lub asfaltobetonowych i ich zagęszczanie; układanie, naprawianie lub demontaż wszelkiego rodzaju nawierzchni z kostki kamiennej i betonowej lub płyt betonowych oraz montaż krawężników; wykonywanie podstawowych prac przy budowie, naprawie i utrzymaniu elementów nawierzchni kolejowej, ręcznie lub przy użyciu sprzętu zmechanizowanego; sprawowanie nadzoru nad stanem nawierzchni, torów, podtorza i budowli inżynierskich dróg kolejowych oraz nad wykonywaniem prac naprawczych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',305);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7117','Monterzy budownictwa wodnego','Monterzy budownictwa wodnego wykonują prace związane z budową, konserwacją i eksploatacją jazów, kanałów i zbiorników wodnych, wałów przeciwpowodziowych i innych budowli wodnych.','Zadania wykonywane przez monterów budownictwa wodnego zazwyczaj obejmują: wykonywanie robót betonowych i żelbetowych oraz fundamentowych, wraz z przygotowaniem i montażem rusztowań, deskowań i zbrojenia, przy budowie obiektów hydrotechnicznych; wykonywanie robót umocnieniowych faszynowych, kamiennych i betonowych przy umacnianiu skarp, rowów, regulacji rzek, wałów przeciwpowodziowych; usuwanie szkód powodziowych; utrzymywanie i eksploatowanie systemów melioracji wodnych; dokonywanie okresowych przeglądów budowli i urządzeń wodnych oraz wykonywanie prac remontowo-konserwacyjnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',306);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7119','Robotnicy robót stanu surowego i pokrewni gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje robotników robót stanu surowego i pracowników pokrewni niesklasyfikowanych w innych podgrupach grupy 711 Robotnicy budowlani robót stanu surowego i pokrewni. Grupa ta obejmuje na przykład monterów rusztowań i reklam, montażystów dekoracji, robotników rozbiórki budowli i pracowników zatrudnionych do pracy na dużych wysokościach.','Zadania wykonywane przez robotników robót stanu surowego i pokrewnych pracowników gdzie indziej niesklasyfikowanych zazwyczaj obejmują: wznoszenie tymczasowych rusztowań metalowych lub drewnianych na placach budowy; montowanie na budynkach lub słupach tablic reklamowych (bilbordów); budowę, montaż i naprawę sprzętu scenicznego na potrzeby przedstawień teatralnych, filmów lub produkcji telewizyjnych; rozbiórkę budynków oraz innych obiektów; wykonywanie różnych prac konserwacyjnych budynków i konstrukcji na dużych wysokościach np. na wieżach, kominach i iglicach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',307);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7121','Dekarze','Dekarze pokrywają i naprawiają dachy na wszystkich rodzajach budynków przy użyciu jednego lub kilku rodzajów materiałów.','Zadania wykonywane przez dekarzy zazwyczaj obejmują: analizę dokumentacji technicznej, w szczególności rysunków niezbędnych dla przygotowania i realizacji robót dekarskich; pokrywanie konstrukcji dachowej: papą, blachą i wyrobami z blachy, dachówką, wyrobami z tworzyw sztucznych, płytkami łupkowymi, gontami drewnianymi itp.; układanie warstwy wodoodpornej i mocowanie metalowych lub syntetycznych materiałów do konstrukcji budynku; wymiarowanie i cięcie materiałów dachowych tak, aby dopasować je do krawędzi i takich obiektów jak np. kominy; instalowanie rynien i rur spustowych; montaż okien połaciowych, świetlików i wyłazów dachowych; wykonywanie obróbek blacharskich dachowych i elewacyjnych; stosowanie do pokrycia dachu materiałów naturalnych, takich jak słoma, montaż tymczasowych rusztowań i drabin; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',308);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7122','Posadzkarze, parkieciarze i glazurnicy','Posadzkarze, parkieciarze i glazurnicy wykonują, konserwują i naprawiają podłogi lub układają na podłogach, ścianach i innych powierzchniach płytki lub mozaikę do celów dekoracyjnych lub innych.','Zadania wykonywane przez posadzkarzy, parkieciarzy i glazurników zazwyczaj obejmują: przygotowanie zapraw, mieszanek betonowych oraz materiałów pomocniczych do wykonywania robót podłogowych i okładzinowych; przygotowanie podłóg do ułożenia różnych materiałów; układanie wykładziny, parkietu, płytek lub innych materiałów na podłodze zgodnie z dokumentacją; przygotowanie ścian do ułożenia na nich płytek lub innych materiałów do celów dekoracyjnych lub innych, takich jak izolacja akustyczna, itp.; układanie płytek i mozaik na ścianach i innych powierzchniach; wykonywanie napraw i renowacji podłóg oraz okładzin; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',309);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7123','Tynkarze i pokrewni','Tynkarze i pokrewni wykonują, konserwują i naprawiają tynki w budynkach i nakładają warstwy dekoracyjne lub ochronne tynku, betonu lub podobnego materiału we wnętrzach i na zewnętrznych ścianach obiektów.','Zadania wykonywane przez tynkarzy i pokrewnych pracowników zazwyczaj obejmują: nakładanie jednej lub większej ilości warstw tynku na ściany wewnętrzne i sufity budynków w celu wykończenia powierzchni; odmierzanie, oznaczanie i instalację paneli dekoracyjnych oraz przycinanie i montaż gzymsów ozdobnych; nakładanie warstw ochronnych i dekoracyjnych z betonu, tynku i podobnych materiałów na ściany zewnętrzne budynków; wykonywanie i montaż dekoracyjnych tynków włókiennych; dekorowanie ścian, sklepień, sufitów ozdobnymi elementami wykonanymi w gipsie lub stiuku wykonywanie robót konserwatorskich i rekonstrukcyjnych w obiektach zabytkowych; montowanie płyt ocieplających przed tynkowaniem budynków; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',310);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7124','Monterzy izolacji','Monterzy izolacji układają i naprawiają materiały izolacyjne w budynkach, na kotłach, rurach lub sprzęcie chłodzącym i klimatyzacyjnym.','Zadania wykonywane przez monterów izolacji zazwyczaj obejmują: określanie rodzaju, jakości i ilości wymaganych materiałów izolacyjnych w oparciu o analizę dokumentacji; przycinanie materiałów izolacyjnych do właściwego rozmiaru i kształtu; montowanie płyt i arkuszy materiałów izolacyjnych i dźwiękochłonnych na ścianach, podłogach i sufitach budynków; układanie materiałów izolacyjnych i dźwiękochłonnych w przestrzeniach pomiędzy ścianami, podłogami i sufitami budynków; układanie materiałów izolacyjnych na zewnętrznych powierzchniach urządzeń takich jak: kotły, rury i zbiorniki; wykonywanie izolacji urządzeń chłodzących i klimatyzacyjnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',311);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7125','Szklarze','Szklarze dokonują pomiarów, cięcia, wykończenia, dopasowania i montażu płaskich powierzchni szklanych i luster lub montażu gotowych elementów szklanych.','Zadania wykonywane przez szklarzy zazwyczaj obejmują: wybór rodzaju szkła, docięcie do właściwego rozmiaru i kształtu oraz montaż w oknach, drzwiach, prysznicach i elementach budynków; montaż szkła i luster w wieżowcach, gablotach wystawienniczych, ścianach wewnętrznych i na sufitach; montaż lub wymianę szyb w pojazdach lub na łodziach; montaż szkła dekoracyjnego, takiego jak: szklane ściany, klatki schodowe, balustrady i szyby ze szkła zbrojonego; wytwarzanie i montaż witraży; montaż gotowych okien drewnianych, plastikowych (z PCV) lub aluminiowych w otworach okiennych, fasad aluminiowych i naświetli dachowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',312);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7126','Hydraulicy i monterzy rurociągów','Hydraulicy i monterzy rurociągów montują, instalują, konserwują i naprawiają rurociągi, instalacje i armatury wodne, gazowe, drenaże, instalacje ściekowe i sprzęt hydrauliczny i pneumatyczny.','Zadania wykonywane przez hydraulików i monterów rurociągów zazwyczaj obejmują: odmierzanie, cięcie, gwintowanie, gięcie, łączenie, montaż, instalację, konserwację i naprawę rur, instalację urządzeń drenażowych, grzewczych, wodnych i ściekowych; instalację urządzeń gazowych i hydraulicznych; kładzenie ceramicznych, betonowych lub żelaznych rur w kanałach ściekowych, drenażowych, wodnych, itp.; przeprowadzanie inspekcji, badanie i testowanie zainstalowanych systemów i rur, przy użyciu ciśnieniomierzy, badań hydrostatycznych, obserwacji lub innych metod; wykonywanie studni kopanych oraz instalowanie pomp i rur do stałego czerpania wody i hydroforów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',313);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7127','Monterzy i konserwatorzy instalacji klimatyzacyjnych i chłodniczych','Monterzy i konserwatorzy instalacji klimatyzacyjnych i chłodniczych montują, instalują, konserwują i naprawiają instalacje i urządzenia klimatyzacyjne ichłodnicze.','Zadania wykonywane przez monterów i konserwatorów instalacji klimatyzacyjnych i chłodniczych zazwyczaj obejmują: analizę szkiców, rysunków i innych elementów dokumentacji; montaż, instalację i naprawę elementów instalacji klimatyzacyjnych i chłodniczych; podłączanie rur i sprzętu poprzez gwintowanie, nitowanie, spawanie lub lutowanie; testowanie instalacji, diagnozowanie usterek i wykonywanie rutynowych prac konserwacyjno – utrzymaniowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',314);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7129','Robotnicy budowlani robót wykończeniowych i pokrewni gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje robotników budowlanych robót wykończeniowych i pracowników pokrewnych niesklasyfikowanych w innych podgrupach grupy 712 Robotnicy budowlani robót wykończeniowych i pokrewni. Grupa ta obejmuje na przykład technologów robót wykończeniowych w budownictwie, monterów płyt kartonowo-gipsowych i suchej zabudowy, a także monterów żaluzji.','Zadania wykonywane przez robotników budowlanych robót wykończeniowych i pokrewnych pracowników gdzie indziej niesklasyfikowanych zazwyczaj obejmują: wykonywanie różnorodnych prac wykończeniowych w budownictwie, takich jak: prace murarskie, np. związane z budową ścianek działowych, prace tynkarskie, osadzanie stolarki i ślusarki budowlanej oraz jej szklenie, wykonywanie różnego typu podłóg oraz prac glazurniczych; licowanie ścian i sufitów płytami gipsowo-kartonowymi w celu uzyskania idealnie gładkiej i równej powierzchni; montaż konstrukcji nośnej z profili aluminiowych lub z innych materiałów oraz mocowanie płyt gipsowo-kartonowych przy wykonywaniu ścianek działowych; montaż żaluzji okiennych zewnętrznych i wewnętrznych, bram rolowanych, rolet, itp.; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',315);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7131','Malarze budowlani i pokrewni','Malarze budowlani i pokrewni przygotowują powierzchnie budynków oraz innych obiektów do malowania, nakładają warstwy farb ochronnych i dekoracyjnych lub pokrywają ściany wewnętrzne i sufity budynków tapetami lub innymi materiałami wykończeniowymi.','Zadania wykonywane przez malarzy budowlanych i pokrewnych pracowników zazwyczaj obejmują: czyszczenie i przygotowanie ścian i innych powierzchni w budynkach do malowania lub tapetowania; wybór i przygotowanie farb w wymaganych kolorach poprzez mieszanie barwników i dodatków; nakładanie lub natryskiwanie farb, lakierów lub podobnych materiałów na powierzchnie, instalacje lub urządzenia w budynkach; nakładanie farb, lakierów i bejc na powierzchnie przy użyciu pędzli, wałków i sprayu; odmierzanie i układanie tapet lub materiałów na ścianach wewnętrznych i sufitach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',316);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7132','Lakiernicy','Lakiernicy stosują urządzenia rozpylające do pokrycia farbą lub lakierem konstrukcji lub sprzętu przemysłowego, powierzchni metalowych, drewnianych i innych.','Zadania wykonywane przez lakierników zazwyczaj obejmują: przygotowanie powierzchni do pokrycia przy użyciu różnych metod usuwania tłuszczu, brudu i rdzy; malowanie samochodów, autobusów, ciężarówek i innych pojazdów, a także lakierowanie i wykonywanie innych warstw ochronnych; nakładanie farby i warstw ochronnych emalii lub lakieru na produkty metalowe, drewniane i inne produkty przemysłowe, głównie za pomocą rozpylacza ręcznego; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',317);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7133','Robotnicy czyszczący konstrukcje budowlane i pokrewni','Robotnicy czyszczący konstrukcje budowlane i pokrewni czyszczą powierzchnie zewnętrzne budynków i innych obiektów oraz czyszczą i sprawdzają stan kominów i przewodów kominowych.','Zadania wykonywane przez robotników czyszczących konstrukcje budowlane i pokrewnych pracowników zazwyczaj obejmują: czyszczenie powierzchni zewnętrznych z kamienia, cegieł, metalu lub podobnych materiałów środkami chemicznymi lub strumieniem piasku czy wody pod ciśnieniem; usuwanie sadzy z przewodów kominowych, kominów i rur; usuwanie w przewodach kominowych i paleniskach usterek stanowiących zagrożenie bezpieczeństwa budowlanego i pożarowego; usuwanie azbestu, pokrytych pleśnią lub spalonych elementów z budynków; osuszanie i odgrzybianie obiektów budowlanych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',318);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7211','Formierze odlewniczy i pokrewni','Formierze odlewniczy i pokrewni wykonują formy i rdzenie do odlewania metali.','Zadania wykonywane przez formierzy odlewniczych i pokrewnych pracowników zazwyczaj obejmują: wykonywanie modeli gotowych wyrobów do odwzorowania kształtów zewnętrznych odlewu; przygotowywanie mas formierskich i rdzeniowych; wykonywanie elementów formy odlewniczej, ręcznie lub przy użyciu różnych urządzeń pomocniczych; wykonywanie rdzeni do odwzorowania kształtów wewnętrznych odlewu; czyszczenie i wygładzanie form odlewniczych i rdzeni oraz naprawianie ich wad; montowanie form i wykonywanie otworów technologicznych, służących do wlewania metalu i odprowadzania powietrza podczas wypełniania formy; wykonywanie odlewów dzwonów, posągów, odważników i innych wyrobów z metali kolorowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',319);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7212','Spawacze i pokrewni','Spawacze i pokrewni spawają i tną elementy metalowe przy użyciu palników gazowych, spawarek łukowych oraz innych źródeł ciepła przez ich miejscowe stopienie.','Zadania wykonywane przez spawaczy i pokrewnych pracowników zazwyczaj obejmują: spawanie elementów metalowych przy użyciu palników gazowych lub spawarek elektrycznych i spoiw metalowych lub za pomocą innych metod; zgrzewanie elementów metalowych przy użyciu zgrzewarek rezystancyjnych; stosowanie lamp lutowniczych do wykonywania i naprawy wykładzin ołowianych, rur, podłóg ołowianych oraz innych instalacji ołowianych; lutowanie elementów metalowych przy użyciu lutownic; cięcie elementów metalowych przy użyciu palnika gazowego lub spawarki łukowej; monitorowanie procesu montażu, zgrzewania i spawania w celu uniknięcia przegrzania elementów metalowych lub ich wypaczenia, skurczenia, zniekształcenia lub rozszerzenia materiału; analizę elementów metalowych w celu wyszukania usterek i dokonywanie pomiarów elementów za pomocą wzorców lub szablonów celem zapewnienia zgodności z wymaganiami określonymi w instrukcjach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',320);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7213','Blacharze','Blacharze wykonują, montują i naprawiają elementy i artykuły wykonane z blachy, takiej jak: blacha stalowa, z cyny, aluminiowa, mosiężna, cynkowa lub ze stali galwanizowanej.','Zadania wykonywane przez blacharzy zazwyczaj obejmują: oznaczanie na blasze punktów cięcia i kształtowania; wykonywanie i naprawę urządzeń gospodarstwa domowego oraz innych artykułów z cyny, miedzi oraz stopów lekkich lub artykułów dekoracyjnych; wykonywanie i naprawę kotłów, zbiorników, cystern i podobnych urządzeń; instalowanie i naprawę elementów blaszanych w pojazdach, samolotach, na statkach; wykonywanie, instalowanie i naprawę elementów blaszanych na potrzeby przemysłu i budownictwa; wykonywanie rysunków roboczych na potrzeby budowy i montażu elementów blaszanych; identyfikację wymogów produkcyjnych, w tym: zakresu prac, etapów montażu oraz wymaganych metod i materiałów; sprawdzanie jakości produktów oraz montażu w celu zapewnienia zgodności z wymaganiami określonymi w instrukcjach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',321);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7214','Robotnicy przygotowujący i wznoszący konstrukcje metalowe','Robotnicy przygotowujący i wznoszący konstrukcje metalowe montują, wznoszą i dokonują demontażu konstrukcji metalowych budynków oraz innych obiektów.','Zadania wykonywane przez robotników przygotowujących i wznoszących konstrukcje metalowe zazwyczaj obejmują: oznaczanie na elementach metalowych punktów wytyczających miejsca wiercenia, cięcia i kształtowania, wykorzystywanych przy wznoszeniu budynków, mostów, okrętów i innych konstrukcji; wiercenie, cięcie i kształtowanie stali konstrukcyjnej w warsztatach; wznoszenie konstrukcji stalowych na potrzeby budynków, mostów i innych obiektów; montaż i wznoszenie ram konstrukcyjnych i innych elementów metalowych konstrukcji statków; kształtowanie i montaż płyt ze stali konstrukcyjnej w budowanych lub naprawianych statkach; nitowanie elementów metalowych ręcznie, maszynowo lub pneumatycznie; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',322);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7215','Takielarze i monterzy konstrukcji linowych','Takielarze i monterzy konstrukcji linowych montują wciągniki służące do przenoszenia i lokowania sprzętu i elementów konstrukcyjnych lub do instalowania i konserwacji kabli, lin i przewodów na placach budowy, w budynkach lub innych obiektach konstrukcyjnych.','Zadania wykonywane przez takielarzy i monterów konstrukcji linowych zazwyczaj obejmują: szacowanie rozmiaru, kształtu i wagi przenoszonych obiektów oraz podejmowanie decyzji o rodzaju sprzętu przenoszącego; instalację i naprawę kabli, lin, przewodów, bloków i innych konstrukcji linowych; łączenie, naprawę i mocowanie elementów do przewodów, lin i kabli; wykonywanie prac jako członek załogi wznoszącej i naprawiającej wieże wiertnicze wodne, gazowe i ropy naftowej; instalację i montaż scenerii, oświetlenia i innych urządzeń w teatrach i na planach filmowych; instalację i konserwację masztów komunikacyjnych, kabli napowietrznych, kolei linowych, wyciągów narciarskich i podobnych konstrukcji; wykonywanie, montaż i demontaż oraz naprawę takielunku na żaglowych jednostkach pływających; wykonywanie innych zadań pokrewnych.',323);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7221','Kowale i operatorzy pras kuźniczych','Kowale i operatorzy pras kuźniczych kują ręcznie lub mechanicznie pręty, wlewki, i płyty ze stali i metali kolorowych w celu wykonania i naprawy różnych rodzajów narzędzi, wyrobów metalowych, elementów sprzętu, w tym rolniczego i innych.','Zadania wykonywane przez kowali i operatorów pras kuźniczych zazwyczaj obejmują: rozgrzewanie metalu w piecu oraz nadawanie mu żądanego kształtu przez wydłużanie, rozklepywanie, zgrubianie, zginanie, skręcanie itp.; kucie metalu na kowadle, cięcie metalu przecinakami, przebijanie otworów, łączenie elementów przez zgrzewanie lub czopowanie, ich utwardzanie lub hartowanie; kształtowanie rozgrzanego metalu przy użyciu młota parowo-powietrznego lub innego młota maszynowego do kucia swobodnego; obsługę młota maszynowego lub prasy do wykonywania wyrobów metalowych metodą kucia matrycowego; prostowanie konstrukcji stalowych; naprawę narzędzi i maszyn rolniczych; naprawę pojazdów konnych oraz podkuwanie kopyt końskich; wykonywanie i naprawę wyrobów kowalskich zdobniczych; analizę zamówień lub instrukcji w celu określenia wymaganej tolerancji i odpowiedniego ustawienia maszyn; dokonywanie kontroli wykonanych prac w celu zapewnienia zgodności wyrobów z wymaganiami jakościowymi; czyszczenie i konserwowanie narzędzi, maszyn i urządzeń kuźniczych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',324);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7222','Ślusarze i pokrewni','Ślusarze i pokrewni wykonują i naprawiają robione na zamówienie i specjalistyczne narzędzia, broń sportową, zamki, matryce, elementy maszyn odlewniczych oraz inne wyroby metalowe ręcznie lub przy użyciu maszyn do obróbki metali.','Zadania wykonywane przez ślusarzy i pokrewnych pracowników zazwyczaj obejmują: analizę i interpretację rysunków i dokumentacji technicznej narzędzi, matryc, prototypów wyrobów lub modeli; przygotowanie szablonów i rysunków oraz ustalenie przebiegu prac; dobieranie materiałów do wykonania prac ślusarskich, ich gromadzenie i zabezpieczanie; ustawianie, obsługę i konserwację maszyn konwencjonalnych i sterowanych komputerowo maszyn numerycznych do cięcia, walcowania, szlifowania, wiercenia, wygładzania lub innej obróbki elementów do opisanych wymiarów i wykończenia; wytwarzanie i naprawę przyrządów i uchwytów obróbkowych; wykonywanie i naprawianie części maszyn i urządzeń oraz montowanie zespołów maszynowych; naprawę i modyfikację broni sportowej lub innej małej broni; wykonywanie, mocowanie, montaż, naprawę i instalację zamków i ich części; wykonywanie i naprawę matryc metalowych do odlewów; wykonywanie i naprawianie galanterii metalowej; wyznaczanie linii i punktów odniesienia na elementach metalowych do wykorzystania przez innych robotników przy cięciu, wyginaniu szlifowaniu i innej obróbce; sprawdzanie przy użyciu instrumentów pomiarowych wymiarów, regulacji i luzów wykończonych elementów w celu sprawdzenia zgodności z dokumentacją oraz testowanie gotowych elementów pod kątem poprawnego działania; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',325);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7223','Ustawiacze i operatorzy obrabiarek do metali i pokrewni','Ustawiacze i operatorzy obrabiarek do metali i pokrewni ustawiają oraz/lub obsługują różne precyzyjne obrabiarki.','Zadania wykonywane przez ustawiaczy i operatorów obrabiarek do metali i pokrewnych pracowników zazwyczaj obejmują: ustawianie jednego lub różnych rodzajów obrabiarek celem wyprodukowania artykułów metalowych w produkcji seryjnej; obsługę i monitorowanie obrabiarek, takich jak: maszyny do wytłaczania, toczenia, frezowania, szlifowania, wiercenia, wygładzania lub ostrzenia, w tym wielofunkcyjnych obrabiarek numerycznych; wykonywanie podobnych zadań przy obrabianiu elementów z tworzyw sztucznych lub innych zastępujących metal; obserwację działania maszyny w celu wykrycia wad elementów lub usterek maszyny oraz korektę ustawień maszyny, jeśli to konieczne; mierzenie i sprawdzanie wymiarów i kształtów obrabianych przedmiotów za pomocą sprawdzianów oraz przyrządów pomiarowych w celu wykrycia wad i określenia dokładności pracy maszyny; wymianę zużytych elementów maszyn, takich jak narzędzia tnące i szczotki, przy użyciu narzędzi ręcznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',326);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7224','Szlifierze narzędzi i polerowacze metali','Szlifierze narzędzi i polerowacze metali polerują i szlifują powierzchnie metalowe i narzędzia.','Zadania wykonywane przez szlifierzy narzędzi i polerowaczy metali zazwyczaj obejmują: obsługiwanie stacjonarnych lub przenośnych maszyn polerujących; ostrzenie narzędzi tnących przy użyciu ściernic talerzowych lub szlifierek mechanicznych; naprawę, ustawianie i ostrzenie ostrzy pił i zębów w gręplarkach; wykonywanie obróbki na ściernicach talerzowych zgodnie z instrukcjami; monitorowanie pracy maszyn w celu określenia niezbędnej korekty ustawień oraz zatrzymywanie maszyn w przypadku wystąpienia problemów; sprawdzanie oraz mierzenie obrabianych elementów celem upewnienia się, czy ich powierzchnie i wymiary są zgodne z dokumentacją; wybór i montaż ściernic talerzowych w maszynach przy użyciu narzędzi ręcznych i wykorzystywanie wiedzy o procesach ściernych i szlifowania; wykonywanie innych zadań pokrewnych.',327);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7231','Mechanicy pojazdów samochodowych','Mechanicy pojazdów samochodowych montują, instalują, serwisują i naprawiają silniki oraz sprzęt mechaniczny i pokrewny samochodów osobowych, dostawczych, motocykli i innych pojazdów silnikowych.','Zadania wykonywane przez mechaników pojazdów samochodowych zazwyczaj obejmują: wykrywanie i diagnostykę usterek silników i ich części; montowanie, sprawdzanie, testowanie i serwisowanie silników pojazdów i motocykli; wymianę elementów silników lub całych silników; montowanie, sprawdzanie, regulowanie, demontowanie, renowację i wymianę wadliwych elementów pojazdów silnikowych; instalowanie lub regulowanie silników i hamulców oraz regulowanie układów kierowniczych lub innych elementów pojazdów silnikowych; instalowanie, regulowanie, serwisowanie i wymianę układów mechatronicznych pojazdów silnikowych; prowadzenie serwisu okresowego, tj.: wymiany oleju, smaru oraz wykonywanie tuningu silnika w celu osiągnięcia lepszej pracy pojazdów i zapewnienia zgodności z przepisami dotyczącymi zanieczyszczeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',328);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7232','Mechanicy statków powietrznych i pokrewni','Mechanicy statków powietrznych i pokrewni montują, serwisują, naprawiają i dokonują przeglądów silników lotniczych, elementów statków powietrznych, takich jak elementy poszycia i konstrukcyjne, układy hydrauliczne i pneumatyczne oraz elementów awioniki.','Zadania wykonywane przez mechaników statków powietrznych i pokrewnych pracowników zazwyczaj obejmują: montaż, badanie, testowanie i serwisowanie silników statków powietrznych; wymianę elementów silnika lub całych silników; badanie i przegląd elementów poszycia i elementów konstrukcyjnych, w tym podwozia, układów hydraulicznych i odmrażaczy w celu wykrycia zużytych elementów, pęknięć, złamań, wycieków lub innych usterek; konserwację, naprawę, przeglądy i modyfikacje układów konstrukcyjnych, mechanicznych i hydraulicznych statków powietrznych; konserwację, naprawy i renowację konstrukcji statków powietrznych, elementów funkcjonalnych oraz takich elementów jak: skrzydła i kadłub, osprzęt, zespoły hydrauliczne, natlenienia, paliwowe, elektryczne i uszczelki; instalowanie i testowanie elementów elektrycznych i elektronicznych zespołów i układów statków powietrznych; montaż elementów, takich jak: wyposażenie radiowe, przyrządy pokładowe, iskrowniki, inwertory i układy tankowania; zapoznawanie się, interpretowanie i przestrzeganie technicznych instrukcji lotniczych przy wykonywaniu prac prewencyjnych i naprawczych; dokonywanie inspekcji ukończonych prac w celu stwierdzenia, że prace konserwacyjne spełniają normy i że statek powietrzny jest sprawny; wypełnianie dokumentów rejestracji wykonanych prac prewencyjnych i naprawczych oraz zużycia materiałów i części; naprawa i konserwacja lotniczego sprzętu ratowniczego i wysokościowego, takiego jak: spadochrony transportowe i hamujące, wysokościowa aparatura tlenowa, kamizelki ratunkowe i urządzenia katapultowe; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',329);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7233','Mechanicy maszyn i urządzeń rolniczych i przemysłowych','Mechanicy maszyn i urządzeń rolniczych i przemysłowych montują, instalują, testują, serwisują i naprawiają silniki, urządzenia i maszyny rolnicze i przemysłowe, za wyjątkiem pojazdów silnikowych, statków lotniczych oraz silników elektrycznych.','Zadania wykonywane przez mechaników maszyn i urządzeń rolniczych i przemysłowych zazwyczaj obejmują: montaż, instalację, testowanie, serwisowanie i naprawy silników, maszyn i urządzeń mechanicznych; olejenie i smarowanie silników i maszyn stacjonarnych; inspekcje i testowanie nowych maszyn i urządzeń pod kątem zgodności z normami i dokumentacją techniczną; demontaż urządzeń i maszyn w celu usunięcia wadliwych części i przeprowadzenia napraw; badanie części na obecność usterek, takich jak złamania lub zużycie; przeprowadzanie prób działania naprawionych maszyn i urządzeń w celu zweryfikowania poprawności napraw; rejestrację napraw i prac konserwacyjnych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',330);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7234','Mechanicy rowerów i pokrewni','Mechanicy rowerów i pokrewni montują, konserwują, serwisują i naprawiają elementy mechaniczne rowerów, riksz, wózków dla dzieci, wózków inwalidzkich i podobnych środków transportu nieposiadających silnika.','Zadania wykonywane przez mechaników rowerów i pokrewnych pracowników zazwyczaj obejmują: badanie, serwisowanie i naprawę rowerów oraz innych środków transportu nieposiadających silnika; czyszczenie i smarowanie łożysk oraz innych części ruchomych; wymianę i naprawę elementów i akcesoriów, takich jak hamulce, łańcuch, koła, kierownica, itp.; wymianę opon i kontrolę ciśnienia; malowanie ram lakierem w sprayu. Do ich zadań może także należeć montaż nowych rowerów, wózków inwalidzkich i podobnych środków transportu nieposiadających silnika.',331);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7311','Mechanicy precyzyjni','Mechanicy precyzyjni wykonują, kalibrują, naprawiają, konserwują, dostosowują i montują zegarki mechaniczne, zegary, przyrządy nawigacji morskiej, meteorologiczne, optyczne, ortopedyczne i inne instrumenty i urządzenia precyzyjne i nastawiają je tak, aby działały poprawnie.','Zadania wykonywane przez mechaników precyzyjnych zazwyczaj obejmują: naprawę, czyszczenie i nastawianie mechanizmów instrumentów czasowych, takich jak zegary lub zegarki; nastawianie regulatorów czasowych przy użyciu suwmiarek, rejestratorów czasu i pęset; czyszczenie, mycie i suszenie elementów zegarów przy użyciu roztworów oraz ultradźwiękowych lub mechanicznych maszyn do czyszczenia; badanie dokładności i pracy zegarów przy użyciu mierników oraz innych instrumentów elektronicznych; badanie dokładności mierników, liczników, wskaźników lub innych instrumentów rejestracyjnych lub kontrolnych w celu zlokalizowania wadliwych elementów oraz w celu sprawdzenia zgodności z normami; skalowanie instrumentów lub wag przy użyciu narzędzi ręcznych, komputerowych lub elektronicznych; badanie elementów, złącz oraz mechanizmów napędowych w celu wykrycia usterek; montaż instrumentów i urządzeń, takich jak: barometry, zawory sterujące, żyroskopy, wilgotnościomierze, prędkościomierze, tachometry i termostaty; badanie, skalowanie i nastawianie instrumentów elektronicznych, rtęciowych, przeponowych oraz innych przyrządów meteorologicznych w celu sprawdzenia zgodności z dokumentacją i schematami, przy użyciu woltomierzy, oscyloskopów, testerów i innych instrumentów; nastawianie i naprawę masztów, konstrukcji wsporczych, lamp pozycyjnych, paneli kontrolnych, okablowania kontrolnego oraz innych urządzeń elektrycznych i mechanicznych; naprawę i nastawianie instrumentów optycznych, takich jak: mikroskopy, teleskopy, teodolity, sekstansy; sprawdzanie zgodności zamontowanych zespołów z dokumentacją oraz odpowiedniej pracy i wrażliwości za pomocą testów standardowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',332);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7312','Monterzy instrumentów muzycznych','Monterzy instrumentów muzycznych produkują, montują, naprawiają, nastawiają i odnawiają instrumenty muzyczne oraz stroją je do odpowiedniej tonacji za pomocą narzędzi ręcznych lub elektrycznych. Zwykle specjalizują się oni w jednym rodzaju instrumentów, jak: instrumenty strunowe, dęte, pianina, instrumenty stroikowe lub instrumenty perkusyjne.','Zadania wykonywane przez monterów instrumentów muzycznych zazwyczaj obejmują: produkcję i montaż instrumentów muzycznych i części instrumentów z drewna, ebonitu, metalu, skóry oraz innych materiałów; naprawę lub wymianę elementów i części instrumentów muzycznych, takich jak: struny, mostki, filc i klucze, przy użyciu narzędzi ręcznych lub elektrycznych; wykonywanie próbnej gry i badanie instrumentów w celu ocenienia jakości ich dźwięku i identyfikację usterek; nastawianie napięcia strun w celu osiągnięcia właściwego tonu lub tonacji instrumentów strunowych; nastawianie ustników, piszczałek lub klawiszy organów przy użyciu narzędzi ręcznych, w celu regulacji przepływu powietrza i natężenia dźwięku; strojenie i serwisowanie piszczałek poprzez regulację tonacji organów w celu zapewnienia zgodności z tonacją kamertonu oraz dostosowanie tonacji innych piszczałek w odniesieniu do tonacji już nastrojonych; montaż nowych membran na instrumentach perkusyjnych; strojenie akordeonów poprzez słuchowe porównanie tonacji stroików ze stroikami basowymi i akordowymi, w celu otrzymania tonacji standardowej; dostosowywanie klapek i otworów klapowych instrumentów stroikowych lub dętych; strojenie instrumentów perkusyjnych do wymaganej tonacji poprzez regulację obręczy naciągu wykonanego ze skóry po obu stronach instrumentu; montaż i instalacja nowych organów i pianin w budynkach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',333);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7313','Jubilerzy, złotnicy i pokrewni','Jubilerzy, złotnicy i pokrewni projektują, wykonują, dostosowują, naprawiają lub dokonują oceny biżuterii, przedmiotów ceremonialnych lub przedmiotów religijnych, złota, srebra, innych metali szlachetnych lub kamieni szlachetnych. Zajmują się cięciem, opiłowywaniem, polerowaniem i osadzaniem kamieni szlachetnych i półszlachetnych, w tym brylantów i diamentów oraz grawerowaniem wzorów na biżuterii i artykułach z metali szlachetnych, a także tną i polerują diamenty do celów przemysłowych. Zajmują się również pozłotnictwem oraz wykonywaniem biżuterii i wyrobów artystycznych z bursztynu.','Zadania wykonywane przez jubilerów, złotników i pokrewnych pracowników zazwyczaj obejmują: ręczne odlewanie biżuterii oraz innych artykułów z metali nieżelaznych; tworzenie nowych projektów biżuterii i modyfikację projektów istniejących, również przy użyciu komputerów; wykonywanie modeli przestrzennych projektowanych wyrobów; modyfikację istniejących opraw biżuterii w celu zmiany umieszczenia kamieni szlachetnych lub dostosowania oprawy; naprawę, zmianę kształtu lub stylizacji starej biżuterii oraz wyrobów z metali szlachetnych zgodnie z projektami lub instrukcjami; wyrabianie biżuterii, takiej jak: pierścionki, naszyjniki, obręcze, broszki i bransoletki z materiałów takich jak: złoto, srebro, platyna i kamienie szlachetne lub półszlachetne; badanie powierzchni i struktury kamieni szlachetnych przy użyciu polaryskopów, refraktometrów, mikroskopów oraz innych instrumentów optycznych, w celu selekcji kamieni, identyfikacji rzadkich okazów lub do wykrycia skaz, wad lub nieprawidłowości wpływających na wartość kamieni; cięcie i szlifowanie kamieni i osadzanie ich w oprawie; grawerowanie lub wytłaczanie liter lub wzorów dekoracyjnych na biżuterii i wyrobach z metali szlachetnych; szlifowanie, wiercenie i wykańczanie kamieni wykorzystywanych w instrumentach precyzyjnych, takich jak kompasy i chronometry; wykonywanie, naprawianie i odnawianie biżuterii i przedmiotów artystycznych z bursztynu i metali kolorowych; badanie produktów montowanych lub wykończonych przy użyciu szkła powiększającego lub precyzyjnych instrumentów pomiarowych w celu sprawdzenia zgodności z dokumentacją. Do ich zadań może także należeć nadzorowanie innych pracowników.',334);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7314','Ceramicy i pokrewni','Ceramicy i pokrewni produkują ręcznie lub maszynowo wyroby z ceramiki, porcelany, wyroby sanitarne, cegły, płytki ceramiczne i tarcze ścierne.','Zadania wykonywane przez ceramików i pokrewnych pracowników zazwyczaj obejmują: produkcję wyrobów ceramicznych i porcelanowych; produkcję odlewanych wyrobów glinianych lub gipsowych; analizę rysunków technicznych celem zapoznania się z wymogami klienta; formowanie wyrobów na kole garncarskim służącym do modelacji gliny za pomocą dłoni i palców; dostosowywanie prędkości koła w trakcie formowania wyrobu zgodnie z rodzajem gliny; obsługę maszyn do produkcji wyrobów ceramicznych, takich jak: misy, kubki, talerze i spodki; dostosowywanie i ustawianie parametrów wygniatarek miksujących, wytłaczających, tnących i osadzających glinę w formach zgodnie z instrukcją technologiczną; przygotowywanie form do odlewania wyrobów ceramicznych; formowanie półfabrykatów wyrobów ceramicznych o skomplikowanych kształtach techniką odlewania i łączenie części wyrobu poprzez klejenie; wygładzanie powierzchni elementów gotowych, przy użyciu skrobaczek gumowych oraz wilgotnych gąbek; szkliwienie wyrobów ręcznie lub za pomocą aparatu natryskowego; wykonywanie tarcz ściernych poprzez formowanie i prasowanie mieszanki ściernej ręcznie lub maszynowo; badanie gotowych produktów na obecność wad, weryfikację dokładności kształtów i wymiarów wyrobów przy użyciu szablonów i suwmiarek, eliminowanie wyrobów wadliwych oraz rozsortowywanie wyrobów na gatunki; przygotowywanie prac do sprzedaży lub na wystawy oraz utrzymywanie stosunków z sieciami detalicznymi, sprzedawcami garncarstwa, sztuki i innymi podmiotami, które mogą ułatwić sprzedaż lub wystawę prac; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',335);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7315','Formowacze wyrobów szklanych, krajacze i szlifierze szkła','Formowacze wyrobów szklanych, krajacze i szlifierze szkła dmuchają, formują, prasują, tną, przycinają, szlifują i polerują szkło, kształtują płynne szkło zgodnie z wzorcami.','szkła zazwyczaj obejmują: podgrzewanie szkła do stanu płynnego, przy użyciu palników gazowych lub pieców; dmuchanie i gięcie szkła do określonych kształtów w celu uformowania przyrządów naukowych jak: kolby, retorty i pipety; oraz innych wyrobów; szlifowanie i polerowanie wyrobów szklanych lub ich części w celu skorygowania wad lub przygotowania powierzchni do wykończenia oraz wygładzanie i polerowanie ostrych krawędzi przy użyciu polerek lub innym sposobem; badanie szkła i gotowych produktów oraz oznaczanie i eliminację produktów wadliwych, posiadających plamki, skazy, ostre fragmenty, rysy lub których wykończenie jest poniżej standardów; analizę zamówień w celu określenia wymiarów, miejsc cięcia i wielkości zamówienia; obserwację przyrządów pomiarowych, wydruków komputerowych i wideomonitorów w celu zweryfikowania określonych warunków obróbki i dokonywanie niezbędnych korekt; wykonanie wzorów lub rysunków na szkle, mierzenie wymiarów i oznaczanie linii cięcia oraz stosowanie narzędzi do cięcia szkła, do przecinania wyrobu w oznaczonych miejscach lub wokół wzorów; nastawianie, obsługę i dostosowywanie komputerowych i mechanicznych narzędzi do cięcia szkła; kontrolę, ważenie i mierzenie produktów w celu zweryfikowania zgodności z dokumentacją, przy zastosowaniu takich przyrządów, jak: mikrometry, suwmiarki, szkła powiększające i linijki; regulację temperatury pieca zgodnie z rodzajem przetwarzanego szkła; przenoszenie wzorów z projektu na poszczególne elementy wyrobu szklanego przy użyciu rylca; rozpylanie roztworu srebra na szkle przy użyciu pistoletu rozpylającego w celu uzyskania efektu odbicia; formowanie, cięcie i szlifowanie szkła optycznego i innych rodzajów szkła do określonych rozmiarów i wagi, z przeznaczeniem na soczewki i szkiełka do zegarków; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',336);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7316','Szyldziarze, grawerzy i zdobnicy ceramiki, szkła i pokrewni','Szyldziarze, grawerzy i zdobnicy ceramiki, szkła i pokrewni dekorują wyroby z drewna, metalu, tekstyliów, szkła, ceramiki i innych materiałów; planują, projektują i malują litery, postacie, monogramy i rysunki w celu wykonania znaków, grawerowania i wytrawiania rysunków ornamentowych i kwiatowych na szkle i innych materiałach.','Zadania wykonywane przez szyldziarzy, grawerów i zdobników ceramiki, szkła i pokrewnych pracowników zazwyczaj obejmują: ręczne malowanie ozdobnych wzorów na wyrobach ceramicznych, szklanych i z innych materiałów; zdobienie wyrobów za pomocą kalkomanii lub przy użyciu stempli; metalizowanie powierzchni szklanych różnymi sposobami: malarskim, chemicznym, termoplastycznym i galwanicznym; grawerowanie i drukowanie wzorów, zdobień, znaków towarowych, cyfr oraz liter na płaskich lub wypukłych powierzchniach wyrobów z różnych materiałów, takich jak: metal, szkło, plastik lub ceramika; wykonywanie ozdobnych wzorów, oznaczanie kalibracji i innych rysunków na wyrobach szklanych metodą rytowania i wytrawiania; projektowanie i malowanie liter w jednym lub większej liczbie języków, rysunków, monogramów i wzorów w celu wykonania oznakowania wyrobu; podlewanie luster i srebrzenie wyrobów szklanych; łączenie i wykorzystywanie elementów wizualnych, takich jak: linie, przestrzeń, bryła, kolor i perspektywa, w celu uzyskania zamierzonego efektu wyrażającego ideę, emocje lub nastrój; projektowanie rysunków lub liter w celu ich umieszczenia na wyrobach, takich jak: szyldy, wyroby szklane, ceramiczne lub cynkowe; szkicowanie lub kreślenie rysunków oraz liter na wyrobach lub materiałach w celu wykonania wzoru lub szablonu; stosowanie techniki komputerowej do tworzenia trójwymiarowych rzeźbionych obrazów do zastosowania na większych powierzchniach oraz do grawerowania i inkrustowania; projektowanie i produkcję szablonów literowych na powierzchniach winylowych lub liternictwa gotowego do wycięcia; wycinanie ręczne lub maszynowe liter i znaków z płyt ściennych lub tektury do celów ekspozycyjnych; złocenie lub srebrzenie różnymi technikami drewna, rzeźb kamiennych i gipsowych, elementów sztukaterii i metalu; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',337);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7317','Rękodzielnicy wyrobów z drewna i pokrewnych materiałów','Rękodzielnicy wyrobów z drewna i pokrewnych materiałów stosują tradycyjne techniki do przygotowania drewna, słomy, rattanu, trzciny, kamienia, gliny, muszli, włosia, łyka i innych materiałów oraz do rzeźbienia, formowania, montowania, wyplatania, malowania i dekorowania różnych wyrobów do użytku osobistego lub gospodarstwa domowego lub też do celów dekoracyjnych.','Zadania wykonywane przez rękodzielników wyrobów z drewna i pokrewnych materiałów zazwyczaj obejmują: przygotowanie drewna, wikliny, słomy, rattanu, trzciny, kamienia, muszli lub podobnych materiałów; rzeźbienie wzorów kwiatowych lub artystycznych na drewnianych powierzchniach do celów dekoracyjnych; ręczne malowanie wzorów ozdobnych na szkle i ceramice lub porcelanie; rzeźbienie, montowanie, malowanie i zdobienie różnych przedmiotów, takich jak: misy, łyżki, deski do krojenia, tacki, wazy, dzbany, statuetki, figury szachowe, fajki, biżuteria i podobne wyroby; wytwarzanie wyplatanych mebli z wikliny, z okorowanego i zmiękczonego rattanu i pokrewnych materiałów; wyplatanie różnych rodzajów koszy z wikliny, łoziny, rattanu, trzciny, sitowia lub podobnych materiałów; wytwarzanie kapeluszy słomkowych i innych wyrobów ze słomy; przygotowanie materiałów i wytwarzanie szczotek, pędzli mioteł i miotełek z takich surowców, jak włosie, sztuczne włókna, rózgi wierzbowe, druty, itp.; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',338);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7318','Rękodzielnicy wyrobów z tkanin, skóry i pokrewnych materiałów','Rękodzielnicy wyrobów z tkanin, skóry i pokrewnych materiałów stosują tradycyjne techniki i wzory do wyrobu tkanin, konfekcji robionej na drutach, haftowanej, tkanej i innej oraz artykułów gospodarstwa domowego, a także obuwia tradycyjnego, toreb, pasków i innych akcesoriów.','Zadania wykonywane przez rękodzielników wyrobów z tkanin, skóry i pokrewnych materiałów zazwyczaj obejmują: selekcjonowanie i klasyfikowanie włókien wełny, bawełny i lnu; czyszczenie włókien i nadawanie im puszystości; rozczesywanie włókien ręcznie lub za pomocą gręplarek; pranie wełny i farbowanie włókien barwnikami naturalnymi; wytwarzanie przędzy na kołowrotku ręcznym lub nożnym i zwijanie przędzy; ręczne tkanie na krosnach jedno- i wielobarwnych tkanin, takich jak samodziały, pasiaki, makaty lub narzuty; sznurowanie i ustawianie na krośnie mechanizmu Żakarda do wytwarzania tkanin wzorzystych; wykonywanie na krosnach ręcznych lub ramach wyrobów dywanowych, gładkich i z okrywą, metodą tkania, przeplatania i wiązania; wytwarzanie, ręcznie lub na ręcznie obsługiwanych maszynach dziewiarskich, dzianych wyrobów odzieżowych, bieliźnianych, pończoszniczych i galanteryjnych; wykonywanie wyrobów odzieżowych i innych artykułów poprzez szydełkowanie; ręczne wyplatanie sieci; wyplatanie taśm, frędzli, sznurów, linek i innych wyrobów z naturalnej i sztucznej przędzy przy użyciu różnego typu maszyn plecionkarskich; wytwarzanie krajek przy użyciu bardka lub techniką tkania tabliczkowego; wykonywanie różnymi technikami koronek, ręcznie lub maszynowo; wiązanie i plecenie sznurków, tasiemek, wiązanie frędzli; haftowanie wyrobów odzieżowych i innych artykułów; wyrób fragmentów tkanin z włókien, łączenie fragmentów w przykrycia, kapy lub koce; przygotowywanie i barwienie skóry naturalnymi barwnikami i wyrabianie tradycyjnego obuwia, torebek, pasków i innych akcesoriów wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',339);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7319','Rzemieślnicy gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje rzemieślników niesklasyfikowanych w innych podgrupach grupy 731 Rzemieślnicy. Grupa ta obejmuje na przykład osoby wytwarzające z różnych materiałów takie wyroby, jak: zabawki, pamiątki, wyroby galanteryjne i sztuczne kwiaty.','Zadania wykonywane przez rzemieślników gdzie indziej niesklasyfikowanych zazwyczaj obejmują: wykonanie szablonów, matryc, wykrojów wzorcowych poszczególnych elementów zabawek, różnego rodzaju pamiątek i wyrobów galanteryjnych lub sztucznych kwiatów; planowanie kolejności czynności ręcznych lub mechanicznych, które należy wykonać; dobieranie i przygotowywanie surowców, takich jak: kamienie, metal, drewno, tworzywa sztuczne, tkaniny, skóry, papier i guma, w zależności od rodzaju oraz przeznaczenia wyrobu; wykonywanie ręczne lub maszynowe poszczególnych elementów wyrobów, z zastosowaniem właściwej obróbki dla danego rodzaju surowca; montowanie elementów wyrobu przez klejenie, zszywanie, lutowanie, spawanie lub łączenie ich w inny sposób; wykańczanie wyrobów przez wygładzanie powierzchni, lakierowanie, zdobienie, itp.; czyszczenie i konserwowanie narzędzi i maszyn; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',340);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7321','Pracownicy przy pracach przygotowawczych do druku','Pracownicy przy pracach przygotowawczych do druku komponują tekst i grafikę w celu nadania im formy do wykorzystania w różnych procesach drukowania lub do prezentacji w mediach elektronicznych.','Zadania wykonywane przez pracowników przy pracach przygotowawczych do druku zazwyczaj obejmują: wprowadzanie do komputera materiału tekstowego poprzez kopiowanie z wymiennego nośnika danych, bezpośrednie wpisanie z klawiatury lub za pomocą oprogramowania do rozpoznawania pisma z zeskanowanych materiałów; wprowadzanie do komputera materiału ilustracyjnego poprzez zeskanowanie rysunków lub materiałów fotograficznych, wprowadzanie obrazów z cyfrowych aparatów fotograficznych i pojedynczych klatek video lub wykorzystanie obrazów utworzonych w oprogramowaniu graficznym; opracowywanie elementów graficznych: retuszowanie, poprawianie kolorów, jasności i kontrastu, kadrowanie, skalowanie, zapisywanie w odpowiednim formacie i rozdzielczości, nadawanie odpowiedniego profilu koloru itp.; wykonywanie poszczególnych stron publikacji (łamanie) poprzez odpowiednie rozmieszczenie na nich tekstu i grafiki; nanoszenie korekt i zmian klienta oraz nanoszenie znaków dla drukarza; przenoszenie obrazu drukowego z komputera bezpośrednio na formę drukową lub pośrednio – na klisze (formy kopiowe) i z klisz na formę drukową, przy użyciu naświetlarek; obsługiwanie skanerów formowych, służących do wykonywania kopii na błonach fotograficznych dla form drukowych do różnych technik drukowania, przez naświetlanie kompletów wyciągów barw lub diapozytywów, w celu korekcji barw, regulacji rozpiętości gęstości optycznej, wycofywania barw podstawowych spod barwy czarnej oraz korekcji ostrości obrazu; dokonywanie impozycji arkusza drukarskiego przy zastosowaniu oprogramowania do montażu elektronicznego lub wykonywanie montażu ręcznego form kopiowych z negatywów lub diapozytywów kreskowych lub siatkowych; wykonywanie cyfrowych lub analogowych odbitek próbnych z systemu cyfrowego lub negatywów i pozytywów filmowych, ocenianie ich jakości i dokonywanie korekty; elektroniczne grawerowanie lub trawienie naświetlonych form drukowych; opracowywanie, wg projektu, giloszy (linie, znaki graficzne, mikrodruki, tło) dla banknotów i papierów wartościowych lub do celów dekoracyjnych za pomocą maszyny giloszowej lub elektronicznej maszyny grawerującej; ręczne rytowanie w płytce stalorytniczej, wg projektu, rysunku znaczka pocztowego, skarbowego lub sądowego i przenoszenie go na cylinder do maszyny drukującej; wykonywanie rysunków jedno- i wielokolorowych na płytach litograficznych przeznaczonych do drukowania, za pomocą piórka, grafionu, prószu, kredki i tanżeru; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',341);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7322','Drukarze','Drukarze ustawiają i obsługują różnego typu maszyny drukujące do wykonywania druków jedno- i wielobarwnych na wyrobach papierowych, tworzywach sztucznych i innych materiałach przy użyciu form drukowych typograficznych, fleksograficznych, offsetowych, wklęsłodrukowych i sitodrukowych lub metodą druku cyfrowego.','Zadania wykonywane przez drukarzy zazwyczaj obejmują: mocowanie w maszynie drukującej formy drukowej; przygotowywanie farb o odpowiedniej barwie i konsystencji; zasilanie maszyn drukujących w podłoża drukowe (arkusze, zwoje, kształtki); ustawianie i regulowanie mechanizmów podawania podłoża drukowego, mechanizmów odbierających, systemów podawania farb drukarskich oraz innych mechanizmów; uruchamianie kontrolne maszyny drukującej w celu oceny jej działania; wykonanie odbitki kontrolnej i na podstawie jej oceny ewentualne korygowanie ustawień maszyny i farb użytych do drukowania; drukowanie nakładu i kontrolowanie procesu drukowania; obsługiwanie maszyn do druku cyfrowego; obsługiwanie kserokopiarek; czyszczenie i konserwowanie maszyn drukujących; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',342);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7323','Introligatorzy i pokrewni','Introligatorzy i pokrewni bindują książki oraz inne publikacje i wykańczają produkty wydrukowane ręcznie lub maszynowo, a także naprawiają zniszczone oprawy książek i wykonują różne wyroby introligatorskie, takie jak: klasery, ozdobne futerały i inne.','Zadania wykonywane przez introligatorów i pokrewnych pracowników zazwyczaj obejmują: ustawianie i monitorowanie pracy automatycznych maszyn introligatorskich i wykańczających; wykonywanie oprawy książek z okładką twardą lub miękką oraz naprawianie opraw zniszczonych; składanie, zestawianie i szycie maszynowe lub ręczne wkładek książkowych; obsługiwanie gilotyny do cięcia i przycinania papieru na potrzeby przygotowania do druku i prac wykończeniowych, programowanie modułów elektronicznych; obsługiwanie systemów do składania wydrukowanych materiałów w gazety, magazyny; ręczne lub automatyczne ozdabianie wydrukowanych produktów; wykonywanie, albumów, klaserów i innych wyrobów introligatorskich o specjalnym przeznaczeniu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',343);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7411','Elektrycy budowlani i pokrewni','Elektrycy budowlani i pokrewni instalują, konserwują i naprawiają okablowanie elektryczne budynków oraz pokrewny sprzęt i urządzenia.','Zadania wykonywane przez elektryków budowlanych i pokrewnych pracowników zazwyczaj obejmują: instalację, konserwację i naprawy okablowania elektrycznego oraz pokrewnego sprzętu w budynkach takich jak: szkoły, szpitale, budynki komercyjne, mieszkalne i inne obiekty; analizę rysunków, schematów i dokumentacji okablowania w celu określenia etapów i metod działania; planowanie położenia i instalację okablowania, sprzętu i urządzeń w oparciu o specyfikację czynności i odnośne standardy; przeprowadzanie inspekcji systemów, sprzętu i elementów elektrycznych w celu wykrycia zagrożeń, usterek oraz określenia koniecznych napraw lub korekty; selekcję, cięcie i podłączanie kabli i przewodów do terminali i złączy; mierzenie i wyznaczanie punktów odniesienia dla instalacji; pozycjonowanie i instalowanie tablic rozdzielczych; instalowanie reklam świetlnych; testowanie ciągłości obwodu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',344);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7412','Elektromechanicy i elektromonterzy','Elektromechanicy i elektromonterzy montują, dostosowują, instalują i naprawiają maszyny elektryczne oraz inny sprzęt i urządzenia elektryczne w budynkach, fabrykach, warsztatach i innych obiektach.','Zadania wykonywane przez elektromechaników i elektromonterów zazwyczaj obejmują: montaż, dostosowanie i naprawę różnych urządzeń i maszyn elektrycznych, silników, generatorów, tablic rozdzielczych i urządzeń kontrolnych, instrumentów lub części elektrycznych wind i pokrewnych; montaż, dostosowanie i naprawę części elektrycznych urządzeń domowych, maszyn przemysłowych i innych urządzeń; kontrolowanie i testowanie wytworzonych produktów elektrycznych; instalowanie, badanie, przyłączanie, odbiór, konserwację i modyfikacje sprzętu elektrycznego, okablowania oraz systemów kontrolnych; projektowanie, instalację, konserwację, serwis i naprawy przenośników elektrycznych i hydraulicznych oraz podnośników, dźwigów, ruchomych kładek dla pieszych oraz innych podnośników; podłączanie systemów elektrycznych do źródła zasilania; wymianę i naprawę wadliwych elementów; podłączanie telekomunikacyjnych urządzeń zasilających; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',345);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7413','Monterzy linii elektrycznych','Monterzy linii elektrycznych instalują, reperują i podłączają kable transmisyjne i zasilające oraz pokrewne urządzenia.','Zadania wykonywane przez monterów linii elektrycznych zazwyczaj obejmują: instalację i naprawę podziemnych i naziemnych linii trakcji elektrycznej i zasilania; podłączanie kabli naziemnych i podziemnych; stosowanie się do zasad bhp, takich jak: regularne przeglądy sprzętu i wznoszenie barier wokół miejsc pracy; operowanie przełącznikami lub podłączanie urządzeń uziemiających w celu eliminacji zagrożeń wynikających z wadliwych lub przerwanych linii lub w celu ułatwienia napraw; wspinanie się na słupy wysokiego napięcia lub korzystanie z podnośników koszowych w celu uzyskania dostępu do urządzeń; identyfikację wadliwych urządzeń rozdzielających, bezpieczników, automatycznych wyłączników, regulatorów napięcia, transformatorów, przełączników, przekaźników lub okablowania przy użyciu schematów okablowania lub urządzeń do badania stanu okablowania; lokalizowanie i usuwanie przyczyn uszkodzeń i awarii w eksploatacji sieci elektroenergetycznej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',346);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7421','Monterzy i serwisanci urządzeń elektronicznych','Monterzy i serwisanci urządzeń elektronicznych montują, konserwują, dostosowują i naprawiają sprzęt elektroniczny, taki jak: sprzęt RTV, sprzęt komputerowy, maszyny biurowe, instrumenty elektroniczne i systemy kontrolne.','Zadania wykonywane przez monterów i serwisantów urządzeń elektronicznych zazwyczaj obejmują: dobieranie elementów i montowanie komputerów, urządzeń do nagrywania i odtwarzania dźwięku i obrazu, elektronicznych instrumentów muzycznych, sprzętu medycznego, urządzeń przemysłowych, systemów sygnalizacyjnych i innych urządzeń elektronicznych; badanie i testowanie urządzeń elektronicznych w celu identyfikacji usterek; dostosowanie, naprawę i wymianę zużytych lub wadliwych części i okablowania przy użyciu narzędzi ręcznych, elektrycznych lub lutownic oraz konserwację urządzeń i instrumentów; strojenie, regulację i testowanie urządzeń po naprawie; montowanie anten nadawczych i odbiorczych na dachach, ścianach lub konstrukcjach stalowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',347);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7422','Monterzy i serwisanci instalacji i urządzeń teleinformatycznych','Monterzy i serwisanci instalacji i urządzeń teleinformatycznych instalują, reperują i serwisują sprzęt telekomunikacyjny, urządzenia przesyłu danych, okablowanie, anteny i przewody.','Zadania wykonywane przez monterów i serwisantów instalacji i urządzeń teleinformatycznych zazwyczaj obejmują: instalację, serwis, naprawę i diagnozowanie usterek urządzeń mikrofalowych, telemetrycznych, multipleksów, satelitarnych oraz innych systemów komunikacyjnych opartych o fale elektromagnetyczne; zapewnianie doradztwa technicznego oraz informacji technicznej, monitoring pracy sieci telekomunikacyjnych i sprzętu; instalację, przyłączanie i naprawę okablowania komputerów, radia, telefonów oraz transmisji telewizyjnej; przyłączanie kabli telekomunikacyjnych i transmisji danych i kładzenie kabli w osłonach; instalowanie, konserwację i naprawy anten stosowanych w telekomunikacji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',348);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7511','Masarze, robotnicy w przetwórstwie ryb i pokrewni','Masarze, robotnicy w przetwórstwie ryb i pokrewni dokonują uboju zwierząt, czyszczą, tną i oprawiają mięso i ryby, usuwają kości i przygotowują produkty spożywcze lub konserwują mięso, ryby oraz inną żywność lub produkty spożywcze poprzez suszenie, solenie lub wędzenie.','Zadania wykonywane przez masarzy, robotników w przetwórstwie ryb i pokrewnych pracowników zazwyczaj obejmują: dokonywanie uboju zwierząt i obróbki poubojowej tusz; rozbieranie tusz na części, usuwanie kości, cięcie i oprawianie mięsa i ryb na sprzedaż lub do dalszego przetwarzania; czyszczenie, obrabianie i przygotowanie jelit zwierząt rzeźnych z przeznaczeniem na osłonki do produkcji wędlin; przygotowanie składników i wyrób kiełbas i podobnych produktów za pomocą maszyn do krojenia, mieszania; obróbkę mięsa, ryb i innych produktów spożywczych; obsługę wędzarni lub pieców do wędzenia mięsa, ryb i innych produktów; wytwarzanie z mięsa, ryb i innych składników wyrobów garmażeryjnych na sprzedaż; sprzedaż mięsa lub ryb klientom, w tym: pakowanie, ważenie oraz etykietowanie produktów; sprawdzanie czystości sprzętu oraz pomieszczeń produkcyjnych pod względem bhp; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',349);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7512','Piekarze, cukiernicy i pokrewni','Piekarze, cukiernicy i pokrewni wypiekają różne rodzaje chleba, ciast i innych produktów mącznych oraz prowadzą wyrób własny czekolady i produktów cukiernictwa.','Zadania wykonywane przez piekarzy, cukierników i pokrewnych pracowników zazwyczaj obejmują: wypiek chleba, ciast, biszkoptów, ciastek, placków i innych produktów mącznych; wyrób własny wyrobów cukierniczych z mieszanki cukru, czekolady i innych składników przy użyciu narzędzi ręcznych i niektórych maszyn; łączenie odmierzonych składników w misach maszyn mieszających, miksujących lub w urządzeniach kuchennych; sprawdzanie jakości surowców w celu zapewnienia zgodności z normami i dokumentacją technologiczną; wytwarzanie i stosowanie glazury, polew lub innych rodzajów przybrania gotowych produktów przy użyciu szpachelek, pędzelków, itp.; sprawdzanie czystości sprzętu oraz pomieszczeń przed rozpoczęciem produkcji w celu zapewnienia zgodności z przepisami bhp; monitorowanie temperatury piekarnika oraz wyglądu produktu w celu określenia czasu pieczenia; koordynowanie formowania, wkładania do piekarnika, pieczenia, wyjmowania z piekarnika i chłodzenia partii chleba, rolad, ciastek i produktów cukiernictwa; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',350);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7513','Robotnicy w produkcji wyrobów mleczarskich','Robotnicy w produkcji wyrobów mleczarskich wytwarzają masło, różne rodzaje serów, jogurtów i innych wyrobów mleczarskich.','Zadania wykonywane przez robotników w produkcji wyrobów mleczarskich zazwyczaj obejmują: oddzielanie śmietany od mleka oraz prowadzenie procesu zmaślania śmietany, płukania i wygniatania masła; dodawanie do mleka określonych ilości „startera” i innych składników; ukwaszanie mleka, podgrzewanie twarogu do momentu osiągnięcia określonych parametrów, odsączanie twarogu i umieszczanie go w formach w celu nadania mu pożądanego kształtu; solenie sera, dziurkowanie lub pokrycie sera pożywką z zarodnikami w celu stymulacji rozwoju pleśni; umieszczanie i obracanie bloków sera na półkach w procesie dojrzewania; monitorowanie jakości produktu przed procesem pakowania poprzez inspekcje, pobieranie próbek i ewentualne dostosowywanie warunków obróbki; wytwarzanie bundzu z mleka owczego lub krowiego i przetwarzanie bundzu na bryndzę; rejestrację ilości wykorzystanych składników, wyników testów oraz długości cykli produkcyjnych; przeprowadzanie czyszczenia, mycia i dezynfekcji pomieszczeń i urządzeń stosowanych przy produkcji wyrobów mleczarskich; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',351);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7514','Robotnicy przetwórstwa surowców roślinnych','Robotnicy przetwórstwa surowców roślinnych wytwarzają różne produkty z owoców, warzyw i grzybów oraz zajmują się gospodarczym przemiałem zbóż lub ekstrakcją oleju.','Zadania wykonywane przez robotników przetwórstwa surowców roślinnych zazwyczaj obejmują: wytwarzanie kompotów i marynat owocowych, warzywnych i grzybowych; kwaszenie kapusty i ogórków, gotowanie zalew kompotowych, octowych i solankowych; gotowanie dżemów i konfitur; suszenie owoców, warzyw i grzybów; wykonywanie prac związanych z przemiałem zbóż na różne frakcje przemiałowe i mąkę w młynach gospodarczych; tłoczenie oleju z rzepaku i nasion oleistych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',352);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7515','Klasyfikatorzy żywności i pokrewni','Klasyfikatorzy żywności i pokrewni kontrolują, degustują i oceniają różne rodzaje produktów rolnych, żywności i napojów.','Zadania wykonywane przez klasyfikatorów żywności i pokrewnych pracowników zazwyczaj obejmują: klasyfikowanie jaj i przeprowadzanie wyrywkowej kontroli ich jakości na urządzeniach do ich prześwietlania; selekcjonowanie drobiu żywego lub bitego; klasyfikowanie do celów handlowych lub przetwórczych ryb morskich i słodkowodnych oraz skorupiaków i głowonogów, znajdujących się w stanie świeżym (żywym), solonym lub mrożonym; dokonywanie oceny jakości tuszy dziczyzny i ptactwa; dokonywanie oceny grzybów świeżych i suszonych przeznaczonych do obrotu handlowego lub produkcji przetworów grzybowych i z udziałem grzybów oraz wystawianie atestów; kontrolę, testowanie, degustację i wąchanie produktów rolnych, żywności i napojów na różnych etapach obróbki; ocenę jakości, zgodności z gustami konsumentów i przybliżonej wartości produktów oraz przydzielanie ich do odpowiednich klas; zapisywanie oceny w dokumentach odbioru lub sprzedaży; ważenie i mierzenie produktów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',353);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7521','Robotnicy przygotowujący drewno i pokrewni','Robotnicy przygotowujący drewno i pokrewni oceniają przydatność drewna, przemieszczają i składują drewno w stosach, sortują drewno oraz obsługują i nadzorują sprzęt do usuwania kory z bali, sezonowania, konserwacji i uplastycznienia drewna przeznaczonego do dalszej obróbki.','Zadania wykonywane przez robotników przygotowujących drewno i pokrewnych pracowników zazwyczaj obejmują: sortowanie pod względem przydatności do produkcji i klas jakości półfabrykatów oraz materiałów drzewnych; ocenę przydatności drewna okrągłego oraz wyznaczanie miejsca podziału dłużyc w celu racjonalnego wykorzystania drewna; korowanie drewna okrągłego i materiałów tartych ręcznie lub przy użyciu korowarek mechanicznych; obsługę i nadzorowanie suszarni, zbiorników przygotowawczych i innego sprzętu do suszenia drewna, przygotowywania i sezonowania drewna i innych produktów drzewnych, oraz do obróbki chemicznej i impregnacji wyrobów drewnianych środkami konserwującymi; monitorowanie pracy sprzętu, przyrządów pomiarowych i paneli świetlnych w celu wykrycia odchyleń od normy i upewnienia się, że procesy przebiegają zgodnie z dokumentacją produkcyjną; asystowanie przy wymaganej konserwacji sprzętu i maszyn do obróbki; czyszczenie, smarowanie i regulację sprzętu; transport materiałów i produktów do i z miejsca pracy, ręcznie lub przy użyciu wozów, wózków ręcznych, lub podnośników; układanie drewna okrągłego w stosy (mygły) za pomocą urządzeń mechanicznych w celu zabezpieczenia go przed ujemnymi wpływami warunków atmosferycznych; uzupełnianie, kompletowanie i przechowywanie sprawozdań z produkcji; wykonywanie innych zadań pokrewnych.',354);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7522','Stolarze meblowi i pokrewni','Stolarze meblowi i pokrewni wytwarzają, dekorują i naprawiają drewniane meble, wozy i inne pojazdy, koła, beczki, wzory, modele oraz inne wyroby drewniane przy pomocy maszyn stolarskich, narzędzi mechanicznych i specjalistycznych narzędzi ręcznych.','Zadania wykonywane przez stolarzy meblowych i pokrewnych zazwyczaj obejmują: obsługę maszyn stolarskich, takich jak: piły mechaniczne, strugi, dłutarki i strugarki, wykorzystanie ręcznych narzędzi do cięcia, kształtowania i formowania części i elementów; studiowanie planów produkcyjnych, określanie ilości i jakości potrzebnych materiałów; przygotowywanie złączy, dopasowywanie części i podzespołów w celu stworzenia kompletnych wyrobów przy użyciu kleju i zacisków oraz wzmacnianie złączy przy pomocy gwoździ wkrętów lub innych łączników; produkcję, przerabianie i naprawę różnego rodzaju wyrobów drewnianych, takich jak: meble, pojazdy, modele, beczki i kadzie, wyroby galanterii użytkowej, sprzęt sportowy i inne części lub produkty; dekorowanie mebli i wyposażenia poprzez inkrustowanie ich drewnem, nakładanie okleiny oraz wycinanie wzorów; wykańczanie malarsko-lakiernicze artykułów drewnianych lub mebli, montaż okuć i akcesoriów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',355);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7523','Ustawiacze i operatorzy maszyn do obróbki i produkcji wyrobów z drewna','Ustawiacze i operatorzy maszyn do obróbki i produkcji wyrobów z drewna ustawiają i/lub obsługują i monitorują automatyczne lub pół-automatyczne maszyny do obróbki drewna, takie jak: maszyny do piłowania precyzyjnego, formowania, wygładzania, wiercenia, toczenia i snycerki, służące do produkcji lub naprawy drewnianych części mebli, wyposażenia i innych wyrobów drewnianych, a także maszyny tartaczne i maszyny do obróbki wikliny.','Zadania wykonywane przez ustawiaczy i operatorów maszyn do obróbki i produkcji wyrobów z drewna zazwyczaj obejmują: ustawianie, programowanie, obsługę i monitorowanie różnych rodzajów maszyn stolarskich do piłowania, formowania, wiercenia, drążenia, wygładzania, prasowania, toczenia, piaskowania lub snycerki służących do produkcji i naprawy drewnianych części mebli, wyposażenia oraz innych wyrobów drewnianych; obsługę uprzednio zaprogramowanych specjalistycznych maszyn stolarskich do produkcji wyrobów drewnianych, takich jak: wieszaki na ubrania, kije od mioteł, spinacze do bielizny, kopyta szewskie i obcasy oraz inne produkty; dobór noży, pił, ostrzy, głowic tnących, krzywek, wierteł i pasów w zależności od obrabianego materiału, funkcji maszyny i specyfikacji wyrobu; mocowanie i regulacja]ę ostrzy, głowic tnących, wierteł i pasów piaskujących oraz wykorzystanie narzędzi ręcznych; ustawianie i regulację różnego typu maszyn stolarskich obsługiwanych przez inne osoby; analizę i interpretację dokumentacji produkcyjnej lub realizację instrukcji ustnych; ustawianie i obsługę traków i innych urządzeń tartacznych; obsługę urządzeń stosowanych przy pozyskiwaniu i obróbce wikliny; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',356);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7531','Krawcy, kuśnierze, kapelusznicy i pokrewni','Krawcy, kuśnierze, kapelusznicy i pokrewni produkują, dopasowują, przerabiają i naprawiają ubrania szyte na miarę, takie jak: garnitury, płaszcze i sukienki z materiałów włókienniczych, lekkich skór, z futra i innych materiałów, szyją bieliznę osobistą, pościelową i stołową, wykonują gorsety, kapelusze i czapki oraz rękawiczki, zgodnie ze zamówieniem klienta albo producenta odzieży.','Zadania wykonywane przez krawców, kuśnierzy, kapeluszników i pokrewnych pracowników zazwyczaj obejmują: produkcję płaszczy, garniturów, spódnic, koszul, bluzek, damskiej bielizny, gorsetów, kapeluszy i tym podobnej konfekcji, często według indywidualnych wymagań klienta; zdejmowanie wymiarów z sylwetki lub odczytywanie ich z tabel pomiarów antropometrycznych; wybór materiałów włókienniczych, skór lub futer posiadających pożądany dla danej odzieży rozmiar, kolor, fakturę i jakość; docinanie ich do szablonów odzieżowych i układanie zgodnie z projektem odzieży; dokonywanie zmian stylistycznych odzieży, takich jak: zwężanie nogawek spodni, zwężanie klap marynarek, dodawanie i usuwanie watowania; wybór i modyfikację komercyjnych wzorów do wymagań klienta lub producenta odzieży; dopasowywanie, przerabianie i naprawę odzieży zgodnie z wymaganiami klienta; formowanie i szycie kapeluszy i czapek, ozdabianie kapeluszy wstążkami lub innymi dodatkami oraz odświeżanie i przerabianie kapeluszy i czapek; wykonywanie ze skóry, tworzyw sztucznych i materiałów włókienniczych rękawiczek galanteryjnych i o specjalnym przeznaczeniu; wykonywanie, przerabianie i naprawę odzieży futrzanej i innych wyrobów z futra; wykonywanie bielizny pościelowej i stołowej; wykonywanie i opiekę nad kostiumami wykorzystywanymi w produkcjach teatralnych, telewizyjnych i filmowych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',357);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7532','Konstruktorzy i krojczowie odzieży','Konstruktorzy i krojczowie odzieży tworzą szczegółowe szablony podstawowe do produkcji odzieży oraz innych wyrobów z tkanin i skór lub futra; znakują, tną, formują i porządkują tkaniny, lekkie skóry i inne materiały zgodnie z rysunkami technicznymi lub specyfikacją producenta odzieży, kapeluszy i czapek, rękawiczek i innych wyrobów.','Zadania wykonywane przez konstruktorów i krojczych odzieży zazwyczaj obejmują: tworzenie szablonu podstawowego dla każdego rozmiaru w zakresie rozmiarów odzieżowych, przy użyciu tabel, narzędzi do szkicowania, komputerów i/lub przyrządów do stopniowania; tworzenie rysunku technicznego lub wzoru dla konkretnego projektu ubrania przy pomocy komputera; obliczanie wymiarów szablonów w zależności od rozmiaru, przy uwzględnieniu rozciągliwości materiału; rysowanie detali na obrysowanych elementach w celu wyznaczenia miejsc łączenia części, jak również umiejscowienia na odzieży zakładek, kieszeni, dziurek od guzików, dekoracyjnych ściegów na elementach obuwia lub oczek na wyrobach z płótna, przy pomocy komputerów lub narzędzi do szkicowania; układanie wzorów lub materiałów mierniczych w celu lokalizacji ustalonych miejsc cięcia, żeby uzyskać maksymalną wydajność i odpowiednio oznakować tkaninę; rozkładanie szablonów podstawowych na tkaninie i wycinanie próbnego wzoru; testowanie szablonów poprzez tworzenie i dopasowywanie próbnych elementów odzieży; umieszczanie szablonów na wierzchu stosu tkanin i wycinanie tkanin zgodnie z szablonem, przy użyciu noży elektrycznych lub ręcznych, nacinaków lub sterowanych komputerowo cyfrowych narzędzi tnących; cięcie tkanin lub skór futerkowych w celu wykonania części odzieży i innych artykułów z futra; układanie skóry na blacie roboczym maszyny maksymalizując jej wykorzystanie na podstawie ziarnistości, skaz i rozciągliwości skóry; wykonywanie zadań związanych z tworzeniem szablonów, znakowaniem i cięciem przy produkcji innych wyrobów, takich jak na przykład meble miękkie i wyroby z płótna; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',358);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7533','Szwaczki, hafciarki i pokrewni','Szwaczki, hafciarki i pokrewni zszywają, naprawiają, odnawiają i dekorują odzież, rękawiczki i inne wyroby z tkanin, futra, lekkiej skóry i innych materiałów, a także produkują żagle, namioty, zasłony, plandeki, parasole i abażury; wykonują głownie prace ręczne przy użyciu igły i nici, ale mogą też wykonywać pewne zadania przy użyciu maszyny do szycia.','Zadania wykonywane przez szwaczki, hafciarki i pokrewnych pracowników zazwyczaj obejmują: zszywanie ręczne lub za pomocą maszyn szwalniczych elementów wyrobów odzieżowych z materiałów włókienniczych, skóropodobnych i skór naturalnych przy masowej produkcji odzieży; ręczną naprawę wadliwych lub uszkodzonych tkanin lub odzieży przy użyciu odpowiednio dobranej igły nici; usuwanie ściegów z odzieży przeznaczonej do przeróbki, przy użyciu narzędzia do prucia lub żyletki; wybór nici zgodnie ze specyfikacją lub kolorem elementów lub farbowanie nici odpowiednio do koloru i odcienia cerowanego materiału; łatanie dziur, zszywanie rozdarć, pękniętych szwów lub cerowanie uszkodzonych elementów przy użyciu igieł i nici; przeciąganie węzłów na lewą stronę odzieży przy użyciu haczyków; przycinanie końcówek przy pomocy nożyczek, tak aby naprawiany fragment pasował do wzoru materiału; ręczne wyszywanie dekoracyjnych wzorów odbitych, wydrukowanych albo odrysowanych na tkaninie od szablonu przy użyciu igły i kolorowej nici; przyszywanie lub przyklejanie dekoracyjnych wykończeń do kapeluszy lub czapek; zszywanie elementów pokrycia parasoli i pokrowca, ręczne przyszywanie pokrycia do szkieletu parasola oraz przyszywanie na zewnątrz pokrycia paska utrzymującego złożony parasol; wytwarzanie i montaż żagli, zasłon, plandek i namiotów z grubych tkanin, brezentu i tym podobnych materiałów; wytwarzanie i montaż abażurów z tkanin, skóry i innych materiałów; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',359);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7534','Tapicerzy i pokrewni','Tapicerzy i pokrewni montują, naprawiają i wymieniają obicia tapicerskie mebli, wyposażenia i przyrządów ortopedycznych; siedzenia, panele, składane i winylowe dachy i inne wyposażenie samochodów, wagonów pociągowych, samolotów, statków i tym podobnych elementów, przy użyciu tkanin, skóry, skaju lub innych materiałów tapicerskich. Produkują i naprawiają poduszki, kołdry, narzuty i materace, itp.','Zadania wykonywane przez tapicerów i pokrewnych pracowników zazwyczaj obejmują: uzgadnianie z klientami koloru i stylu materiału do wyrobu tapicerki i przedstawianie szacunkowych kosztów tapicerowanych mebli lub innych przedmiotów; wykonywanie szablonów tapicerskich na podstawie szkiców, opisów klienta lub rysunków technicznych; rozkładanie, mierzenie i wycinanie materiałów na podstawie wzorów, szablonów, szkiców i specyfikacji projektowych; montaż, ustawianie i mocowanie sprężyn, wyściółki i pokrycia do ram mebli; ręczne szycie materiałów tapicerskich przy zszywaniu poduszek i sąsiadujących fragmentów materiałów pokryciowych; zszywanie rozdarć lub pęknięć materiału lub pikowanie przy użyciu igły i nici; przybijanie, przyklejanie lub przyszywanie ozdobnych wykończeń klamer, galonów, guzików, i innych akcesoriów do obić lub ram tapicerowanych przedmiotów; produkcję i montaż tapicerki w samolotach, pojazdach motorowych, wagonach pociągów, na łodziach i statkach; naprawę skórzanego pokrycia sztucznych kończyn; renowację zabytkowych mebli przy użyciu rozmaitych narzędzi, takich jak: dłuta, młotki magnetyczne i długie igły; współpracę z projektantami wnętrz przy dekoracji pomieszczeń i koordynację pracy zakładów meblowych; produkcję narzut, kołder, poduszek i materaców; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',360);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7535','Wyprawiacze skór, garbarze i pokrewni','Wyprawiacze skór, garbarze i pokrewni zajmują się czyszczeniem, garbowaniem, polerowaniem i farbowaniem zwierzęcych skór surowych, skórek zwierząt futerkowych lub powłok skórnych celem wytworzenia skór wykończonych i wyprawionych skór futerkowych, używanych do produkcji ubiorów i innych produktów.','Zadania wykonywane przez wyprawiaczy skór, garbarzy i pokrewnych pracowników zazwyczaj obejmują: sortowanie i klasyfikowanie skórek, skór surowych i powłok skórnych według koloru, odcienia, rozmiaru i gęstości; zeskrobywanie resztek mięsa, tłuszczu lub tkanki ochronnej z powłok skórnych i skórek celem ich oczyszczenia lub zmiękczenia; usuwanie sierści z powłok skórnych lub skór surowych zanurzonych w wodzie wapiennej; przygotowanie skór surowych poprzez zakonserwowanie ich solą; usuwanie długiego i grubego włosia ze skórek oraz przycinanie warstwy dolnej do równej długości; garbowanie i ozdabianie skórek w celu uzyskania większego połysku oraz upiększanie lub przywracanie naturalnego wyglądu skórek; przygotowywanie kąpieli do garbowania oraz prowadzenie obróbki skór surowych i powłok skórnych w roztworze garbnikowym celem uzyskania skóry wyprawionej; koloryzowanie lub farbowanie skór futerkowych celem wyeksponowania naturalnych odcieni futra; usuwanie zmarszczek i utrwalanie lica skór surowych i powłok skórnych mokrych; ozdabianie i nakładanie farb i barwników na skórę wyprawioną; rozciąganie i wygładzanie ozdobionych skórek; apreturowanie skóry wyprawionej poprzez równomierne rozprowadzenie roztworu chemicznego lub oleju szczotką ręczną po powierzchni i pozostawienie na otwartej przestrzeni celem wyschnięcia; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',361);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7536','Obuwnicy i pokrewni','Obuwnicy i pokrewni zajmują się wytwarzaniem, modyfikowaniem i naprawianiem obuwia standardowego, wytwarzanego na zamówienie i ortopedycznego.','Zadania wykonywane przez obuwników i pokrewnych pracowników zazwyczaj obejmują: wytwarzanie, modyfikowanie i naprawę obuwia standardowego aby dostosować je do wymagań klientów indywidualnych; wytwarzanie, modyfikowanie i naprawę obuwia ortopedycznego lub terapeutycznego, zgodnie z zaleceniami lekarzy oraz modyfikowanie istniejącego obuwia dla osób mających problemy ze stopami lub osób o specjalnych potrzebach; sporządzanie odlewów gipsowych zdeformowanych nóg lub stóp celem przygotowania rysunków; sporządzanie wkładek, podkładek pod pięty oraz fleki na bazie odlewów stóp klientów; studiowanie rysunków i innej dokumentacji celem wytworzenia obuwia zgodnego z potrzebami klienta; analizę zleceń w celu uzyskania informacji o zapotrzebowaniu i rodzajach materiałów, których należy użyć; sprawdzanie tekstury, koloru i wytrzymałości skóry celem upewnienia się, że jest ona odpowiednia do określonego przeznaczenia; wycinanie, zszywanie, formowanie i impregnowanie części przeznaczonych do produkcji obuwia, montaż obuwia i mocowanie dodatkowych akcesoriów lub ozdób; zszywanie rozdarć lub łatanie dziur celem naprawy obuwia; badanie obuwia, jego części i wzorów celem sprawdzenia zgodności z dokumentacją produkcyjną; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',362);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7537','Kaletnicy, rymarze i pokrewni','Kaletnicy, rymarze i pokrewni wytwarzają, modyfikują i naprawiają wyroby galanteryjne ze skóry, materiałów syntetycznych i włókienniczych, takie jak: portfele, torebki, torby podróżne i paski (z wyjątkiem ubiorów skórzanych, kapeluszy i rękawiczek) oraz wyroby rymarskie ze skóry, takie jak siodła, uprzęże, artykuły sportowe i galanteria dla psów, a także wykonuje prace związane z wytwarzaniem i naprawą części skórzanych, tekstylnych i pasmanteryjnych do protez kończyn dolnych i górnych oraz gorsetów ortopedycznych.','Zadania wykonywane przez kaletników, rymarzy i pokrewnych pracowników zazwyczaj obejmują: wytwarzanie i naprawę takich artykułów, jak: siodła i uprzęże, torby podróżne, torby podręczne, walizki, paski i inne akcesoria; wykonywanie, modyfikowanie i naprawę elementów do protez kończyn, aparatów i gorsetów ortopedycznych, zgodnie z wymogami i zaleceniami lekarskimi; sporządzanie modeli gipsowych produktów ortopedycznych; sporządzanie modeli wzorów wyrobów kaletniczych i rymarskich, wykonywanie rysunków zaprojektowanych wzorów oraz przygotowanie szablonów; dobieranie materiałów na elementy wyrobów według przeznaczenia i przydatności na określone wyroby; wycinanie, zszywanie i montaż elementów wyrobów, mocowanie dodatkowych akcesoriów lub ozdób; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',363);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7541','Nurkowie','Nurkowie pracują pod powierzchnią wody, przy wykorzystaniu podwodnej aparatury oddechowej (akwalungów) w celu dokonania inspekcji, instalacji, naprawy i usunięcia sprzętów i konstrukcji, przeprowadzenia testów lub eksperymentów, przytwierdzania materiałów wybuchowych, fotografowania konstrukcji lub życia podwodnego oraz znajdowania i wydobywania poszukiwanych przedmiotów i osób.','Zadania wykonywane przez nurków zazwyczaj obejmują: przestrzeganie zasad bezpieczeństwa, poprzez monitorowanie czasu trwania i głębokości zanurzeń oraz zgłaszanie ekspedycji nurkowej odpowiednim służbom; sprawdzanie i konserwację sprzętu do nurkowania, takiego jak: kaski, zbiorniki powietrza, skafandry nurkowe, uprzęże i przyrządy pomiarowe; zanurzanie się w wodzie przy asyście pomocników, przy wykorzystaniu akwalungów lub skafandrów nurkowych; wykonywanie pracy pod powierzchnią wody celem montowania i naprawy mostów, molo oraz fundamentów ścian portowych; sprawdzanie kadłubów statków i instalacji podwodnych celem wykrycia uszkodzeń oraz dokonywanie drobnych napraw; składanie sprawozdań ze stanu, w jakim znajdują się wraki statków; usuwanie podwodnych przeszkód; wiercenie otworów do podwodnych prac minerskich; wykonywanie różnych podwodnych czynności związanych z ratownictwem lub wydobywaniem ciał; utrzymywanie kontaktu z pracownikami nad powierzchnią wody przy użyciu lin sygnałowych lub telefonów; uzyskiwanie informacji o zadaniach nurkowych i warunkach środowiska; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',364);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7542','Strzałowi i pokrewni','Strzałowi i pokrewni zajmują się umieszczaniem, montowaniem i detonowaniem materiałów wybuchowych na terenach kopalń, w kamieniołomach i przy wyburzaniu obiektów.','Zadania wykonywane przez strzałowych i pokrewnych pracowników zazwyczaj obejmują: zapewnienie bezpieczeństwa w miejscu pracy i przy obchodzeniu się z materiałami wybuchowymi, kontrolę przestrzegania procedur i przepisów magazynowania i transportu; planowanie i wydawanie instrukcji dotyczących rozmieszczenia, głębokości i średnicy otworów strzałowych; sprawdzanie głębokości i czystości otworów strzałowych; określanie ilości i rodzaju wykorzystywanych materiałów wybuchowych; umieszczanie materiałów wybuchowych w otworach strzałowych; montowanie lub kierowaniem montażem spłonek wykonywanych przez innych pracowników, przy wykorzystaniu detonatorów i naboi udarowych, podłączenie drutów elektrycznych, zapalników i lontów detonujących do spłonek; szeregowe podłączanie drutów, zapalników i lontów detonujących, testowanie obwodów elektrycznych i dokonywanie napraw niesprawnych elementów oraz podłączanie szeregu do zapalarek; przykrywanie naboi, wypełnianie otworów strzałowych pyłem skalnym, piaskiem i innymi materiałami oraz przybitkami celem ubicia naboi; upewnianie się, że wszystkie materiały wybuchowe zostały zdetonowane, a także składanie raportów i nadzorowanie miejsca, w którym nastąpił niewypał; zapewnianie bezpieczeństwa obszarów, na których prowadzone są prace strzałowe przed i po detonacji materiałów wybuchowych; gromadzenie i zachowywanie zapisów dotyczących stosowania materiałów wybuchowych zgodnie z przepisami.',365);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7543','Klasyfikatorzy wyrobów przemysłowych','Klasyfikatorzy wyrobów przemysłowych kontrolują, testują, segregują, pobierają próbki oraz ważą surowce, wytworzone komponenty oraz produkowane i sprzedawane towary nieżywnościowe w celu sprawdzenia ich zgodności z normami jakości, identyfikacji braków, śladów zużycia i odchyleń od specyfikacji oraz sklasyfikowania ich według klas jakości.','Zadania wykonywane przez klasyfikatorów wyrobów przemysłowych zazwyczaj obejmują: kontrolę i testowanie produktów i ich części oraz materiałów pod kątem ich zgodności ze specyfikacjami i normami; odrzucanie produktów, materiałów i sprzętów niespełniających warunków zawartych w specyfikacjach; analizę i interpretację projektów, danych, instrukcji obsługi i innych materiałów celem określenia procedur kontroli i testowania; mierzenie wymiarów produktów przy użyciu takich narzędzi, jak: przymiar liniowy, suwmiarka, cyrkiel kalibrowy, szablon lub mikrometr; analizowanie danych testowych, w razie konieczności, przeprowadzanie obliczeń celem określenia wyników testów; zapisywanie danych kontrolnych i testowych oraz ilość sprawdzonych lub sklasyfikowanych towarów; określanie klasy jakości produktów oraz wydawanie opinii akceptującej lub odrzucającej dany produkt; powiadamianie przełożonych i innych pracowników o problemach produkcyjnych oraz asystowanie przy identyfikacji i rozwiązywaniu tych problemów; wykonywanie innych zadań pokrewnych.',366);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7544','Robotnicy zwalczania szkodników i chwastów','Robotnicy zwalczania szkodników i chwastów stosują środki chemiczne w celu usunięcia szkodliwych owadów, małych zwierząt, dziko rosnącej roślinności i innych niepożądanych organizmów w celu zapobieżenia wystąpienia szkód, w uprawach, budynkach i innych konstrukcjach oraz ich otoczeniu oraz w celu zapobieżenia wystąpieniu zagrożeń dla zdrowia.','Zadania wykonywane przez robotników zwalczania szkodników i chwastów zazwyczaj obejmują: obsługę i monitorowanie sprzętu do zwalczania szkodników i chwastów; mieszanie środków chemicznych zgodnie z instrukcjami; wykonywanie oprysków terenu środkami chemicznymi (pestycydami) w celu zwalczania szkodników lub chwastów, z uwzględnieniem warunków pogodowych; przeprowadzanie dezynsekcji w budynkach przez opryskiwanie pomieszczeń roztworami chemicznymi lub rozpylanie toksycznych gazów w celu zwalczania robactwa domowego; przeprowadzanie deratyzacji w budynkach i ich otoczeniu poprzez ustawianie pułapek i rozkładanie trucizn w wytypowanych miejscach w celu zwalczania gryzoni; informowanie mieszkańców o niebezpieczeństwach i zagrożeniach dla ludzi, wynikających z zastosowania danego środka oraz niezbędnej ostrożności i czasie działania preparatu oraz oznakowywanie, z zastosowaniem właściwej symboliki, miejsc prowadzenia deratyzacji; przestrzeganie zasad obowiązujących w czasie posługiwania się środkami chemicznymi z uwagi na ochronę własną i środowiska, czyszczenie i obsługę techniczną sprzętu w celu zapewnienia jego sprawności; powadzenie dokumentacji przeprowadzonych zabiegów; wykonywanie innych zadań pokrewnych.',367);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('7549','Robotnicy przemysłowi i rzemieślnicy gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje robotników przemysłowych i rzemieślników niesklasyfikowanych w innych grupach elementarnych w ramach grupy 7. Robotnicy przemysłowi i rzemieślnicy. Grupa ta obejmuje na przykład osoby zajmujące się montowaniem znaków nawigacyjnych.','Zadania wykonywane przez robotników przemysłowych i rzemieślników gdzie indziej niesklasyfikowanych zazwyczaj obejmują: montaż, demontaż, remontowanie i konserwowanie elementów pływającego oznakowania nawigacyjnego; obsługę i usuwanie bieżących awarii stałego i pływającego oznakowania nawigacyjnego (w terenie i na wodzie); prace związane z transportem poziomym, załadunkiem i rozładunkiem oznakowania nawigacyjnego przy użyciu dźwigu i wózka widłowego; wykonywanie innych zadań pokrewnych.',368);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8111','Górnicy podziemnej i odkrywkowej eksploatacji złóż i pokrewni','Górnicy podziemnej i odkrywkowej eksploatacji złóż i pokrewni wykonują roboty udostępniające i przygotowawcze do eksploatacji złóż, obsługują maszyny i urządzenia oraz wykorzystują narzędzia ręczne przeznaczone do wydobywania kopalin stałych (węgla, rud, skał i innych) w kopalniach podziemnych i odkrywkowych.','Zadania wykonywane przez górników podziemnej i odkrywkowej eksploatacji złóż i pokrewnych pracowników zazwyczaj obejmują: ustawianie, obsługę i monitorowanie działania różnych podziemnych i odkrywkowych urządzeń górniczych, w tym maszyn górniczych o działaniu ciągłym i urządzeń tnących i kopiących rowy poszukiwawcze; nastawianie i obsługę sprzętu wiertniczego w kopalniach i kamieniołomach podziemnych i odkrywkowych; obsługę maszyn i korzystanie z narzędzi usuwających luźne skały, rudy, węgiel i inne złoża; przygotowywanie, montaż i instalację obudów w miejscach prowadzenia prac podziemnych; obsługę maszyn stosowanych przy otwieraniu nowych szybów, wyrobisk, odpowietrzników i dowierzchni; obsługę urządzeń pomocniczych, takich jak pompy odsysające powietrze, wodę i szlam; ładowanie i transport urobku; likwidowanie wyrobisk podziemnych; dokonywanie przeglądów, prowadzenie niewielkich prac konserwacyjnych i dokonywanie niewielkich napraw maszyn i urządzeń oraz narzędzi; dokonywanie wpisów w dokumentacji zawierającej szczegóły dotyczące działań prowadzonych w trakcie każdej zmiany; pobieranie próbek kopalin z wyrobisk na potrzeby analizy laboratoryjnej; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',369);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8112','Operatorzy maszyn i urządzeń do przeróbki mechanicznej kopalin','Operatorzy maszyn i urządzeń do przeróbki mechanicznej kopalin obsługują i nadzorują maszyny i urządzenia do rozdrabniania, oczyszczania, sortowania i innej obróbki skał, węgla, rud, torfu i kamienia w celu uzyskania produktów przeznaczonych do bezpośredniego użytku lub do dalszego przetwarzania.','Zadania wykonywane przez operatorów maszyn i urządzeń do przeróbki mechanicznej kopalin zazwyczaj obejmują: nastawianie i obsługę stacjonarnych urządzeń szlifujących, kruszących, tnących, piłujących i skrawających skały, kopaliny i kamienie, zgodnie ze specyfikacjami dla tego typu pracy; umieszczanie bloków i płyt kamiennych na maszynach celem ich piłowania i cięcia oraz prowadzenia dalszych prac; monitorowanie i utrzymywanie przepływu nieprzetworzonych skał, kopalin i kamieni z przenośników do maszyn; obsługę sprzętu myjącego, oddzielającego, ługującego, zraszającego, filtrującego, wydobywającego i łączącego celem usunięcia odpadów i odzyskania kopalin; łączenie rud minerałów z rozpuszczalnikami celem ułatwienia dalszego przetwarzania; oddzielanie metali i koncentratów mineralnych od złóż rudy i złóż naniesionych poprzez zagęszczanie, flotację, wzbogacanie grawitacyjne, filtrowanie i/lub oddzielanie magnetyczne i elektrostatyczne; kontrolowanie mierników, przyrządów pomiarowych i odczytów oraz regulowanie zaworów i urządzeń sterujących celem zapewnienia bezpiecznej i efektywnej obsługi maszyn i urządzeń; wykrywanie niesprawności maszyn i urządzeń i asystowanie przy ich konserwacji i naprawach; wzrokowe lub ręczne badanie przetwarzanych materiałów celem upewnienia się co do zgodności z przyjętymi standardami i specyfikacjami, a także pobieranie próbek na potrzeby badań laboratoryjnych; zapisywanie informacji dotyczących przetwarzania materiałów w trakcie zmiany, takich jak: ilości, rodzaje i wymiary produkowanych materiałów; segregowanie, pryzmowanie i przenoszenie przetworzonych kopalin i kamieni celem ich zapakowania, dalszego przetwarzania lub wysyłki; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',370);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8113','Operatorzy urządzeń wiertniczych i wydobywczych ropy, gazu i innych surowców','Operatorzy urządzeń wiertniczych i wydobywczych ropy, gazu i innych surowców instalują i obsługują urządzenia wiertnicze i pokrewny sprzęt, przeznaczone do pogłębiania odwiertów i wydobywania ropy naftowej, gazu ziemnego, wody, siarki i soli.','Zadania wykonywane przez operatorów urządzeń wiertniczych i wydobywczych ropy, gazu i innych surowców zazwyczaj obejmują: demontowanie, przenoszenie i montowanie wiertnic i sprzętów pomocniczych; montowanie i demontowanie żerdzi wiertniczych, orurowania i głowic wiertnic oraz dokonywanie wymiany niesprawnego sprzętu; obsługę urządzeń sterujących celem opuszczania do i podnoszenia z odwiertów żerdzi wiertniczych i orurowania, regulowanie ciśnienia w odwiercie i kontrolowanie prędkości działania narzędzi; przygotowywanie płuczki wiertniczej i sprawdzanie działania pomp celem zapewnienia odpowiedniego przepływu płuczki w żerdzi wiertniczej i odwiercie; monitorowanie przyrządów pomiarowych i innych wskaźników w celu wykrycia niesprawności i niestandardowych warunków wiercenia oraz stwierdzanie potrzeby zmiany sposobu wiercenia lub wymiany sprzętu; konserwację, regulowanie, naprawę i czyszczenie wiertnic, wciągników i innych maszyn; prowadzenie rejestru odwiertów i obsługi; obsługę maszyn i narzędzi przeznaczonych do wydobywania piasku, zwiercin oraz zagubionych i zniszczonych sprzętów wiertniczych z otworów i odwiertów; zamykanie i uszczelnianie nieużywanych odwiertów. Do ich zadań może także należeć nadzorowanie i szkolenie członków załogi.',371);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8114','Operatorzy maszyn i urządzeń do produkcji wyrobów cementowych, kamiennych i pokrewni','Operatorzy maszyn i urządzeń do produkcji wyrobów cementowych, kamiennych i pokrewni obsługują i monitorują pracę maszyn przeznaczonych do wytwarzania i obróbki wykończeniowej różnych prefabrykatów budowlanych z betonu, kamienia i innych materiałów.','Zadania wykonywane przez operatorów maszyn i urządzeń do produkcji wyrobów cementowych, kamiennych i pokrewnych pracowników zazwyczaj obejmują: obsługę maszyn wytłaczających, mieszających, pompujących, szlifujących i tnących, przeznaczonych do wytwarzania i obróbki wykończeniowej prefabrykowanych elementów betonowych i kamiennych; obsługę urządzeń przeznaczonych do wytwarzania cementu, wapna i klinkieru, w tym urządzeń ładujących i rozładowujących składniki mieszanin oraz podajników o działaniu ciągłym, takich jak: pompy lub przenośniki; obsługę urządzeń i maszyn odważających i mieszających piasek, żwir, cement, wodę i inne składniki w celu wytworzenia betonu lub asfaltobetonu; obsługę urządzeń służących do prowadzenia procesu przyspieszonego dojrzewania betonów (hartowania); obsługę urządzeń i maszyn montujących i wypełniających formy betonem i mieszankami ze sztucznego kamienia, wyjmowanie odlewów z form i obróbkę wykończeniową powierzchni elementów prefabrykowanych; cięcie, szlifowanie, wiercenie, piaskowanie i polerowanie produktów betonowych, bloków i płyt kamiennych, itp. produktów; obsługę agregatów i urządzeń do formowania gipsowych taśm tynkowych, bloków, pustaków stropowych, płyt podłogowych, posadzkowych i elementów wielkowymiarowych; sprawdzanie planów i dokumentacji produkcyjnej w celu określenia i wyboru materiałów, składników, procedur, komponentów, ustawień i regulacji maszyn wytłaczających, mieszających i ubijających; monitorowanie urządzeń i maszyn w trakcie ich pracy poprzez obserwację mierników temperatury i ciśnienia oraz regulację urządzeń sterujących i, w razie konieczności, zgłaszanie niesprawności; pobieranie i badanie próbek mieszanin i gotowych produktów celem sprawdzenia ich zgodności ze specyfikacjami i odpowiedniej regulacji ustawień maszyn; sprawdzanie i prowadzenie rejestrów produkcji, w tym informacji o ilościach, wymiarach rodzajach wyprodukowanych materiałów i towarów; organizowanie i asystowanie przy czynnościach konserwacyjnych i naprawczych urządzeń i maszyn; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',372);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8121','Operatorzy maszyn i urządzeń do produkcji i przetwórstwa metali','Operatorzy maszyn i urządzeń do produkcji i przetwórstwa metali obsługują, monitorują, regulują i konserwują wyspecjalizowane maszyny i urządzenia do przetwarzania i konwersji rud minerałów oraz rafinowania, hartowania, walcowania i wytłaczania metali.','Zadania wykonywane przez operatorów maszyn i urządzeń do produkcji i przetwórstwa metali zazwyczaj obejmują: nastawianie, przygotowywanie i regulowanie maszyn do przetwarzania rud minerałów i metali; obsługę wyspecjalizowanych maszyn do szlifowania, oddzielania, filtrowania, mieszania, uzdatniania, wytapiania, walcowania, rafinowania lub innego przetwarzania metali i rud minerałów; obserwowanie przyrządów pomiarowych, mierników, wydruków komputerowych, monitorów wideo oraz produktów w celu kontroli prawidłowości działania maszyn i utrzymania określonych warunków przetwarzania; regulację sprzętu, zaworów, pomp, urządzeń sterujących i sprzętu przetwarzającego; kontrolę przygotowywania, pomiaru i zasilania urządzeń surowcami i środkami przetwórczymi; kontrolę rozpoczynania i zakańczania procesu, wykrywanie i usuwanie usterek oraz monitorowanie zewnętrznego sprzętu przetwarzającego; sprawdzanie sprzętu pod kątem niesprawności, przeprowadzanie rutynowych badań działania oraz organizowanie prac konserwacyjnych; badanie próbek, przeprowadzanie testów, zapisywanie danych oraz dokonywanie wpisów w dziennikach produkcji; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',373);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8122','Operatorzy urządzeń do obróbki powierzchniowej metali i nakładania powłok','Operatorzy urządzeń do obróbki powierzchniowej metali i nakładania powłok zajmują się obsługą i monitorowaniem sprzętu przeznaczonego do obróbki wykańczającej, platerowania i nakładania powłok na wyroby lub części metalowe w celu nadania im zwiększonej odporności na korozję i ścieranie, na potrzeby dekoracji lub nadania im właściwości elektrycznych lub magnetycznych.','Zadania wykonywane przez operatorów urządzeń do obróbki powierzchniowej metali i nakładania powłok zazwyczaj obejmują: obsługę i monitorowanie urządzeń do oczyszczania powierzchni wyrobów metalowych, przygotowując je do powlekania elektrolitycznego, galwanizacji, emaliowania lub podobnych procesów; obsługę i monitorowanie urządzeń do powlekania elektrolitycznego; obsługę i monitorowanie urządzeń do powlekania ogniowego żelaza i produktów stalowych; obsługę i monitorowanie maszyn do automatycznego powlekania drutów metalami nieżelaznymi; obsługę i monitorowanie sprzętu przeznaczonego do natryskiwania produktów metalowych stopionym metalem i innymi substancjami w celu uzyskania powłoki ochronnej, dekoracyjnej oraz odbudowy zużytych lub zniszczonych powierzchni; obsługę i monitorowanie urządzeń przeznaczonych do nakładania nierdzewnego wykończenia wyrobów metalowych poprzez obróbkę środkami chemicznymi i podgrzewanie; sprawdzanie prawidłowej gęstości powłoki galwanicznej przy użyciu mikrometrów, macek i innych urządzeń do zapisywania danych oraz prowadzenie dzienników produkcji; przygotowywanie i mieszanie roztworów metalizujących zgodnie ze wzorami lub specyfikacjami; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',374);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8131','Operatorzy maszyn i urządzeń do produkcji wyrobów chemicznych','Operatorzy maszyn i urządzeń do produkcji wyrobów chemicznych obsługują i monitorują urządzenia oraz maszyny do mieszania, przetwarzania i pakowania szerokiego zakresu wyrobów: chemicznych, farmaceutycznych, kosmetycznych i sanitarnych, materiałów wybuchowych i innych wyrobów.','Zadania wykonywane przez operatorów maszyn i urządzeń do produkcji wyrobów chemicznych zazwyczaj obejmują: nastawianie, uruchamianie, kontrolowanie, regulowanie i zatrzymywanie maszyn i urządzeń; monitorowanie procesów reakcji oraz przenoszenia produktów zgodnie z procedurami bezpieczeństwa; monitorowanie mierników, przyrządów pomiarowych i instrumentów elektronicznych lub innych urządzeń i aparatów chemicznych, takich jak: mieszacze, kociołki, mieszarki, suszarki, tabletkarki, maszyny granulujące, powlekające i do hermetycznego pakowania; mierzenie, ważenie i wprowadzanie składników chemicznych zgodnie z recepturą; pobieranie próbek oraz przeprowadzanie rutynowych badań chemicznych i fizycznych produktów oraz zapisywanie danych produkcyjnych; czyszczenie i dokonywanie drobnych napraw maszyn i urządzeń; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',375);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8132','Operatorzy urządzeń do produkcji materiałów światłoczułych i obróbki filmów','Operatorzy urządzeń do produkcji materiałów światłoczułych i obróbki filmów obsługują i monitorują pracę urządzeń do wytwarzania filmów i papieru fotograficznego oraz do przetwarzania naświetlonych filmów fotograficznych i sporządzania odbitek.','Zadania wykonywane przez operatorów urządzeń do produkcji materiałów światłoczułych i obróbki filmów zazwyczaj obejmują: obsługę i monitorowanie urządzeń do wytwarzania filmów i papieru fotograficznego; obsługę, monitorowanie i testowanie urządzeń do przetwarzania fotografii i sporządzania odbitek oraz przestrzeganie norm eksploatacji; przygotowywanie naświetlonych filmów do różnych form przetwarzania w ciemniach fotograficznych i optycznych; sprawdzanie obrazu, filmów, odbitek oraz regulowanie ustawień maszyny do odbitek w celu uzyskania pożądanego koloru, kontrastu, ilości, rozmiaru i rodzaju odbitek; regulację ustawień i obsługę urządzeń do automatycznego wywoływania filmów; obsługę urządzeń do przenoszenia filmu na taśmę wideo lub na inne elektroniczne nośniki danych; wykonywanie czynności związanych z przetwarzaniem fotografii; obsługę urządzeń do automatycznego wywoływania negatywów kolorowych, sporządzania odbitek i slajdów w punktach detalicznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',376);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8141','Operatorzy maszyn do produkcji wyrobów gumowych','Operatorzy maszyn do produkcji wyrobów gumowych obsługują i monitorują działanie maszyn do ugniatania i mieszania gumy i mieszanek gumowych oraz wytwarzających różnorakie komponenty oraz produkty z naturalnej i syntetycznej gumy, takie jak: profilowane obuwie, artykuły gospodarstwa domowego, materiały izolacyjne, akcesoria przemysłowe lub opony.','Zadania wykonywane przez operatorów maszyn do produkcji wyrobów gumowych zazwyczaj obejmują: obsługę i monitorowanie działania maszyn do ugniatania i mieszania gumy i mieszanek gumowych przeznaczonych do dalszego przetwarzania; obsługę i monitorowanie maszyn do wytwarzania arkuszy gumowych lub tkanin powlekanych gumą w procesie walcowania; obsługę i monitorowanie pracy maszyn wytłaczających mieszanki gumowe lub kształtujących gumę poprzez jej formowanie; obsługę i monitorowanie pracy maszyn nasadzających opony na formie, wulkanizujących opony lub regenerujących zużyte opony; badanie gotowych produktów pod kątem usterek i zgodności ze specyfikacjami; wykrywanie usterek i naprawianie zużytych i wadliwych opon poprzez wulkanizację lub inne procesy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',377);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8142','Operatorzy maszyn do produkcji wyrobów z tworzyw sztucznych','Operatorzy maszyn do produkcji wyrobów z tworzyw sztucznych obsługują i monitorują pracę maszyn ugniatających i mieszających mieszanki tworzyw sztucznych celem uzyskania wyrobów z tworzyw sztucznych oraz maszyn wytwarzających komponenty i wyroby z tworzyw sztucznych.','Zadania wykonywane przez operatorów maszyn do produkcji wyrobów z tworzyw sztucznych zazwyczaj obejmują: obsługę i monitorowanie działania maszyn do ugniatania i mieszania mieszanek celem uzyskania wyrobów z tworzyw sztucznych; obsługę i monitorowanie działania maszyn kształtujących wyroby z tworzyw sztucznych poprzez ich: formowanie, wytłaczanie, wydmuchiwanie, itp.; obsługę i monitorowanie działania maszyn laminujących tworzywa sztuczne oraz impregnujących materiały tworzywami sztucznymi; nakładanie powłoki z tworzyw sztucznych na niepowleczone druty, przewody, kable lub światłowody; badanie gotowych produktów pod kątem usterek i zgodności ze specyfikacjami; recykling odpadów z tworzyw sztucznych; produkcję sztucznych oczu oraz soczewek kontaktowych, produkcję i naprawy oprawek okularowych oraz części przyrządów ortopedycznych wykonanych z tworzyw sztucznych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',378);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8143','Operatorzy maszyn do produkcji wyrobów papierniczych','Operatorzy maszyn do produkcji wyrobów papierniczych obsługują i monitorują działanie maszyn przygotowujących półprodukty oraz wytwarzających pudełka, koperty, torby i inne artykuły z papieru, kartonu, tektury i podobnych materiałów.','Zadania wykonywane przez operatorów maszyn do produkcji wyrobów papierniczych zazwyczaj obejmują: obsługę i monitorowanie maszyn krojących i wykrawających w celu otrzymania zwoików lub arkuszy papieru i tektury do bezpośredniego użytku albo dalszego przerobu; obsługę i monitorowanie maszyn do produkcji papieru i tektury falistej; obsługę i monitorowanie pracy maszyn przyklejających papier do tektury, tnących i przegniatających kartonowy lub tekturowy wykrój w celu uformowania półfabrykatów; obsługę i monitorowanie maszyn do pokrywania powierzchni papieru czy tektury warstwą lakieru lub przezroczystą folią w celu podniesienia wizualnych walorów powierzchni, zwiększenia jej odporności mechanicznej i odporności na czynniki zewnętrzne; obsługę i monitorowanie pras wytwarzających papierowe, kartonowe lub tekturowe kubki i inne pojemniki; obsługę i monitorowanie pracy maszyn tnących, zginających i przyklejających papier w celu wykonania kopert i toreb papierowych lub z podobnych materiałów; obsługę i monitorowanie maszyn do produkcji sznurka, tulei i opakowań zwijanych; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',379);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8151','Operatorzy maszyn przędzalniczych i pokrewni','Operatorzy maszyn przędzalniczych i pokrewni obsługują i monitorują pracę maszyn do przygotowywania włókien tekstylnych oraz do przędzenia, skręcania i nawijania na szpulę wytworzonej przędzy i nici.','Zadania wykonywane przez operatorów maszyn przędzalniczych i pokrewnych zazwyczaj obejmują: obsługę i monitorowanie pracy maszyn do rozluźniania wełny na włókna oraz maszyn do prania i suszenia wełny; obsługę i monitorowanie pracy maszyn czyszczących i przetwarzających odpady z przędz wełnianych; obsługę i monitorowanie pracy maszyn przeznaczonych do wydobywania włókien długich i pakuł ze słomy lnianej lub konopnej; obsługę i monitorowanie pracy maszyn łączących pojedyncze włókna tekstylne w jednorodną mieszankę; obsługę i monitorowanie pracy maszyn do czyszczenia i spulchniania włókien tekstylnych, wytwarzania z nich taśm przędzy, wyczesywania ich oraz łączenia taśm w zwoje; obsługę i monitorowanie pracy rozciągarek w celu wyrównania grubości taśm i równoległego ułożenia włókien; obsługę i monitorowanie pracy maszyn wyciągających taśmy otrzymane z wyciągarki i skręcających je w niedoprzęd; obsługę i monitorowanie pracy maszyn do przędzenia nici i przędzy z niedoprzędu, nawijania dwu lub więcej nici na szpulę, skręcania dwu lub większej liczby splotów przędzy albo nici w pojedynczy, grubszy i bardziej wytrzymały splot; obsługę i monitorowanie urządzeń do końcowej obróbki przędzy, w tym m.in.: urządzeń do stabilizacji skrętu nitek po procesach przędzenia i skręcania, w celu wyeliminowania skłonności nitek do samorzutnego odkręcania się, urządzeń do wykurczania nitek w celu uzyskania włóczki puszystej albo przewijania przędzy lub nici z jednego nawoju na drugi; czyszczenie maszyn oraz wykonywanie prostych czynności konserwacyjnych; zapisywanie wykonanej produkcji w kartach pracy; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',380);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8152','Operatorzy maszyn tkackich i dziewiarskich','Operatorzy maszyn tkackich i dziewiarskich obsługują i monitorują krosna i maszyny dziewiarskie oraz maszyny pokrewne stosowane do wytwarzania: tkanin, wyrobów pasmanteryjnych i dywanowych, dzianin metrażowych i dekoracyjnych, dziewiarskich wyrobów odzieżowych, itp.','Zadania wykonywane przez operatorów maszyn tkackich i dziewiarskich zazwyczaj obejmują: obsługę i monitorowanie krosien czółenkowych i bezczółenkowych do wytwarzania tkanin bawełnianych i bawełnopodobnych, wełnianych i wełnopodobnych, jedwabnych, lnianych, o różnorodnym przeznaczeniu użytkowym; obsługę i nadzorowanie krosien oraz maszyn igłowych do wytwarzania wyrobów dywanowych i pluszowych; obsługę i monitorowanie krosien, plecionkarek i koronkarek do wytwarzania wyrobów pasmanteryjnych: taśm tkanych, aksamitek, wstawek i koronek itp.; obsługę i monitorowanie maszyn dziewiarskich, takich, jak: osnowarki, szydełkarki, falowarki, przeznaczonych do wytwarzania dzianin metrażowych, firanek, koronek, dziewiarskich wyrobów odzieżowych oraz pończoszniczych; obsługę i monitorowanie hafciarek przeznaczonych do wykonywania haftu na tkaninach; obsługę i monitorowanie cewiarek wątku oraz snowarek, klejarek, wiązarek osnów; obsługę i monitorowanie nawijarek do formowania wielobarwnych nawojów osnowy przeznaczonych do zasilania osnową runową dywanowych krosien aksminster; sprawdzanie stanu krosien tkackich celem stwierdzenia przyczyn zatrzymania się krosna, takich jak: nadmierne wypełnienie osnową, złamanie nicielnicy lub usterki mechaniczne; dokonywanie napraw lub wymiany zużytych lub niesprawnych igieł i innych elementów; czyszczenie, oliwienie i smarowanie maszyn; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',381);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8153','Operatorzy maszyn do szycia','Operatorzy maszyn do szycia obsługują i monitorują działanie maszyn do szycia, służących do wytwarzania, naprawiania, cerowania i regeneracji wyrobów włókienniczych, futrzarskich, syntetycznych lub skórzanych, do haftowania wzorów ozdobnych na ubiorach lub innych materiałach, a także do wykonywania dziurek, przyszywania guzików i przytwierdzania nitów rurkowych do ubiorów.','Zadania wykonywane przez operatorów maszyn do szycia zazwyczaj obejmują: obsługę specjalistycznych, jedno- lub wieloigłowych maszyn do szycia wyrobów z tkanin, dzianin, skór i tworzyw skóropodobnych, filców itp.; obsługę specjalistycznych, jedno- lub wieloigłowych maszyn do haftowania wzorów na elementach wyrobów z tkanin lub innych materiałów; obsługę maszyn szyjących futra, łączących pasma skóry futerkowej w wymaganym rozmiarze i łączących skórki w odcinki ubioru lub warstwę zewnętrzną; obsługę dziurkarek i guzikarek oraz przyszywanie haczyków, suwaków, zapięć lub innych akcesoriów do tkanin; monitorowanie pracy maszyn w celu wykrycia problemów, takich jak: wadliwe ściegi, przerwy w przewlekaniu lub usterki maszyny; wymianę igieł i przeprowadzanie czynności konserwacyjnych sprzętu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',382);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8154','Operatorzy maszyn wykończalniczych wyrobów włókienniczych','Operatorzy maszyn wykończalniczych wyrobów włókienniczych obsługują i monitorują pracę maszyn bielących, kurczących, barwiących włókna, przędze, tkaniny lub wyroby dziewiarskie i poddające je innym formom obróbki.','Zadania wykonywane przez operatorów maszyn wykończalniczych wyrobów włókienniczych zazwyczaj obejmują: uruchamianie i monitorowanie maszyn i urządzeń do bielenia, barwienia lub innego przetwarzania i wykańczania materiałów, przędzy, nici i/lub innych wyrobów włókienniczych; nadzorowanie pracy maszyn kurczących utkane lub dziane tkaniny do określonego rozmiaru lub wzmacniających ich splot; obsługę i monitorowanie pracy maszyn do obróbki jedwabiu celem nadania mu określonego wyglądu i ciężaru; obsługę i monitorowanie pracy maszyn impregnujących wyroby włókiennicze za pomocą środków chemicznych, aby nadać im cechę wodoodporności; barwienie wyrobów celem zmiany lub przywrócenia ich kolorów; obsługę i nadzorowanie pracy maszyn stabilizujących i nadających wyrobom włókienniczym połysk lub inny rodzaj wykończenia; nadzorowanie różnych automatycznych maszyn czeszących i polerujących skóry futerkowe; obsługę maszyn czyszczących, sterylizujących i spulchniających pierze i filc; czyszczenie maszyn oraz wykonywanie prostych czynności konserwacyjnych.',383);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8155','Operatorzy maszyn do wyprawiania futer i skór','Operatorzy maszyn do wyprawiania futer i skór obsługują i monitorują działanie maszyn do mechanicznej i chemicznej obróbki skóry lub obróbki skórek futro- lub wełnodajnych w procesach przygotowania, garbowania i wykańczania w celu wyprodukowania wyrobów skórzanych i wyprawionych futer.','Zadania wykonywane przez operatorów maszyn do wyprawiania futer i skór zazwyczaj obejmują: obsługę i monitorowanie pracy maszyn oddzielających mięso i tłuszcz od skór surowych i skórek celem ich wyczyszczenia i zmiękczenia przed rozpoczęciem przetwarzania; obsługę i monitorowanie pracy maszyn do usuwania długiego i grubego włosia ze skór futerkowych, przycinania włosia do równej długości oraz rozciągania i wygładzania obrobionych skórek; obsługę i monitorowanie pracy maszyn do usuwania naskórka i cebulek włosów, komórek pigmentowych oraz soli wapniowych z lica skóry; obsługę i monitorowanie pracy maszyn zmniejszających grubość skór surowych lub powłok skórnych do jednakowego rozmiaru; obsługę i monitorowanie pracy maszyn do nadania szorstkości lub polerowania skór surowych lub powłok skórnych celem dokonania określonej obróbki wykończeniowej; obsługę i monitorowanie pracy maszyn oddzielających pozostałości wełny od powłok skórnych lub mięsa i włosia od skór surowych; obsługę i monitorowanie pracy maszyn do rozcinania skór surowych w celu uformowania dwóch lub więcej części, zapewniając jednorodną grubość; obsługę i monitorowanie pracy maszyn do obróbki skór surowych i powłok skórnych w roztworach celem ich przetworzenia w skórę wyprawioną; obróbkę powierzchni skór wyprawionych olejem i obsługę nabłyszczarki w celu nadania błyszczącego wykończenia skórze wyprawionej; obsługę i monitorowanie pracy maszyn nakładających barwniki i farby na skórę wyprawioną; czyszczenie i konserwowanie maszyn i kadzi oraz dokonywanie drobnych napraw.',384);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8156','Operatorzy maszyn do produkcji obuwia i pokrewni','Operatorzy maszyn do produkcji obuwia i pokrewni obsługują i monitorują pracę maszyn do produkcji obuwia standardowego i specjalnego, wyrobów kaletniczych i rymarskich, wykonanych głównie ze skóry.','Zadania wykonywane przez operatorów maszyn do produkcji obuwia i pokrewnych pracowników zazwyczaj obejmują: obsługę i monitorowanie pracy maszyn wycinających części obuwia; obsługę i monitorowanie pracy maszyn zszywających części obuwia, wykonujących obróbkę krawędzi, polerujących lub nakładających wzory; obsługę i monitorowanie pracy maszyn do montażu obuwia i wykonujących czynności wykończeniowe; obsługę i monitorowanie maszyn do produkcji toreb podróżnych, toreb podręcznych, pasków i innych akcesoriów, a także innych przedmiotów, takich jak: siodła, obroże lub uprzęże, wykonywanie innych zadań pokrewnych.',385);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8157','Operatorzy maszyn do prania','Operatorzy maszyn do prania obsługują i nadzorują maszyny do prania, chemicznego czyszczenia, barwienia i prasowania odzieży, bielizny i innych wyrobów włókienniczych w pralniach i punktach prania chemicznego.','Zadania wykonywane przez operatorów maszyn do prania zazwyczaj obejmują: przygotowywanie partii technologicznych do prania lub czyszczenia: segregowanie bielizny i odzieży pod względem koloru, asortymentu, stopnia zabrudzenia i rodzaju włókien; zapieranie lub kierowanie do zapierania odzieży szczególnie zabrudzonej, usuwanie plam i zacieków; obsługiwanie i monitorowanie pralnic i pralnico-wirówek służących do prania bielizny i odzieży na mokro; obsługiwanie i monitorowanie maszyn do odwadniania bielizny i odzieży po praniu: wirówek, wyżymarek i pras odwadniających oraz urządzeń do suszenia; obsługiwanie i nadzorowanie barwiarek służących do barwienia i przebarwiania wyrobów włókienniczych; obsługiwanie i nadzorowanie agregatów do czyszczenia (prania) odzieży i innych wyrobów włókienniczych za pomocą środków chemicznych; załadowywanie i rozładowywanie maszyn; dozowanie środków piorących, przygotowywanie i dozowanie apretur; obsługiwanie pras i manekinów parowo-powietrznych do prasowania odzieży oraz prasownic do bielizny płaskiej (magli prasujących); obsługiwanie wytwornicy pary zasilającej urządzenia prasowalnicze; czyszczenie obsługiwanych maszyn oraz wykonywanie podstawowych zabiegów konserwacyjnych; pakowanie i umieszczanie wyrobów na półkach lub wieszanie wyrobów przeznaczonych do dostawy lub odbioru. Do ich zadań może także należeć obsługiwanie agregatu do czyszczenia pierza i maszyn do plisowania.',386);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8159','Operatorzy maszyn do produkcji wyrobów włókienniczych, futrzarskich i skórzanych gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje operatorów maszyn do produkcji wyrobów włókienniczych, futrzarskich i skórzanych niesklasyfikowanych w innych podgrupach grupy 815 Operatorzy maszyn do produkcji wyrobów włókienniczych, futrzarskich i skórzanych. Grupa ta obejmuje na przykład osoby zajmujące się obsługą i monitorowaniem pracy maszyn do produkcji włóknin i przędzin, kapeluszy, namiotów lub wyrobów, takich jak: plecionki i inne wykończenia oraz maszyn do przygotowywania materiałów i elementów tapicerskich, nawarstwiania tkanin, wykrawania elementów wyrobów i ich klejenia.','Zadania wykonywane przez operatorów maszyn do produkcji wyrobów włókienniczych, futrzarskich i skórzanych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: obsługę i monitorowanie maszyn do produkcji włóknin igłowanych, igłowano-wykurczanych, klejonych i przeszywanych lub wytwarzanych systemem mokrym; obsługę i monitorowanie maszyn do wytwarzania przędzin płaskich lub z okrywą pętlową; obsługę i monitorowanie maszyn do składania tkanin w odcinki o odmierzonej długości; obsługę i monitorowanie maszyn do wykrawania elementów wyrobów oraz łączenia elementów wyrobów metodą klejową; obsługę i monitorowanie różnego rodzaju maszyn służących do przygotowywania tapicerskich materiałów wyściółkowych oraz wykonywania części tapicerowanych mebli lub innych wyrobów; obsługę i monitorowanie pracy maszyn do formowania kapeluszy z wyrobów włókienniczych, futrzarskich lub skórzanych; obsługę i monitorowanie pracy maszyn do produkcji takich wyrobów, jak: wstążki, tasiemki, wyroby powroźnicze, liny, szpagaty i wyroby sieciowe; obsługę i monitorowanie pracy maszyn do nawijania nici, sznurków lub przędzy w kłębki, przygotowując je do wysyłki lub dalszego przetwarzania; okresowe czyszczenie maszyn i urządzeń oraz wykonywanie prostych czynności konserwacyjnych.',387);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8160','Operatorzy maszyn do produkcji wyrobów spożywczych i pokrewni','Operatorzy maszyn do produkcji wyrobów spożywczych i pokrewni ustawiają i obsługują maszyny do uboju zwierząt, oddzielania mięsa od tusz; wypiekania, zamrażania, podgrzewania, rozgniatania, mieszania i innych form przetwarzania artykułów spożywczych, napojów i tytoniu.','Zadania wykonywane przez operatorów maszyn do produkcji wyrobów spożywczych i pokrewnych pracowników zazwyczaj obejmują: obsługę i monitorowanie pracy maszyn do uboju zwierząt i drobiu, cięcia tusz mięsnych i rybich na standardowe kawałki; nastawianie i obsługę maszyn i pieców stosowanych do mieszania składników, formowania ciasta i wypiekania pieczywa i wyrobów ciastkarskich; obsługę urządzeń do przygotowania i mieszania surowców oraz prowadzenia procesu fermentacji przy produkcji piwa, wina, wyrobów spirytusowych, octu, drożdży i pokrewnych produktów; obsługę urządzeń do produkcji dżemów, wyrobów cukierniczych, czekolady, lodów, makaronów, kiełbas, serów, masła i innych tłuszczów jadalnych, mączki ziemniaczanej i dekstryny; obsługę urządzeń do chłodzenia, podgrzewania, suszenia, pieczenia, blanszowania, pasteryzowania, wędzenia, sterylizowania, zamrażania, odparowywania i koncentrowania artykułów spożywczych i płynów, wykorzystywanych w procesach przetwarzania żywności; rozcieranie na miazgę, mielenie, sporządzanie mieszanek i oddzielanie płynów za pomocą sprzętu prasującego, przesiewającego, mielącego i filtrującego; obsługę maszyn do obróbki liści tytoniu i wyrobu papierosów; czyszczenie, mycie, dezynfekowanie i konserwowanie maszyn i urządzeń; wykonywanie innych zadań pokrewnych.',388);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8171','Operatorzy urządzeń do wyrobu masy papierniczej i produkcji papieru','Operatorzy urządzeń do wyrobu masy papierniczej i produkcji papieru obsługują i monitorują pracę maszyn do przetwarzania drewna i innych materiałów celulozowych (makulatury, słomy) na masę papierniczą, maszyn stosowanych przy wytwarzaniu papieru i tektury oraz urządzeń do uszlachetniania papieru.','Zadania wykonywane przez operatorów urządzeń do wyrobu masy papierniczej i produkcji papieru zazwyczaj obejmują: obsługę i monitorowanie pracy rębaków i maszyn do ścierania drewna; obsługę i monitorowanie pracy urządzeń sortujących, bielących, warników, mieszalników, aparatów myjących i innych urządzeń do przetwarzania masy włóknistej na masę papierniczą; obsługę i monitorowanie pracy maszyn formujących papier lub tekturę z mokrej masy papierniczej, kalandrów stosowanych do nadawania odpowiedniej gładkości lub połysku albo wytłaczania papieru oraz urządzeń do impregnowania lub powlekania papierów i tektur mieszankami pigmentowo-klejowymi; obsługę i nadzorowanie urządzeń towarzyszących, służących do zwijania, przewijania i przecinania papieru; kontrolę rozruchu i zakończenia pracy maszyn i urządzeń wykorzystywanych w trakcie procesu produkcyjnego oraz obserwowanie wskaźników i przyrządów pomiarowych stanowiących ich wyposażenie w celu wykrycia nieprawidłowości w działaniu maszyn oraz upewnienia się, że poszczególne etapy procesu są wykonywane zgodnie z obowiązującymi instrukcjami; analizowanie odczytów z przyrządów pomiarowych i wyników badania próbek produkcyjnych oraz, w razie konieczności, regulowanie parametrów procesu i pracy maszyn; dokonywanie wpisów w raportach produkcyjnych; wykonywanie innych zadań pokrewnych.',389);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8172','Operatorzy maszyn i urządzeń do obróbki drewna','Operatorzy maszyn i urządzeń do obróbki drewna obsługują, kontrolują i monitorują pracę zautomatyzowanych maszyn do piłowania drewna na surowiec drzewny, pozyskania forniru, wyrobu sklejki i różnego rodzaju płyt oraz przygotowujących drewno do dalszego użytku.','Zadania wykonywane przez operatorów maszyn i urządzeń do obróbki drewna zazwyczaj obejmują: badanie kłód i surowca drzewnego w celu określenia rozmiaru, stanu, jakości i innych cech koniecznych do podjęcia decyzji co do najlepszego sposobu cięcia lub obsługę zautomatyzowanego sprzętu do przesuwania kłód przez skaner laserowy, który określa najbardziej wydajne i korzystne wzory cięć; obsługę i monitorowanie systemu podajników i przenośników kłód; obsługę i monitorowanie pracy zautomatyzowanych maszyn tartacznych z pozycji sterowni lub konsoli sprzętu: pił głowicowych, pił rozdzielczych oraz pił wieloostrzowych, służących do piłowania kłód, ścinania krawędzi, piłowania płyt oraz obrabiania chropowatych krawędzi spiłowanego surowca drzewnego celem przetworzenia go na drewno obrobione o różnych rozmiarach, a także maszyn do piłowania i rozdrabniania pękniętych kawałków drewna; obsługę i monitorowanie pracy rdzeniarki do produkcji sklejki oraz pras do sklejki wyposażonych w płyty grzejne, maszyn do cięcia forniru oraz maszyn do wytwarzania płyt pilśniowych, wiórowych i paździerzowych; czyszczenie i smarowanie maszyn tartacznych; wykonywanie innych zadań pokrewnych.',390);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8181','Operatorzy urządzeń do produkcji wyrobów szklanych i ceramicznych','Operatorzy urządzeń do produkcji wyrobów szklanych i ceramicznych obsługują i monitorują pracę pieców i innych urządzeń stosowanych do wyrobu szkła, ceramiki, porcelany, płytek ceramicznych lub cegieł oraz urządzeń do odprężania, hartowania lub dekorowania szkła i ceramiki.','Zadania wykonywane przez operatorów urządzeń do produkcji wyrobów szklanych i ceramicznych zazwyczaj obejmują: obsługę i monitorowanie pracy pieców do produkcji wyrobów szklanych poprzez stapianie wymieszanych składników; obsługę urządzeń do pokrywania wyrobów szklanych utwardzaczem, natryskiwanym na gorąco lub zimno; obsługę i monitorowanie pracy maszyn prasujących lub nadmuchujących stopione szkło w celu uformowania takich wyrobów, jak: butelki, słoiki i szklanki; obsługę prasy ręcznej do formowania szkła do pożądanego kształtu; obsługę maszyn do formowania szkła płaskiego typu float oraz metodą walcowania i ciągnienia; obsługę i monitorowanie pieców do hartowania szkła; obsługę i monitorowanie maszyn do obróbki wykończeniowej, stosowanych przy szlifowaniu, wierceniu, wygładzaniu, cięciu skośnym, dekorowaniu, myciu lub polerowaniu szkła lub wyrobów szklanych; obsługę maszyn do mieszania gliny z wodą w celu zagniecenia mieszaniny aż do uzyskania odpowiedniej formy plastycznej lub półpłynnej przy produkcji wyrobów ceramicznych; nastawianie, obsługę i monitorowanie maszyn prasujących w celu formowania wyrobów ceramicznych z wilgotnej gliny; obsługę i monitorowanie pieców garncarskich stosowanych przy wyrobie porcelany oraz cegieł i płytek ceramicznych; obsługę i monitorowanie maszyn do wyrobu glazury lub ściernic; obsługę i monitorowanie maszyn do wytłaczania włókien szklanych ze stopionego szkła; badanie gotowych produktów w celu wykrycia rozszczepień, pęknięć, przerw, przebarwień i innych wad; czyszczenie i konserwowanie obsługiwanych urządzeń; wykonywanie innych zadań pokrewnych.',391);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8182','Maszyniści kotłów parowych i pokrewni','Maszyniści kotłów parowych i pokrewni konserwują i obsługują silniki i kotły parowe oraz urządzenia pomocnicze w celu zapewnienia zasilania w parę wodną i energię cieplną obiektów usługowych, przemysłowych i instytucji oraz statków i samobieżnych pojazdów.','Zadania wykonywane przez maszynistów kotłów parowych i pokrewnych pracowników zazwyczaj obejmują: obsługę, czyszczenie, smarowanie i monitorowanie pracy silników i kotłów parowych oraz sprzętów pomocniczych, takich jak: pompy, kompresory i klimatyzatory w celu zaopatrzenia i utrzymania dostaw pary wodnej i energii do budynków, statków morskich lub narzędzi pneumatycznych; badanie i zapisywanie odczytów przyrządów kontrolno-pomiarowych, wykrywanie i usuwanie usterek oraz dokonywanie drobnych napraw w celu zapobieżenia awariom sprzętu lub systemu; monitorowanie i sprawdzanie pracy sprzętu pod kątem skutecznego działania i utrzymania poziomu wody kotłowej, środków chemicznych i paliwa na wymaganym poziomie; ręczne rozpalanie palenisk węglowych lub za pomocą rusztu mechanicznego; rozpalanie kotłów gazowych lub olejowych przy użyciu automatycznych zasilaczy gazowych lub pomp olejowych; uruchamianie i obsługiwanie urządzeń do nawęglania, sterowanie ruchem urządzeń transportowych z centralnej nastawni nawęglania oraz obsługę i nadzorowanie urządzeń do transportu popiołu i żużla; badanie jakości wody kotłowej lub organizację badania; regulację pracy kotła lub podejmowanie niezbędnych działań naprawczych, takich jak: uzupełnianie poziomu środków chemicznych zapobiegających wystąpieniu korozji i szkodliwych osadów; monitorowanie pracy silnika statku, maszyn i wskaźników działania sprzętu, zapisywanie zmiennych i zgłaszanie odchyleń od normy oficerowi mechanikowi; obsługę i konserwację pomp i zaworów przeładunkowych; wykonywanie zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',392);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8183','Operatorzy urządzeń pakujących, znakujących i urządzeń do napełniania butelek','Operatorzy urządzeń pakujących, znakujących i urządzeń do napełniania butelek obsługują i monitorują pracę maszyn do ważenia, pakowania i znakowania produktów lub umieszczania produktów w pojemnikach.','Zadania wykonywane przez operatorów urządzeń pakujących, znakujących i urządzeń do napełniania butelek zazwyczaj obejmują: obsługę i monitorowanie pracy maszyn do ważenia, zawijania, pieczętowania i pakowania rozmaitych produktów; obsługę i monitorowanie pracy maszyn do wypełniania tubek, butelek, puszek, pudełek, torebek i innych pojemników takimi produktami, jak: żywność, napoje, farby, oleje i płyny kosmetyczne oraz do ich pieczętowania; obsługę i monitorowanie maszyn do znakowania produktów, opakowań i rozmaitych pojemników za pomocą klejenia lub innych metod; wykonywanie innych zadań pokrewnych.',393);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8189','Operatorzy innych maszyn i urządzeń przetwórczych gdzie indziej niesklasyfikowani','Ta grupa elementarna obejmuje operatorów innych maszyn i urządzeń przetwórczych niesklasyfikowanych w innych podgrupach grupy 81 Operatorzy maszyn i urządzeń wydobywczych i przetwórczych. Grupa ta obejmuje na przykład operatorów urządzeń utylizacji surowców zwierzęcych, operatorów urządzeń do paletyzacji, operatorów maszyn do wyrobu płytek krzemowych, złącz kablowych i zaplatanych lin stalowych.','Zadania wykonywane przez operatorów innych maszyn i urządzeń przetwórczych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: obsługę i monitorowanie pracy urządzeń stosowanych do przerobu odpadów zwierzęcych i padłych zwierząt na mączki paszowe; obsługę i monitorowanie pracy urządzeń służących do układania wyrobów warstwami na paletach i do ich rozładowywania; obsługę i monitorowanie maszyn do wyrobu płytek krzemowych, mających zastosowanie w mikroprocesorach; obsługę i monitorowanie maszyn do wyrobu złącz kablowych; obsługę i monitorowanie maszyn do wyrobu zaplatanych lin stalowych; wykonywanie innych zadań pokrewnych.',394);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8211','Monterzy maszyn i urządzeń mechanicznych','Monterzy maszyn i urządzeń mechanicznych zajmują się montowaniem komponentów maszyn i urządzeń mechanicznych, takich jak: silniki, pojazdy mechaniczne, turbiny i samoloty, maszyny i urządzenia przemysłowe, zgodnie ze ściśle wytyczonymi procedurami.','Zadania wykonywane przez monterów maszyn i urządzeń mechanicznych zazwyczaj obejmują: montaż i instalację części lub komponentów prefabrykowanych w celu produkcji podzespołów, maszyn i urządzeń mechanicznych i gotowych pojazdów mechanicznych; sprawdzanie współdziałania części, podzespołów i zespołów za pomocą przyrządów kontrolno-pomiarowych oraz wykrywanie i usuwanie wadliwej pracy w montowanych układach, maszynach czy urządzeniach; dokonywanie przeglądów zamówień, specyfikacji, diagramów i rysunków w celu określenia koniecznych materiałów i zapoznania się z instrukcjami montażu; zapisywanie danych produkcyjnych i operacyjnych na określonych formularzach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',395);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8212','Monterzy sprzętu elektrycznego','Monterzy sprzętu elektrycznego montują lub modyfikują, zgodnie ze ściśle wytyczonymi procedurami, komponenty sprzętów elektrycznych i elektromechanicznych, takich jak: aparatura rozdzielcza, liczniki energii elektrycznej, maszyny elektryczne, elektryczny sprzęt gospodarstwa domowego, akumulatory i baterie oraz inne urządzenia elektryczne.','Zadania wykonywane przez monterów sprzętu elektrycznego zazwyczaj obejmują: montaż komponentów i systemów elektrycznych raz ustawianie, zestrajanie i przytwierdzanie jednostek do zespołów, podzespołów lub ram, przy użyciu narzędzi ręcznych lub z napędem mechanicznym, sprzętu lutującego i do mikrospawania; dokonywanie przeglądów zamówień, specyfikacji, diagramów i rysunków w celu określenia koniecznych materiałów i zapoznania się z instrukcjami montażu; obsługę maszyn do nawijania drutów w celu nawijania drutów stosowanych w sprzęcie elektrycznym i takich komponentów, jak: urządzenia rejestrujące, transformatory, tworniki, silniki elektryczne i prądnice; sprawdzanie i badanie gotowych komponentów i zespołów, instalacji i obwodów elektrycznych oraz odrzucanie wadliwych zespołów i komponentów; zapisywanie danych produkcyjnych i operacyjnych na określonych formularzach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',396);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8213','Monterzy sprzętu elektronicznego','Monterzy sprzętu elektronicznego montują lub modyfikują zgodnie ze ściśle wytyczonymi procedurami komponenty sprzętów elektronicznych: podzespoły, zespoły i gotowe wyroby sprzętu elektronicznego.','Zadania wykonywane przez monterów sprzętu elektronicznego zazwyczaj obejmują: montaż komponentów i systemów elektronicznych oraz ustawianie, zestrajanie i przytwierdzanie jednostek do zespołów, podzespołów lub ram, przy użyciu narzędzi ręcznych lub z napędem mechanicznym, sprzętu lutującego i mikrospawającego; dokonywanie przeglądów zamówień, specyfikacji, diagramów i rysunków w celu określenia koniecznych materiałów i zapoznania się z instrukcjami montażu; sprawdzanie i badanie gotowych komponentów i zespołów oraz odrzucanie wadliwych zespołów i komponentów; zapisywanie danych produkcyjnych i operacyjnych na określonych formularzach; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć nadzorowanie innych pracowników.',397);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8219','Monterzy gdzie indziej niesklasyfikowani','Monterzy gdzie indziej niesklasyfikowani zajmują się montowaniem, zgodnie ze ściśle wytyczonymi procedurami, rozmaitych wyrobów niezawierających komponentów elektronicznych, elektrycznych lub mechanicznych.','Zadania wykonywane przez monterów gdzie indziej niesklasyfikowanych zazwyczaj obejmują: montaż komponentów wyrobów z metalu, gumy i tworzyw sztucznych, wyrobów z drewna, wyrobów z tektury tkanin i skóry lub wyrobów bardziej złożonych, przy użyciu narzędzi ręcznych lub z napędem mechanicznym, sprzętu lutującego i spawającego; dokonywanie przeglądów zamówień, specyfikacji, diagramów i rysunków w celu określenia koniecznych materiałów i zapoznania się z instrukcjami montażu; sprawdzanie i badanie gotowych komponentów i zespołów; odrzucanie wyrobów wadliwych; zapisywanie danych produkcyjnych i operacyjnych na określonych formularzach; wykonywanie innych zadań pokrewnych.',398);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8311','Maszyniści kolejowi i metra','Maszyniści kolejowi i metra zajmują się prowadzeniem lub pomocą w prowadzeniu lokomotywy w celu wykonania przewozów pasażerów i towarów.','Zadania wykonywane przez maszynistów kolejowych i metra zazwyczaj obejmują: sterowanie lub pomoc w sterowaniu lokomotywą parową, elektryczną lub spalinowo-elektryczną; sterowanie podziemnymi lub nadziemnymi pociągami pasażerskimi; sterowanie lokomotywą do holowania wózków pod ziemią lub na powierzchni w kopalniach i kamieniołomach; obserwowanie trasy pod kątem niebezpieczeństw, obserwowanie sygnalizacji i odczytów przyrządów pomiarowych; obsługę systemu komunikacyjnego do utrzymywania kontaktu z załogami pociągów i kontrolerami ruchu w celu zagwarantowania bezpiecznej pracy pociągów i zgodności z rozkładem jazdy; prowadzenie na stacjach i liniach kolejowych drezyn i wózków motorowych oraz obsługiwanie żurawi zamontowanych na tych wózkach; prowadzenie i obsługiwanie maszyn wieloczynnościowych i ciężkich maszyn do kolejowych robót budowlanych i kolejowej sieci trakcyjnej; sporządzanie raportów służbowych oraz prowadzenie dokumentacji z zakresu swojej pracy i pracy pojazdu kolejowego; wykonywanie innych zadań pokrewnych.',399);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8312','Dyżurni ruchu, manewrowi i pokrewni','Dyżurni ruchu, manewrowi i pokrewni odpowiadają za ochronę towarów w trakcie ich przewozu koleją, sprawują kontrolę nad ruchem pociągów towarowych i osobowych przez obsługę sygnalizatorów, obsługują zwrotnice kierując tabor na odpowiednie tory i zestawiają pociągi na stacjach rozrządowych lub zestawiają pociągi do wykonywania transportu w kopalniach i sprawują kontrolę nad ich ruchem.','Zadania wykonywane przez dyżurnych ruchu, manewrowych i pokrewnych pracowników zazwyczaj obejmują: kontrolę płynności ruchu kolejowego na podległym odcinku przez obsługiwanie urządzeń sygnalizacyjnych i zwrotnic z pulpitu operatora lub nastawni; obejmowanie kierownictwa i sprawowanie ochrony nad pociągami towarowymi podczas ruchu; przełączanie i ustawianie taboru na stacjach rozrządowych i bocznicach zgodnie z dyspozycjami dotyczącymi ładowania, rozładowywania i zestawiania pociągów; zestawianie pociągów holowanych przez lokomotywę oraz kolej linową i kierowanie ich ruchem w chodnikach przewozowych kopalń lub kamieniołomów; sprawdzanie systemów i urządzeń znajdujących się w pociągu, takich jak: klimatyzacja i ogrzewanie, hamulce i węże sprzęgu hamulcowego, przed rozpoczęciem ruchu pociągu; kompletowanie listów przewozowych i innej dokumentacji towarzyszącej wagonowi lub przesyłce towarowej; wykonywanie innych zadań pokrewnych.',400);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8321','Kierowcy motocykli','Kierowcy motocykli prowadzą i obsługują motocykle lub trójkołowce silnikowe, przeznaczone do przewozu pasażerów, materiałów lub towarów.','Zadania wykonywane przez kierowców motocykli zazwyczaj obejmują: kierowanie i obsługę motocykli lub trójkołowców silnikowych w celu przewozu pasażerów, materiałów i towarów; dostarczanie wiadomości; przestrzeganie przepisów i sygnalizacji drogowej; czyszczenie i mycie pojazdów oraz wykonywanie czynności konserwacyjnych i drobnych napraw; prowadzenie rejestru podróży; wykonywanie innych zadań pokrewnych.',401);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8322','Kierowcy samochodów osobowych i dostawczych','Kierowcy samochodów osobowych i dostawczych prowadzą i obsługują samochody osobowe i dostawcze w celu przewozu pasażerów, poczty lub towarów.','Zadania wykonywane przez kierowców samochodów osobowych i dostawczych zazwyczaj obejmują: kierowanie i obsługę samochodów osobowych, dostawczych i taksówek; pomaganie pasażerom w przenoszeniu bagaży; udzielanie pomocy pasażerom niepełnosprawnym ruchowo; obsługę sprzętu ułatwiającego wchodzenie i wychodzenie pasażerom niepełnosprawnym ruchowo; obserwację ruchu drogowego w celu zapewnienia bezpiecznego przejazdu; określanie najdogodniejszej trasy przejazdu; pobieranie opłat za przejazd, opłat za przewóz lub dokumentów potwierdzających wykonanie dostawy; obsługę sprzętu telekomunikacyjnego w celu zgłaszania położenia i dostępności oraz wykonywania poleceń centrum sterowania; wykonywanie innych zadań pokrewnych.',402);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8331','Kierowcy autobusów i motorniczowie tramwajów','Kierowcy autobusów i motorniczowie tramwajów prowadzą i obsługują autobusy, trolejbusy lub tramwaje do przewozu pasażerów.','Zadania wykonywane przez kierowców autobusów i motorniczych tramwajów zazwyczaj obejmują: kierowanie i obsługę autobusów, trolejbusów lub autokarów do przewozu pasażerów na krótkich lub długich dystansach; kierowanie i obsługę tramwajów do przewozu pasażerów; otwieranie i zamykanie drzwi przed wejściem lub po wyjściu pasażerów; obsługę sprzętu ułatwiającego wchodzenie i wychodzenie pasażerom niepełnosprawnym ruchowo; pomoc pasażerom w przenoszeniu bagaży; kontrolowanie oświetlenia, ogrzewania i wentylacji w autobusach i tramwajach; obserwację ruchu drogowego w celu zapewnienia bezpiecznego przejazdu; pobieranie opłat za przejazd lub sprawdzanie biletów pasażerów; obsługę sprzętu telekomunikacyjnego w celu utrzymywania kontaktu z centrum sterowania; wykonywanie innych zadań pokrewnych.',403);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8332','Kierowcy samochodów ciężarowych','Kierowcy samochodów ciężarowych prowadzą i obsługują samochody ciężarowe i autocysterny przeznaczone do przewozu różnego rodzaju towarów, cieczy i materiałów ciężkich na krótkich lub długich dystansach.','Zadania wykonywane przez kierowców samochodów ciężarowych zazwyczaj obejmują: kierowanie i obsługę samochodów ciężarowych z naczepą lub bez naczepy albo samochodu wywrotki w celu przewozu towarów, w tym cieczy i sypkich materiałów ciężkich; czuwanie nad prawidłowym załadowaniem, zamocowaniem i nakryciem towaru celem zapobiegnięcia jego stratom i uszkodzeniom oraz zapewnienia bezpieczeństwa przejazdu; określanie najdogodniejszej trasy przejazdu oraz ciężaru ładunku uwzględniającego ograniczenia drogowe na tej trasie; obserwację ruchu drogowego w celu zapewnienia bezpiecznego przejazdu; pomoc lub wykonywanie czynności ładunkowych i rozładunkowych przy użyciu różnych urządzeń podnoszących i przechylających; usuwanie usterek powstałych w czasie jazdy, przeprowadzanie drobnych prac konserwacyjnych oraz organizowanie napraw i konserwacji głównych; prowadzenie dokumentacji jazdy; wykonywanie innych zadań pokrewnych.',404);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8341','Operatorzy wolnobieżnych maszyn rolniczych i leśnych','Operatorzy wolnobieżnych maszyn rolniczych i leśnych prowadzą, obsługują i monitorują pracę rozmaitych typów zmotoryzowanych, wyspecjalizowanych, ruchomych maszyn lub urządzeń stosowanych w rolnictwie, sadownictwie i leśnictwie.','Zadania wykonywane przez operatorów wolnobieżnych maszyn rolniczych i leśnych zazwyczaj obejmują: kierowanie i obsługę samobieżnych lub napędzanych ciągnikiem, wyspecjalizowanych maszyn rolniczych do orania, wysiewania, nawożenia, uprawiania i zbierania plonów; kierowanie i obsługę samobieżnych lub napędzanych ciągnikiem, wyspecjalizowanych maszyn leśniczych do oczyszczania, sadzenia, zbierania i przenoszenia drzew lub drewna lub do wykonywania innych czynności leśniczych; przygotowywanie i ustawianie urządzeń przed rozpoczęciem pracy; dostosowywanie prędkości maszyn i ustawienia ich elementów roboczych w trakcie pracy; obsługę urządzeń do przytrzymywania, podnoszenia i ścinania drzew; obsługę wyposażenia do podnoszenia, kołysania, wykorzeniania i sortowania drzew i kłód oraz obsługę urządzeń pomocniczych, takich jak: maszyny strugające, rozłupujące kłody, itp.; podawanie ściętych drzew do urządzeń przetwarzających w celu usuwania kory z gałęzi i ładowania kłód na stosy, a następnie na ciężarówkę; konserwowanie i dokonywanie drobnych napraw maszyn i urządzeń; wykonywanie innych zadań pokrewnych.',405);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8342','Operatorzy sprzętu do robót ziemnych i urządzeń pokrewnych','Operatorzy sprzętu do robót ziemnych i urządzeń pokrewnych obsługują maszyny do kopania, profilowania, wyrównywania, wygładzania i ubijania ziemi lub podobnych materiałów.','Zadania wykonywane przez operatorów sprzętu do robót ziemnych i urządzeń pokrewnych zazwyczaj obejmują: obsługę i monitorowanie maszyn kopiących do kopania i przemieszczania ziemi, głazów, piasku, żwiru lub podobnych materiałów; obsługę i monitorowanie pracy maszyn kopiących kanały ściekowe, rowy odwadniające, rurociągi wodne, olejowe, gazowe lub podobne; obsługę i monitorowanie pracy maszyn przeznaczonych do przemieszczania, rozdzielania i wyrównywania ziemi, piasku, śniegu i innych materiałów; obsługę i monitorowanie pracy sprzętu do usuwania piasku, żwiru i szlamu z dna zbiorników wodnych; obsługę i monitorowanie kafarów do wbijania drewnianych, betonowych lub stalowych pali w ziemię; obsługę i monitorowanie pracy walców samobieżnych do ubijania i wygładzania warstw różnych materiałów przy budowie dróg, chodników i przy podobnych pracach; obsługę i monitorowanie pracy maszyn do rozściełania i wygładzania betonu, preparatów bitumowych lub smolnych, stosowanych przy budowie jezdni, dróg lub podobnych powierzchni; przeprowadzanie konserwacji sprzętu oraz drobnych napraw jego zespołów roboczych; wykonywanie innych zadań pokrewnych.',406);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8343','Maszyniści i operatorzy maszyn i urządzeń dźwigowo-transportowych i pokrewni','Maszyniści i operatorzy maszyn i urządzeń dźwigowo-transportowych i pokrewni obsługują i monitorują pracę dźwignic i przenośników służących do przemieszczania materiałów, sprzętu i ludzi.','Zadania wykonywane przez maszynistów i operatorów maszyn i urządzeń dźwigowo-transportowych i pokrewnych pracowników zazwyczaj obejmują: obsługę i monitorowanie pracy umiejscowionych lub ruchomych żurawi służących do podnoszenia, przenoszenia, ustawiania lub rozmieszczania ładunków poprzez podnoszenie i opuszczanie wysięgników; obsługę i monitorowanie pracy suwnic pomostowych mających zastosowanie do przemieszczania ładunków w halach produkcyjnych i suwnic bramowych używanych do obsługi składowisk i statków w portach; obsługę i monitorowanie pracy urządzeń do dźwigania, obniżania lub podnoszenia pracowników i materiałów na placu budowy lub w kopalniach; obsługę i monitorowanie pracy kolejek linowych i wyciągów narciarskich; obsługę i monitorowanie pracy wyciągarek szybowcowych, urządzeń do holowania wagonów, promów, itp.; obsługę i monitorowanie pracy systemów przenośników centralnie sterowanych, do przemieszczania materiałów sypkich; obsługę i monitorowanie pracy urządzeń do napełniania i opróżniania zbiorników; obsługę i monitorowanie pracy urządzeń do otwierania i zamykania mostów celem udrożnienia drogi wodnej dla jednostek pływających; obsługę i monitorowanie pracy urządzeń roboczych śluzy, jazu lub zapory celem umożliwienia żeglugi, przepuszczania wód i gospodarki wodą; obsługę i monitorowanie pracy urządzeń dokowych służących do ustawiania lub zdjęcia statku z doku; obsługę urządzeń dźwigowych osadzonych na łodziach lub barkach, przeznaczonych do podnoszenia, przenoszenia i rozmieszczania sprzętów i materiałów; wykonywanie innych zadań pokrewnych.',407);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8344','Kierowcy operatorzy wózków jezdniowych','Kierowcy operatorzy wózków jezdniowych prowadzą i obsługują elektryczne i spalinowe wózki platformowe lub widłowe przeznaczone do przewożenia towarów oraz podnoszenia i układania palet z towarami w stosy.','Zadania wykonywane przez kierowców operatorów wózków jezdniowych zazwyczaj obejmują: prowadzenie i obsługę elektrycznych lub spalinowych wózków widłowych podnośnikowych do ładowania, rozładowywania, przewożenia, podnoszenia i układania palet z towarami w stosy lub na półkach regałów na dworcach lotniczych, w portach, magazynach, fabrykach i innych zakładach; prowadzenie i obsługę elektrycznych lub spalinowych wózków platformowych stosowanych do poziomego przemieszczania towarów; sprawdzanie działania i przygotowywanie wózków do pracy, w tym doładowywanie akumulatorów lub uzupełnianie paliwa; wykonywanie rutynowych czynności konserwacyjnych wózków; prowadzenie rejestrów podejmowanych działań i awarii pojazdów; wykonywanie innych zadań pokrewnych.',408);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('8350','Marynarze i pokrewni (z wyłączeniem sił zbrojnych)','Marynarze i pokrewni (z wyłączeniem sił zbrojnych) wykonują obowiązki pokładowe na statku, związane z eksploatacją statku, konserwacją i utrzymaniem porządku, wykonują podobne obowiązki na pokładzie innych jednostek pływających oraz obsługują latarnie morskie.','Zadania wykonywane przez marynarzy i pokrewnych (z wyłączeniem sił zbrojnych) pracowników zazwyczaj obejmują: ustawianie wież obserwacyjnych i pełnienie wachty na morzu, podczas wchodzenia i opuszczania portu oraz przy przepływaniu statku przez wąskie drogi wodne; sterowanie statkiem zgodnie z instrukcjami; obsługę lin i sznurów oraz obsługę sprzętu cumowniczego; konserwację oraz w niektórych przypadkach, obsługę sprzętu, urządzeń ładunkowych, takielunku, sprzętu ratowniczego i przeciwpożarowego; czyszczenie pokładu i kadłuba, skrobanie, malowanie i wykonywanie innych obowiązków konserwacyjnych; nadzór nad pracami załadowczymi statku i sztauowanie ładunku; przygotowywanie urządzeń zejściowych dla pasażerów i załogi; kierowanie tratwą i załogą podczas spławu drewna lub przewozu pasażerów; wykonywanie prac zapewniających bezawaryjne funkcjonowanie latarni morskiej; wykonywanie innych zadań pokrewnych.',409);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9111','Pomoce domowe i sprzątaczki','Pomoce domowe i sprzątaczki zamiatają, odkurzają, myją i polerują, dbają o czystość bielizny pościelowej, nabywają akcesoria domowe, przygotowują żywność, podają posiłki i wykonują różne inne obowiązki domowe.','Zadania wykonywane przez pomoce domowe i sprzątaczki zazwyczaj obejmują: zamiatanie, odkurzanie, polerowanie i mycie podłóg i mebli lub innego wyposażenia; mycie okien i innych powierzchni szklanych; sprzątanie kuchni, łazienek i toalet; pranie, prasowanie i naprawa bielizny pościelowej i innych wyrobów włókienniczych; zmywanie naczyń; pomoc w przygotowywaniu, gotowaniu i podawaniu posiłków; zakup artykułów żywnościowych i innych artykułów potrzebnych w gospodarstwie domowym; sprawowanie opieki nad dziećmi i osobami starszymi; wykonywanie innych zadań pokrewnych.',410);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9112','Pomoce i sprzątaczki biurowe, hotelowe i pokrewne','Pomoce i sprzątaczki biurowe, hotelowe i pokrewne wykonują rozmaite zadania w celu utrzymania czystości i porządku wnętrz i wyposażenia hoteli, biur i innych zakładów, laboratoriów, a także samolotów, pociągów, autobusów i podobnych pojazdów.','Zadania wykonywane przez pomoce i sprzątaczki biurowe, hotelowe i pokrewne zazwyczaj obejmują: zamiatanie i odkurzanie, mycie i polerowanie podłóg, mebli i innego wyposażenia w budynkach, autokarach, autobusach, tramwajach, pociągach i samolotach; ścielenie łóżek, sprzątanie łazienek, dostarczanie ręczników, mydła i pokrewnych wyrobów; pomaganie w pracach kuchennych, wraz ze sprzątaniem kuchni i myciem naczyń; utrzymywanie w czystości narzędzi i naczyń laboratoryjnych; zbieranie śmieci, opróżnianie pojemników na odpadki oraz przewożenie ich zawartości na składowiska odpadów; wykonywanie innych zadań pokrewnych.',411);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9121','Praczki ręczne i prasowacze','Praczki ręczne i prasowacze ręcznie piorą, prasują lub piorą chemicznie bieliznę pościelową i inne wyroby włókiennicze.','Zadania wykonywane przez praczki ręczne i prasowaczy zazwyczaj obejmują: pranie i prasowanie ręczne bielizny pościelowej, ubiorów, tkanin i podobnych wyrobów, w pralni lub innym punkcie pralniczym; prasowanie bielizny pościelowej, obrusów, ręczników za pomocą maglownic; ręczne czyszczenie ubiorów, wyrobów skórzanych i podobnych przy użyciu roztworów chemicznych, w pralni chemicznej lub innym punkcie pralniczym; wymianę guzików i dokonywanie drobnych napraw; umieszczanie wyrobów na półkach lub ich rozwieszanie celem przygotowania do odbioru; wykonywanie innych zadań pokrewnych.',412);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9122','Czyściciele pojazdów','Czyściciele pojazdów myją, czyszczą i polerują pojazdy.','Zadania wykonywane przez czyścicieli pojazdów zazwyczaj obejmują: czyszczenie, mycie i polerowanie samochodów i innych pojazdów, ręcznie lub przy użyciu narzędzi ręcznych; odkurzanie wnętrz pojazdów i pranie chemiczne dywaników i tapicerki; stosowanie środków czyszczących w celu usunięcia plam we wnętrzu i na karoserii pojazdu; mycie opon i wnęk na koła oraz czernienie opon; mycie i polerowanie szyb pojazdu; opróżnianie i czyszczenie pojemników we wnętrzu pojazdów; wykonywanie innych zadań pokrewnych.',413);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9123','Zmywacze okien','Zmywacze okien myją i polerują okna i inne sprzęty szklane.','Zadania wykonywane przez zmywaczy okien zazwyczaj obejmują: mycie okien i innych powierzchni szklanych przy użyciu wody lub różnych roztworów, suszenie i polerowanie tych powierzchni; dobieranie odpowiednich przyrządów czyszczących lub polerujących; korzystanie z drabin, obrotowych rusztowań, ławeczek bosmańskich, samochodów ciężarowych o zdejmowanej hydraulicznie skrzyni w celu sięgnięcia i wyczyszczenia okien budynków wielopiętrowych; wykonywanie innych zadań pokrewnych.',414);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9129','Pozostali pracownicy zajmujący się sprzątaniem','Pozostali pracownicy zajmujący się sprzątaniem czyszczą powierzchnie, materiały i przedmioty, takie jak: dywany, ściany, baseny i chłodnie kominowe przy użyciu specjalistycznego sprzętu czyszczącego i środków chemicznych.','Zadania wykonywane przez pozostałych pracowników zajmujących się sprzątaniem zazwyczaj obejmują: czyszczenie dywanów i mebli tapicerowanych przy użyciu maszyn czyszczących i dołączonych do nich przyrządów; dobieranie i stosowanie środków czyszczących w celu usunięcia plam z dywanów; czyszczenie dywanów środkami przeciwdziałającymi osadzaniu się brudu, takimi jak: środki chemiczne i odwaniacze oraz nakładanie środków szkodnikobójczych; czyszczenie ścian kamiennych, powierzchni metalowych i pasów architrawu przy użyciu urządzeń czyszczących wodą i rozpuszczalnikami pod ciśnieniem; stosowanie środków chemicznych i metod czyszczenia pod ciśnieniem celem usunięcia mikroorganizmów z wody i systemów filtrujących oraz stosowanie pomp próżniowych mokrych i innego sprzętu ssącego w celu usunięcia produktów łuszczenia, gromadzącego się brudu i innych osadów z basenów, chłodni kominowych i zaworów upustowych; wykonywanie innych zadań pokrewnych.',415);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9211','Robotnicy wykonujący proste prace polowe','Robotnicy wykonujący proste prace polowe wykonują proste i rutynowe zadania przy produkcji owoców, orzechów, zbóż i warzyw w gospodarstwach rolnych.','Zadania wykonywane przez robotników wykonujących proste prace polowe zazwyczaj obejmują: kopanie i odgarnianie ziemi w celu budowy rowów lub na inne potrzeby; ładowanie i rozładowywanie dostaw, produktów i innych materiałów; grabienie, przerzucanie i układanie słomy, siana i podobnych materiałów; ręczne sadzenie upraw polowych; podlewanie, przerzedzanie i odchwaszczanie upraw, ręcznie lub przy użyciu narzędzi ręcznych; zbieranie owoców, orzechów, warzyw i innych upraw; sortowanie, zbieranie w wiązki i pakowanie produktów do pojemników. Do ich zadań może także należeć przeprowadzanie drobnych napraw sprzętów, budynków i ogrodzeń.',416);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9212','Robotnicy wykonujący proste prace w hodowli zwierząt','Robotnicy wykonujący proste prace w hodowli zwierząt wykonują proste i rutynowe zadania przy chowie i hodowli zwierząt, w tym drobiu i owadów.','Zadania wykonywane przez robotników wykonujących proste prace w hodowli zwierząt zazwyczaj obejmują: kopanie i odgarnianie ziemi w celu budowy rowów lub na inne potrzeby; ładowanie i rozładowywanie dostaw, produktów i innych materiałów; karmienie, pojenie i czyszczenie zwierząt oraz utrzymywanie w czystości ich kwater; monitorowanie inwentarza żywego i raportowanie jego stanu; pomoc w utrzymywaniu zdrowia i dobrostanu zwierząt; pomoc w zaganianiu, zapędzaniu i oddzielaniu zwierząt w celu wydojenia, strzyżenia, transportu lub uboju oraz pomiędzy pastwiskami; zbieranie jaj i umieszczanie ich w inkubatorach; grabienie, przerzucanie, układanie i magazynowanie siana, słomy i innych rodzajów karmy i ściółki dla zwierząt; sortowanie i pakowanie produktów do pojemników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć przeprowadzanie drobnych napraw sprzętów, budynków i ogrodzeń.',417);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9213','Robotnicy wykonujący proste prace przy uprawie roślin i hodowli zwierząt','Robotnicy wykonujący proste prace przy uprawie roślin i hodowli zwierząt wykonują proste i rutynowe zadania przy produkcji roślinnej i zwierzęcej w gospodarstwach rolnych.','Zadania wykonywane przez robotników wykonujących proste prace przy uprawie roślin i hodowli zwierząt zazwyczaj obejmują: kopanie i odgarnianie ziemi w celu budowy rowów lub na inne potrzeby; ładowanie i rozładowywanie dostaw, produktów i innych materiałów; grabienie, przerzucanie i układanie słomy, siana i podobnych materiałów; ręczne sadzenie upraw polowych; podlewanie, przerzedzanie i odchwaszczanie upraw, ręcznie lub przy użyciu narzędzi ręcznych; zbieranie owoców, orzechów, warzyw i innych upraw oraz zbieranie jaj; karmienie, pojenie i czyszczenie zwierząt oraz utrzymywanie w czystości ich kwater; monitorowanie inwentarza żywego i raportowanie jego stanu; pomoc w zaganianiu, zapędzaniu i oddzielaniu zwierząt w celu wydojenia, strzyżenia, transportu, uboju lub pomiędzy pastwiskami; sortowanie, zbieranie w wiązki i pakowanie produktów do pojemników; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć przeprowadzanie drobnych napraw sprzętów, budynków i ogrodzeń.',418);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9214','Robotnicy wykonujący proste prace w ogrodnictwie i sadownictwie','Robotnicy wykonujący proste prace w ogrodnictwie i sadownictwie wykonują proste i rutynowe zadania przy działaniach związanych z uprawą i utrzymaniem drzewek, krzewów, kwiatów i innych roślin w sadach, parkach, ogrodach prywatnych w celu produkcji owoców, młodych drzewek, cebulek i nasion lub uprawy warzyw i kwiatów przy zastosowaniu technik intensywnej uprawy.','Zadania wykonywane przez robotników wykonujący proste prace w ogrodnictwie i sadownictwie zazwyczaj obejmują: ładowanie, rozładowywanie i przenoszenie dostaw, produktów i sprzętów; przygotowywanie terenów ogrodowych i poletek przy użyciu narzędzi ręcznych i prostych maszyn; pomoc w sadzeniu i przesadzaniu kwiatów, krzewów, drzew i trawników; utrzymywanie ogrodów poprzez podlewanie, odchwaszczanie i koszenie trawników; sprzątanie ogrodów i usuwanie odpadków; pomoc w rozsadzaniu, sadzeniu i doniczkowaniu nasion, cebulek i sadzonek; opiekę nad roślinami poprzez podlewanie i odchwaszczanie ręczne; zbieranie i pakowanie roślin przeznaczonych na sprzedaż lub do transportu; wykonywanie innych zadań pokrewnych. Do ich zadań może także należeć przeprowadzanie drobnych napraw sprzętów, budynków i ogrodzeń.',419);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9215','Robotnicy wykonujący proste prace w leśnictwie','Robotnicy wykonujący proste prace w leśnictwie wykonują proste i rutynowe zadania przy uprawie i utrzymywaniu lasów naturalnych i plantacyjnych oraz przy cięciu drewna na kłody, ścinaniu i piłowaniu drzew.','Zadania wykonywane przez robotników wykonujących proste prace w leśnictwie zazwyczaj obejmują: kopanie dołów do sadzenia drzew; stertowanie i ładowanie kłód i drewna; wycinanie podszycia w drzewostanie i przerzedzanie młodych plantacji; konserwacja wież obserwacyjnych przeznaczonych do wypatrywania pożarów lasów; obcinanie dużych gałęzi i czubków drzew, przycinanie gałęzi oraz piłowanie pni na kłody; obsługę i konserwację ręcznych pił do ścinania drzew oraz cięcia ściętych drzew i gałęzi na kłody; zbieranie nasion i sadzenie rozsad; przygotowywanie siana oraz liścianek i wykładanie karmy dla zwierząt; pełnienie funkcji naganiacza w polowaniach z nagonką oraz uczestniczenie w odłowach zwierzyny za pomocą sieci; wykonywanie drobnych napraw i czynności konserwacyjnych dróg, budynków, obiektów i sprzętu leśnego; wykonywanie innych zadań pokrewnych.',420);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9216','Robotnicy wykonujący proste prace w rybołówstwie i zakładach akwakultury','Robotnicy wykonujący proste prace w rybołówstwie i zakładach akwakultury wykonują proste i rutynowe zadania w celu kultywacji, odławiania i zbierania ryb i owoców morza w działaniach związanych z akwakulturą i rybołówstwem słodkowodnym, przybrzeżnym i dalekomorskim.','Zadania wykonywane przez robotników wykonujących proste prace w rybołówstwie i zakładach akwakultury zazwyczaj obejmują: oczyszczanie dna zbiornika wodnego i karmienie hodowanych ryb i mięczaków; przygotowywanie sieci, lin i innych takli oraz sprzętów pokładowych; obsługę sprzętu rybackiego do odławiania ryb i innych żyjątek morskich; zbieranie wodorostów morskich, mchów morskich, małży i innych mięczaków; czyszczenie, sortowanie i pakowanie ryb i owoców morza przy użyciu lodu i soli oraz rozmieszczanie odłowu w ładowni; czyszczenie powierzchni pokładu i ładowni rybnych; obsługa lin cumowniczych przy dokowaniu; wykonywanie innych zadań pokrewnych.',421);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9311','Robotnicy wykonujący proste prace w kopalniach i kamieniołomach','Robotnicy wykonujący proste prace w kopalniach i kamieniołomach wykonują rutynowe zadania przy czynnościach górniczych i urabianiu skał.','Zadania wykonywane przez robotników wykonujących proste prace w kopalniach i kamieniołomach zazwyczaj obejmują: pomoc górnikom i robotnikom kamieniołomów w konserwacji maszyn, sprzętów oraz instalacji kopalnianych i znajdujących się w kamieniołomie; montowanie i rozmontowywanie sprzętu górniczego; usuwanie obudów tuneli w zużytych wyrobiskach w kopalniach i kamieniołomach; usuwanie niebezpiecznych występów w wyrobiskach kopalni lub kamieniołomu; usuwanie odpadów i materiałów zdatnych do użytku oraz sprzętu z terenu prowadzenia prac po zakończeniu czynności wydobywczych oraz sprzątanie rozsypisk skalnych i mineralnych; czyszczenie maszyn, sprzętów, narzędzi, chodników przewozowych i ciągów dostawczych; sortowanie, ładowanie, rozładowywanie, układanie i magazynowanie narzędzi, materiałów i zasobów wykorzystywanych przez innych pracowników kopalni; wykonywanie innych zadań pokrewnych.',422);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9312','Robotnicy wykonujący proste prace w budownictwie drogowym, wodnym i pokrewni','Robotnicy wykonujący proste prace w budownictwie drogowym, wodnym i pokrewni wykonują rutynowe zadania związane z budową i konserwacją dróg, linii kolejowych, tam i innych obiektów budownictwa lądowego.','Zadania wykonywane przez robotników wykonujących proste prace w budownictwie drogowym, wodnym oraz robotników pokrewnych zazwyczaj obejmują: kopanie i wypełnianie dołów i rowów przy użyciu narzędzi ręcznych; odgarnianie i rozpościeranie żwiru i pokrewnych materiałów; przycinanie i cięcie skał oraz powierzchni betonowych i bitumowych przy użyciu wiertarek udarowych pneumatycznych; ładowanie i rozładowywanie materiałów budowlanych, materiałów wydobytych oraz sprzętu i transportowanie ich w obrębie miejsc prowadzenia prac przy użyciu taczek i wózków ręcznych; sprzątanie miejsc pracy i usuwanie przeszkód; wykonywanie innych zadań pokrewnych; ewentualnie nadzorowanie innych pracowników.',423);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9313','Robotnicy wykonujący proste prace w budownictwie ogólnym','Robotnicy wykonujący proste prace w budownictwie ogólnym wykonują rutynowe zadania związane z pracami budowlanymi i rozbiórkowymi.','Zadania wykonywane przez robotników wykonujących proste prace w budownictwie ogólnym zazwyczaj obejmują: uprzątanie zużytych cegieł i wykonywanie innych prostych prac w miejscach rozbiórki; mieszanie, wylewanie i rozpościeranie materiałów, takich jak: beton, gips i zaprawa murarska; kopanie i wypełnianie dołów i rowów przy użyciu narzędzi ręcznych; rozpościeranie piasku, ziemi, żwiru i podobnych materiałów; ładowanie i rozładowywanie materiałów budowlanych, materiałów wydobytych oraz sprzętu i transportowanie ich w obrębie miejsc prowadzenia prac przy użyciu taczek, nosiłek do ręcznego transportu cegieł i wózków ręcznych; sprzątanie miejsc pracy i usuwanie przeszkód; wykonywanie innych zadań pokrewnych.',424);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9321','Ręczni pakowacze i znakowacze','Ręczni pakowacze i znakowacze zajmują się ręcznym ważeniem, pakowaniem i znakowaniem materiałów i produktów.','Zadania wykonywane przez ręcznych pakowaczy i znakowaczy zazwyczaj obejmują: ręczne ważenie, owijanie, pieczętowanie i pakowanie materiałów i rozmaitych produktów; ręczne umieszczanie produktów w butelkach, puszkach, pudełkach, torbach i innych pojemnikach; ręczne znakowanie produktów, opakowań i rozmaitych pojemników; wykonywanie innych zadań pokrewnych.',425);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9329','Robotnicy wykonujący proste prace w przemyśle gdzie indziej niesklasyfikowani','Robotnicy wykonujący proste prace w przemyśle gdzie indziej niesklasyfikowani asystują przy pracy robotników przemysłowych, rzemieślników, operatorów maszyn i monterów oraz wykonują rozmaite prace manualne, z wyłączeniem pakowania gotowych produktów.','Zadania wykonywane przez robotników wykonujących proste prace w przemyśle gdzie indziej niesklasyfikowanych zazwyczaj obejmują: przenoszenie i przewożenie surowców, materiałów, sprzętów, itp. na miejsce prowadzenia prac, usuwanie gotowych części; przygotowywanie materiałów i narzędzi; wykonywanie prostego, ręcznego demontażu różnych maszyn i urządzeń pod nadzorem osób wyżej wykwalifikowanych; mycie i czyszczenie zdemontowanych podzespołów i części; ręczne sortowanie produktów lub komponentów; ładowanie i rozładowywanie pojazdów, ciężarówek i wózków; czyszczenie maszyn, sprzętów i narzędzi; wykonywanie innych zadań pokrewnych na zlecenie nadzorującej kadry kierowniczej.',426);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9331','Prowadzący pojazdy napędzane siłą mięśni','Prowadzący pojazdy napędzane siłą mięśni prowadzą rowery i podobne pojazdy do transportu pasażerów lub towarów.','Zadania wykonywane przez prowadzących pojazdy napędzane siłą mięśni zazwyczaj obejmują: ładowanie i rozładowywanie towarów lub pomoc pasażerom we wsiadaniu i wysiadaniu z pojazdu; przemieszczanie pojazdu w pożądanym kierunku, przestrzegając przepisów drogowych i mając na uwadze innych uczestników ruchu;; sprawdzanie części składowych pojazdu w celu wykrycia śladów zużycia i zniszczeń; konserwacja pojazdów, wykonywanie drobnych napraw i instalowanie części zamiennych; pobieranie opłat za przejazd; wykonywanie innych zadań pokrewnych.',427);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9332','Prowadzący pojazdy ciągnięte przez zwierzęta','Prowadzący pojazdy ciągnięte przez zwierzęta prowadzą pojazdy ciągnięte przez zwierzęta w celu przewozu pasażerów lub towarów oraz maszyny ciągnięte przez zwierzęta, co zwykle ma związek z pracami rolnymi.','Zadania wykonywane przez prowadzących pojazdy ciągnięte przez zwierzęta zazwyczaj obejmują: zaprzęganie zwierząt i podczepianie ich do pojazdów lub maszyn; ładowanie i rozładowywanie towarów lub pomoc pasażerom we wsiadaniu i wysiadaniu z pojazdu; kierowanie zwierzętami w pożądanym kierunku, przestrzegając przepisów drogowych i mając na uwadze innych uczestników ruchu; pobieranie opłat za przejazd; kierowanie zwierzętami do ciągnięcia wózków w kopalniach lub kamieniołomach; kierowanie zwierzętami, do których podczepiono maszyny rolnicze lub inne; obrządzanie i karmienie zwierząt; konserwację pojazdów lub maszyn, wykonywanie drobnych napraw i instalowanie części zamiennych; wykonywanie innych zadań pokrewnych.',428);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9333','Robotnicy pracujący przy przeładunku towarów','Robotnicy pracujący przy przeładunku towarów wykonują takie czynności, jak: pakowanie, przenoszenie, ładowanie i rozładowywanie mebli i innych sprzętów gospodarstwa domowego lub ładowanie i rozładowywanie ładunków przeznaczonych do transportu drogą morską lub powietrzną lub innych ładunków, lub przenoszenie i sztaplowanie towarów w rozmaitych magazynach.','Zadania wykonywane przez robotników pracujących przy przeładunku towarów zazwyczaj obejmują: pakowanie mebli biurowych lub domowych, maszyn, urządzeń i pokrewnych towarów przeznaczonych do transportu; przenoszenie towarów do załadunku lub rozładunku z samochodów dostawczych, ciężarowych, wagonów, statków lub samolotów; ładowanie i rozładowywanie zboża, węgla, piasku i podobnych towarów poprzez umieszczanie ich na przenośnikach, w rurach itp.; łączenie przewodów pomiędzy głównymi brzegowymi instalacjami rurowymi a zbiornikami na barkach, tankowcach i innych statkach w celu załadunku i rozładunku ropy naftowej, gazów płynnych i innych cieczy; przenoszenie i sztaplowanie towarów w magazynach i podobnych zakładach; sortowanie ładunków przed załadunkiem i rozładunkiem; wykonywanie innych zadań pokrewnych.',429);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9334','Układacze towarów na półkach','Układacze towarów na półkach rozmieszczają towary na półkach i witrynach i utrzymują towary w czystości i porządku w supermarketach i innych punktach sprzedaży detalicznej i hurtowej.','Zadania wykonywane przez układaczy towarów na półkach zazwyczaj obejmują: staranne układanie towarów w koszach i na stojakach oraz towarów masywnych – w stosach na podłodze; układanie towarów na półkach; usuwanie towarów o przekroczonym okresie przydatności; utrzymywanie porządku na półkach poprzez usuwanie towarów przynależnych do innych działów; odnotowywanie towarów sprzedanych i pobieranie potrzebnych towarów z magazynu; pobieranie artykułów z półek lub magazynu na prośbę klienta; udzielanie klientom informacji i kierowanie ich do odpowiednich działów; przyjmowanie, otwieranie, rozpakowywanie i sprawdzanie pod kątem uszkodzeń towarów otrzymanych od wytwórcy lub dystrybutora: wykonywanie innych zadań pokrewnych.',430);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9411','Pracownicy przygotowujący posiłki typu fast food','Pracownicy przygotowujący posiłki typu fast food przygotowują niewielki asortyment posiłków i napojów na zamówienie, których przygotowanie jest proste i wymaga użycia niewielkiej liczby składników. Mogą oni przyjmować zamówienia od klientów i podawać gotowe posiłki przy bufecie lub do stołów.','Zadania wykonywane przez pracowników przygotowujących posiłki typu fast food zazwyczaj obejmują: przygotowywanie prostych i gotowych posiłków i napojów, takich jak: kanapki, pizza, ryba z frytkami, sałatki i kawa, itp.; mycie, krojenie, odmierzanie i mieszanie składników przeznaczonych do gotowania; obsługa wyspecjalizowanego sprzętu gotującego, takiego jak; grille, frytkownice lub blachy do pieczenia; odgrzewanie gotowej żywności; sprzątanie pomieszczeń, w których przygotowywane są posiłki, powierzchni i przyborów do gotowania; przyjmowanie i obsługę zamówień na posiłki i napoje w jadłodajniach specjalizujących się w serowaniu dań typu fast food lub na wynos; zamawianie i przyjmowanie dostaw składników do dań typu fast food; utrzymywanie norm sanitarnych, zdrowotnych i bezpieczeństwa w miejscach pracy; sprawdzanie czy przygotowana żywność spełnia wymogi jakościowe i ilościowe; wykonywanie innych zadań pokrewnych.',431);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9412','Pomoce kuchenne','Pomoce kuchenne zajmują się sprzątaniem stołów, powierzchni kuchennych, zmywaniem naczyń, przygotowywaniem składników i wykonywaniem innych obowiązków, w celu pomocy pracownikom przygotowującym lub serwującym posiłki i napoje.','Zadania wykonywane przez pomoce kuchenne zazwyczaj obejmują: sprzątanie kuchni, obszarów, w których przygotowywane są posiłki i miejsc obsługi; pomoc kucharzom i szefom kuchni w przygotowywaniu posiłków poprzez mycie, obieranie, siekanie, krojenie, odmierzanie i mieszanie składników; układanie potraw gotowych do serwowania; rozpakowywanie i przechowywanie zasobów w lodówkach, kredensach i innych powierzchniach magazynowych, ich sprawdzanie, przenoszenie i ważenie; zmywanie naczyń i przyborów do gotowania i odkładanie ich na miejsce; przygotowywanie, gotowanie, opiekanie i podgrzewanie prostych produktów żywnościowych; wykonywanie innych zadań pokrewnych.',432);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9510','Pracownicy świadczący usługi na ulicach','Pracownicy świadczący usługi na ulicach świadczą rozmaite usługi na ulicach i w innych miejscach publicznych, takie jak: czyszczenie butów, mycie szyb samochodowych, wykonywanie zadań jako osoba na posyłki, doglądanie cudzej własności i świadczenie innych usług na ulicach.','Zadania wykonywane przez pracowników świadczących usługi na ulicach zazwyczaj obejmują: przyjmowanie materiałów niezbędnych do świadczenia usług; podchodzenie do ludzi na ulicach w celu oferowania towarów lub usług; czyszczenie i polerowanie butów; mycie i polerowanie szyb samochodowych; wykonywanie zadań jako osoba na posyłki; pomoc kierowcom w znajdowaniu miejsc do parkowania i pilnowanie, aby samochód nie został zniszczony pod nieobecność kierowcy; wręczanie ulotek i darmowych gazet; rozlepianie afiszy; przyjmowanie płatności od ręki; wykonywanie innych zadań pokrewnych.',433);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9520','Uliczni sprzedawcy produktów (z wyłączeniem żywności o krótkim terminie przydatności do spożycia)','Uliczni sprzedawcy produktów (z wyłączeniem żywności o krótkim terminie przydatności do spożycia) zwykle ograniczony asortyment towarów (w tym także gotowych, niepsujących się produktów żywnościowych, jak np. wyroby cukiernicze, z wyłączeniem żywności do natychmiastowego spożycia) na ulicach i w miejscach publicznych, takich jak: dworce, kina, teatry, itp.','Zadania wykonywane przez ulicznych sprzedawców produktów (z wyłączeniem żywności o krótkim terminie przydatności do spożycia) zazwyczaj obejmują: nabywanie lub otrzymywanie produktów przeznaczonych do sprzedaży lub wytwarzanie prostych produktów; ładowanie i rozładowywanie kosza, koszyka, wózka, roweru, wózka ręcznego lub innego pojazdu do transportu towarów na ulice i do miejsc publicznych, takich jak: dworce, kina, itp.; pokazywanie towarów lub namawianie do ich kupna w celu przyciągnięcia uwagi klienta; podchodzenie do potencjalnych klientów na ulicach i oferowanie towarów przeznaczonych do sprzedaży; przyjmowanie płatności od ręki; wykonywanie innych zadań pokrewnych.',434);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9611','Ładowacze nieczystości','Ładowacze nieczystości usuwają odpadki i zbierają produkty nadające się do recyklingu z budynków, podwórek, ulic i innych miejsc oraz usuwają nieczystości płynne.','Zadania wykonywane przez ładowaczy nieczystości zazwyczaj obejmują: zbieranie odpadków i materiałów nadających się do ponownego wykorzystania i umieszczanie ich w pojemnikach na odpady oraz w śmieciarkach i pojazdach do przewożenia materiałów nadających się do ponownego wykorzystania; przemieszczanie się na lub w śmieciarkach, pojazdach do przewożenia materiałów nadających się do ponownego wykorzystania i pojazdach do przewożenia nieczystości płynnych; podnoszenie pojemników na odpady i opróżnianie ich do ciężarówek i większych pojemników; usuwanie nieczystości płynnych; rozładowywanie pojemników na odpady, pojazdów do przewożenia materiałów nadających się do ponownego wykorzystania i pojazdów do przewożenia nieczystości płynnych: wykonywanie innych zadań pokrewnych.',435);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9612','Sortowacze odpadów','Sortowacze odpadów identyfikują, zbierają i sortują odrzucone produkty nadające się do przetworzenia – na wysypisku śmieci i w zakładach recyklingu oraz w budynkach, na ulicach i w miejscach publicznych.','Zadania wykonywane przez sortowaczy odpadów zazwyczaj obejmują: przeszukiwanie odpadów i zbieranie produktów nadających się do przetworzenia na wysypiskach śmieci, z obiektów prywatnych, handlowych i przemysłowych lub z miejsc publicznych, np. z ulic; sortowanie odpadów na poszczególne rodzaje, takie jak: karton, papier, szkło, tworzywa sztuczne, aluminium lub inne materiały dające się ponownie wykorzystać; umieszczanie wysortowanych odpadów w wyznaczonych pomieszczeniach i pojemnikach do magazynowania lub transportu; identyfikację i odkładanie produktów meblarskich, sprzętów, maszyn lub komponentów możliwych do naprawiania lub ponownego wykorzystania; transport materiałów dających się ponownie wykorzystać, ręcznie lub za pomocą pojazdów niesilnikowych; sprzedaż materiałów nadających się do ponownego wykorzystania lub do wielokrotnego użytku; wykonywanie innych zadań pokrewnych.',436);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9613','Zamiatacze i pokrewni','Zamiatacze i pokrewni zamiatają i sprzątają ulice, parki, lotniska, dworce i inne miejsca publiczne.','Zadania wykonywane przez zamiataczy i pokrewnych pracowników zazwyczaj obejmują: zamiatanie ulic, parków, lotnisk, dworców i innych miejsc publicznych; trzepanie dywanów przy użyciu trzepaczki; sprzątanie śmieci, liści i śniegu z dróg dojazdowych i gruntów; wykonywanie innych zadań pokrewnych.',437);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9621','Gońcy, bagażowi i pokrewni','Gońcy, bagażowi i pokrewni zajmują się noszeniem i dostarczaniem wiadomości, paczek i innych przedmiotów w obrębie instytucji lub pomiędzy instytucjami, do gospodarstw domowych i innych miejsc, noszeniem bagaży w hotelach, na dworcach i lotniskach, przenoszenie rannych lub chorych na noszach.','Zadania wykonywane przez gońców, bagażowych i pokrewnych pracowników zazwyczaj obejmują: dostarczanie wiadomości, paczek i innych przedmiotów w obrębie instytucji, pomiędzy instytucjami lub do innych miejsc; wykonywanie obowiązków gońca pocztowego; dostarczanie różnych towarów do i z przedsiębiorstw, sklepów, gospodarstw domowych, itp.; noszenie i dostarczanie bagaży w hotelach, na dworcach, lotniskach i w innych miejscach; odbieranie i znaczenie bagaży poprzez wypełnianie i dołączanie kwitów; planowanie i korzystanie z najbardziej efektywnej trasy; sortowanie produktów przeznaczonych do dostarczenia według trasy dostaw; przemieszczanie osób rannych lub chorych; przenoszenie towarów wewnątrz budynków lub za pomocą windy; wykonywanie innych zadań pokrewnych.',438);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9622','Pracownicy wykonujący dorywcze prace proste','Pracownicy wykonujący dorywcze prace proste podejmują się wykonywania różnorodnych prac, na przykład takich jak: statystowanie przy produkcji filmu, wykonywanie prostych napraw, malowanie budynków i innych obiektów, rozładowywanie stałych materiałów opałowych i umieszczanie ich w piwnicach.','Zadania wykonywane przez pracowników wykonujących dorywcze prace proste zazwyczaj obejmują: występowanie przy produkcji filmu w podrzędnej roli, głównie w scenach zbiorowych; naprawianie rozbitych szyb, ekranów, zepsutych drzwi, ogrodzeń, grillów, stolików ogrodowych, półek, kredensów i innych przedmiotów; wymianę niesprawnych przedmiotów, takich jak: żarówki; naprawę i malowanie powierzchni wewnętrznych i zewnętrznych, jak np.: ściany, sufity i ogrodzenia; regulowanie drzwi i okien; wymianę uszczelek; mocowanie prowadnic i uchwytów; rozładowywanie węgla lub drewna i umieszczanie ich w piwnicach prywatnych gospodarstw domowych lub przedsiębiorstw; wykonywanie innych zadań pokrewnych.',439);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9623','Odczytujący liczniki i wybierający monety z automatów','Odczytujący liczniki i wybierający monety (żetony) z automatów, zaopatrują automaty sprzedające i wybierają z nich lub z parkometrów i innych pojemników monety (żetony) oraz dokonują odczytów liczników zużycia elektryczności, gazu i wody.','Zadania wykonywane przez odczytujących liczniki i wybierających monety z automatów zazwyczaj obejmują: zaopatrywanie automatów do sprzedaży w produkty i wybieranie monet (żetonów) z ich pojemników; wybieranie monet (żetonów) z parkometrów i podobnych pojemników na monety; prowadzenie rejestrów dostarczonych towarów i pobranych monet; odczytywanie liczników zużycia elektryczności, gazu lub wody i zapisywanie odczytów; poruszanie się po ustalonych trasach w celu dokonania odczytów z urządzeń pomiarowych; sprawdzanie odczytów w przypadkach, gdy wydaje się, że zużycie odbiega od normy i zapisywanie możliwych przyczyn odchyleń; sprawdzanie liczników pod kątem nieuprawnionych podłączeń, usterek i uszkodzeń, takich jak złamane pieczęcie, itp.; wykonywanie innych zadań pokrewnych.',440);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('9629','Pracownicy przy pracach prostych gdzie indziej niesklasyfikowani','Grupa ta obejmuje pracowników przy pracach prostych, niesklasyfikowanych w innych podgrupach grupy 9 Pracownicy przy pracach prostych. Grupa ta obejmuje na przykład osoby wystawiające i zbierające bilety za parkowanie lub bilety wstępu, podające osobiste przedmioty klientom lub interesantom w szatniach, pomagają klientom podczas imprez rozrywkowych, itp.','Zadania wykonywane przez pracowników przy pracach prostych gdzie indziej niesklasyfikowanych zazwyczaj obejmują: sprzedaż i zbieranie biletów wstępu oraz przepustek od klientów podczas imprez rozrywkowych; sprawdzanie biletów lub przepustek w celu zweryfikowania autentyczności, kierowanie klientów do wyjść lub podawanie innych instrukcji, świadczenie pomocy w sytuacjach zagrożenia; kierowanie klientów, np. do stoisk, telefonów, itp.; kierowanie prowadzących pojazdy na miejsca parkingowe; patrolowanie parkingów w celu zapobieżenia uszkodzeń pojazdów i kradzieży; naliczanie opłat za postój i pobieranie opłat od klientów; prowadzenie szatni, przechowalni bagażu lub toalety; przydzielanie miejsc w przebieralniach, szafek lub pojemników na ubrania klientom siłowni, basenów, itp.; wykonywanie innych zadań pokrewnych.',441);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('0110','Oficerowie sił zbrojnych','Oficerowie sił zbrojnych sprawują funkcję dowódczą, przywódczą i menedżerską na stanowiskach służbowych w jednostkach wojskowych w ramach Sił Zbrojnych RP oraz jednostkach organizacyjnych poza resortem obrony narodowej. Oficerowie posiadają stopnie wojskowe zgodnie ze stopniami wojskowymi zaszeregowanymi dla danego stanowiska służbowego, na które zostali wyznaczeni. Grupa oficerów obejmuje wszystkich żołnierzy zawodowych Sił Zbrojnych posiadających stopnie wojskowe od porucznika do generała.','Zadania wykonywane przez oficerów sił zbrojnych zazwyczaj obejmują: dowodzenie podległym pododdziałem (zespołem, formacją) w działaniach bojowych oraz w warunkach pokojowego funkcjonowania wojska, wywieranie wpływu na skuteczność oraz sprawność działań wojskowych pododdziału lub całej formacji, motywowanie do skutecznego oraz sprawnego funkcjonowania jednostki, organizowanie i zarządzanie realizowanymi przedsięwzięciami. Do ich zadań może także należeć sprawowanie funkcji specjalistyczno-wojskowych typowych dla lekarzy, prawników, nauczycieli akademickich itp.',442);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('0210','Podoficerowie sił zbrojnych','Podoficerowie sił zbrojnych pełnią w Siłach Zbrojnych RP funkcje dowódcze, szkoleniowe i wychowawcze w stosunku do podległych żołnierzy na szczeblu grupy, załogi, sekcji, urządzenia i drużyny oraz plutonu. Odpowiadają za jakość merytoryczną i metodyczną wyszkolenia i wychowania podległych żołnierzy szeregowych. Grupa ta obejmuje wszystkich żołnierzy zawodowych Sił Zbrojnych posiadających co najmniej stopień kaprala do stopnia starszego chorążego sztabowego włącznie.','Zadania wykonywane przez podoficerów sił zbrojnych zazwyczaj obejmują: dowodzenie pododdziałem podległych żołnierzy szeregowych w działaniach bojowych oraz w warunkach pokojowego funkcjonowania wojska, sprawowanie funkcji szkoleniowych i wychowawczych, wywieranie wpływu na skuteczność oraz sprawność działań wojskowych pododdziału, motywowanie żołnierzy szeregowych do skutecznego oraz sprawnego funkcjonowania jednostki. Do ich zadań może także należeć pełnienie funkcji magazynierów, intendentów, referentów itp.',443);
-- INSERT INTO elementary_group(code,name,synthesis,tasks,id) VALUES ('0310','Żołnierze szeregowi','Żołnierze szeregowi posiadają stopień szeregowego lub starszego szeregowego i stanowią największy korpus kadry zawodowej Sił Zbrojnych RP.','Zadania wykonywane przez żołnierzy szeregowych zazwyczaj obejmują: wykonywanie poleceń podoficerów w działaniach bojowych oraz w warunkach pokojowego funkcjonowania wojska, uczestniczenie w różnych misjach wojskowych, uczestniczenie w szkoleniach, musztrach, paradach wojskowych z okazji różnych uroczystości państwowych. Do ich zadań może także należeć pełnienie funkcji kierowców, mechaników, operatorów sprzętu wojskowego, udzielanie pomocy ludności cywilnej w pożarach, powodziach i innych kataklizmach przyrody. Żołnierze szeregowi oprócz zadań typowo wojskowych, wykonują również miedzy innymi funkcje: kierowców, operatorów sprzętu, specjalistów.',444);

